INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(3, 1, 1, 1, 1, 'Xả vai ác nữ Penthouse, Kim So Yeon về lại bên chồng \"real\", gây choáng với mặt mộc khác xa trên phim', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617790594081-1617790594227922635025.jpg', '|', 'Mặt mộc của nữ minh tinh Kim So Yeon sau khi \"xả vai\" khiến dân mạng không khỏi choáng váng và thích thú bàn tán.', '<p>\"Ác nữ Penthouse\" Kim So Yeon nổi tiếng với vẻ đẹp sắc sảo, ánh mắt \"như dao cau khoét vào mỏm đá\", lườm cú nào là dân tình sợ hãi cú đấy. Thế nhưng ở ngoài đời, cô lại là người có tính cách khác hoàn toàn.</p><p>Mới đây, trên trang cá nhân, Lee Sang Woo - chồng tài tử của Kim So Yeon đã đăng tải ảnh của 2 vợ chồng với chú thích: \"Em đi lâu quá rồi, chúng ta quay trở lại nhé\". Có thể thấy rằng \"ác nữ\" hiện đã xả vai sau khi kết thúc phần 2 của bộ phim. Về nhà nghỉ ngơi bên ông xã, nữ minh tinh đình đám chỉ diện trang phục thoải mái và đặc biệt là để mặt mộc. Nhưng mặt mộc xinh đẹp cùng biểu cảm dễ thương của Kim So Yeon đã khiến netizen phát sốt vì \"cưng xỉu\", khác xa với gương mặt dữ dằn trên phim Penthouse. Trông cô cũng trẻ ra trông thấy vì không có lớp trang điểm đậm, nhấn điểm dữ dằn giống trong phim.</p><p>Kim So Yeon vốn có gương mặt trưởng thành trước tuổi, từng bị \"ném đá\" vì tưởng gian lận tuổi khi nhận giải diễn viên nhí. Nhờ vẻ ngoài sắc sảo, cô chuyên trị loại vai phản diện và thậm chí còn được mệnh danh là \"ác nữ đẹp nhất màn ảnh Hàn Quốc\". Nhưng khi ở ngoài đời, khi bỏ đi lớp trang điểm dày cộp, Kim So Yeon khiến dân tình mê mẩn với vẻ đẹp tươi tắn và khả năng \"hack tuổi\" đỉnh cao.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/bbbjovi1690670161947822722073002425239601621685864n-1-1617788616847903348333.jpg\" ><figcaption class=\"caption\">Trong ảnh chụp chung với ông xã mới đây, Kim So Yeon gây xôn xao vì để mặt mộc, lộ nhan sắc xinh đẹp ngút ngàn và vẻ đáng yêu...</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/kimso-16177894206511720283332.gif.mp4\" ></video><div class=\"caption\">... khác xa với vẻ ngoài như muốn \"ăn tươi nuốt sống\" đối phương trong phim</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/sysysy110241326449270242933611368229186367100664674n-1617790149681526307718.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/kim-so-yeon-smile-face-1617790149652346868315.jpg\" ><figcaption class=\"caption\">Nhan sắc tự nhiên khiến công chúng mê đắm của Kim So Yeon</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/sysysy110237838538243114112984586562672249425887232n-1617790149666253884764.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/sysysy1102471072144908243246569215941873121639357231n-16177901496921876482882.jpg\" ><figcaption class=\"caption\">Ở ngoài đời, Kim So Yeon nhìn trẻ trung và hiền lành, dễ thương hơn rất nhiều</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/bbbjovi192280331219175217371114227054938108198912n-16177904335301300055472.jpg\" ><figcaption class=\"caption\">Khi chụp bên ông xã, \"ác nữ\" càng tươi không cần tưới, không hề dữ dằn chút nào</figcaption></figure><p>Nguồn: Instagram</p>', NULL, 'xa-vai-ac-nu-penthouse-kim-so-yeon-ve-lai-ben-chong-real-gay-choang-voi-mat-moc-khac-xa-tren-phim', 0, NULL, NULL),
(4, 1, 1, 1, 1, 'NS Thương Tín chính thức lộ diện hậu sửa sang nhan sắc, nụ cười tươi tắn nói lên tất cả', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617785370676-16177853708591878357946.jpg', '|', 'NS Thương Tín đã thực hiện trùng tu nhan sắc sau khi ra viện.', '<p>Ngoài số tiền dùng để đóng bảo hiểm cho con, sửa sang nhà cửa, NS Thương Tín còn trích một khoản tiền ủng hộ để sửa sang nhan sắc, cụ thể là làm răng sứ. NS Trịnh Kim Chi là người đã dẫn NS Thương Tín đến gặp nha sĩ để thực hiện được nguyện vọng này của mình.</p><p>Cho tới sáng ngày 7/4, hình ảnh hé lộ diện mạo mới nhất của NS Thương Tín hậu sửa sang nhan sắc đã được NS Trịnh Kim Chi đăng tải lên trang cá nhân. Trong khung hình, nam nghệ sĩ xuất hiện với nụ cười tươi tắn, thần sắc của ông tốt hơn hẳn những ngày mới ra viện.  Điều này khiến khán giả không khỏi vui mừng và yên tâm phần nào về tình hình sức khoẻ của NS Thương Tín. Rất đông lời chúc và động viên tinh thần được gửi tới NS Thương Tín dưới phần bình luận.</p><p>\"Nhìn nghệ sĩ Thương Tín hôm nay khác hẳn nha. Chi cũng đã xong nhiệm vụ làm cầu nối những tấm lòng của mọi người giúp đỡ anh Tín rồi ạ. Chi nghĩ với những sự giúp đỡ này anh Tín sẽ rất trân trọng, sẽ biết quý cuộc sống có trách nhiệm với cuộc sống của anh hơn. Sống khoẻ, sống tốt\", NS Trịnh Kim Chi trải lòng.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177806730912332922.jpg\" ><figcaption class=\"caption\">NS Trịnh Kim Chi công khai hình ảnh mới nhất của NS Thương Tín</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617780667361884240211.jpg\" ><figcaption class=\"caption\">Theo đó, ông xuất hiện tươi tắn và rạng rỡ hẳn lên sau khi làm răng sứ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/trinhkimchithuongtin-1617781837388748730301.jpg\" ><figcaption class=\"caption\">NS Trịnh Kim Chi luôn đồng hành cùng NS Thương Tín suốt thời gian ông gặp khó khăn</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177814791761940059482.jpg\" ><figcaption class=\"caption\">Hình ảnh trước khi làm răng của NS Thương Tín</figcaption></figure><p>Ảnh: Facebook nhân vật </p>', NULL, 'ns-thuong-tin-chinh-thuc-lo-dien-hau-sua-sang-nhan-sac-nu-cuoi-tuoi-tan-noi-len-tat-ca', 0, NULL, NULL),
(5, 1, 1, 1, 1, 'Giữa nghi vấn toang, Matt Liu bất ngờ lộ tình trạng hiện tại với Hương Giang và còn có \"hint\" cưới xin?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617784382442-1617784382845920166308.jpg', '|', 'Chỉ qua 1 dòng bình luận từ anh bạn thân Tống Đông Khuê, fan của couple Hương Giang và Matt Liu được dịp rần rần.', '<p>Nói đến những đôi phát \"cẩu lương\" ngọt lịm thì không thể bỏ qua Matt Liu và Hương Giang được. Ấy thế mà gần 1 tháng nay cặp đôi không còn tương tác trên MXH, \"nữ hoàng chuyển giới\" như lui về ở ẩn, còn Matt Liu lẻ bóng đi đánh golf cùng hội bạn thân.</p><p>Bỏ qua 7749 lần bị soi \"hint\" nghi vấn toang với Hương Giang, Matt Liu mới đây vẫn thoải mái tương tác phía dưới bức ảnh bạn thân Tống Đông Khuê chúc mừng sinh nhật người yêu. Đáng nói ở phần bình luận, khi Matt Liu gửi lời chúc và hối thúc cặp đôi nhanh chóng có \"happy ending\", Tống Đông Khuê liền giục Matt Liu nhanh chóng tính chuyện cưới xin: \"Cưới trước đi, đừng có chờ đợi nữa\".</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo2021-04-0715-08-43-1617784330109770617102.jpg\" ><figcaption class=\"caption\">Matt Liu vừa gửi lời chúc: \"Chúc mừng sinh nhật Nhi. Đợi lời mời ăn cưới của hai người sớm\", bạn thân liền hối thúc ngược nam CEO nên tính chuyện cưới xin trước</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/liu-3-1617784356726185124429.jpg\" ><figcaption class=\"caption\">Chỉ với chi tiết này, dân tình cho rằng có lẽ Matt Liu và Hương Giang vẫn đang mặn nồng, chỉ là hạn chế thể hiện tình cảm công khai như trước đây. Bởi lẽ nếu Matt Liu đang độc thân thì bạn thân sẽ không hối chuyện trọng đại như thế kia</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/tu-vu-viec-huong-giang-bi-tay-chay-dung-ca-gian-mat-khon-eca-5347541-16177843561701703387506.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/mattllh1496829424243431720105621490365220896466074n-1615204604149666648371-1617784356351946349647.jpg\" ><figcaption class=\"caption\">Dân tình đang chờ đợi người trong cuộc lên tiếng hoặc đăng 1 bức ảnh ngọt ngào để yên tâm rằng cặp đôi vẫn đang viên mãn</figcaption></figure><p>Ai đời lại đi chúc người đang độc thân sớm cưới đúng không? Qua chi tiết này, netizen cho rằng có thể Matt Liu và Hương Giang vẫn đang âm thầm bên nhau, chỉ là hạn chế phát \"cẩu lương\" hơn trước.</p>', NULL, 'giua-nghi-van-toang-matt-liu-bat-ngo-lo-tinh-trang-hien-tai-voi-huong-giang-va-con-co-hint-cuoi-xin', 0, NULL, NULL),
(6, 1, 1, 1, 1, 'Diễn biến đảo chiều 180 độ từ phiên toà thứ 2: Trương Hằng lươn lẹo, lộ quá nhiều sơ hở trước luật sư của Trịnh Sảng', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617785096097-16177850962521583954453.jpeg', '|', 'Nhiều netizen không khỏi ngán ngẩm và cho rằng Trương Hằng càng ngày càng lộ rõ sơ hở trước những tình tiết quan trọng.', '<p>Phiên toà thứ 2 tranh giành quyền nuôi con của Trương Hằng và Trịnh Sảng đang trở thành mối quan tâm lớn của làng giải trí xứ Trung ngày hôm nay. Điều đáng nói, nếu như ở phiên toà thứ nhất, Trịnh Sảng tỏ ra ấp úng, nói dối nhiều chi tiết, thì sang phiên toà thứ 2, đối diện với luật sư của bạn gái cũ, Trương Hằng lộ ra quá nhiều sơ hở, thậm chí bị lên án là kẻ nói dối, lươn lẹo.</p><p>Theo đó, Trương Hằng thừa nhận rằng Trịnh Sảng có chủ động trong việc trao đổi với anh về vấn đề của 2 đứa con, ngoài ra còn trao đổi về cách thức đưa 2 đứa trẻ trở về Trung Quốc. Tuy nhiên, Trương Hằng khẳng định lại rằng, động thái này của Trịnh Sảng chỉ được bắt đầu kể từ khi những bê bối của nữ diễn viên liên tiếp bị phanh phui trên MXH.</p><p>Trịnh Sảng có yêu cầu Trương Hằng không đưa hình ảnh và thông tin của con lên MXH, tuy nhiên Trương Hằng không đáp ứng theo yêu cầu này. Trương Hằng liên tục trả lời \"không biết\" khi được hỏi liệu có biết rằng việc đăng ảnh của con cái sẽ tác động tiêu cực tới sự phát triển của 2 đứa trẻ sau này hay không. Anh cho rằng, việc mình làm giống như những ông bố bà mẹ khác thích đăng ảnh của con cái lên MXH, không hề biết rằng việc này lại thu hút sự chú ý lớn từ phía dư luận Trung Quốc.</p><p>Đáng chú ý, khi thẩm phán đặt câu hỏi về thời gian 2 đứa trẻ ngủ trưa là bao lâu, Trương Hằng ấp úng không trả lời được. Cnet cho rằng, điều này là quá vô lý bởi Trương Hằng luôn xây dựng hình ảnh là người cha chăm con tận tụy, vậy nhưng anh lại không biết đến điều cơ bản này của cả 2 đứa con, chứng tỏ Trương Hằng không thành thật.</p><p>Khi Trịnh Sảng đặt câu hỏi có phải Trương Hằng đang lừa dối cô, liệu có đang ở nước ngoài và muốn biết vị trí hiện tại của bạn trai đang ở đâu, tuy nhiên Trương Hằng từ chối cung cấp thông tin. Sau đó, khi Trịnh Sảng hỏi về việc có quay trở lại Trung Quốc sau khi dịch bệnh qua đi hay không, Trương Hằng khẳng định muốn để mọi việc tự nhiên, chưa có bất cứ kế hoạch nào. Trên MXH, nhiều người cảm thấy khó hiểu với câu trả lời của Trương Hằng, điều này có nghĩa kể cả khi Trịnh Sảng có ký giấy để 2 con về nước, chưa chắc Trương Hằng đã đưa 2 con về Trung Quốc sinh sống.</p><p>Trương Hằng nhiều lần từ chối tham gia các nhóm chat được Trịnh Sảng mời vào. Lý giải về việc này, Trương Hằng cho biết vì trong nhóm có nhiều người anh không biết, việc này không liên quan đến quyền lợi của 2 đứa con.</p><p>Trương Hằng từng 2 lần yêu cầu người mang thai hộ quay video kể về diễn biến sự việc, mục đích là để ngăn chặn Trịnh Sảng giở trò. CEO này cho hay, anh biết Trịnh Sảng là người giàu có, sẵn sàng bỏ tiền để mua bằng chứng người khác làm giả. Vì sợ rằng Trịnh Sảng sẽ mua chuộc 2 người mang thai hộ nên Trương Hằng phải hành động trước để đảm bảo mình không rơi vào thế bất lợi.</p><p>Phía Trịnh Sảng đặt câu hỏi về việc vì sao sau 1 lần liên lạc không thành công với nữ diễn viên, Trương Hằng lại chọn cách liên tục giữ im lặng, CEO này trả lời rằng với tư cách là một người mẹ thì Trịnh Sảng phải chủ động tìm hiểu thông tin về đứa trẻ. Đó là quyền và nghĩa vụ cơ bản của Trịnh Sảng, không cần đến anh.</p><p>Đoạn hội thoại giữa luật sư của Trịnh Sảng và Trương Hằng gây chú ý như sau:</p><p>Luật sư của Trịnh Sảng: Trong phiên toà trước, anh có nói anh rất lo lắng cho vấn đề tinh thần của Trịnh Sảng đúng không?</p><p>Trương Hằng: Đúng.</p><p>Luật sư của Trịnh Sảng: Có phải anh nói rằng trong khoảng thời gian từ 2018 - 2020, Trịnh Sảng có khuynh hướng muốn tự sát?</p><p>Trương Hằng: Từ năm 2018 - 2019, cô ấy đúng là như vậy (bật cười).</p><p>Luật sư của Trịnh Sảng: Có phải trong năm 2018, anh và Trịnh Sảng đồng ý việc mang thai hộ?</p><p>Trương Hằng: Đúng.</p><p>Luật sư của Trịnh Sảng: Vậy là anh vốn không quan tâm tới tình trạng tinh thần của Trịnh Sảng, anh không hề dừng lại việc mang thai hộ?</p><p>Trương Hằng (ngập ngừng một lúc): Đúng.</p><p>Luật sư của Trịnh Sảng: Anh có yêu cầu Trịnh Sảng đi giám định lại vấn đề tâm lý không?</p><p>Trương Hằng: Vấn đề này tôi muốn hỏi luật sư của mình. Tôi cảm thấy trạng thái tinh thần của cô ấy và việc cô ấy vứt bỏ việc nuôi con không hề liên quan đến nhau.</p><p>Đến đoạn hội thoại giữa Trương Hằng và thẩm phán:</p><p>Thẩm phán: Anh biết Trịnh Sảng có khuynh hướng tự sát và vấn đề về tâm lý, vậy tại sao vẫn cùng cô ấy tìm người mang thai hộ?</p><p>Luật sư của Trương Hằng phản đối câu hỏi.</p><p>Thẩm phán bác bỏ yêu cầu từ phía luật sư Trương Hằng.</p><p>Trương Hằng: Thứ nhất tôi cảm thấy người có vấn đề về tâm lý vẫn có quyền muốn sinh con. Thứ hai, sau khi tôi và cô ấy hẹn hò, tôi là người dẫn cô ấy đi theo phương hướng tốt, khiến cô ấy trở nên tốt đẹp hơn. Thứ ba, lúc ở bên nhau, tôi rất yêu cô ấy.</p><p>Trên MXH, netizen xứ Trung bắt đầu \"đảo chiều\": \"Trương Hằng cũng chẳng phải tay vừa\", \"Tôi thấy anh ta muốn lợi dụng 20 triệu tệ (70 tỷ đồng) của Trịnh Sảng thì đúng hơn\", \"Ả thì xảo quyệt, anh đây cũng nào có vừa\", \"Lươn lẹo quá, thật không còn gì để nói\", \"Những câu hỏi quan trọng anh này đều lấp liếm, toàn \'Tôi không biết\' với \'Tôi không quan tâm\'\", \"Trương Hằng thấy Trịnh Sảng có tiền nên định dùng con cái để uy hiếp\", \"Anh ta chỉ đợi 2 đứa con được sinh ra rồi lấy đó làm công cụ uy hiếp Trịnh Sảng mà thôi\",...</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177846471702017264299.jpeg\" ><figcaption class=\"caption\">Hình ảnh Trịnh Sảng xuất hiện tại phiên toà lần 1</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177847046601909626337.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/-1617784796558578084239.jpg\" ><figcaption class=\"caption\">Trương Hằng liên tục trả lời: \"Tôi không biết\", \"Tôi không rõ\", \"Tôi không quan tâm\", \"Tôi uống say nên tôi không biết\" trước những câu hỏi do luật sư Trịnh Sảng đưa ra</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/-16177846129341560864835.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn: Weibo, Sina, BJH</p>', NULL, 'dien-bien-dao-chieu-180-do-tu-phien-toa-thu-2-truong-hang-luon-leo-lo-qua-nhieu-so-ho-truoc-luat-su-cua-trinh-sang', 0, NULL, NULL),
(7, 1, 1, 1, 1, 'BB Trần lên tiếng vì bị kỳ thị giới tính, giải thích gọn lỏn 3 câu mà netizen đọc xong cũng \"lú\" theo', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617780865448-1617780865853350524680.jpg', '|', 'BB Trần lạ quá, đáp trả căng nhưng nghe vẫn không quên thể hiện tính cách chuẩn “thánh lầy”.', '<p>BB Trần là 1 trong những nghệ sĩ rất thoải mái công khai xu hướng tính dục với công chúng. Được biết, nam diễn viên đang có cuộc tình vô cùng viên mãn với người yêu đồng tính Quang Lâm. Tuy nhiên, cách đây ít phút BB Trần vừa lên tiếng thể hiện rõ thái độ khó chịu khi bị antifan mỉa mai giới tính và chỉ trích về việc anh hay \"chơi dơ\" trong các gameshow.</p><p>Trên trang cá nhân, BB Trần đã chụp lại bình luận của 1 netizen có nội dung: \"BB Trần đã ăn gian rồi còn bê đê nữa, em ghét BB bê đê, dơ cướp đồ người ta\". Ngay lập tức, nam diễn viên lầy lội vạch rõ 3 ý để đáp lại antifan: \"Câu này ẩn ý quá, vậy nghĩa là: Ăn gian thì không được bê đê, đã bê đê thì đừng chơi ăn gian hay giới bê đê và ăn gian đều là cái tội\". Tuy nhiên, đọc xong status của BB Trần, dân mạng phải \"lú\" theo vì pha đáp trả quá cồng kềnh.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo2021-04-0715-23-03-1617783860576422323250.jpg\" ><figcaption class=\"caption\">BB Trần bức xúc lên tiếng khi bị kỳ thị giới tính, đáp trả nhưng vẫn không quên lầy lội \"thả miếng hài\"</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/170166604102163011893628808255008102657653429n-1617780659490760208454.jpg\" ><figcaption class=\"caption\">BB Trần là nam nghệ sĩ khá thoải mái công khai xu hướng tính dục, đây không phải lần đầu anh gặp những bình luận ác ý như thế này</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/164505040102162575317114664316755408401165714n-16177806593001896959818.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Không chỉ thế, BB Trần còn cho biết cảm thấy áp lực vì giữa thời đại văn minh như hiện tại nhưng vẫn còn tồn tại quan điểm những người thuộc giới tính thứ 3 là \"tội phạm\". Chỉ sau ít phút đăng tải dòng trạng thái này, BB Trần đã xoá bỏ hoặc ẩn đi để tránh sự việc thêm ồn ào. Công chúng không khỏi bức xúc thay cho BB Trần vì bị kỳ thị, lên án mạnh mẽ netizen kém duyên với tư tưởng phân biệt giới tính.</p>', NULL, 'bb-tran-len-tieng-vi-bi-ky-thi-gioi-tinh-giai-thich-gon-lon-3-cau-ma-netizen-doc-xong-cung-lu-theo', 0, NULL, NULL),
(8, 1, 1, 1, 1, 'Cựu thành viên KARA hé lộ chế độ ăn kiêng khủng khiếp của idol: Không được uống nước cả ngày chỉ để giữ được điều này!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617783364642-16177833650061762279055.gif.png', '|', 'Chế độ giảm cân của Han Seungyeon (cựu thành viên KARA ) có lẽ thuộc vào hàng khắc nghiệt nhất nhì Kpop.', '<p>Các thần tượng Kpop thường giảm cân bằng nhiều chế độ ăn kiêng khác nhau, từ nhịn ăn, ăn toàn rau cho đến chỉ uống nước, tập thể dục điên cuồng. Tuy vậy, có lẽ ít ai có thể chịu đựng được phương pháp ăn kiêng kinh khủng như Han Seungyeon - cựu thành viên nhóm nhạc nữ đình đám KARA. Mới đây, t rong 1 tập của loạt phim tài liệu Kpop Evolution trên YouTube, Seungyeon đã tiết lộ về trải nghiệm giảm cân bằng cách  nhịn... uống nước.</p><p>Nữ idol đình đám thế hệ 2 đã nhớ lại về khoảng thời gian khi mới bắt đầu giảm cân. Seungyeon lúc đó chưa từng nghĩ đến việc ăn kiêng cho đến khi 1 nhà sản xuất hỏi cô: \"Em đang có dự định ăn kiêng mà, đúng không?\". Sau câu hỏi đột ngột đó, giọng ca chính của KARA đã lập tức bắt đầu quá trình giảm cân khắc nghiệt. Cô chia sẻ: \"Chỉ sau đúng 1 lời bình luận đó thôi. Thật đấy. Chỉ có vài từ đơn giản như thế thôi nhưng ngay sau đó, tôi đã phải bắt đầu ăn kiêng\".</p><p>Còn nhớ, ở thời đỉnh cao của KARA, khi cả nhóm đang quảng bá ca khúc Mister vào năm 2009, Seungyeon cùng các thành viên đã diện trang phục quần thụng và áo croptop bó sát khoe triệt để phần eo con kiến nhỏ đến khó tin. Để xuất hiện với diện mạo quyến rũ, xinh đẹp nhất trên sân khấu, KARA đã phải trải qua quãng thời gian ép cân khắc nghiệt.</p><p>Seungyeon kể rằng cô cùng các thành viên KARA thậm chí không thể uống nước cho đến khi hết ngày. Nữ idol giải thích rằng nước không làm cơ thể con người lên cân, nhưng chỉ 1 cốc nước lọc thôi cũng đủ khiến phần bụng trông to hơn. Do KARA phải để lộ phần cơ bụng săn chắc, eo con kiến thon gọn trong trang phục áo ngắn nên nếu lỡ uống nước, bụng của các thành viên có thể sẽ không thể giữ được trạng thái đẹp và hoàn hảo nhất.</p><p>Dù đã phải trải qua quá trình ăn kiêng khắc nghiệt, đến nước cũng không được uống nhưng Seungyeon không hề trách cứ hay nhắc đến vai trò của công ty khi đã ép nghệ sĩ giảm cân quá đà.  Tuy vậy, nữ idol kì cựu vẫn đưa ra những lời khuyên hữu ích cho thế hệ idol hậu bối thời nay: \"Nếu công ty  đối xử tệ bạc hay  yêu cầu bạn hy sinh sức khỏe, tôi nghĩ các bạn không nên cố gắng chịu đựng. Khi nào cần, bạn phải biết đứng lên chiến đấu\".</p><p>Đây không phải lần đầu tiên thành viên KARA hé lộ những trải nghiệm kinh hoàng khi giảm cân. Vào năm 2012, 5 năm sau khi KARA ra mắt, Seungyeon từng bày tỏ mong muốn được quay ngược quá khứ, về lại khoảng thời gian khi cô chưa phải ăn kiêng hay nhịn ăn khắc nghiệt: \"Đó là khoảng thời gian em có thể ăn cả thìa đầy bơ lạc, thưởng thức mỳ gói với bạn bè vào buổi đêm hay ăn 1 ngày 4-5 bữa\". Đến năm 2014, nữ idol sinh năm 1988 cũng từng chia sẻ hậu quả đáng lo ngại khi giảm cân quá đà. Cô từng ngất đi sau khi phải ăn kiêng và tập thể dục với tần suất 4 buổi/tuần.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/unnamed-1617773313828770401180.jpg\" ><figcaption class=\"caption\">Han Seungyeon hồi tưởng lại quãng thời gian cô bắt đầu chế độ ăn kiêng khắc nghiệt đến nỗi người bình thường ít ai tưởng tượng nổi</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/kara-ilovek-pop-34246595-1599-15-1-1617781836681162632168.jpg\" ><figcaption class=\"caption\">Để giữ được cơ bụng săn chắc và phần eo thon gọn đáng mơ ước trên sân khấu, KARA thậm chí còn không được uống cả nước lọc</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/shimmeringminordassierat-sizerestricted-16177818366352017807636.gif.mp4\" ></video><div class=\"caption\">Thành quả của việc tập luyện và nhịn ăn nhịn uống kham khổ chính là thân hình mảnh mai không chút mỡ thừa, vòng eo nhỏ đến khó tin. Vũ đạo lắc hông và trang phục quần thụng, áo crop top của KARA trong đợt quảng bá Mister cũng trở thành cơn sốt vào thời điểm đó</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/-1617773535229506207126.jpg\" ><figcaption class=\"caption\">Han Seungyeon khuyên các đàn em không nên để bất kì phương pháp giảm cân nào làm ảnh hưởng đến sức khỏe của chính mình</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/bd4f391a223abd562965c990f49203e2-16177816984261438911778.jpg\" ><figcaption class=\"caption\">Thế mới thấy, để có được thân hình hoàn mỹ, mảnh mai và quyến rũ, các idol Hàn đã hy sinh sức khỏe và công sức nhiều đến thế nào</figcaption></figure><p>Nguồn: YouTube, Koreaboo</p>', NULL, 'cuu-thanh-vien-kara-he-lo-che-do-an-kieng-khung-khiep-cua-idol-khong-duoc-uong-nuoc-ca-ngay-chi-de-giu-duoc-dieu-nay', 0, NULL, NULL),
(9, 1, 1, 1, 1, 'Nạn nhân tung file ghi âm Chorong (Apink) nhận tội nhưng nói dối, tất cả bắt nguồn từ chuyện chị chị em em tranh bạn trai?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617779546184-16177795464371909719132.jpg', '|', 'Trong đoạn ghi âm này, trưởng nhóm Apink - Chorong đã thừa nhận hành động bạo lực, làm netizen sốc nặng vì trái ngược hẳn với lời phủ nhận trước đó.', '<p>Vào ngày 5/4, Chorong (Apink) bất ngờ bị bạn cũ tố cáo là kẻ bạo lực học đường, thậm chí còn \"bay lắc\", đi bar khi chưa đủ tuổi. Dù bị phía Chorong kiện phủ đầu trước nhưng nạn nhân Kim cho biết cô sẵn sàng theo đuổi vụ kiện, đồng thời đã ghi âm lại cuộc nói chuyện với Chorong, trong đó trưởng nhóm Apink đã thừa nhận bạo lực và nói lời xin lỗi với nạn nhân.</p><p>Đến hôm qua (6/4), Chorong đã lên tiếng thừa nhận đi bar và uống rượu khi chưa đủ tuổi trưởng thành, nhưng cô phủ nhận mọi cáo buộc bạo lực. Và đến hôm nay (7/4), nạn nhân đã tung ra đoạn ghi âm và ngay lập tức khiến netizen dậy sóng. Trong đoạn ghi âm này, nữ idol sinh năm 1991 thừa nhận hành hung bạn học vì liên quan đến chuyện bạn trai, đồng thời đưa ra lời xin lỗi nạn nhân. Điều này chứng tỏ nữ idol đã nói dối công chúng về tội danh của mình, và tất cả mâu thuẫn trong quá khứ bắt nguồn từ chuyện \"chị chị em em\" tranh giành bạn trai.</p><p>Nội dung của đoạn ghi âm như sau:</p><p>- Chorong: Tôi không làm điều đó mà không có lý do.</p><p>- Kim: Không quan trọng cô có lý do hay không, việc cô làm là sai. Tôi đã làm gì sai với cô? Tôi định báo cảnh sát sau khi cô đánh tôi. Nhưng lúc đó lại có 1 đứa bạn bảo tôi bỏ qua đi vì trông cô như đang say rượu.</p><p>- Chorong: Nhưng mà vấn đề là tôi nên nói rõ với cô vào lúc đó. Đúng là có sự hiểu lầm như cô nói trong tin nhắn. Dù sao thì tôi cũng nên giải thích rõ vào thời điểm đó, nhưng lúc đó tôi hơi bực mình.</p><p>- Kim: Được rồi, vậy thì đó là gì? Vì cái gì? Tại sao cô lại tức giận với tôi?</p><p>- Chorong: Giống như cô nói trong tin nhắn, tôi có thích 1 người vào lúc đó.</p><p>- Kim: Đúng rồi, nhưng tôi đã hẹn hò với người đó cơ mà. Tôi không biết có phải vì thế không nhưng từ những gì tôi nhớ, bạn cô đã nói \"Chorong nói rằng muốn đánh mày\" và tôi đã bị đánh vào hôm đó. Những gì tôi nhớ là cô tát vào giữa má tôi, còn những người khác đá vào ống đồng nên tôi đã bị bầm tím khắp chân. Thời đó chúng ta mặc váy đến trường nên bạn học ngồi cạnh tôi còn nhớ vụ này. Và gì nữa nhỉ? Lúc đó có nhiều đứa có mặt lắm. Vậy nếu cô nghĩ xem, nếu cô là tôi, nỗi đau này có kéo dài mãi mãi không? Liệu cô có quên đi được không?</p><p>- Chorong: Không, tôi không nghĩ là mình có thể quên được.</p><p>- Kim: Thực lòng thì cả người tôi vẫn đang run lên khi nói về chuyện này. Sau này cô lấy chồng sinh con, cô có thấy ổn không nếu con cô gặp phải điều này?</p><p>- Chorong: Không.</p><p>- Kim: Bởi vậy, thật lòng thì hồi đó tôi đâu có mạnh mẽ. Tôi nhỏ người và nhìn yếu đuối nữa. Cũng do tổn thương mà mỗi khi thấy 1 nhóm nữ sinh đang nhìn mình, tôi lại nghĩ \"Liệu họ có định làm điều gì tồi tệ với mình không, hay là đang nói xấu mình?\", rồi tôi lại cúi đầu xuống. Nhưng cô... cô không hề biết về nỗi đau tôi phải trải qua, đúng không? Nên tôi nghĩ mỗi khi Apink xuất hiện thì tôi sẽ không xem, tôi sẽ tắt tivi đi. Nhưng khi tôi trưởng thành hơn, mọi chuyện đã khác. Tôi tự hỏi tại sao lại phải sống như thế này trong khi tôi là nạn nhân cơ mà.</p><p>- Chorong: Tôi thực sự xin lỗi. Đáng lẽ ra lúc đó tôi không nên làm vậy dù có hiểu lầm gì. Tôi nên giải thích rõ ngay lúc đó. Nhưng lúc đó tôi cũng tức giận.</p><p>- Kim: Chuyện đã xảy ra rồi, nhưng cô nên xin lỗi tôi nếu cô có ý định debut làm người nổi tiếng rồi xuất hiện trên tivi. Cô có nghĩ thế không? Có để tâm đến việc tôi vẫn sống với từng này nỗi đau?</p><p>- Chorong: Thực sự thì tôi đã đi thử giọng vội vàng rồi cứ thế debut, hoạt động đến bây giờ, mọi điều khá điên rồ. Bởi vì mọi chuyện diễn ra trong khoảng thời gian ngắn nên đúng là tôi đã không nghĩ đến chuyện sửa chữa lỗi lầm trước khi rời đi.</p><p>Đoạn ghi âm \"bóc trần\" phốt bạo lực của Chorong đã khiến netizen sốc nặng. Đáp lại điều này, Play M Entertainment tuyên bố đoạn ghi âm đã bị cắt xén và chỉnh sửa theo hướng có lợi cho Kim. </p><p>Công ty cho biết Chorong và Kim từng là bạn bè, giữa họ có mâu thuẫn nên nữ idol đã xin lỗi, nhưng không hề sử dụng bạo lực. Play M tuyên bố họ sẵn sàng tung đoạn ghi âm đầy đủ.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/868852923fda84425ae6d45db026ed92-16177794649782047424352.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn: Sports Khan</p>', NULL, 'nan-nhan-tung-file-ghi-am-chorong-apink-nhan-toi-nhung-noi-doi-tat-ca-bat-nguon-tu-chuyen-chi-chi-em-em-tranh-ban-trai', 0, NULL, NULL),
(10, 2, 1, 1, 1, 'Giữa nghi vấn toang, Matt Liu bất ngờ lộ tình trạng hiện tại với Hương Giang và còn có \"hint\" cưới xin?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617784382442-1617784382845920166308.jpg', '|', 'Chỉ qua 1 dòng bình luận từ anh bạn thân Tống Đông Khuê, fan của couple Hương Giang và Matt Liu được dịp rần rần.', '<p>Nói đến những đôi phát \"cẩu lương\" ngọt lịm thì không thể bỏ qua Matt Liu và Hương Giang được. Ấy thế mà gần 1 tháng nay cặp đôi không còn tương tác trên MXH, \"nữ hoàng chuyển giới\" như lui về ở ẩn, còn Matt Liu lẻ bóng đi đánh golf cùng hội bạn thân.</p><p>Bỏ qua 7749 lần bị soi \"hint\" nghi vấn toang với Hương Giang, Matt Liu mới đây vẫn thoải mái tương tác phía dưới bức ảnh bạn thân Tống Đông Khuê chúc mừng sinh nhật người yêu. Đáng nói ở phần bình luận, khi Matt Liu gửi lời chúc và hối thúc cặp đôi nhanh chóng có \"happy ending\", Tống Đông Khuê liền giục Matt Liu nhanh chóng tính chuyện cưới xin: \"Cưới trước đi, đừng có chờ đợi nữa\".</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo2021-04-0715-08-43-1617784330109770617102.jpg\" ><figcaption class=\"caption\">Matt Liu vừa gửi lời chúc: \"Chúc mừng sinh nhật Nhi. Đợi lời mời ăn cưới của hai người sớm\", bạn thân liền hối thúc ngược nam CEO nên tính chuyện cưới xin trước</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/liu-3-1617784356726185124429.jpg\" ><figcaption class=\"caption\">Chỉ với chi tiết này, dân tình cho rằng có lẽ Matt Liu và Hương Giang vẫn đang mặn nồng, chỉ là hạn chế thể hiện tình cảm công khai như trước đây. Bởi lẽ nếu Matt Liu đang độc thân thì bạn thân sẽ không hối chuyện trọng đại như thế kia</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/tu-vu-viec-huong-giang-bi-tay-chay-dung-ca-gian-mat-khon-eca-5347541-16177843561701703387506.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/mattllh1496829424243431720105621490365220896466074n-1615204604149666648371-1617784356351946349647.jpg\" ><figcaption class=\"caption\">Dân tình đang chờ đợi người trong cuộc lên tiếng hoặc đăng 1 bức ảnh ngọt ngào để yên tâm rằng cặp đôi vẫn đang viên mãn</figcaption></figure><p>Ai đời lại đi chúc người đang độc thân sớm cưới đúng không? Qua chi tiết này, netizen cho rằng có thể Matt Liu và Hương Giang vẫn đang âm thầm bên nhau, chỉ là hạn chế phát \"cẩu lương\" hơn trước.</p>', NULL, 'giua-nghi-van-toang-matt-liu-bat-ngo-lo-tinh-trang-hien-tai-voi-huong-giang-va-con-co-hint-cuoi-xin', 0, NULL, NULL),
(11, 2, 1, 1, 1, 'BB Trần lên tiếng vì bị kỳ thị giới tính, giải thích gọn lỏn 3 câu mà netizen đọc xong cũng \"lú\" theo', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617780865448-1617780865853350524680.jpg', '|', 'BB Trần lạ quá, đáp trả căng nhưng nghe vẫn không quên thể hiện tính cách chuẩn “thánh lầy”.', '<p>BB Trần là 1 trong những nghệ sĩ rất thoải mái công khai xu hướng tính dục với công chúng. Được biết, nam diễn viên đang có cuộc tình vô cùng viên mãn với người yêu đồng tính Quang Lâm. Tuy nhiên, cách đây ít phút BB Trần vừa lên tiếng thể hiện rõ thái độ khó chịu khi bị antifan mỉa mai giới tính và chỉ trích về việc anh hay \"chơi dơ\" trong các gameshow.</p><p>Trên trang cá nhân, BB Trần đã chụp lại bình luận của 1 netizen có nội dung: \"BB Trần đã ăn gian rồi còn bê đê nữa, em ghét BB bê đê, dơ cướp đồ người ta\". Ngay lập tức, nam diễn viên lầy lội vạch rõ 3 ý để đáp lại antifan: \"Câu này ẩn ý quá, vậy nghĩa là: Ăn gian thì không được bê đê, đã bê đê thì đừng chơi ăn gian hay giới bê đê và ăn gian đều là cái tội\". Tuy nhiên, đọc xong status của BB Trần, dân mạng phải \"lú\" theo vì pha đáp trả quá cồng kềnh.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo2021-04-0715-23-03-1617783860576422323250.jpg\" ><figcaption class=\"caption\">BB Trần bức xúc lên tiếng khi bị kỳ thị giới tính, đáp trả nhưng vẫn không quên lầy lội \"thả miếng hài\"</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/170166604102163011893628808255008102657653429n-1617780659490760208454.jpg\" ><figcaption class=\"caption\">BB Trần là nam nghệ sĩ khá thoải mái công khai xu hướng tính dục, đây không phải lần đầu anh gặp những bình luận ác ý như thế này</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/164505040102162575317114664316755408401165714n-16177806593001896959818.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Không chỉ thế, BB Trần còn cho biết cảm thấy áp lực vì giữa thời đại văn minh như hiện tại nhưng vẫn còn tồn tại quan điểm những người thuộc giới tính thứ 3 là \"tội phạm\". Chỉ sau ít phút đăng tải dòng trạng thái này, BB Trần đã xoá bỏ hoặc ẩn đi để tránh sự việc thêm ồn ào. Công chúng không khỏi bức xúc thay cho BB Trần vì bị kỳ thị, lên án mạnh mẽ netizen kém duyên với tư tưởng phân biệt giới tính.</p>', NULL, 'bb-tran-len-tieng-vi-bi-ky-thi-gioi-tinh-giai-thich-gon-lon-3-cau-ma-netizen-doc-xong-cung-lu-theo', 0, NULL, NULL),
(12, 2, 1, 1, 1, 'Thiều Bảo Trâm tạo dáng như nữ Chủ tịch bên xế hộp, dân tình chỉ ra ngay chi tiết liên quan đến Sơn Tùng M-TP?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617775066861-1617775067198374932866.jpg', '|', 'Gần 3 tháng sau khi xảy ra liên hoàn lùm xùm tình ái, Thiều Bảo Trâm bất ngờ có động thái làm gợi nhớ tình cũ hay gì đây?', '<p>Sau 8 năm âm thầm bên nhau, cặp đôi Sơn Tùng và Thiều Bảo Trâm bị cho là đã \"toang\" hậu liên hoàn drama. Sau 3 tháng từ ngày rộ tin đồn chia tay, Sơn Tùng hạn chế đăng ảnh trên MXH cũng như ít khi xuất hiện trước công chúng còn Thiều Bảo Trâm hoàn toàn ngược lại, cô liên tục cập nhật khoảnh khắc khoe visual thăng hạng.</p><p>Đặc biệt, cách đây ít phút (ngày 7/4), Thiều Bảo Trâm còn diện đồ như nữ Chủ tịch và tạo dáng bên xế hộp màu trắng sang trọng. Có thể thấy, thần sắc của nữ ca sĩ vô cùng rạng rỡ, cô nở nụ cười tươi tắn và tràn đầy năng lượng. Đáng nói, dưới phần bình luận, ngoài lời khen ngợi, dân tình cho rằng Thiều Bảo Trâm đang đứng cạnh chiếc xế hộp màu trắng của Sơn Tùng. Cách đây ít ngày, khi bị chúng tôi bắt gặp tại một địa điểm ở TP.HCM, Sơn Tùng đã đi chiếc xe có màu sắc tương tự.</p><p>Tuy nhiên, lần này các \"thánh soi\" đã bị nhầm mất rồi, chiếc xe được Thiều Bảo Trâm tựa vào để chụp ảnh \"sống ảo\" so với xế hộp của Sơn Tùng từ kích cỡ cho đến thương hiệu đều không giống nhau, điểm trùng hợp duy nhất là 2 chiếc cùng có màu trắng.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/img9321-16177750533052057167342.jpg\" ><figcaption class=\"caption\">Thiều Bảo trâm khoe góc nghiêng xinh đẹp, thần thái như nữ chủ tịch bên xế hộp</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/img9319-16177750521961455140717.jpg\" ><figcaption class=\"caption\">3 tháng sau lùm xùm tình ái, Thiều Bảo Trâm của hiện tại đã vui vẻ hơn rất nhiều</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/tinngan0653215099447640-16177750531031089619980.jpg\" ><figcaption class=\"caption\">Cách đây ít ngày, Sơn Tùng đã bị bắt gặp đi chiếc xe màu trắng và lộ diện tại 1 phim trưởng ở TP.HCM</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/img9322-16177750520131841238996.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo1611569593696-16115695945171942391454-16177750528912023958186.png\" ><figcaption class=\"caption\">Trước khi xảy ra nghi vấn \"toang\", Thiều Bảo Trâm từng bị bắt gặp lái siêu xe được cho là của Sơn Tùng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1660435562808728234089902694472980381602733n-1617776276997784327784.jpg\" ><figcaption class=\"caption\">Trong khi Sơn Tùng im hơi lặng tiếng thì Thiều Bảo Trâm rất thoải mái khoe ảnh đời thường lên mạng xã hội</figcaption></figure><p>Suốt thời gian qua, Thiều Bảo Trâm không nhắc đến Sơn Tùng cũng như hiếm khi có những chia sẻ ẩn ý về chuyện tình cảm. Cô đang dành thời gian tận hưởng cuộc sống vui vẻ, đi diễn thường xuyên, thậm chí còn rộ tin nữ ca sĩ chuẩn bị ra mắt sản phẩm mới.</p>', NULL, 'thieu-bao-tram-tao-dang-nhu-nu-chu-tich-ben-xe-hop-dan-tinh-chi-ra-ngay-chi-tiet-lien-quan-den-son-tung-m-tp', 0, NULL, NULL),
(13, 2, 1, 1, 1, 'Tròn 100 ngày NS Vân Quang Long vĩnh viễn ra đi: Xót xa cảnh vợ trẻ con thơ đội khăn trắng, Lâm Vũ về quê thăm mộ bạn quá cố', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617772501820-1617772502040364974545.jpg', '|', 'Vào đúng 100 ngày nghệ sĩ Vân Quang Long ra đi, vợ con anh và Lâm Vũ đồng loạt có động thái tưởng nhớ.', '<p>Tháng 12/2020, cả showbiz chìm trong tang thương suốt nhiều ngày khi hay tin ca sĩ Vân Quang Long qua đời ở tuổi 41 vì đột quỵ. Sau 100 ngày nam danh ca vĩnh viễn lìa xa trần thế, gia quyến và dàn sao Vbiz vẫn chưa thể nguôi nỗi buồn.</p><p>Và hôm nay (ngày 7/4), Lâm Vũ và bố Vân Quang Long đã về tận Đồng Tháp thăm mộ người anh em. Không giấu nổi cảm xúc nghẹn ngào, anh nhắn nhủ: \"Mới đây mà 100 ngày của bạn rồi, bạn linh thiêng phù hộ cho ba mẹ và các con của bạn được bình an bạn nhé\". Về phần Linh Lan, cô và con gái cũng có mặt ở quê nhà để tổ chức lễ cúng cho chồng quá cố.</p><p>Lúc sinh thời, cả hai thân thiết với nhau không chỉ trong công việc mà còn ở cuộc sống hằng ngày. Nay Vân Quang Long mất đi, Lâm Vũ thường xuyên thay bạn về thăm gia đình, thăm hỏi và động viên bố mẹ anh.</p><p></p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/anh-chup-man-hinh-2021-04-07-luc-120932-1617772298795759331873.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/anh-chup-man-hinh-2021-04-07-luc-121017-16177722989641655565309.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-161777178110368413950.jpg\" ><figcaption class=\"caption\">Lâm Vũ cùng bố NS Vân Quang Long ra thăm mộ vào lễ cúng 100 ngày nam danh ca vĩnh viễn lìa xa</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617771869346484328527.jpg\" ><figcaption class=\"caption\">Nhìn bố mẹ nam NS nở nụ cười sau biến cố mà khán giả phần nào nguôi ngoai, an tâm hơn</figcaption></figure><p></p>', NULL, 'tron-100-ngay-ns-van-quang-long-vinh-vien-ra-di-xot-xa-canh-vo-tre-con-tho-doi-khan-trang-lam-vu-ve-que-tham-mo-ban-qua-co', 0, NULL, NULL),
(14, 2, 1, 1, 1, 'NTK Đỗ Mạnh Cường hé lộ chi phí nuôi 8 con mỗi tháng, cả du lịch và sắm đồ \"khủng\" đến mức nào?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617777199812-1617777200047345953162.jpg', '|', 'Chi phí nhận nuôi 8 đứa trẻ mỗi tháng của NTK Đỗ Mạnh Cường không hề ít ỏi.', '<p>Thời gian vừa qua, NTK Đỗ Mạnh Cường khiến công chúng vô cùng cảm kích khi nhận nuôi 8 đứa trẻ và dự định sẽ cưu mang thêm nhiều trường hợp đặc biệt. Từ đó, những câu chuyện xoay quanh việc nuôi dưỡng các con của NTK Đỗ Mạnh Cường luôn thu hút đông đảo sự quan tâm.</p><p>Cho tới mới đây, NTK Đỗ Mạnh Cường lần đầu hé lộ chi phí nuôi con khi trả lời bình luận của khán giả. Theo đó, NTK Đỗ Mạnh Cường cho biết: \"Một tháng nuôi 8 đứa hết khoảng 200 triệu đồng\". Khi nhận được phản hồi: \"Em nghĩ là số tiền phải hơn ý. Bố hứng lên mua cho con cái túi xách hàng hiệu đã hết mất hơn 100 triệu rồi\", NTK Đỗ Mạnh Cường chia sẻ: \"Thì đó là chi phí cơ bản hàng tháng em, đâu có tính tiền shopping cho các con, đi du lịch... đâu em\".</p><p>Ảnh: Sưu tầm, Facebook nhân vật</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177759503681940465793.jpg\" ><figcaption class=\"caption\">Đỗ Mạnh Cường tiết lộ chi phí nuôi 8 đứa con lên tới 200 triệu mỗi tháng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177759434871885205878.jpg\" ><figcaption class=\"caption\">Đây là chi phí cơ bản chưa tính tiền đi du lịch, shopping...</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177761452881017124134.jpg\" ><figcaption class=\"caption\">Tuy chi phí nuôi con không ít ỏi nhưng nam nhà thiết kế luôn lựa chọn cho con những thứ tốt nhất từ trang phục đến môi trường học tập...</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617776263383379406020.jpg\" ><figcaption class=\"caption\">Trước đó, hình ảnh con nuôi thứ 8 của NTK Đỗ Mạnh Cường được bố chăm sóc tận tình, xách túi hiệu, lên đồ như tiểu thư đi học đã gây xôn xao MXH</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617776156141425057623.jpg\" ><figcaption class=\"caption\">NTK Đỗ Mạnh Cường được gọi là \"ông bố đông con nhất showbiz Việt\"</figcaption></figure><p></p>', NULL, 'ntk-do-manh-cuong-he-lo-chi-phi-nuoi-8-con-moi-thang-ca-du-lich-va-sam-do-khung-den-muc-nao', 0, NULL, NULL),
(15, 2, 1, 1, 1, 'Vợ Hồng Đăng ngỡ ngàng phát hiện khả năng hiếm có của con gái út, hiếm đến mức cả triệu người mới có một', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617773592134-16177735922271773926325.jpg', '|', 'Khả năng đặc biệt của cô bé Kẹo khiến mẹ giật mình. Được biết đây là khả năng mà chỉ 1% dân số thế giới làm được.', '<p>Hồng Đăng hiện là một trong những nam diễn viên có cuộc sống gia đình viên mãn nhất showbiz Việt. Thường xuyên đau khổ vì tình trên màn ảnh nhưng ngoài đời, anh có gia đình hạnh phúc bên vợ và 2 con gái Nhím, Kẹo. Trên trang facebook cá nhân, cả hai vợ chồng nam diễn viên thường xuyên chia sẻ những hình ảnh, câu chuyện về các con.</p><p>Mới đây, bà xã Hồng Đăng khiến ai nấy bất ngờ khi khoe khả năng cực đặc biệt của con gái út - bé Kẹo. Cụ thể cô chia sẻ hình ảnh con gái uốn lưỡi thành hình hoa ba cánh, kèm status: \"Không hiểu mốt lưỡi hoa ở đâu ra nữa... Bà ý cứ hỏi nhìn đẹp không mẹ? Làm mẹ giật cả mình. Mà sao tập mãi em không làm được các bác nhỉ?\".</p><p>Con gái Hồng Đăng có thể uốn lưỡi thành hình cánh hoa. Đây là khả năng hiếm có, không phải cũng có thể làm được. Theo một số nghiên cứu, chỉ có khoảng 1% dân số thế giới có thể uốn lưỡi thành hình hoa 3 cánh.</p><p>Được biết đây là một khả năng vô cùng đặc biệt mà không phải ai cũng có thể làm được. Trang tin Brightside từng đăng tải thông tin: Một nghiên cứu của các nhà khoa học cho biết: Trong số những người có khả năng uốn lưỡi, chỉ 63% người cuộn lưỡi thành dạng ống, 14% có thể tách lưỡi làm đôi và chỉ khoảng 1% tạo được hình “hoa lưỡi” 3 cánh.</p><p></p><p>Lý do được các nhà nghiên cứu đưa ra là phần cơ bên trong lưỡi như cơ dọc, cơ dưới, cơ ngang hay cơ thẳng đứng - có tác dụng chính là giữ hình dạng cho lưỡi, giúp con người thuận tiện hơn trong việc nói, nuốt và ăn uống.</p><p>Tuy nhiên ở những cá nhân có khả năng đặc biệt, chính những cơ này mang lại cho họ khả năng uốn lưỡi thành những hình dạng độc đáo. Trong số các nhóc tỳ nhà sao Việt, ngoài bé Kẹo thì còn có Cola nhà Ốc Thanh vân cũng có thể làm được điều này.</p><p>Được biết, Kẹo năm nay học lớp 1. Giống như nhiều đứa trẻ khác, cô bé cũng chưa quen với nề nếp ở bậc tiểu học. Thời gian trước, Hồng Đăng từng chia sẻ bức ảnh con gái ngủ quên ngay dưới sàn nhà. Có lẽ quá mệt với việc học nên cô bé đã đánh một giấc sau khi làm bài tập Tiếng Việt. Tuy nhiên Hồng Đăng không mắng, cũng chẳng tạo áp lực mà luôn mong muốn con có thể vui vẻ ở trường, thành tích không quá quan trọng.</p><p>Hình ảnh bé Kẹo ngủ quên dưới bàn học từng khiến cộng đồng mạng phải phì cười vì quá đáng yêu. Không chỉ Kẹo, nhiều cô cậu học trò lớp 1 khác cũng khá chật vật trong những ngày đầu đi học ở môi trường mới.</p><p>\"Tuổi các con đi học chưa bao giờ là sướng cả, nên ba chỉ mong là các con tìm được niềm vui trong học tập để các con không cảm thấy mệt mỏi trên con đường học hành đầy gian nan này... \", Hồng Đăng từng chia sẻ trong ngày đầu Kẹo đi khai giảng.</p><p>Trong cuộc sống hàng ngày, Kẹo và chị gái Nhím được bố rèn tính cách tự tin, năng động thông qua nhiều hoạt động ngoại khóa, thể chất. Hai bé không bao giờ bị bố đặt ra các giới hạn như: Trò này chỉ dành cho con gái, trò này chỉ dành cho con trai mà được thỏa sức khám phá.</p><p>Nói về việc nuôi dạy con, Hồng Đăng từng chia sẻ: \"Tôi sẽ luôn dành cho các con những gì tốt nhất trong khả năng của bố, còn việc bơi được bao xa là việc của con.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177735397362053338445.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177735407061181984555.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-2-16177735417261295652513.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-3-16177735412241309655367.jpg\" ><figcaption class=\"caption\">Con gái Ốc Thanh Vân cũng có khả năng uốn lưỡi hình cánh hoa khiến mẹ ngỡ ngàng.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-4-16177735422611654105446.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-5-16177735417451943902350.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Tôi nghĩ rằng việc giáo dục con cái tốt nhất phải vừa bắt nguồn từ tình yêu thương, chăm sóc, nhưng cũng phải tôn trọng sự phát triển tự nhiên của con, để con tự bước đi trên đôi chân của mình\".</p>', NULL, 'vo-hong-dang-ngo-ngang-phat-hien-kha-nang-hiem-co-cua-con-gai-ut-hiem-den-muc-ca-trieu-nguoi-moi-co-mot', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(16, 2, 1, 1, 1, 'Á hậu Ngọc Thảo đã về Việt Nam hậu dừng chân ở top 20 Miss Grand, tình trạng sức khoẻ hiện tại thế nào?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617767635822-16177676360091270768393.jpg', '|', 'Sau khi kết thúc hành trình Miss Grand tại Thái Lan, Á hậu Ngọc Thảo đã về Việt Nam để tiếp tục hoạt động nghệ thuật.', '<p>10 ngày sau đêm Chung kết Miss Grand International 2020 diễn ra tại Thái Lan, đại diện Việt Nam - Ngọc Thảo đã đáp chuyến bay về Việt Nam vào hôm qua (ngày 6/4). Có thể thấy, Ngọc Thảo vẫn giữ tinh thần khá vui vẻ, cô đeo khẩu trang cẩn thận khi lên máy bay từ Thái Lan về nước. Được biết Á hậu Ngọc Thảo sẽ thực hiện cách ly tập trung tại Đà Nẵng theo đúng quy định phòng chống dịch Covid-19 trước khi trở lại hoạt động nghệ thuật.</p><p>Thời gian chinh chiến tại Miss Grand 2020, Ngọc Thảo cho biết cô luôn đeo khẩu trang, rửa tay khi xuất hiện ở những sự kiện đông người. Ngoài ra, nàng hậu cũng tăng cường tập thể thao, uống thực phẩm chức năng hỗ trợ đề kháng nên hiện tại sức khoẻ cô vẫn khá tốt, chưa có biểu hiện bất thường. Hơn 1 tháng xa quê hương, Ngọc Thảo đang có rất nhiều dự định muốn thực hiện cùng bạn bè, người thân sau khi hoàn thành 14 ngày cách ly.</p><p>Tại Hoa hậu Hoà bình Thế giới 2020, dù đã có phần thể hiện rất tốt nhưng Ngọc Thảo chỉ dừng chân tại top 20 chung cuộc khiến khán giả vô cùng tiếc nuối. Nói về việc bị tuột hạng so với đại diện Việt Nam các năm trước, Ngọc Thảo từng chia sẻ: \"Đối với bản thân Thảo có rất nhiều điểm yếu, đặc biệt là không hoạt ngôn cũng như ngôn ngữ Tiếng Anh còn nhiều hạn chế, Thảo không thích nơi đông người hay nói chuyện quá nhiều và Thảo đã cố gắng thay đổi để phù hợp với tiêu chí mà cuộc thi đã đưa ra.</p><p>Có thể đó không phải là kết quả tốt nhất, nhưng đó là tất cả sự cố gắng của mình nên Thảo sẽ không hối hận vì những nỗ lực bản thân và mọi người cùng Thảo đã bỏ ra. Nhưng vẫn muốn gửi một lời xin lỗi đến tất cả mọi người vì đã không thể cho mọi người thấy được một màn thi cuối cùng thật sự nhiệt huyết và cháy hết mình\".</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/saostar-d6e543faoxb2ici6-16177676187021493588586.jpeg\" ><figcaption class=\"caption\">Ngọc Thảo đã trở về Việt Nam sau khi dừng chân tại top 20 Hoa hậu Hoà bình Thế giới 2020</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/saostar-1zz6bcfx8yf3z6rc-2-1617767619382784945571.jpeg\" ><figcaption class=\"caption\">Người đẹp dường như đã khá thoải mái sau khi cho biết bị suy sụp vì kết quả chung cuộc</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/ngoc-thao-cach-ly-2-ngoisaovn-w660-h880-16177676183341983656061.jpg\" ><figcaption class=\"caption\">Hiện tại, sức khoẻ Ngọc Thảo khá ổn định, cô sẽ cách ly 14 ngày trước khi trở lại showbiz</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/16439958326275248775463614986457472109350009n-16177676185292060345896.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1616352482620697184895797176809016141085051n-16177676179791112683960.jpg\" ><figcaption class=\"caption\">Dù cố gắng hết sức nhưng đại diện Việt Nam chỉ dừng chân ở top 20 Miss Grand 2020</figcaption></figure><p>Clip: Phần thi tự giới thiệu của Ngọc Thảo tại chung kết Miss Grand 2020</p>', NULL, 'a-hau-ngoc-thao-da-ve-viet-nam-hau-dung-chan-o-top-20-miss-grand-tinh-trang-suc-khoe-hien-tai-the-nao', 0, NULL, NULL),
(17, 3, 1, 1, 1, 'Sao hạng A chia tay bạn gái nhưng nhà là phải có nóc, người mới cứ việc vào \"tổ ấm\" cũ mà hưởng đặc ân!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/1/27/photo1611686061328-1611686061802533859123.png', '|', 'Chia tay sao cho đẹp mắt luôn là câu hỏi khó đưa ra lời giải đáp, hãy cùng thử xem đôi celeb làm gì trong trường hợp này!', '<p>Dù chia tay là chuyện chẳng ai muốn, nhưng đến lúc chúng ta vẫn phải chấp nhận một sự thật rằng có lẽ người ấy đã hết yêu rồi. Và đôi khi, sự xuất hiện của người thứ 3 chỉ như một chất xúc tác để đẩy nhanh quá trình. Tan vỡ trong mối quan hệ này lại tạo tiền đề cho mối quan hệ khác bắt đầu. Như câu chuyện \"tổ ấm\" thay \"bà chủ\" mà Hội bạn thân mới hóng hớt được dạo gần đây.</p><p>Chuyện kể rằng có cặp đôi nổi tiếng hàng đầu showbiz đã hẹn hò với nhau gần một thập kỷ. Dù ai cũng ngờ ngợ chuyện bên trong nhưng cả đàng trai (tạm gọi là A) và đàng gái (tạm gọi là B) quyết giữ im lặng cho đến cùng. Drama chỉ bắt đầu khi họ \"đường ai nấy đi\" và câu chuyện bị đem lên khắp mạng xã hội. Khán giả đều đang trông chờ câu trả lời từ chính chủ. Tình hình ngày càng trở nên bất lợi cho anh A tuy nhiên với style \"lẳng lặng chờ bão tan\" thì anh lại càng phải nhẫn nhịn hơn.</p><p>Nhưng nội tình bên trong thực chất ra sao? Hội bạn thân showbiz hóng được chuyện như thế này...</p><p>Lửa gần rơm lâu ngày cũng bén, nhưng bén hoài bén mãi đến một lúc cũng phải tàn. Có ít chi, cũng trao nhau cả thanh xuân rồi nên lời chia tay đâu dễ dàng mà thốt ra. Hết tình còn nghĩa, hết nghĩa rồi anh A... tỏ vẻ chán nản, tự động rời nhà đi bụi vài ngày. Trong lúc đó, anh lên mạng hết \"thả thính\" đến cưa cẩm cô C. Cũng phải thông cảm, dễ gì được tự do, độc lập và thoải mái đến vậy. Cô C lại đúng gu anh từ trước, có một chút \"nổi loạn\" ngầm (thật ra cũng không ngầm lắm).</p><p>Cãi nhau cả tháng, nhưng sức chịu đựng có \"giới thiệu\" của đàng gái đạt đỉnh điểm khi cô nhìn thấy món đồ quen thuộc của bạn trai được bóng hồng khác mặc rồi đăng đầy trên mạng xã hội. 1 tháng cãi vã chẳng đi tới hồi kết, cô B đành buông xuôi. Cô cùng hội bạn ăn tân gia nhà mới \"oách xà lách\" như thể muốn hét lên với cả thế giới rằng tôi đây đã trở lại đời sống độc thân.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/1/27/ngang-16116860088281252630405.png\" ><figcaption class=\"caption\">(Ảnh minh hoạ)</figcaption></figure><p>Nhưng nhà thì phải có nóc, cô B vừa đi thì cô C đã tới lấp đầy khoảng trống. Tất nhiên scandal nổ to banh chành, cô B ra đi cũng không chỉ mang 2 chữ \"oan ức\" không. Ẩn tình tiếp theo ra sao, hồi sau sẽ rõ.</p>', NULL, 'sao-hang-a-chia-tay-ban-gai-nhung-nha-la-phai-co-noc-nguoi-moi-cu-viec-vao-to-am-cu-ma-huong-dac-an', 0, NULL, NULL),
(18, 3, 1, 1, 1, 'Tin nóng hổi trong drama \"trà xanh\": Thiều Bảo Trâm đã dọn ra khỏi \"tổ ấm\" với Sơn Tùng M-TP?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/1/21/photo1611169613206-1611169613736127335639.png', '|', 'Theo tin hội bạn nghe ngóng được, Thiều Bảo Trâm đã có động thái vô cùng mạnh mẽ giữa lúc rộ tin đã chia tay Sơn Tùng.', '<p>Rạng sáng ngày 21/1, khắp nơi xôn xao bàn tán về drama Thiều Bảo Trâm nghi toang với Sơn Tùng vì có người thứ 3 xuất hiện. Sau khi dàn chị em bạn thân ẩn ý ám chỉ Tuesday, Thiều Bảo Trâm cũng có động thái dứt khoát khi unfollow Sơn Tùng ngay trong đêm.</p><p>Đáng nói nhất, hội bạn thân Vbiz mới đây nghe ngóng được tin nóng hổi rằng, Thiều Bảo Trâm đã dọn ra khỏi \"tổ ấm\" với Sơn Tùng! Hành động mạnh mẽ của Thiều Bảo Trâm khiến nghi vấn cặp đôi đã tan vỡ càng có thêm cơ sở. Tiết lộ này của hội bạn thân showbiz đã làm drama Thiều Bảo Trâm - Sơn Tùng và \"trà xanh\" càng thêm căng đét.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/1/21/dien-mot-outfit-thieu-tram-su-dung-den-2-mon-do-doi-voi-son-tung-c5b5702c-16111643789081400399540.jpg\" ><figcaption class=\"caption\">Theo hội bạn thân nghe ngóng được từ nguồn tin thân cận, Thiều Bảo Trâm đã dọn ra khỏi căn hộ cao cấp từng sống cùng Sơn Tùng!</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/1/21/collage-1611165957972900154132.png\" ><figcaption class=\"caption\">Thiều Bảo Trâm và Sơn Tùng 5 lần 7 lượt bị soi check-in cùng phòng ngủ với nhau</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/1/21/collage-1611165249357687234579.png\" ><figcaption class=\"caption\">Tủ đầu giường của Sơn Tùng với kệ đầu giường phòng ngủ Thiều Bảo Trâm có nhiều nét tương đồng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/1/21/dien-mot-outfit-thieu-tram-su-dung-den-2-mon-do-doi-voi-son-tung-5ef35e16-16111643734341452548937.jpg\" ><figcaption class=\"caption\">Hiện tại, người hâm mộ đang chờ đợi người trong cuộc lên tiếng để làm rõ thực hư</figcaption></figure><p>Trước đây, Thiều Bảo Trâm 5 lần 7 lượt bị soi check-in tại nơi rất giống với phòng ngủ của Sơn Tùng M-TP. Dù liên tục bị \"thám tử showbiz\" soi chi tiết sống cùng nhà với nhau nhưng cặp đôi Thiều Bảo Trâm và Sơn Tùng chưa từng lên tiếng về vấn đề này.</p>', NULL, 'tin-nong-hoi-trong-drama-tra-xanh-thieu-bao-tram-da-don-ra-khoi-to-am-voi-son-tung-m-tp', 0, NULL, NULL),
(19, 3, 1, 1, 1, 'Cô ca sĩ đòi ekip bồi thường 2000 USD và edit... livestream vì bị netizen chê, sân si chuyện poster với rapper cực hot B và DJ C', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2020/12/8/photo1607360408681-16073604092051680425861.png', '|', 'Đọc tít thôi đã thấy cô ca sĩ rất ư là \"cá tánh\" rồi nhỉ, cùng đoán xem là ai?', '<p>Người ta bảo, để làm ngôi sao thì cần chút \"cá tánh\", là lạ mới mong được chú ý. Nhưng điều đó không bao gồm cách pha trò ngô nghê, cách làm việc \"trên trời\".</p><p>Mới tối hôm qua, netizen Việt có dịp chứng kiến pha ứng xử \"đi vào lòng đất\" của cựu người mẫu Linh Chi cùng hội bạn thân Lan Ngọc và Ngọc Trinh. Chỉ với vài đường vuốt tóc, cười nói lớn tiếng mà quên đi sự hiện diện của nhân vật bên cạnh là đàn chị Thuỷ Tiên, cô đã phải đón nhận bão \"gạch đá\" từ cư dân mạng. Vỏn vẹn 2 tiếng sau, hàng loạt hội nhóm anti được lập nên để chỉ trích (các) cô... đường dài. Càng xem càng thấy, không thể đùa với sức mạnh của công chúng, dư luận.</p><p>Bài học rành rành là thế, song, vẫn có các nghệ sĩ xem thường. Như nữ ca sĩ (tạm gọi là A) trong câu chuyện mà Chuyện ông sao đã nghe lại từ nguồn tin ekip cũng \"ra gì và này nọ\" vài ngày trước. Tạm kể đôi nét về A để các bạn dễ hình dung: cô có ngót nghét hơn 10 năm kinh nghiệm trong nghề, đầu tiên bén duyên điện ảnh nhưng rồi chuyển sang làm ca sĩ, tính tình lém lỉnh lí lắc được khán giả nhận xét là khá thẳng tính và dễ thương, nhưng lại biến thể khá nhiều khi cô đối xử với bên đối tác.</p><p>Theo quy trình mọi khi, cô nhận lời đi hát 2-3 bài trong một show âm nhạc cho phía đối tác (gọi tắt là bên B). Cũng chẳng có gì mới lạ hay bất thường cho đến lúc những đoạn clip trên livestream, những hình ảnh team qua đường chụp lại bắt đầu trở nên viral trên MXH. Và rồi cô nổi trận lôi đình, nguyên nhân là do netizen chê cô \"đô con\". Tất nhiên ở đây, trên thực tế cô là nạn nhân khi bị body shaming, song cách cô xử lý tình huống và đối xử với bên B lại khiến người trong ngành cảm thấy khó hiểu. </p><p>Nổi giận thôi chưa đủ, cô đòi kiện ngược lại ekip 2000 USD (tương đương 47.490.800 VNĐ) vì mọi người không biết... chỉnh sửa livestream. Thiệt chứ nghe xong chuyện mà chỉ muốn xin sếp cho nghỉ hẳn một buổi trưa để cười (hoặc khóc) cho đã. Ảnh có thể photoshop, clip quay sẵn có thể cắt tạm và chỉnh màu nhưng edit livestream... có lẽ bất cứ ekip làm sự kiện nào nghe xong cũng phải ngả mũ xin \"bó tay\" hoặc đành lách cách đi tìm phần mềm bóp mặt bóp thân \"giả trân\" của các cô hotgirl xứ Trung hay dùng trên TikTok.</p><p>Làm tình làm tội đến đó vẫn chưa đủ, A giữ nguyên tác phong đó cho show diễn tiếp theo. Riêng chuyện poster, cả ekip đã phải họp nhau trên dưới 10 lần để giải đề toán A đưa ra. Ở đây có sự xuất hiện của rapper B đang nổi đình nổi đám vừa được trao giải gì to lắm cùng cô DJ C từng hợp tác chung 1 chương trình với anh B. Đề toán A đưa ra cũng là câu chuyện muôn thuở mà các ekip làm chương trình phải nhức đầu lý giải:</p><p>- Ban đầu: A xuất hiện bên cạnh rapper B và cô DJ tên C.</p><p>- A đứng đằng sau B nhưng muốn phải TO HƠN C.</p><p>Cuối cùng, ekip buộc phải quăng cô C \"vô chuồng gà\" để hợp với ước nguyện cô A dù chẳng hiểu rốt cuộc cô muốn gì. Đến nay, poster trông tròn hay méo vẫn là một ẩn số vì vừa đăng lên 30 phút, A đã bắt xoá. Nguyên nhân à? A không thích diễn nữa, chúc ekip may mắn lần sau vậy.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2020/12/7/artboard-3-copy-2-1607360397567624064887.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2020/12/8/bannercallout-16052558159881944374491-16073635830131422907851.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Dẫu biết ngôi sao nào cũng chịu áp lực về chuyện ngoại hình. Bởi dù ekip có đông người chăm chút, nhưng đến cuối, công chúng sẽ chỉ gọi tên idol ra để đánh giá hoặc chê bai diện mạo của họ. Chính vì vậy, chuyện họ trở nên nhạy cảm cũng là điều khá dễ hiểu. Nhưng đòi hỏi thế này liệu có thoả đáng, có lẽ để khán giả tự nhận xét.</p>', NULL, 'co-ca-si-doi-ekip-boi-thuong-2000-usd-va-edit-livestream-vi-bi-netizen-che-san-si-chuyen-poster-voi-rapper-cuc-hot-b-va-dj-c', 0, NULL, NULL),
(20, 3, 1, 1, 1, 'Có cô nghệ sĩ giỏi nói đạo lý nhưng lại thích sân si, hành cả ekip vì so đo từng chút với KOL hot nhất hiện nay', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2020/11/30/photo1606714537064-1606714538053848786278.png', '|', 'Cô nghệ sĩ dưới đã từng là một viên đá quý, nhưng viên đá đó có lẽ còn toả sáng hơn nếu được lồng vào tính khiêm nhường.', '<p>Cái gì showbiz có thể thiếu chứ drama thì lúc nào cũng mâm cao cỗ đầy cho các bạn đọc cùng chung vui. Hôm nay, chúng tôi tiếp tục mời các bạn đến với một bàn tiệc drama, mà ngôi sao sáng nhất bàn tiệc ấy được chúng tôi gọi nôm na là cô A.</p><p>Chuyện là có cô nghệ sĩ, đa phần được mọi người yêu mến nhờ ngoại hình xinh đẹp và tính tình có vẻ là hiền lành. Trang cá nhân của A thường được cập nhật theo đúng công thức: Eat clean, ăn mặc giản dị và nói những câu đạo lý đang trend trên mạng hoặc lời hay ý đẹp. Bề ngoài như vậy, thế nên tưởng đâu bên trong cô cũng hiền lành, khiêm nhường và giản dị. Cho đến khi có cơ hội được hợp tác, người ta mới biết A có thái độ làm việc khá “lồi lõm”.</p><p>Cụ thể, một công ty nọ mời A chiếc job quay quảng cáo. Để tăng tính hấp dẫn cho chiếc job này, A sẽ được kết hợp cùng KOL nữ (hoa đã có chậu) cũng có “số má” ở thời điểm hiện tại. Hợp đồng đặt ở trên bàn, thời gian địa điểm rõ ràng, tưởng đâu cứ thế mà thực hiện thôi. Thế nhưng, có ngờ đâu tới ngày quay, A đến rồi dùng dằng bỏ về để vài chục người ngơ ngác chờ đợi, ekip lúc này mới tá hỏa: Alo chị ơi, chị đâu rồi? Ơ mất sóng ạ? Alo...</p><p>Lý do được đưa ra cho sự bỏ về đột ngột này là vì A là... \"ngôi sao” (?) Hóa ra, việc nhãn hàng để nàng KOL kia được quay trước đã làm phật ý cô nàng, trong khi A mặc niệm rằng mình có tiếng tăm như thế, là “người quan trọng” như thế thì phải luôn được ưu tiên. Thực tế 5-6 năm trước, A cũng từng là gương mặt hot lên từ màn ảnh, luôn chiếm spotlight của những sự kiện thảm đỏ, thậm chí lồng lộn lộng lẫy đến mức được netizen phong hẳn biệt danh riêng. Đến bây giờ, công chúng cũng không còn quá mặn mà như xưa, nhưng lại khá tò mò về lối sống thân thiện, thoát tục của cô. Chủ yếu cũng để \"hóng hớt\" xem, A đã thay đổi như thế nào sau thời gian hoạt động nghệ thuật khá thị phi và quyết định chuyển hẳn sang hình tượng ngoan hiền.</p><p>Ngày thứ 1 thôi thì ghi “nợ”, chờ ngày A trả. Ngày thứ 2 ekip cũng lưu tâm hơn rồi, đành “ngậm bồ hòn làm ngọt” chiều chuộng A hết nấc. Gặp ekip A cũng hào sảng, may thay lần này cô vui vẻ và công việc trôi chảy thêm phần nào.</p><p>Vài ngày sau, thấy A tiếp tục đăng status đạo lý, khuyên dạy người ta nên sống khiêm nhường, không sân si và A  vẫn là một tấm gương nức nở trong lòng nhiều người.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2020/11/30/artboard-3-copy-2-2-16067144937522016736805.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2020/11/28/bannercallout-16065695951691317751727.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Thế nhưng  ở trong ngành này, hãy tin rằng không có gì là bí mật. Họ biết cả đấy! Và mất uy tín một lần, thì sau chắc chắn chơi một mình. Người trong ngành cũng truyền tai nhau, cũng tiếc nuối như sau: A đã từng là một  viên đá quý, và viên đá đó có lẽ sẽ còn có thể lấp lánh, đẹp đẽ hơn nếu được lồng vào một chút tính khiêm nhường.</p>', NULL, 'co-co-nghe-si-gioi-noi-dao-ly-nhung-lai-thich-san-si-hanh-ca-ekip-vi-so-do-tung-chut-voi-kol-hot-nhat-hien-nay', 0, NULL, NULL),
(21, 3, 1, 1, 1, '\"Ông tướng\" Vbiz lật kèo phút 89: Chỉ thiếu nước trải thảm hoa hồng, mang kiệu gỗ ra khiêng mà vẫn không vừa lòng!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2020/11/16/photo1605544556416-16055445569552118379560.png', '|', 'Người có người \"this\" người \"that\", sao cũng có sao \"this\" sao \"that\"!', '<p>Dạo này trên mạng đang rần rần trước câu quote: \"Ngẫm thấy nghề chọn người chứ người đâu có ai chọn đi làm\". Thiệt, đi làm ngoài lúc lãnh lương ra thì giây phút nào cũng mệt mỏi đè trên vai...</p><p>Chuyện bất đồng trong cách làm việc với đối tác, chuyện tự dưng mình... nổi hứng lười, tự dưng muốn... hành mọi người xung quanh vốn dĩ rất thường và xảy ra đối với bất kể ai. Ngôi sao cũng không nằm ngoài lòng luẩn quẩn này. Chẳng ngôi sao nào có trách nhiệm phải nói \"Có\" với tất cả. Nhưng tất cả ngôi sao đều phải có trách nhiệm nói \"Không\" một cách văn minh.</p><p>Lời từ chối thốt ra sau khi đã nhận cục tiền \"siêu to khổng lồ\", lờ đi giao kèo ban đầu với ekip thật sự rất khó dung tha. Ví như \"sao chổi\" trong câu chuyện mà Chuyện ông sao sắp nói đến đây.</p><p>Có một anh diễn viên nọ, vốn nổi tiếng mặn mòi lại siêng làm clip parody hài hước nên được khán giả quý mến. Nhờ hiệu ứng tốt nên một agency có tiếng liên hệ anh để xuất hiện trên một livestream quảng bá sản phẩm.</p><p>Tiền trao cháo múc, ban đầu anh chẳng hề ý kiến gì. Kịch bản, tất tần tật đều được gửi trước một tuần để anh xem qua và học thuộc. Ai cũng biết, một sự kiện phát sóng trực tiếp luôn yêu cầu độ chỉn chu, hoàn thiện ở mức cao nhất và đương nhiên điều đó sẽ không xảy ra nếu nhân vật chính lật kèo phút 89. Và cơn ác mộng ấy ập tới sau một tin nhắn của anh diễn viên: \"Mai kêu khách tự live đi. Đưa cái tập làm văn đó ai nhớ nổi\".</p><p>Ơ kìa, chỉ còn vài tiếng nữa event diễn ra mà anh nói vậy coi sao đặng? Nuốt cục tức vào người, ekip vẫn dỗ ngon dỗ ngọt để anh đến trót lọt. À ừ anh cũng đến, nhưng 9h30 lên sóng thì 9h45 xe hơi của anh mới chầm chậm chạy tới. Nhưng thế đã hết đâu, đến rồi anh cũng chẳng chịu bước xuống xe mà bắt bảo vệ mở đường riêng cho chạy thẳng vô phòng chờ. Thiếu điều anh yêu cầu ekip trải thảm đỏ hoa hồng, mang kiệu gỗ ra khiêng vào... Nói ra thì anh cũng chưa phải sao hạng A, sao quyền lực hay super star gì, nhưng sao lại yêu sách và tuỳ hứng đến thế?</p><p>Đúng như dự đoán, kịch bản vỏn vẹn vài câu anh vẫn chưa đọc một chữ. Xuống livestream chào mà anh phải nhờ một nhân viên ở quầy sản phẩm lên đứng cùng để đỡ lời, còn anh thì cứ mỉm cười cho qua chuyện. Hài nhất ở chỗ, tên sản phẩm mà anh nhận tiền để quảng cáo... anh vẫn không nắm được. </p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2020/11/16/artboard-1-copy-20-16055445466901994560174.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2020/11/17/bannercallout-16055815278961263472012.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Là người ngoài cuộc mà nghe xong câu chuyện này, chúng tôi chỉ biết ước Doraemon có thật để give-away mỗi agency và brand một cái cỗ máy quay ngược thời gian, sửa sai mọi lỗi lầm. </p>', NULL, 'ong-tuong-vbiz-lat-keo-phut-89-chi-thieu-nuoc-trai-tham-hoa-hong-mang-kieu-go-ra-khieng-ma-van-khong-vua-long', 0, NULL, NULL),
(22, 3, 1, 1, 1, 'Drama của 4 IT girl Việt: Chỉ vì tấm váy áo hãng gửi nhờ chụp mà giành giật nhau đôm đốp, \"sân si\" hơn thua tới cả giờ up bài', 'https://kenh14cdn.com/thumb_w/300/2020/11/9/ngang-1604897910616841229666-crop-1604897921578879579331.png', '|', 'Bạn đã nghe phát ngán về drama giới diễn viên, ca sĩ, IT girl hay WAGs? Đã đến lúc để đổi vị rồi đấy.', '<p>\"Drama\" là kịch, là văn nghệ, là những món giải trí trên sân khấu chói đèn.</p><p>Nhưng ở cái thời 4.0 này, drama trên sân khấu thì ít, giữa đời thường thì lắm. Mỗi ngày lại có một drama khác nhau: từ cô người yêu không biết đã cũ lâu rồi hay vừa mới thành ra cũ của anh cầu thủ ưa nhún nhảy, hay mỹ nhân ưa thảng thốt lời vàng lời ngọc rồi hóa ra mắc chứng mất trí nhớ tạm thời nên nay một kiểu mai lại một kiểu chẳng biết đường nào mà lần.</p><p>Và bổ sung cho thực đơn drama đa dạng dành cho khán giả lần này là mảng miếng thời trang, với các nhân vật cùng thuộc một ngành nghề vô cùng thú vị: fashionista, đã được nâng cấp thành \"IT girl\".</p><p>Số lượng nhân vật chung tay góp sức vào drama này cũng đông vui, lên đến tận 4 cô! Các cô gọi tắt lần lượt là A, B, C và D đi nhé.</p><p>Trong đó A và B vốn xuất phát điểm là hai hot girl Hà thành, tiếng tăm đã lâu, đi fashion week nhiều như đi chợ. C và D lại được khán giả nhớ mặt đặt tên từ một show truyền hình thực tế về người mẫu - tức còn khá mới, trong đó D từng bị so sánh là một bản sao của A.</p><p>Drama nhen nhóm từ khi công chúng và giới chuyên môn nhận ra rằng một số thương hiệu thời trang cao cấp hơi bị \"ki bo\" nên mỗi mùa chỉ nhập về lèo tèo vài thiết kế mẫu (sample), nhưng lại muốn phát số lượng đồ ít ỏi đó cho thật nhiều IT girl đình đám nhằm tăng độ phủ sóng trên mạng xã hội. Chính vì lẽ đó thành ra cái cảnh \"chung chạ\" đến khổ, mỗi sample cứ lướt qua tay từng cô như một cơn gió, cô này vừa mặc lên chụp hình chưa ấm hơi người đã phải cởi ra gửi sang cho cô khác chụp tiếp. Các cô bị soi ra là \"đụng hàng\" nhau liên tục, có khi đụng từ-đầu-đến-chân không chút cải biên. Nhiều người còn cười rằng các cô nàng là IT girl thế mà cải trang thành bản sao của nhau.</p><p>Mâu thuẫn bùng nổ thật sự khi một thương hiệu thời trang cao cấp của Pháp nhập về 6 sample của mùa, chọn 6 gương mặt để trao đến tay từng mẫu. Trong 6 người đó có đầy đủ A - B - C - D và một sao nữ thứ hạng trội bật, một nàng hot girl khác tính tình dễ chịu không mấy sân si. Có lẽ vì sự cách biệt của 2 nhân vật còn lại nên trận địa chỉ xoay quanh 4 cô nàng chúng ta đang nói đến. Và diễn biến tiếp đó là:</p><p>- Cô A nghĩ rằng mình \"hot\" như thế, phải đòi hỏi được chụp đầu tiên và lấy tận 2 mẫu đồ với lý do \"phòng hờ\" nếu bộ đầu tiên chụp lên không thấy hợp, trong 2 mẫu đó có một mẫu hãng của cô nàng D. Tuy nhiên khi sản xuất hình ảnh, A chỉ chụp đúng bộ \"giật\" từ D.</p><p>- Cô C thấy A ra yêu cầu như thế cũng không kém cạnh, đòi thêm một bộ nữa của cô B với lý do tương tự. Nhưng C lại chơi đẹp hơn A ở chỗ đã trót \"giật\" thì thôi chụp cả 2 bộ, lên cả 2 bài cho thương hiệu mát lòng mát dạ.</p><p>- Cô B biết thừa mình bị C \"chẹt\" mấy lần nhưng cũng ngậm bồ hòn làm ngọt. Tuy nhiên cô im ỉm phá lệ của thương hiệu là các KOLs cùng đăng tải hình ảnh và thông tin BST mới trong cùng một khung giờ. Cô ra tay up hình trước A và C đến tận 2 tiếng. Khi A và C nắm được tình hình thì gây sức ép với bên thương hiệu để bắt B phải gỡ mọi hình ảnh xuống. Lúc này, B chốt hạ nhẹ nhàng là \"KHÔNG\" với lý do lượng follower của B gấp gần 5 lần của C, xử lý thế không thỏa đáng.</p><p>- Cô D muốn đứng ngoài drama nhưng đúng là nằm im cũng dính đạn. Cô và ekip của mình biết thừa A có ý chơi xấu mình nhưng vì rõ vị thế không bằng nên khó có thể lật ngược ván cờ. Thay vào đó, cô chuyên tâm sản xuất hình ảnh tử tế và được khen ngợi là đúng tinh thần thương hiệu hơn A. Ekip của cô cũng bóng gió rằng A tuy \"giật\" của cô nhưng lại không thèm đầu tư chất xám đúng mức, chẳng qua là đang đi chụp ảnh thời trang cho thương hiệu khác rồi \"lạc kèm bia\" thêm một bộ của thương hiệu này cho có mà thôi.</p><p>Trên là drama giữa 4 IT girl Việt, nóng rẫy nhưng không phát tiết ra ngoài. Các cô từ trước đến nay ngoài mặt là nước sông không phạm nước giếng nhưng trong lòng thì lại cạnh tranh tới bến, chủ đích \"chặt chém\" nhau đến cùng mỗi khi có cơ hội. Dù sao cũng phải khen là các cô không phải loại thôn thốt nói cười với nhau nhưng tay chỉ lăm le đâm nhau một nhát, nói cách khác là giả tạo với nhau.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/11/9/ngang-1604897910616841229666.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Các cô có thể tâm cơ chứ không hề giả tạo. Thế là tốt.</p>', NULL, 'drama-cua-4-it-girl-viet-chi-vi-tam-vay-ao-hang-gui-nho-chup-ma-gianh-giat-nhau-dom-dop-san-si-hon-thua-toi-ca-gio-up-bai', 0, NULL, NULL),
(23, 3, 1, 1, 1, 'Lật mặt \"thánh thảo mai\" Vbiz: Ca sĩ hứa thật nhiều rồi thất hứa thật nhiều, \"hành\" cả học sinh nổi tiếng mới chịu!', 'https://kenh14cdn.com/thumb_w/300/2020/11/9/artboard-1-copy-34-16049340712741140980099-crop-16049361414851772087477.png', '|', 'Hống hách hay thảo mai, cái nào khiến người ta phải e dè hơn? Trong tập mới nhất của Chuyện ông sao, chúng ta sẽ cùng đến với một nhân vật có độ \"lật mặt\" nhanh hơn lật bánh tráng của showbiz Việt.', '<p>Khéo mồm, khéo miệng mọi việc khi trôi chảy. Sống thẳng, sống thật quá đôi khi lại chuốc hoạ vào thân. Vốn dĩ, thảo mai sao cho vừa vặn là cả một nghệ thuật mà người nào có trình độ \"nhảy số\" nói dối nhanh là một nghệ sĩ. Nhưng để diễn tròn vai từ A đến Z thì cần nhiều hơn thế, may mắn chẳng hạn? Đây là điều mà có vẻ ông trời đã bất công lấy mất khỏi anh ca sĩ A trong câu chuyện sẽ kể đến sau đây.</p><p>Trái ngược những \"ông sao, bà sao\" với tính tình hống hách, hay làm trịch làm thượng, anh A nói chuyện ngọt như mía lùi. Người xa lạ, mới gặp lần đầu mà anh cũng lấy lòng được trong một giây. Bằng cách nào đó, anh tô vẽ cho mình hình hài thân thiện như chàng trai nhà bên, khiến bao anh (em) say đắm. FC của A cũng lọt top những fandom hùng hậu nhất Vbiz, cứ hôm nào anh đi diễn show là fan xuất hiện đông nghẹt, giơ cao lightstick sáng như những ánh sao.</p><p>Giữ đúng tác phong mọi khi, anh A xởi lởi ngay khi công ty B đề nghị giúp đỡ kênh truyền thông của mình. Nhưng lúc bên này liên hệ lại \"chốt đơn\", anh liền giở màu thảo mai với 7749 lý do \"Chờ anh diễn xong show này\", \"Hẹn vài hôm anh vào Sài Gòn\", \"Đợi anh bàn bạc thêm cùng ekip\",... Chung quy vẫn là những lời hứa chót lưỡi đầu môi, một đi không trở lại. Cũng nghe tiếng anh A từ lâu nên công ty B buông xuôi sớm sau vài tháng đợi chờ. Câu chuyện sẽ chẳng có gì đáng nói nếu không phải một ngày đẹp trời, anh A chủ động gọi điện xin hẹn để nói lại cái deal hợp tác mà chính anh đã nhiều lần ruồng bỏ.</p><p>Lần này liệu có khá khẩm hơn? Xin thưa là không. Được vài hôm tưởng êm xuôi, công ty B vỡ lẽ ra anh A đã đi trước một nước khi kí hợp đồng với công ty đối thủ của B là C, cái hợp đồng mà chính ảnh đã chủ động hứa hẹn với công ty B. Vậy mà khi được hỏi đến, anh ca sĩ lại ú ớ giả ngơ rằng anh không biết chuyện gì đang xảy ra. Oke fine... Chứ ai đâu mà nghe anh diễn mãi cái màu thảo mai.</p><p>Lục lại quá khứ, cũng nhờ cái miệng múa giỏi quá mà \"thầy giáo\" A nhận được sự tin tưởng tuyệt đối của các bậc phụ huynh khi gửi con em mình đi học thanh nhạc. Mà bố mẹ các cháu đâu có biết rằng, thầy dạy xướng âm nhưng \"dùng vợt muỗi để chích điện học sinh\" (Chú ý: Hình ảnh ẩn dụ, không phải nghĩa đen). Ai ngờ rằng cậu học trò ngày nào lại thành ca sĩ thuộc hàng top của showbiz Việt, ra bài nào hit bài đấy.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/11/9/artboard-1-copy-34-16049340712741140980099.png\" ><figcaption class=\"caption\">Nhớ không A, lời hứa ngày xưa?</figcaption></figure><p>Nhưng đâu phải nổi tiếng là xong, trần đời có thầy giáo nào yêu sách như A, vẫn cho học sinh \"ăn hành\" như thường. Mỗi khi học trò đi diễn, chỉ cần đó là bar/ bầu show anh không ưa, anh liền một một hai hai bắt em quay xe về dù có phải đền cát-xê ngay sau đó. Đúng là trên đời này cái gì cũng có thể xảy ra.</p>', NULL, 'lat-mat-thanh-thao-mai-vbiz-ca-si-hua-that-nhieu-roi-that-hua-that-nhieu-hanh-ca-hoc-sinh-noi-tieng-moi-chiu', 0, NULL, NULL),
(24, 4, 2, 1, 1, 'Quiz: Nhìn dáng đoán người, bạn có nhận ra hết cựu thí sinh Vietnam\'s Next Top Model?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617782185297-1617782186200352122525.jpg', '|', 'Cùng làm bài trắc nghiệm dưới đây để xem bạn có phải là fan cứng của Vietnam\'s Next Top Model không nhé!', '<p></p><p>Vietnam\'s Next Top Model là show thực tế có tuổi đời hơn 10 năm, là nơi phát hiện ra nhiều thế hệ người mẫu triển vọng cho làng thời trang Việt Nam. Cùng làm bài trắc nghiệm nhìn dáng đoán người dưới đây để xem bạn có nhận ra hết các cựu thí sinh của chương trình không nhé!</p>', NULL, 'quiz-nhin-dang-doan-nguoi-ban-co-nhan-ra-het-cuu-thi-sinh-vietnams-next-top-model', 0, NULL, NULL),
(25, 4, 2, 1, 1, 'Mai Phương Thuý khoe visual trẻ trung, xinh đẹp khi đi ghi hình Ký Ức Vui Vẻ', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617785591733-1617785591963611223025.jpg', '|', 'Với Mai Phương Thuý, Ký Ức Vui Vẻ là chương trình mang lại cho cô những khoảng thời gian vô cùng ý nghĩa.', '<p>Mới đây, mỹ nhân đình đám làng Hoa hậu Việt - Mai Phương Thuý vừa có cơ hội tham gia ghi hình chương trình Ký Ức Vui Vẻ. Mai Phương Thuý xuất hiện giản dị trong set đồ đen trắng cách điệu từ chiếc áo sơ mi của NTK Công Trí.</p><p>Chị đại làng Hoa hậu cho biết cô rất vui khi được tham dự Ký Ức Vui Vẻ và muốn tham gia từ lâu nhưng đến giờ mới có cơ hội được xuất hiện trong chương trình.</p><p>Mai Phương Thuý cho biết: \"Thuý được xếp vào thập niên 2000 cùng đội trưởng là Nhan Phúc Vinh, lâu rồi mới ngồi lại cùng lắng nghe chia sẻ về những hồi ức mới thấy thời gian trôi thật nhanh. Thuý thích những thử thách lồng ghép với các điểm đặc trưng mang lại những kiến thức thú vị cho người chơi lẫn người xem\".</p><p>Mai Phương Thuý ít xuất hiện tại các chương trình cũng như gameshow vì lịch trình bận rộn. Tuy vậy khi có thời gian rảnh là cô thường tham gia để trải nghiệm và học hỏi nhiều hơn cũng như được gặp người hâm mộ của mình. Trước đây, lần xuất hiện của cô tại Người Ấy Là Ai mùa 2 cũng để lại nhiều câu chuyện thú vị cho fan cùng bàn luận.</p><p>Dưới phần bình luận của tập phát sóng lần này, người hâm mộ không tiếc lời khen ngợi sự duyên dáng và xinh đẹp, trẻ trung của Mai Phương Thuý. Nhiều khán giả viết: \"Mai Phương Thuý xinh đẹp như một bài dân ca\" hay \"Mai Phương Thuý duyên dáng và thông minh quá\", cũng có người kể lại kỷ niệm như: \"Lúc trước Thuý có về trường mình và được chụp ảnh cùng, cả hai bằng tuổi mà sau bao năm cô ấy vẫn trẻ đẹp thế nhỉ!\"...</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/vquy1845-16177849965901007234141.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/vquy1944-1-16177849966211338771807.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/vquy1950-2-1617785468775287653977.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/vquy2000-16177854688701205289243.jpg\" ><figcaption class=\"caption\">Mai Phương Thuý trẻ trung, xinh đẹp trong lần tái xuất truyền hình này</figcaption></figure><p>Ảnh: Viết Quý</p>', NULL, 'mai-phuong-thuy-khoe-visual-tre-trung-xinh-dep-khi-di-ghi-hinh-ky-uc-vui-ve', 0, NULL, NULL),
(26, 4, 2, 1, 1, 'Quân Hồ - Mai Sương sau 2 năm rời khỏi Người Ấy Là Ai: Nàng vượt qua cú sốc bệnh tật, chàng tìm thấy tình yêu mới', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617771622995-16177716232871201563377.jpg', '|', 'Cả Quân Hồ & Mai Sương đều đã tìm thấy niềm vui riêng sau khi dừng lại mối quan hệ do Người Ấy Là Ai mai mối.', '<p>Mai Sương là nữ chính ở tập 13 Người Ấy Là Ai mùa 2 vào năm 2019. Giữa 5 chàng trai được chương trình chọn lựa, cô nàng xinh đẹp quyết định trao đóa hoa cho nhiếp ảnh gia Quân Hồ. Không phụ lòng nữ chính đã \"bật đèn xanh\" từ trước, Quân Hồ đã giơ cao chiếc khăn màu xanh, khẳng định Độc thân.</p><p>Cả 2 đều là những gương mặt có độ nổi tiếng nhất định trên mạng xã hội nên khi chính thức ghép đôi đã khiến con dân nức nở. Sau đó, Quân Hồ - Mai Sương đã trải qua những tháng ngày hẹn hò ngắn ngủi trước khi quyết định dừng lại ở mối quan hệ bạn bè, để lại nhiều tiếc nuối cho mọi người.</p><p>Sau khi dừng lại mối quan hệ, cả 2 đã tìm cho mình những lối đi riêng. Vào giữa năm 2020, Mai Sương gây xôn xao cộng đồng mạng khi công bố một sự cố gây ảnh hưởng nghiêm trọng đến sức khỏe của bản thân. </p><p>Cô nàng chia sẻ ngày 13/6/2019, cô có đến một địa chỉ spa thân thiết để thực hiện tiêm filler cho môi. Vì chân của cô có vài nốt thâm do muỗi đốt nên chủ spa gợi ý cô tiêm xóa thâm vào chân với chi phí là 1,5 triệu/mũi tiêm. Dù vết thâm rất nhỏ nhưng có thể gây ảnh hưởng đến công việc, cơ sở này lại là nơi Mai Sương sử dụng dịch vụ thường xuyên và có sự yên tâm nên cô nàng đã đồng ý.</p><p>Sau khoảng 3 tháng, bên chân bị tiêm của Mai Sương nổi gân xanh, vết thâm không hết mà trái lại càng nặng hơn, mỗi lần tập gym hoặc đi lại đều rất đau. Cô đến khám tại Bệnh viện Da liễu Trung ương, được bác sĩ chẩn đoán là teo cơ, giãn tĩnh mạch do tác dụng phụ của thuốc và kỹ thuật tiêm quá sâu khiến chân tổn thương.</p><p>Sau khi dành thời gian để chữa bệnh, Mai Sương đã trở lại khỏe mạnh. Cô nàng dành nhiều thời gian cho công việc kinh doanh cá nhân cũng như tận hưởng cuộc sống độc thân bằng những chuyến du lịch sang chảnh. </p><p>Về phía Quân Hồ, anh chàng vẫn tiếp tục công việc mà mình đang theo đuổi. Chuyện tình cảm của mỹ nam cũng có dấu hiệu khởi sắc khi anh công khai hẹn hò với nữ diễn viên Vi Xù. Cả 2 được nhận xét là vô cùng đẹp đôi.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/tai-xuong-15632900665721624044227-1617771919645582157275.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/nhan1417-15645552705711741309826-16177719196461710301889.jpg\" ><figcaption class=\"caption\">Quân Hồ - Mai Sương từng để lại nhiều tiếc nuối khi dừng lại chuyện tình cảm</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/9253868316770162724657045848742925885767680o-15875277654371794710073-1617772077070283618709.jpg\" ><figcaption class=\"caption\">Hình ảnh vết thương của Mai Sương</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/16908718420053821762957772978955851673161096n-1617772173079361879307.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/17059226020052263529780261098178772239611120n-16177721839931594089133.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/16641782320050208463319108770574227854267133n-16177721965312079216556.jpg\" ><figcaption class=\"caption\">Mai Sương đang tận hưởng cuộc sống độc thân sang chảnh</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/12915900110215068511105343357108774075981826n-16177724091702023463280.jpg\" ><figcaption class=\"caption\">Quân Hồ vẫn điển trai ngời ngời</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/15056472410215500038453257971250847969548073n-1617772409172243944436.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/16502485516402005961740805659739598134216087n-1617772409172460945349.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1667734251638051013055705911493062147878020n-1617772409173752773801.jpg\" ><figcaption class=\"caption\">Anh đang hẹn hò với nữ diễn viên Vi Xù</figcaption></figure><p>Ảnh: FBNV</p>', NULL, 'quan-ho---mai-suong-sau-2-nam-roi-khoi-nguoi-ay-la-ai-nang-vuot-qua-cu-soc-benh-tat-chang-tim-thay-tinh-yeu-moi', 0, NULL, NULL),
(27, 4, 2, 1, 1, 'Khôi hài chuyện bê nguyên xi đồ của người khác làm trang phục dân tộc cho Trân Đài, còn biến Tấm thành... Âu Cơ', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617770789552-16177707898051348248362.png', '|', 'Nhưng làm gì có nàng Âu Cơ nào mà liên quan đến chim vàng anh, giỏ xúc tép với quả thị như vậy? Và tất nhiên, thiết kế trang phục dân tộc của thí sinh này đã nhanh chóng bị tháo xuống.', '<p>Cuộc thi tuyển chọn trang phục dân tộc cho Trân Đài - đại diện Việt Nam tại Miss International Queen 2021 đang diễn ra vô cùng sôi động. Rất nhiều bài thi với những thiết kế đặc sắc đã được gửi về cho ban tổ chức, xem ra ê-kíp sẽ rất đau đầu để chọn ra mẫu trang phục đạt giải Nhất đây!</p><p>Có lẽ vì số lượng bài thi gửi về quá nhiều nên ban tổ chức đã không thể kiểm soát được hết nên mới dẫn đến một sự cố nhỏ. Cụ thể, một bản vẽ bưng nguyên bài thi của thí sinh thiết kế trang phục dân tộc cho Hoàng Thùy ở Miss Universe 2019 bất ngờ lọt qua vòng kiểm duyệt. Thí sinh này thậm chí đổi tên ý tưởng ban đầu từ Tấm sang... Âu Cơ. </p><p>Sau khi nhận được phản hồi mạnh mẽ từ cộng đồng mạng, ban tổ chức Miss International Queen Vietnam đã nhanh chóng tháo bài thi này xuống. Đây là sơ sót có thể thông cảm được vì số lượng thiết kế gửi về quá nhiều và khó kiểm soát được!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/base64-1617770585235198056869.png\" ><figcaption class=\"caption\">Thiết kế Tấm dành cho đại diện Việt Nam tại Miss Universe 2019</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/base64-16177706587582045543750.png\" ><figcaption class=\"caption\">Không những bị bê nguyên xi sang Miss International Queen 2021 mà còn biến thành... Âu Cơ nhưng làm gì có nàng Âu Cơ nào mà liên quan đến chim vàng anh, giỏ xúc tép với quả thị như vậy</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/15805062214573258711285606527314988283120358n-1617770734682301097949.jpg\" ><figcaption class=\"caption\">Cuộc thi tuyển chọn trang phục dân tộc cho Trân Đài vẫn đang diễn ra sôi nổi</figcaption></figure><p>Ảnh: Internet</p>', NULL, 'khoi-hai-chuyen-be-nguyen-xi-do-cua-nguoi-khac-lam-trang-phuc-dan-toc-cho-tran-dai-con-bien-tam-thanh-au-co', 0, NULL, NULL),
(28, 4, 2, 1, 1, 'Nathan Lee và giám đốc sản xuất Vietnam\'s Next Top Model chính thức xoá bỏ mâu thuẫn 11 năm về trước', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617765404534-16177654048011352106008.jpg', '|', 'Sau 11 năm kể từ drama cực căng tại Vietnam\'s Next Top Model 2010, mọi vấn đề hiểu lầm giữa Nathan Lee và bà Trang Lê đã hoàn toàn được gỡ bỏ.', '<p>Nathan Lee mới đây đã xuất hiện trong sự kiện chúc mừng bà Trang Lê - Giám đốc sản xuất Vietnam\'s Next Top Model nhận \"Huân chương công trạng Ý\" trong lĩnh vực thời trang và ngành truyền thông. Đây được xem là lần \"tái ngộ\" đầu tiên giữa cả hai sau những scandal xảy ra tại Vietnam\'s Next Top Model 2010.</p><p>Thời điểm 11 năm trước, Nathan Lee là một trong những giám khảo đầu tiên của Vietnam\'s Next Top Model 2010 cùng người mẫu Elizabeth Thuỷ Tiên và nhà thiết kế Hoàng Ngân. Tuy nhiên, sau vòng casting, toàn bộ \"bộ sậu\" này đã được thay thành 3 vị giám khảo mới là người mẫu Hà Anh, Đức Hải và nhà thiết kế Huy Võ. </p><p>Lý do được đưa ra từ bà Trang Lê khi đó là do mâu thuẫn nội bộ giữa dàn giám khảo, cụ thể là Nathan Lee và Elizabeth Thuỷ Tiên, có người cố tình tung tin thất thiệt, phá hoại chương trình. Trong khi đó, cả hai đều cho rằng không thể tiếp tục hợp tác cùng chương trình vì mâu thuẫn với nhà sản xuất. </p><p>Trong sự kiện đặc biệt vừa diễn ra tối qua (ngày 6/4), bà Trang Lê và Nathan Lee đã chính thức xoá bỏ mọi hiểu lầm trong quá khứ. </p><p>Bà Trang Lê thừa nhận vào thời điểm 11 năm trước cả hai bên đã có một \"trận chiến\" và Nathan Lee đã khiến bà trở nên nổi tiếng như một \"drama queen\" trên mạng xã hội lúc bấy giờ. Thế nhưng hôm nay, mọi thứ đã hoàn toàn được gỡ bỏ. Không những thế, Nathan Lee còn lên sân khấu khoe giọng hát xem như lời chúc mừng gửi đến bà Trang Lê.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177649510781985384208.jpg\" ><figcaption class=\"caption\">Bà Trang Lê nhận Huân chương công trạng Ý</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177650656431361438199.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177649913901624186841.jpg\" ><figcaption class=\"caption\">Nathan Lee xuất hiện trong sự kiện đặc biệt này</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/remini20210407100435674-16177647558521450731205.jpg\" ><figcaption class=\"caption\">Bộ ba giám khảo vòng casting Vietnam\'s Next Top Model 2010: Hoàng Ngân, Nathan Lee, Elizabeth Thủy Tiên</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/remini20210407100522912-1617764755809894036852.jpg\" ><figcaption class=\"caption\">Sau được đổi thành nhà thiết kế Huy Võ, người mẫu Hà Anh, Đức Hải</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/remini20210407101547660-16177653974451508194874.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Ảnh: Internet</p>', NULL, 'nathan-lee-va-giam-doc-san-xuat-vietnams-next-top-model-chinh-thuc-xoa-bo-mau-thuan-11-nam-ve-truoc', 0, NULL, NULL),
(29, 4, 2, 1, 1, 'Thúy Vân sẽ làm thế nào trước 2 \"nữ hoàng drama\" Minh Tú - Mâu Thủy trong show thực tế mới?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617715208975-1617715209985732967394.jpg', '|', 'Minh Tú - Mâu Thủy thì đã quá quen thuộc với khán giả qua các màn drama tung trời rồi, Thúy Vân sẽ thể hiện như thế nào đây?', '<p>Hoa Khôi Ngôi Sao Thể Thao Việt Nam - Miss Fitness Star Vietnam là đấu trường sắc đẹp hoàn toàn mới tôn vinh vẻ đẹp hiện đại, khỏe khoắn, đại diện cho thế hệ trẻ có bản lĩnh - tri thức và tài năng. Mùa giải đầu tiên sẽ được dẫn dắt bởi 3 HLV Minh Tú - Mâu Thủy - Thúy Vân, cả 3 đều đã quá quen thuộc với khán giả qua các show thực tế nên việc tạo kịch tính cho chương trình như thế nào thì chắc cũng khiến mọi người yên tâm phần nào.</p><p>Nếu so về độ drama thì Minh Tú - Mâu Thủy sẽ có phần nổi trội hơn Thúy Vân. Minh Tú & Mâu Thủy từng có một cuộc chạm trán nho nhỏ ở Tôi Là Hoa Hậu Hoàn Vũ Việt Nam 2019 - cuộc thi mà Thúy Vân làm thí sinh. Khi một thí sinh thực hiện thử thách không tốt, Minh Tú đã phải vào hướng dẫn. Điều này làm Mâu Thủy không giữ được bình tĩnh và lên tiếng nhắc nhở: \"Bây giờ chị không muốn các em diễn mà mentor khác vô chỉnh các em. Ok? Rất là xấu hổ!\". </p><p>Minh Tú không đồng quan điểm với Mâu Thủy: \"Các em ơi, thư giãn đi, cái này là Hoa hậu Hoàn vũ chứ không phải những cuộc thi chia đội đâu, đội nào vô chị cũng chỉ à!\". Mâu Thủy chia sẻ: \"Ok, hơi buồn nhưng rất là cảm ơn Tú đã giúp đỡ một phần nào!\".</p><p>Bên cạnh đó, sự bất đồng ý kiến giữa Minh Tú và Mâu Thủy khiến các thí sinh trở nên bối rối và không khí vô cùng căng thẳng. Cả hai HLV liên tục cãi nhau về vấn đề màu sắc cũng như có những bất đồng quan điểm về cách thị phạm cho các thí sinh.</p><p>Minh Tú cho rằng: \"Màu chủ đạo thương hiệu mình là màu đỏ. Quá nhiều màu xanh sẽ gây tùm lum hết. Mình muốn nêu tinh thần chứ không phải bán quả bóng hay cục tạ\". Trước ý kiến này, Mâu Thủy đã bày tỏ sự bất mãn: \"Cái màu xanh trong phòng tập gym mình lại đi từ chối nó. Thủy cũng không hiểu\".</p><p>Ngoài cuộc chạm trán ở trên, Minh Tú - Mâu Thủy cũng có không ít lần giật spotlight ở những chương trình mà mình tham gia bằng các màn drama nổ tung trời.</p><p>So với 2 đàn chị, Thúy Vân chỉ mới xuất hiện ở 2 show thực tế là Hoa khôi Áo dài & Tôi Là Hoa hậu Hoàn vũ Việt Nam, nhưng hình ảnh mà cô để lại khá hoàn hảo, trang nhã, thanh lịch và ít khi vướng vào drama. Chính vì vậy nhiều người lo rằng cô sẽ bị lép vế Minh Tú & Mâu Thủy trong cuộc chiến drama mới này!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/2-bo-ba-mentor-miss-fitness-star-vietnam-1jpg-16176351024231658801208.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/2f850ee480b779e920a6-1391-1574420130-16177157114631950485386.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/thuthachchupanhcitigymtap8toilahoahauhoanvuvietnam20195polb-1617715616543295703538.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/img-1128-1498403449441-16177158991235187792.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/dang-quay-hinh-mau-thuy-bo-di-ve-sinh-choc-tuc-tyhd-chung-nao-thich-quay-thi-quay-cbd-5570990-16177158991242119746082.jpg\" ><figcaption class=\"caption\">Cả Minh Tú lẫn Mâu Thủy đều rất drama</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/bil-5699-5848-1617674702-1617716051160906160225.jpg\" ><figcaption class=\"caption\">Thúy Vân liệu có bị lép vế về drama?</figcaption></figure><p>Ảnh: Internet</p>', NULL, 'thuy-van-se-lam-the-nao-truoc-2-nu-hoang-drama-minh-tu---mau-thuy-trong-show-thuc-te-moi', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(30, 4, 2, 1, 1, 'Lại thêm \"giai thoại mới\" về nhân cách vàng của Yoo Jae Suk: Tự tay làm điều này phục vụ hậu bối tại Running Man', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617721596667-16177215969781381372788.jpeg', '|', 'Hành động của \"MC quốc dân\" Yoo Jae Suk khiến nhóm nhạc nổi tiếng tại Hàn Quốc vô cùng cảm động.', '<p>Brave Girls - nhóm nhạc idol hiện đang nổi đình đám tại Hàn Quốc gần đây đã xuất hiện trong một tập phát sóng của Running Man. Sau khi kết thúc ghi hình, 4 cô gái đã có bài phỏng vấn với một tờ báo Hàn Quốc, gửi lời cảm ơn đến các thành viên chương trình cùng ê-kíp, đặc biệt là \"MC quốc dân\" Yoo Jae Suk.</p><p>Cụ thể, các thành viên Brave Girls chia sẻ: \"Running Man là một trong những chương trình chúng tôi muốn xuất hiện nhất. Chúng tôi rất lo lắng vì thiếu kinh nghiệm, nhưng nhờ sự giúp đỡ của các nhân viên và các thành viên, chúng tôi đã có thể ghi hình một cách thoải mái\". Không những thế, Brave Girls còn tiết lộ \"MC quốc dân\" Yoo Jae Suk đã khiến họ vô cùng cảm động: \"Trong giờ nghỉ, tiền bối Yoo Jae Suk đã đích thân nấu mì ramen cho chúng tôi. Điều này thực sự khiến chúng tôi cảm động\".</p><p>Trước đó, Yoo Jae Suk đã gặp Brave Girls khi ghi hình chương trình You Quiz On The Block. Tại đây, \"MC quốc dân\" đã thể hiện sự ủng hộ của mình dành cho hậu bối bằng cách tặng ảnh kèm chữ ký cùng lời chúc: \"Thật vui khi gặp lại các bạn sau 10 năm. Chúc các bạn gặt hái được nhiều thành công. Chúc Brave Girls may mắn! ”</p><p>Từ trước đến nay, Yoo Jae Suk vốn đã nổi danh là ngôi sao sở hữu nhân cách vàng của Kbiz, được người dân cả xứ kim chi yêu mến. \"Giai thoại\" về lòng tốt của Yoo Jae Suk được nhiều người nổi tiếng chia sẻ nhiều vô số kể. </p><p>Diễn viên hài Kim Shin Young cũng từng kể lại một câu chuyện khiến cô thấy rất biết ơn \"MC Quốc dân\" Yoo Jae Suk: \"Năm đó tôi chỉ ở độ tuổi 20, lần đầu tiên tôi được mời làm thành viên cố định trên show Come To Play và bắt đầu sự nghiệp diễn viên hài, thế nhưng suốt cả buổi tôi không nói được lời nào, cũng chẳng theo kịp cuộc trò chuyện của mọi người, tôi xấu hổ vô cùng và bắt đầu miên man suy nghĩ: \'Bản thân chỉ có thể như vậy thôi sao?\'. Thế rồi đột nhiên Yoo Jae Suk đến gặp và nói riêng với cô rằng: \'Anh xin lỗi do anh chưa chăm sóc nhiều cho em, anh biết rõ trong lòng em sẽ tổn thương khi trở về nhà khi cả buổi quay không nói được câu nào. Không phải vì em không tốt. Đó là sự thiếu sót của anh. Tuần tới anh sẽ chú ý nhiều hơn nhé!\'\".</p><p>Chính những lời động viên đó đã khiến Kim Shin Young bình tâm lại, đồng thời gạt bỏ suy nghĩ rút lui khỏi sự nghiệp diễn viên hài, nếu không làng giải trí đã không có một Kim Shin Young bản lĩnh như hiện tại.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/1-16177221253151967184950.png\" ><figcaption class=\"caption\">Brave Girls tại Running Man</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/screen-shot-2021-04-06-at-221658-16177226734222074661578.png\" ><figcaption class=\"caption\">Yoo Jae Suk đã tận tình chăm sóc Brave Girls khi 4 cô gái tham gia Running Man</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/unnamed-1617722755936589476188.jpeg\" ><figcaption class=\"caption\">Brave Girls khi ghi hình You Quiz On The Block</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177232038671979442025.jpg\" ><figcaption class=\"caption\">Thời NU\'EST còn là tân binh, \"MC quốc dân\" đã âm thầm thanh toán bữa tiệc đắt đỏ của nhóm nhạc nam này khi vô tình gặp các hậu bối tại một nhà hàng sang trọng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177234361191177419688.jpg\" ><figcaption class=\"caption\">Nữ diễn viên Hậu Duệ Mặt Trời Kim Ji Won cũng từng hết lời cảm ơn Yoo Jae Suk vì đã giúp cô nàng thoát khỏi nhiều hình phạt khắc nghiệt khi tham gia Running Man</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177235899401610239559.jpg\" ><figcaption class=\"caption\">Kim Shin Young xúc động khi kể lại câu chuyện cùng Yoo Jae Suk nhiều năm về trước.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/yoojaesuk-1617721590341662722291.jpeg\" ><figcaption class=\"caption\">Nhân cách vàng của \"MC quốc dân\" Yoo Jae Suk là điều không cần bàn cãi</figcaption></figure><p>Nguồn: Koreaboo</p>', NULL, 'lai-them-giai-thoai-moi-ve-nhan-cach-vang-cua-yoo-jae-suk-tu-tay-lam-dieu-nay-phuc-vu-hau-boi-tai-running-man', 0, NULL, NULL),
(31, 4, 2, 1, 1, 'BB Trần lần đầu tiên nhắc đến Running Man Việt hậu tin đồn rút khỏi mùa 2', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617726189966-16177261901661871538892.png', '|', 'Sau tin đồn không có mặt trong dàn cast mùa 2 của Running Man Vietnam, đây là lần đầu tiên BB Trần nhắc đến chương trình.', '<p>Running Man Vietnam - Chạy Đi Chờ Chi sắp trở lại với mùa 2 đang là thông tin được khán giả vô cùng trông chờ. Tuy nhiên, bên cạnh đó lại xuất hiện nhiều tin đồn \"thánh chơi dơ\" BB Trần và \"Thỏ đen\" Ngô Kiến Huy sẽ không có mặt trong dàn cast mùa 2 khiến người hâm mộ xôn xao.</p><p>Sau khoảng thời gian dài không đề cập đến Running Man Vietnam, mới đây, BB Trần bất ngờ có động thái nhắc đến chương trình. Anh chia sẻ lại đoạn clip trong tập 14 với 2 khách mời Đông Nhi và Song Luân cùng khoảnh khắc đuối sức khi phải chạy trốn khỏi 2 đội đối thủ còn lại, kèm dòng trạng thái: \"Ngày này 2 năm trước lên sóng tập đầu tiên của Chạy Đi Chờ Chi, giờ xem lại vẫn thấy mắc cười tập này. Không hiểu sao lúc đó có thể làm được như vậy... Tự thấy sợ mình thiệt chứ\".</p><p>Tại Running Man Vietnam mùa 1, BB Trần là nhân tố gây cười cho khán giả với những khoảnh khắc lầy lội, \"chơi dơ\", phản bội trong chương trình. Nếu thiếu đi BB Trần trong mùa 2, chương trình sẽ mất đi một \"mảnh ghép\" quan trọng mang đến tiếng cười cho người xem.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/img0830-16177264272241337421618.png\" ><figcaption class=\"caption\">BB Trần \"nhắc nhẹ\" ngày Running Man Vietnam lần đầu tiên lên sóng</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/giphy-1617726828349419841989.gif.mp4\" ></video><div class=\"caption\">Trong clip, BB Trần phải cải trang, mượn khăn trải bàn làm áo choàng né các đội đối thủ</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/giphy-1-1617726828341261252988.gif.mp4\" ></video><div class=\"caption\">\"Thánh chơi dơ\" còn bị Jun Phạm cùng Đông Nhi uy hiếp phải hợp tác để diệt đội Song Luân</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/1-1617726409839532104959.jpg\" ><figcaption class=\"caption\">Dưới bài đăng, BB Trần cũng tag Jun Phạm ôn lại kỷ niệm</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/7-thanh-vien-running-man-viet-gay-tranh-cai-ban-dau-nhung-cuoi-cung-lai-an-tuong-ngoai-mong-doi-d13d4f-16177261649321547289652.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Ảnh: Chụp màn hình, Internet</p>', NULL, 'bb-tran-lan-dau-tien-nhac-den-running-man-viet-hau-tin-don-rut-khoi-mua-2', 0, NULL, NULL),
(32, 4, 2, 1, 1, 'Cung Tuấn (Thiên Nhai Khách) khiến fan \"đội quần\" với tạo hình Na Tra \"hồng lòe\"!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617719520508-1617719521448936523659.jpg', '|', 'Không ngờ Cung Tuấn lại có thể tự dìm hàng mình như vậy!', '<p>Nhờ có thành công của vai diễn Ôn Khách Hành trong Sơn Hà Lệnh - Thiên Nhai Khách, cái tên Cung Tuấn đã trở nên phổ biến hơn bao giờ hết. Trên khắp các trang mạng xã hội, không khó để khán giả có thể tìm thấy những thông tin, những hoạt động mới nhất của nam diễn viên.</p><p>Tên tuổi của nam diễn viên được phủ sóng rộng rãi và trở thành khách mời thường xuyên cho các show truyền hình như Sáng Tạo Doanh 2021, mới đây nhất là Happy Camp. Tuy nhiên, các fan đã được một phen hết hồn khi thấy tạo hình của Cung Tuấn. Nam diễn viên đã hóa thân thành Na Tra với trang phục \"hồng lòe\" cùng gương mặt được trát phấn kỹ lưỡng.</p><p>Nhìn hình ảnh này chỉ thấy buồn cười mà thôi, nhưng nhiều fan cũng thú nhận họ muốn \"đội quần\" khi thấy tạo hình này của Idol!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/1677289581684466484576834556991472717622182n-16177197774961786559181.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/790131275153907491893928464354743311925248n-16177198119931056648223.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/1011313236345407439410588670818824867545088n-16177198119931583363733.jpg\" ><figcaption class=\"caption\">Cung Tuấn đẹp trai mọi người thường thấy</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/16868099937770887524090214833109663517552710n-16177198747241617429789.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/17025101737770887824090186353089194722911760n-16177198747251831254701.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/16740733237770888157423487111296306216315550n-16177198747261297099368.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/16907113237770888057423491785076682819255530n-16177198747261198329380.jpg\" ><figcaption class=\"caption\">Tạo hình Na Tra gây cười của Cung Tuấn</figcaption></figure><p>Ảnh: Internet</p>', NULL, 'cung-tuan-thien-nhai-khach-khien-fan-doi-quan-voi-tao-hinh-na-tra-hong-loe', 0, NULL, NULL),
(33, 6, 3, 1, 1, 'Netizen sững sờ với diễn xuất của Song Luân ở Cây Táo Nở Hoa: Đó giờ tưởng chỉ đẹp thôi cơ!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617781546991-16177815470941891070012.gif.png', '|', 'Diễn xuất của Song Luân ở cuối tập 2 Cây Táo Nở Hoa đang nhận về vô số lời khen có cánh.', '<p>Vào vai cậu út Dư của Cây Táo Nở Hoa, Song Luân lọt thỏm giữa một dàn diễn viên toàn những người đã được kiểm chứng về khả năng diễn xuất ở lĩnh vực phim truyền hình. Sau tập 1, ấn tượng duy nhất đọng lại trong khán giả về Song Luân có lẽ chỉ là vẻ ngoài tỏa sáng ngoài ra cả diễn xuất lẫn câu chuyện của nhân vật đều không có gì nổi bật. Thế nhưng sang tới tập 2, khi quá khứ của Dư được gợi mở, Song Luân đã khiến dân tình phải trầm trồ với sự lột xác ấn tượng của mình.</p><p>Cụ thể trong phân đoạn này, Dư đã nhớ về những ký ức với bố, khi mà 25 năm cuộc đời anh chỉ gặp bố chưa tới 10 lần, lần nào cũng toàn bị mắng chửi, nguyền rủa. Vốn là cậu em út ngoan ngoãn, hiểu chuyện, lần hiếm hoi Dư đã lớn tiếng cãi lại anh trai, cho rằng mẹ mình mới là người khổ khi một đời phải chịu những trận đòn roi từ chồng đến độ uất hận bỏ đi, giờ không biết sống chết ra sao. Dư cũng vô cùng hận bố, người đàn ông chỉ biết rượu chè, đánh đập cậu, thậm chí còn khiến cậu bị tai nạn, gãy xương vai. Không chỉ tuyên bố sẽ không để tang, Dư còn xé cáo phó và lãnh ngay một cái tát từ anh trai. Phân đoạn bùng nổ cảm xúc này được Song Luân thể hiện rất tốt khiến khán giả phải ngỡ ngàng.</p><p>Bình luận của khán giả:</p><p>- Song Luân diễn hay quá.</p><p>- Tập này ấn tượng với Song Luân ghê. Anh diễn nhập vai làm mình không thể rời mắt được luôn, cả anh Thái Hòa nữa, 2 người làm cảm động lòng người xem.</p><p>- Tưởng Song Luân chỉ có đẹp trai thôi chứ, ai ngờ diễn hay quá.</p><p>- Coi đoạn Song Luân diễn mà tui cứ tưởng đang chứng kiến ở ngoài đời vậy à trời. Xuất sắc quá.</p><p>- Tập này Thái Hoà và Song Luân diễn đỉnh, nhập vai khủng khiếp luôn.</p><p>- Chính thức thích Song Luân nha, men dã man, khóc cũng men nữa.</p><p>Cây Táo Nở Hoa phát sóng 20:00 thứ Hai đến thứ Tư hàng tuần trên kênh truyền hình HTV2.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/cats-16177725463042026232602.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/chrome-capture-75-16177803211301497497077.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/chrome-capture-74-16177803211521172197572.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/chrome-capture-72-16177803212031398707050.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/chrome-capture-78-161778032087482339269.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/chrome-capture-73-1617780321195845137149.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn ảnh: HTV2</p>', NULL, 'netizen-sung-so-voi-dien-xuat-cua-song-luan-o-cay-tao-no-hoa-do-gio-tuong-chi-dep-thoi-co', 0, NULL, NULL),
(34, 6, 3, 1, 1, 'Đóng thế của Địch Lệ Nhiệt Ba lộ mặt rõ mồn một ở Trường Ca Hành, netizen phản pháo: \"Có 2 giây làm gì căng?\"', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617766018973-1617766019168789094698.gif.png', '|', 'Trường Ca Hành lại bị chê cười vì cảnh phim lộ rõ diễn viên đóng thế của Địch Lệ Nhiệt Ba.', '<p>Sau cảnh hôn giả trân gây tranh cãi, Trường Ca Hành lại bị ném đá vì cẩu thả trong hậu kì. Trong một cảnh giao chiến giữa Lý Trường Ca và nhân vật phụ, gương mặt diễn viên đóng thế của Địch Lệ Nhiệt Ba xuất hiện cực kì lộ liễu trên màn ảnh.</p><p>Chuyện diễn viên sử dụng thế thân không có gì xa lạ, nhưng ekip đã thể hiện rõ sự cẩu thả khi cắt ghép, khiến cảnh phim trở nên thiếu thuyết phục. Đây cũng là cái cớ để nhiều cư dân mạng còn chỉ trích Địch Lệ Nhiệt Ba không \"kính nghiệp\", những cảnh đơn giản cũng phải dùng đóng thế. Dù vậy, một số cư dân mạng cho rằng đây rõ ràng là lỗi của đạo diễn và tổ hậu kì, không thể trách diễn viên. Bên cạnh đó, cảnh quay này chỉ lướt qua trên màn ảnh chưa đến 1 giây, khán giả bình thường đa số không chú ý.</p><p>Bình luận của cư dân mạng:</p><p>- Xem qua vài đoạn thì chủ yếu thấy phim này không hề được đầu tư nhiều. Cụt (tên gọi khác của Tencent) tiết kiệm kinh phí làm ăn qua loa chủ yếu có cái chiếu cho fan cô xem để kiếm tiền chứ chả thấy nó bỏ bao nhiêu tâm huyết hay kì vọng vào cái bộ này.</p><p>- Nghe bảo đầu tư cũng nhiều tiền mà sao đoàn phim làm ăn kì thế? Cứ như kiểu quay cho xong để chiếu á, ẩu kinh khủng.</p><p>- Quay rõ hơn xíu có khi thế thân còn diễn ổn hơn cô Ba. Tôi bị ám ảnh cái kiểu diễn nhăn mày của cô này quá, giả trân hết nói nổi.</p><p>- Kính nghiệp là đây. Cảnh nhảy cửa sổ thì lồng hoạt hình, cảnh hôn thì để chim quay, cảnh đánh nhau thì thế thân. Khúc nhẹ nhàng thì để chị kính nghiệp. Chưa kể đến cái nét diễn giả trân trên mặt chị nữa.</p><p>- Rồi mắng hậu kỳ chứ mắng diễn viên là sao? Anti đến mức không phân biệt được khái niệm à? Dù mình không xem phim cô Ba đóng mà còn thấy phản cảm giùm luôn á.</p><p>- Ủa này là lỗi đoàn phim mà. Phim cổ trang ai mà không dùng thế thân. Edit không tốt cái đổ lên đầu diễn viên mấy má. Táp vừa thôi tém tém lại cái đi.</p><p>- Chắc blogger mê phim quá nên tua chậm x 0,25 để soi từng chi tiết đấy, khán giả đa số đều không chú ý đến cảnh này vì nó lướt qua quá nhanh, vào miệng blogger lại thành “lộ liễu”.</p><p>- Cảnh người ta quay cẩn thận, hậu trường có. Nếu có video lộ thế thân thì quăng lên đây chứ đừng có để cái đoạn gif 2s cắt ghép dắt mũi.</p><p>Nguồn: Cbiz: Chuyện chưa kể</p><p></p><p>Trường Ca Hành phát sóng lúc 20h00 vào thứ Hai, thứ Ba, thứ Tư hàng tuần trên VieON.</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/ezgif-7-83f55339f0f0-1617762873803384293188.gif.mp4\" ></video><div class=\"caption\">Diễn viên đóng thế được \"hô biến\" thành Nhiệt Ba khá vụng về</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/ezgif-7-f904198cbf38-16177655775641275854487.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/ezgif-7-a67995b2be5c-16177655775661852149503.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/ezgif-7-ef58b8ac3054-16177655775672006220591.gif.mp4\" ></video><div class=\"caption\">Nhiệt Ba tự mình thực hiện nhiều cảnh hành động</figcaption></figure><p>Nguồn ảnh: Weibo</p>', NULL, 'dong-the-cua-dich-le-nhiet-ba-lo-mat-ro-mon-mot-o-truong-ca-hanh-netizen-phan-phao-co-2-giay-lam-gi-cang', 0, NULL, NULL),
(35, 6, 3, 1, 1, 'Joseon Exorcist \"quỵt\" tiền cát-xê diễn viên tận 12 tập phim, netizen \"thấy cũng tội mà thôi cũng kệ\"', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617769182935-16177691830961069789142.gif.png', '|', 'Thương thay cho dàn cast Joseon Exorcist, vật vã quay 14 tập phim mà chỉ nhận thù lao 2 tập.', '<p>Sau khi bị hủy chiếu vì sai lệch lịch sử, Joseon Exorcist (Pháp Sư Trừ Tà Triều Tiên) đang phải đối mặt với rất nhiều vấn đề. Ngày 6/4, truyền thông Hàn Quốc đưa tin các diễn viên của phim mới được trả thù lao cho 2 tập phát sóng, dù họ đã quay xong 14/16 tập. Trong khi đó, bình thường diễn viên sẽ được trả 80% cát-xê cho các tập phim đã quay, khi phim đã lên sóng.</p><p>Tuy nhiên, nhà sản xuất khó lòng có thể trả đủ thù lao cho các diễn viên, vì họ đang phải gánh khoản lỗ kinh phí lên tới  32 tỷ won. Toàn bộ nhà tài trợ đã rút khỏi bộ phim, khiến  Crave Works, Lotte Cultureworks và  YG Studioplex rơi vào cảnh điêu đứng.</p><p>Jang Dong Yoon - nam chính của bộ phim</p><p>Cư dân mạng có nhiều ý kiến trái chiều. Người cho rằng ekip  Joseon Exorcist phải thanh toán đủ tiền cho các diễn viên theo quy định, người lại thấy dàn cast phải chịu chung trách nhiệm vì họ đã nhận đóng một tác phẩm sai trái. </p><p>Bình luận của cư dân mạng Việt:</p><p>- Nghe nói Jang Dong Yoon vì quay phim này mà gãy cả tay, đi nhận giải nào đó còn phải bó bột cơ mà. Khá là quý anh, thôi thì coi như lần này đen vậy.</p><p>- Nên chịu trách nhiệm chung. Như vậy ok mà.</p><p>- Đâu phải ai cũng học và tìm hiểu kỹ về lịch sử nước nhà đâu. Mình không phản bác chuyện chính trị, nhưng cũng không nên đổ lên đầu diễn viên toàn bộ mọi chuyện như vậy.</p><p>- Chứ có được chiếu đâu mà có tiền trả hết 14 tập ngộ ha?</p><p>Nguồn: Netizenbuzz Vtrans</p><p>Joseon Exorcist kể về cuộc chiến của một gia đình hoàng tộc để bảo vệ người dân khỏi linh hồn ác quỷ. Trong hành trình đó, vua Taejong (Kam Woo Sung) cùng hai con trai của ông là hoàng tử Chung Nyung (Jang Dong Yoon) và hoàng tử Yang Nyeong (Park Sung Hoon) diệt trừ những xác sống không hồn, mang lại sự bình yên cho đất nước như trước đây.</p><p>Teaser 2 của Joseon Exorcist</p><p>Tuy nhiên, sau khi chiếu tập đầu tiên, phim bị tẩy chay dữ dội vì xây dựng vua Taejong là kẻ tàn nhẫn, giết người không ghê tay, đưa vào rất nhiều trang phục, đạo cụ, món ăn truyền thống của Trung Quốc. Sau khi họp bàn, SBS đã quyết định \"khai tử\" bộ phim.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo1308118-1617768289193242026616.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/photo1310117-16177684285561904044813.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1-16177676532791858728333.png\" ><figcaption class=\"caption\">Bình luận của cư dân mạng Hàn (Nguồn: Netizenbuzz Vtrans)</figcaption></figure><p>Nguồn: topstarnews, SBS</p>', NULL, 'joseon-exorcist-quyt-tien-cat-xe-dien-vien-tan-12-tap-phim-netizen-thay-cung-toi-ma-thoi-cung-ke', 0, NULL, NULL),
(36, 6, 3, 1, 1, 'Tiểu thuyết về chiến tranh Việt Nam thắng Pulitzer được \"trùm Oscar\" chuyển thể thành phim', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617767756958-16177677576071973813468.png', '|', 'Tiểu thuyết danh giá The Sympathizer từng đoạt giải Pulitzer sẽ được hãng phim nổi tiếng A24 chuyển thể thành phim truyền hình dưới sự chỉ đạo của một đạo diễn nổi tiếng Hàn Quốc.', '<p>Hãng phim danh giá A24 đã mua lại quyền chuyển thể cuốn sách The Sympathizer (Cảm Tình Viên) của nhà văn Nguyễn Thanh Việt. The Sympathizer là cuốn tiểu thuyết đầu tay của Nguyễn Thanh Việt, cũng là tác phẩm đã mang lại cho ông giải thưởng Pulitzer cao quý ở hạng mục tiểu thuyết.</p><p>The Sympathizer sẽ được hãng A24 chuyển thể thành series truyền hình. Cuốn sách ra mắt năm 2015, kể về một người mang dòng máu lai Pháp - Việt và góc nhìn của nhân vật này xuyên suốt những năm tháng chiến tranh Việt Nam. Nhân vật gián điệp này đã chuyển từ miền Nam Việt Nam đến Los Angeles sau khi chính quyền Sài Gòn sụp đổ, sau đó lại di chuyển khắp Đông Nam Á. The Sympathizer là một cuốn sách đề cập tới nhiều vấn đề phức tạp, từ việc người gián điệp có cảm tình với những người anh ta bị buộc theo dõi cho tới việc định cư ở Mỹ và sống như một người tị nạn.</p><p>Giáo sư Nguyễn Thanh Việt sinh ra tại Buôn Ma Thuột, theo gia đình di cư sang Mỹ từ 1975. Ông tốt nghiệp bằng danh dự ngành tiếng Anh và Nghiên cứu dân tộc học tại Đại học California. Hiện tại, ông là giáo sư tại Đại học Nam California và cũng là một nhà phê bình lớn cho tờ The New York Times.</p><p>Trước đây, tiểu thuyết The Sympathizer đã từng chiến thắng nhiều giải thưởng lớn như Center for Fiction 2015, huy chương Carnegie cho Tiểu thuyết xuất sắc của Hiệp hội Thư viện Mỹ...</p><p>Đạo diễn, biên kịch Park Chan Wook sẽ là người đảm nhiệm dự án The Sympathizer</p><p>Đạo diễn của series The Sympathizer được chính Nguyễn Thanh Việt thông báo là Park Chan Wook - người đứng sau nhiều tác phẩm điện ảnh nổi tiếng là The Handmaiden (Người Hầu Gái) và Oldboy (Báo Thù).</p><p>Hãng A24 cũng vốn nổi tiếng là một hãng phim với biệt tài lựa chọn đề tài, kịch bản thần sầu. Hãng sở hữu rất nhiều tác phẩm danh giá ở cả mảng điện ảnh và truyền hình, tiêu biểu bao gồm The Lighthouse (Ngọn Hải Đăng), Lady Bird (Tuổi Nổi Loạn), Hereditary (Di Truyền), Euphoria (Lâng Lâng). Hiện tại, hãng phim cũng đang chạy nước rút trên đường đua Oscar với bộ phim Minari cùng 6 đề cử.</p><p>Hiện tại, các thông tin khác về dự án phim truyền hình The Sympathizer vẫn chưa được công bố. Nhiều người kỳ vọng nhân vật chính sẽ được thủ vai bởi một diễn viên người Việt hoặc gốc Việt theo đúng nguyên tác.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617766922958960269158.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/-16177669631891785859454.jpg\" ><figcaption class=\"caption\">Tác giả Nguyễn Thanh Việt</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617767403371307724065.jpg\" ><figcaption class=\"caption\">Minh họa của trang The New York Times</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/park-chan-wook-portrait-1453194629-16177671015091254527463.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/a24-best-movies-2016-16177672128281829259862.jpg\" ><figcaption class=\"caption\">A24 có lẽ là hãng phim \"uy tín\" nhất Hollywood hiện giờ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617767254267682609924.jpg\" ><figcaption class=\"caption\">Cuốn sách The Sympathizer từng lọt vào list sách mà Bill Gates khuyên đọc</figcaption></figure><p>Nguồn ảnh: Tổng hợp</p>', NULL, 'tieu-thuyet-ve-chien-tranh-viet-nam-thang-pulitzer-duoc-trum-oscar-chuyen-the-thanh-phim', 0, NULL, NULL),
(37, 6, 3, 1, 1, 'Quên Kiên đi, chân ái của Châu ở Hướng Dương Ngược Nắng chính là Phúc phố núi!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617621285695-16176212858921393769886.jpg', '|', 'Bất đắc dĩ bên nhau, thế nhưng Châu (Hồng Diễm) và Phúc (Doãn Quốc Đam) lại rất tình cảm ở Hướng Dương Ngược Nắng 2.', '<p>Sau cú sốc lớn từ tình yêu và gia đình ở Hướng Dương Ngược Nắng, Châu (Hồng Diễm) đường cùng mà tự vẫn, may mắn thay cô được Phúc (Doãn Quốc Đam) đến cứu kịp thời . Một phần vì Phúc bị thương, phần vì Châu không muốn trở lại thành phố, cặp đôi quyết định \"đưa nhau đi trốn\" một thời gian. Tại đây, tuy không có \"ý\" với nhau nhưng tình cảm của cả hai cũng bắt đầu được nảy nở, bởi sự quan tâm và thấu hiểu từ cả hai phía.</p><p>Từ tình cờ chạm mặt, Châu - Phúc trở nên gắn kết nhờ mối duyên nợ và cả nhiệm vụ mà ông Quân giao phó cho Phúc. Không thân thiết nhưng giữa những lúc Châu gặp nạn hay rơi vào hoàn cảnh éo le nhất, Phúc đều là người có mặt để giải cứu cô. Bị cưỡng hiếp không ai hay biết, đi nhảy cầu tự tử vì quá tuyệt vọng, tất cả những điều xấu xảy đến với Châu thì đều có Phúc nhìn thấu và giúp đỡ.</p><p>Không ngoa khi nói sau Kiên (Hồng Đăng) thì Phúc là người hiểu Châu nhất. Anh chàng không những đọc vị được suy nghĩ, mà còn đưa ra nhiều lời khuyên hữu ích giúp gỡ rối cho Châu. Nếu Châu đang gồng mình chứng tỏ bản thân đã gây ra quá nhiều sai lầm, từ mất hết danh dự, công việc nên mới chạy trốn gia đình, thì Phúc lại nắm thóp được rằng Châu đang đau đớn vì tình yêu là chính. Anh chàng còn ví Châu như một bông hoa rất đẹp, nhưng phải mạnh mẽ và kiên cường hơn nữa.</p><p>Chạy trốn phố thị xô bồ, Châu và Phúc tìm về phố núi, cả hai bắt đầu những ngày tháng yên bình, chưa từng có trước đây. Lửa gần rơm lâu ngày cũng bén, Châu bắt đầu có cảm tình khi Phúc luôn là người quan tâm, gần gũi cô. Phúc cũng có cái nhìn khác với đại tiểu thư nhà họ Cao, khi quan sát và hiểu rõ hơn nội tâm của Châu. Hết lo Châu bị cảm lạnh, Phúc lại chiều ý đưa cô đi chợ, dỗ Châu đang dỗi bằng ly trà sữa bất ngờ và ngọt ngào. Chiều ý Châu nhưng cũng có giới hạn, Phúc quyết liệt đưa Châu về lại thành phố, và muốn bản thân Châu phải tự mình đối mặt với nghịch cảnh thì mới có thể sống và hạnh phúc tiếp được.</p><p>  Hướng Dương Ngược Nắng 2 phát sóng 21:40 thứ Hai đến thứ Tư hàng tuần trên VTV3.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/photo-1-1617621410172507487200.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/photo-1-1617620819223897626712-16176208679431940661393.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/photo-1-16176190798061386263769.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/honeycam-2021-04-05-17-17-37-1617619195835174690002.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/photo-1-1617619337810905302185.gif.mp4\" ></video><div class=\"caption\">Yêu nhau khoác mũ áo cho nhau?</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/photo-1-1617619348817224888935.png\" ><figcaption class=\"caption\">Mua trà sữa cho nè</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/honeycam-2021-04-05-17-40-13-16176192525401015402401.gif.mp4\" ></video><div class=\"caption\">Châu còn bạo hơn khi hỏi khéo Phúc: \"Lỡ đâu mai này mình yêu nhau?\"</figcaption></figure><p>Nguồn ảnh: VTV</p>', NULL, 'quen-kien-di-chan-ai-cua-chau-o-huong-duong-nguoc-nang-chinh-la-phuc-pho-nui', 0, NULL, NULL),
(38, 6, 3, 1, 1, 'Mạc Văn Khoa suýt chết vì bị lật xuồng, ngâm mình dưới nước lạnh suốt 2 tiếng trong Lật Mặt: 48H', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617764417123-1617764417371791930739.jpg', '|', 'Trong lần đầu tiên đóng phim hành động, Mạc Văn Khoa cũng trải qua nhiều cảm xúc lo lắng, hồi hộp và háo hức trước những thử thách mới đặt ra cho vai diễn của mình.', '<p>Lần thứ hai trở lại với chuỗi phim Lật Mặt, Mạc Văn Khoa tiếp tục đảm nhận tuyến hài trong Lật Mặt: 48H. Trong phần phim Lật Mặt thứ 5 này, anh nhập vai Lâm - một chàng trai gốc Bắc, bạn thân của Hiền (Võ Thành Tâm) - một cựu vận động viên võ thuật. Sau khi kết hôn với Thảo (Lê Hạ Anh), Lâm đã cùng vợ về sinh sống nơi miền Tây sông nước. Trong một lần mời gia đình Hiền về nhà chơi, Lâm đã bị cuốn vào hành trình rượt đuổi cam go giữa Hiền và ông trùm xảo quyệt A Dìn. </p><p>Ngay từ trong trailer, vai diễn người bạn thật thà, chân chất với những câu thoại tỉnh bơ, hóm hỉnh của Mạc Văn Khoa đã hứa hẹn sẽ mang tới những giây phút giải trí cho khán giả. Đặc biệt Lật Mặt: 48H lại chính là dự án điện ảnh hành động đầu tiên của anh. Nếu như phần 4 Lật Mặt: Nhà Có Khách kết hợp yếu tố hài hước và kinh dị, thì Lật Mặt 5 trở lại với phong cách hành động xen hài đã quen thuộc ở ba phần phim trước. Do vậy, vẫn là với series điện ảnh Lật Mặt, nhưng Mạc Văn Khoa lại có những trải nghiệm hoàn toàn mới mẻ với các cảnh quay hành động.</p><p>Trong lần đầu tiên đóng phim hành động, Mạc Văn Khoa cũng trải qua nhiều cảm xúc lo lắng, hồi hộp và háo hức trước những thử thách mới đặt ra cho vai diễn của mình. Dòng phim hành động vốn đòi hỏi diễn viên không chỉ chú trọng vào diễn xuất mà cần phải rèn luyện thể lực và kỹ năng phản xạ tốt. Để chuẩn bị cho các cảnh quay hành động, anh đã chăm chỉ tập luyện võ thuật với mong muốn có thể phối hợp với các bạn diễn thật chính xác, hiệu quả theo yêu cầu khắt khe của đạo diễn Lý Hải.</p><p>Với bối cảnh miền Tây, Lật Mặt: 48H có rất nhiều cảnh rượt đuổi trên sông nước, tạo ra kha khá thách thức cho ekip quay phim. Mạc Văn Khoa đã cùng đoàn làm phim dậy từ rất sớm để chuẩn bị cho những cảnh quay phức tạp và không ngần ngại thực hiện các cảnh hành động trên sông. Với các cảnh lái xuồng máy tốc độ cao, anh và bạn diễn Võ Thành Tâm thậm chí còn nhiều lần suýt chết khi xuồng máy không may bị lật úp.</p><p>Theo Mạc Văn Khoa chia sẻ, một trong những kỉ niệm đáng nhớ của anh khi quay Lật Mặt: 48H chính là khoảng thời gian 2 tiếng ngâm mình trong nước lạnh vào sáng sớm chỉ để quay một cảnh ngắn trên phim. Có một sự trùng hợp khi trong Lật Mặt: Nhà Có Khách, anh cũng phải  tắm và ở trần trong suốt 4 tiếng đồng hồ giữa nhiệt độ núi rừng dưới 20 độ, để có được vài phút lên hình. Trong cả hai lần đóng Lật Mặt, anh đều có cảnh nhảy xuống sông, bị hù dọa. </p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-5-1617763892356355302907.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-4-1617763892334141484619.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-2-1617763892176278687589.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-3-1617763892248579778463.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-7-16177638923871513128168.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-6-1617763892376147500960.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-8-16177638924741436124982.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-1-1617763892497680904198.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Lật Mặt: 48H chính thức được khởi chiếu từ ngày 16/4/2021 trên toàn quốc.</p>', NULL, 'mac-van-khoa-suyt-chet-vi-bi-lat-xuong-ngam-minh-duoi-nuoc-lanh-suot-2-tieng-trong-lat-mat-48h', 0, NULL, NULL),
(39, 6, 3, 1, 1, 'Netizen Việt phát sốt với nhan sắc của \"ngọc nữ\" Cướp Biển Vùng Caribbean, xinh và khiêu gợi tột đỉnh chỉ với 1 bộ váy xanh', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617700407954-16177004083431834144574.gif.png', '|', 'Trong bộ váy xanh xẻ bạo ở phần lưng, ngọc nữ Keira Knightley nổi tiếng với loạt phim Cướp Biển Vùng Caribbean đã khiến nhiều khán giả phải xốn xang.', '<p>Một trong những nhan sắc nổi bật nhất của Hollywood có lẽ chính là Keira Knightley - nữ diễn viên được khán giả Việt biết đến nhiều nhất thông qua loạt bom tấn Cướp Biển Vùng Caribbean. Mỹ nữ sinh năm 1985 này là một nhân tố vô cùng xuất sắc của điện ảnh, từng tham gia đóng chính trong bộ phim Kiêu Hãnh Và Định Kiến và được đề cử giải Oscar nhờ vai diễn này. Ngoài ra, cô nàng cũng xuất hiện trong Atonement (Chuộc Lại Lỗi Lầm) - một bộ phim thời chiến tranh năm 2007 và để lại nhiều cảm xúc cho khán giả.</p><p>Mới đây, nhiều khán giả Việt đã bất ngờ \"đào\" lại hình ảnh kiều diễm của Keira Knightley trong Atonement. Nhan sắc trời phú cùng chiều cao 1m7 của nữ diễn viên trong bộ đầm màu xanh ngọc bích ngay lập tức khiến nhiều người phải ngỡ ngàng vì quá xinh đẹp.</p><p>Atonement là một bộ phim kể về một án oan và những hệ lụy kéo dài suốt hơn 6 thập kỷ, lấy bối cảnh chiến tranh Anh quốc. Phim được chuyển thể dựa trên tiểu thuyết cùng tên của tác giả Ian McEwan. Công chiếu năm 2007, phim thu về tận 129 triệu đô trên khắp thế giới với kinh phí chỉ 30 triệu đô, giành được 6 đề cử Oscar.</p><p>Trong phim, Keira Knightley đóng vai Cecilia, một cô gái xinh đẹp nhưng lại vướng phải mối tình tréo ngoe khi đem lòng yêu con trai của người hầu trong gia đình. Phân cảnh 2 nhân vật gặp nhau trong thư viện cũng vô cùng lãng mạn, đẹp mắt khi Keira Knightley diện chiếc váy dạ hội màu xanh ngọc, xẻ lưng táo bạo.</p><p>Để làm nên những bộ trang phục cho Keira Knightley trong phim, đạo diễn Joe Wright đã nhờ đến Jacqueline Durran - người đã từng làm việc với ông trong Kiêu Hãnh Và Định Kiến năm 2000</p><p>Năm 2008, Forbes tuyên bố Keira Knightley là nữ diễn viên có thu nhập cao thứ 2 tại Hollywood. Hình ảnh của bông hồng Hollywood trong bộ váy xanh đã khiến nhiều khán giả háo hức, mong muốn xem luôn bộ phim Atonement.</p><p>- Cơ bản là chị ấy làm gì cũng đẹp rồi.</p><p>- Phim hay, diễn viên đỉnh nhưng xem xong khóc mãi. Sẽ không xem lại đâu...</p><p>- Chị yêu hợp với những phim có bối cảnh và nội dung như thế này dã man!</p><p>- Xinh xỉu, phải coi lại để xỉu thêm thôi...</p><p></p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177000599671494128343.jpg\" ><figcaption class=\"caption\">Keira Knightley trong Cướp Biển Vùng Caribbean</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176999844461409456115.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176999676902108401373.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/3d45bb2a31921083bb1b4e43266b1d4c-16176981031111626452929.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/010a7a19285359-1617698424927451545970.jpg\" ><figcaption class=\"caption\">Bởi lẽ cảnh phim lấy thời điểm là một ngày hè nóng nực, bộ trang phục phải vừa \"hớ hênh\", vừa nhẹ nhàng mà vẫn tinh tế. Màu xanh lá là biểu trưng cho sự hình thành cảm xúc giữa 2 nhân vật, cũng như cảm xúc ghen tị của cô em gái Brionny (Saoirse Ronan)</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/010a7a19285358-16176985142831710012656.jpg\" ><figcaption class=\"caption\">Nhờ bộ váy xanh này, nhà thiết kế Jacqueline Durran cũng được nhận đề cử Oscar vào năm 2008</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176989070011155123821.jpg\" ><figcaption class=\"caption\">Những họa tiết, nếp gấp trên bộ váy góp phần khoe khéo cơ thể đẹp không tì vết của Keira Knightley</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/chrome-capture-5-16176998407861936653554.gif\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn: Tổng hợp, Group Maybe You Never Watched This Movie</p>', NULL, 'netizen-viet-phat-sot-voi-nhan-sac-cua-ngoc-nu-cuop-bien-vung-caribbean-xinh-va-khieu-goi-tot-dinh-chi-voi-1-bo-vay-xanh', 0, NULL, NULL),
(40, 7, 3, 1, 1, 'Rộ tin bom tấn bạo lực Mortal Kombat được chiếu bản \"full không cắt\" ở Việt Nam nhưng dán nhãn 18+, thực hư thế nào?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617707252910-16177072537241311741571.gif.png', '|', 'Mortal Kombat được chiếu trước 2 tuần ở Việt Nam, nhưng thời lượng và độ tuổi giới hạn thì sao?', '<p>Ngày 9/4 tới đây, Mortal Kombat - bom tấn đẫm máu chuyển thể từ trò chơi cùng tên sẽ chính thức ra mắt khán giả Việt Nam, sớm hơn 2 tuần so với lịch ra mắt chính thức trên toàn cầu. Vì nội dung cùng những phân cảnh khá máu me, phù hợp với đối tượng trưởng thành, nhiều người lo lắng Mortal Kombat sẽ bị cắt xén, giới hạn độ tuổi để phù hợp với thuần phong mỹ tục. Vậy thực hư là gì?</p><p>Trên trang web của CGV (đơn vị phát hành  Mortal Kombat  tại Việt Nam), bộ phim sẽ có thời lượng 110 phút, ngang bằng với phiên bản quốc tế. Thêm vào đó, phim đã chính thức được dán nhãn C18 - cấm khán giả dưới 18 tuổi. </p><p>Điều này khả năng cao xác nhận rằng khán giả sẽ có cơ hội được thưởng thức toàn bộ tác phẩm, không cắt xén các cảnh quá máu me như lời đồn. Thế nhưng song song với việc chiếu bản không cắt, những khán giả trên 18 tuổi mới được thưởng thức  Mortal Kombat tại Việt Nam.</p><p>Bản Việt có thời lượng ngang bằng bản quốc tế</p><p>Trước thông tin Mortal Kombat sẽ được chiếu đầy đủ tại thị trường Việt Nam, cư dân mạng vẫn đặt ra nhiều nghi vấn:</p><p>- Không cắt nhưng có phóng to như Deadpool không vậy?</p><p>- Chắc không máu me be bét được như game.</p><p>- Hôm qua hay kia mình đọc tin sợ bị cắt nên lên thẳng IMDB với R otten Tomatoes xem thì cũng có 110 phút thôi.</p><p></p><p></p><p>- Lại phải đi xem rồi.</p><p>- Xem rạp cho có không khí, tận hưởng cảm giác phim chiếu rạp.</p><p>Nguồn: Ra Rạp Xem Gì?, CGV Cinemas Vietnam</p><p>Mortal Kombat chính thức ra mắt vào ngày 9/4.</p><p>Trailer Vietsub chính thức của Mortal Kombat</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/honeycam-2021-04-06-16-39-57-1617702142376833075551.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/honeycam-2021-04-06-16-41-14-16177021424371111894917.gif.mp4\" ></video><div class=\"caption\">Loạt phân cảnh hành động đẫm máu của Mortal Kombat</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/alex-3-16177064653831120016757.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/s-16177705066582010010623.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn ảnh: Chụp màn hình, Warner Bros.</p>', NULL, 'ro-tin-bom-tan-bao-luc-mortal-kombat-duoc-chieu-ban-full-khong-cat-o-viet-nam-nhung-dan-nhan-18-thuc-hu-the-nao', 0, NULL, NULL),
(41, 7, 3, 1, 1, '4 lý do Mortal Kombat là bom tấn nóng nhất cuối tuần này: Phim 18+ đậm bạo lực, đạo diễn Aquaman bảo chứng chất lượng!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617684923341-16176849282141811597983.gif.png', '|', 'Mortal Kombat năm nay sẽ có gì khác biệt so với tất cả những phiên bản trước đó mà chúng ta từng biết?', '<p>Là một tựa game đình đám ra đời vào những năm 90 của thế kỷ 20, Mortal Kombat cùng những nhân vật đình đám nhất như Scorpion, Sub-Zero, Liu Kang… đã trở thành một biểu tượng khó lòng thay thế với thế hệ 8x - 9x đời đầu. Không chỉ làm mưa làm gió với phiên bản game, Mortal Kombat cũng đã từng hai lần bước lên màn ảnh rộng, trong đó phiên bản 1995 cũng có thể coi là khá thành công ở thời điểm bấy giờ.</p><p>Năm 2021, giải đấu Mortal Kombat sẽ một lần nữa khai màn trên màn ảnh rộng. Liệu phiên bản với sự chỉ đạo của đạo diễn Simon McQuoid và sản xuất bởi James Wan - hai \"fan cứng\" của tựa game gốc - sẽ có gì khác biệt so với tất cả những phiên bản Mortal Kombat mà chúng ta từng biết?</p><p>Để phù hợp với đối tượng khán giả tuổi teen (PG-13), phiên bản điện ảnh đầu tiên của tựa game ra mắt vào năm 1995 đã tìm mọi cách để tiết chế các pha hành động hay các cảnh máu me. Rõ ràng điều này là không thật sự hợp lý với tựa game nhãn M (17+) nguyên bản đậm chất bạo lực của mình. Phiên bản làm lại lần này đã phá bỏ đi rào cản đó để phô diễn hết được sự khốc liệt trong một giải đấu Mortal Kombat sinh tử. Đây cũng chính là điều mà những fan hâm mộ lâu năm của tựa game thật sự mong muốn.</p><p>Cũng như phiên bản game đầu tiên năm 1992, tựa phim năm 2021 đưa chúng ta về thời điểm giải đấu Mortal Kombat quyết định vận mệnh của toàn Địa Giới. Lần này chúng ta sẽ được theo chân một nhân vật mới toanh – Cole Young cựu võ sĩ tự do đi tìm câu trả lời cho những câu hỏi xung quanh giải đấu bí ẩn Mortal Kombat. Qua chuyến hành trình của Cole Young, những bí ẩn của  Mortal Kombat  sẽ hiện lên một cách tự nhiên hơn, giúp những người mới dễ dàng theo dõi hơn, nhưng đồng thời cũng làm cho câu chuyện gốc trở nên khó đoán và gây tò mò hơn với những fan gạo cội.</p><p>Bên cạnh sự xuất hiện của nhân vật mới Cole Young, tuyến nhân vật của Mortal Kombat phiên bản điện ảnh cũng ít nhiều có sự thay đổi, ít nhất là với những thông tin đã được tiết lộ cho đến hiện tại. Kano trong tựa game gốc là kẻ luôn đối đầu với nhóm của Sonya, tuy nhiên theo các đoạn trailer và TVC đã được nhà sản xuất công bố, có thể dễ dàng thấy Kano luôn đồng hành với nhóm của Sonya, nhập hội một trong các Đấu vương bảo vệ Địa giới. Bên cạnh đó, cánh tay kim loại của nhân vật Jax trong phiên bản phim 2021 cũng là hậu quả từ việc anh bị bẻ tay, chứ không phải tự nguyện trang bị như trong phiên bản game Mortal Kombat 3.</p><p>Hứa hẹn của phù thủy James Wan và đạo diễn McQuoid</p><p>Chia sẻ về quá trình xây dựng cốt truyện cho phần phim điện ảnh năm 2021, đạo diễn McQuoid cho biết: \"Chìa khoá giúp đạt được thành công là phải tuân thủ đúng tinh thần của Mortal Kombat\". Như vậy, về cơ bản, các fan của Mortal Kombat có thể phần nào yên tâm rằng sẽ không có một màn \"quay xe\" hay \"phá tan\" những ấn tượng về tựa game gốc. Tuy nhiên, đạo diễn McQuoid cũng đồng thời khẳng định, ông không muốn làm một bản remake của phần phim 1995, đồng thời cũng cần một cốt truyện hấp dẫn để khiến những người chưa từng biết đến Mortal Kombat cũng dễ dàng đắm chìm vào tác phẩm. Như vậy, chắc chắn sẽ có những thay đổi bất ngờ cực kỳ phấn khích đến từ đội ngũ sản xuất của bộ phim mà chắc chắn khán giả sẽ phải ra rạp để khám phá.</p><p>Mortal Kombat (Tựa Việt: Cuộc Chiến Sinh Tử) khởi chiếu 9/4/2021.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/mk-3-16176849688031083478101.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/mk-2-16176849686961624510279.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/mk-1-1617684968683100822771.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/mk-5-161768496862938603945.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/mk-4-16176849685891082989999.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn ảnh: Tổng hợp</p>', NULL, '4-ly-do-mortal-kombat-la-bom-tan-nong-nhat-cuoi-tuan-nay-phim-18-dam-bao-luc-dao-dien-aquaman-bao-chung-chat-luong', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(42, 7, 3, 1, 1, 'Ông bố quốc dân Liam Neeson tái xuất ở Honest Thief: Khi đồng tiền là phép thử dễ dàng nhất đối với lòng người', 'https://kenh14cdn.com/thumb_w/300/pr/2021/1617683515686-0-0-375-600-crop-1617683519504-63753311007942.jpg', '|', 'Sau tuyên bố “nghỉ hưu” khỏi lĩnh vực phim hành động vào năm 2015, Liam Neeson gây bất ngờ khi đóng vai chính trong siêu phẩm Phi Vụ Hoàn Lương.', '<p>Tuy đã từng tuyên bố chấm dứt đóng phim hành động vào năm 2015 nhưng 5 năm sau, Liam Neeson tái xuất khiến khán giả tò mò, thích thú trong Honest Thief - Phi Vụ Hoàn Lương. Trong phim, ngôi sao Taken hóa thân vào vai siêu trộm Tom Carter. Vì gặp gỡ chân ái của đời mình nên Tom Carter quyết định hoàn lương và đã chủ động gọi cho cảnh sát để giao nộp số tiền khủng mà anh cướp được. Nhưng hóa ra làm người tốt chẳng dễ khi có nhiều thế lực khác lăm le cuỗm mất số tiền đó, anh và người yêu bị đẩy vào vòng xoáy hiểm nguy.</p><p>Tom Carter phải vận dụng mọi kinh nghiệm \"chinh chiến\" từ nhiều năm làm siêu trộm để vừa phòng thủ, vừa tấn công nhằm ngăn chặn đường đi nước bước của kẻ thù, đồng thời bảo vệ bạn gái khỏi nguy hiểm. Không chỉ gây ấn tượng với cốt truyện nghe đã thấy cuốn, bộ phim còn cài cắm thông điệp: Đồng tiền là phép thử dễ dàng nhất đối với lòng người.</p><p>Từng cam đoan sẽ không trở lại với phim hành động nữa, Liam Neeson lại khiến không ít khán giả bất ngờ khi tham gia Phi Vụ Hoàn Lương. Thế nhưng thay vì vẫn mang hình tượng hùng hổ, mạnh bạo như đặc vụ Bryan truy tìm con gái ở Taken, nhân vật Tom lại mạnh về trí óc, đồng thời mong ước một cuộc sống an nhàn, ít biến cố nhất có thể.</p><p>Thực chất Phi Vụ Hoàn Lương cũng dẫn dắt người xem đến với tình huống gia đình bị đe dọa như Taken. Tuy nhiên nam chính Tom Carter lại lựa chọn thế phòng thủ nhiều hơn là chủ động tấn công như các phim trước. Dù sao đi nữa, bản thân Tom vẫn là một kẻ từng vướng vòng lao lý, vì vậy bất cứ động thái nào cũng phải cẩn trọng, đặc biệt khi \"kẻ thù\" của Tom là cảnh sát. Tom Carter chính là nhân vật giữ thế cân bằng của toàn bộ mạch truyện Honest Thief, thể hiện qua đường đi nước bước khôn ngoan trong kế hoạch đánh trả kẻ thù, cũng như khía cạnh tình cảm của anh dành cho bạn gái Annie vốn cũng đang lâm vào cảnh khốn cùng vì bị truy sát.</p><p>Tuy không còn ở thời kì đỉnh cao như Taken nhưng Liam Neeson vẫn đủ sức khiến khán giả choáng ngợp với những cảnh đánh đấm ít nhưng chất. Bên cạnh đó, Liam Neeson còn thể hiện thành công hàng loạt cảm xúc của một người đàn ông có người thân rơi vào hiểm nguy, từ hoảng sợ, lo lắng khi nhận ra Annie bị hại đến ý chí kiên cường, mạnh mẽ chống trả.</p><p>Ngay từ khi gã siêu trộm To Dolan nhấc máy gọi điện cho cảnh sát, Honest Thief đã phân định rõ hai thế lực thù địch chính của phim. Một bên là nam chính với mong muốn làm lại cuộc đời, bên còn lại là những cảnh sát sa ngã. Tất cả chỉ vì một núi tiền không của riêng ai.</p><p>Phi Vụ Hoàn Lương đẩy sự cám dỗ của đồng tiền lên trực diện, không giấu giếm. Vì tiền, Tom càn quét 12 ngân hàng và mang về 9 triệu đô, để rồi giờ đây hối hận vì quá khứ tội lỗi trước mặt bạn gái. Vì tiền, những cảnh sát liêm chính như John Nivens (Jai Courtney) và Ramon Hall (Anthony Ramos) lại bất ngờ đổ đốn, không từ thủ đoạn để đoạt lấy tiền từ Tom. Cả hai ban đầu vốn chả quen biết, chả có thâm thù gì nhưng với sự thèm muốn vật chất, họ đẩy nhau vào chỗ sinh tử mà không nhận ra rằng gia đình vẫn còn đó, chờ đợi họ bình yên về nhà mỗi ngày.</p><p>Honest Thief là một bộ phim thuần hành động nhưng vẫn thành công trong cách xây dựng các tuyến nhân vật từ thiện đến ác, điều mà rất nhiều phim khác thuộc thể loại tương tự bỏ qua. Khán giả có thể nhận ra nam chính Tom đã đến lúc khao khát một cuộc sống mới, bình yên bên người yêu Annie sau nhiều năm làm siêu trộm. Nhưng một khi tính mạng Annie bị đe dọa thì Tom chẳng ngại ngần \"bật chế độ\" báo thù, sẵn sàng triệt hạ bất kỳ ai ngáng đường anh.</p><p>Ngay cả phe phản diện và tuyến nhân vật phụ của Phi Vụ Hoàn Lương cũng có các câu chuyện bên lề đáng chú ý. Tên Hall luôn phân vân giữa một bên là đạo đức của một người cảnh sát, một bên là ham muốn có được số tiền vì gia đình đang lâm vào cảnh khốn khổ, thiếu thốn. Bên cạnh đó còn có cảnh sát Sean, người vừa ly dị và thường dẫn chú chó Tazzie của mình đến văn phòng cho vơi đi nỗi cô đơn. Theo lời anh, vợ anh bỏ đi và lựa chọn căn nhà, để lại anh và chú chó sống nương tựa lẫn nhau. Những mẩu chuyện nhỏ được kể qua vài câu thoại như thế này đã giúp các nhân vật trở nên đáng nhớ, dễ cảm hơn.</p><p>Honest Thief lồng ghép câu chuyện quen thuộc nhưng sâu sắc, không phải cứ là tội đồ thì không thể hoàn lương và ngay cả những người chính trực nhất cũng có thể mang dã tâm. Những pha đối chọi trong Phi Vụ Hoàn Lương không chỉ về thể lực mà còn ở tâm lý. Sự xung đột về tư tưởng của Tom và hội cảnh sát sa ngã, cũng như ma lực của đồng tiền đã đẩy họ vào tình huống một mất một còn. Sau cùng, chỉ người mang sơ tâm, vượt qua được cám dỗ mới có được hạnh phúc.</p><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-1-16176832948381666012055.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-1-1617683298534122314574.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-2-1617683298537823591665.jpg\" ><figcaption class=\"caption\">Cốt truyện khai thác mặt tối của đồng tiền</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-3-16176832985431233117190.jpg\" ><figcaption class=\"caption\">Chuyện đời nhân vật được đẩy mạnh, cả phản diện cũng có góc khuất đáng suy ngẫm</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-4-16176832985471039372944.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Bộ phim Phi Vụ Hoàn Lương (tựa gốc: Honest Thief) hiện đang được trình chiếu tại mục Phim truyện trên hệ thống Truyền hình FPT. Độc giả tham khảo thông tin chi tiết tại fanpage facebook.com/truyenhinhfpt; website truyenhinh.fpt.vn hoặc liên hệ hotline 19006600.</p>', NULL, 'ong-bo-quoc-dan-liam-neeson-tai-xuat-o-honest-thief-khi-dong-tien-la-phep-thu-de-dang-nhat-doi-voi-long-nguoi', 0, NULL, NULL),
(43, 7, 3, 1, 1, 'Chưa ra mắt ở Việt Nam, Thiên Thần Hộ Mệnh của Victor Vũ đã chốt đơn chiếu toàn thế giới', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617644986122-16176449862501245198506.gif.png', '|', 'Đại diện sản xuất của Thiên Thần Hộ Mệnh cho hay đoàn phim \"đang thỏa thuận để công chiếu bộ phim trên toàn thế giới\".', '<p>Là bộ phim đánh dấu sự trở lại của đạo diễn Victor Vũ ở thể loại tâm linh, kinh dị, Thiên Thần Hộ Mệnh sẽ không chỉ công chiếu ở Việt Nam mà còn mở rộng địa bàn ra thị trường thế giới. Cụ thể nhà sản xuất của bộ phim Thiên Thần Hộ Mệnh vừa công bố rằng họ \"đang thỏa thuận để công chiếu bộ phim trên toàn thế giới\". Dù chưa bật mí cụ thể thời điểm công chiếu là khi nào nhưng có lẽ đây sẽ là tác phẩm điện ảnh đầu tiên của Việt Nam được ra mắt rộng rãi bằng hình thức này.</p><p></p><p>Dự kiến Thiên Thần Hộ Mệnh sẽ được chiếu tại những thị trường yêu phim hàng đầu thế giới như Mỹ, Anh, Pháp, Ireland, Malaysia, Indonesia, Philippines, Canada, Cộng hòa Séc, Singapore, Hungary, Úc,… Đây là một dấu hiệu đáng mừng cho thấy cơ hội vươn xa của điện ảnh Việt. </p><p>Trailer Thiên Thần Hộ Mệnh</p><p>Bộ phim điện ảnh Thiên Thần Hộ Mệnh của đạo diễn Victor Vũ với sự tham gia của Trúc Anh, Amee, Salim, Samuel An... chính thức khởi chiếu từ 30/04/2021.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/thien-than-ho-menh-wolrd-premiere-opt2-16176823180241784622835.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176456237361693191375.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/thien-than-ho-menh-1-1613968246459622437154-16176456090181860117229.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn ảnh: NSX</p>', NULL, 'chua-ra-mat-o-viet-nam-thien-than-ho-menh-cua-victor-vu-da-chot-don-chieu-toan-the-gioi', 0, NULL, NULL),
(44, 7, 3, 1, 1, '\"Thánh lừa\" Loki tung trailer nóng, hé lộ đa vũ trụ khôn lường sau Avengers: Endgame', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617636659775-16176366647161989047620.gif.png', '|', 'Đoạn trailer mới của Loki đã chính thức được phát hành, hé lộ thêm nhiều chi tiết về series bom tấn sắp ra mắt của Marvel.', '<p>Sau những thước phim đầu tiên vào cuối năm 2020, mới đây, Disney và Marvel Studios đã cho ra mắt một trailer \"mới cứng\" của loạt phim Loki. Đoạn giới thiệu vừa ra mắt tập trung vào việc TVA (một tổ chức giám sát dòng thời gian và đa vũ trụ) \"mời gọi\" Loki (Tom Hiddleston) tham gia nhiệm vụ sửa chữa lại những lỗ hổng thời gian mà chính là anh đã phá vỡ trong Avengers: Endgame.</p><p>Trailer Loki</p><p>Một trong những điểm nhấn lớn nhất trong đoạn trailer có lẽ là màn \"song kiếm hợp bích\" của Loki và người được cho là cố vấn xuyên suốt loạt phim của anh, Mobius M. Mobius ( Owen Wilson). Cả hai sẽ đồng hành với nhau để sửa chữa lại sự cố về dòng thời gian và dù từng đâm sau lưng người khác rất rất nhiều lần, Loki vẫn có được sự tin tưởng của vị giáo sư này. Dẫu vậy, không rõ liệu tổ chức TVA có kiểm soát được thần lừa lọc hay không vì một số cảnh trong trailer, anh thường xuyên xuất hiện một mình.</p><p>Loạt màn tung hứng giữa Mobius và Loki hứa hẹn sẽ là điểm nhấn của series</p><p>Chưa dừng lại, đoạn trailer cũng cho người xem cơ hội được thấy rõ hơn dàn diễn viên và cốt truyện chính thức. Có vẻ như Loki và một số bạn đồng hành sẽ ghé thăm nhiều thế giới và dòng thời gian khác nhau, trong đó có cả thế giới nơi Loki cai trị Asgard. Dẫu vậy, nhân vật được chờ đợi nhiều nhất là Lady Loki vẫn chưa hề lộ mặt và điều này khiến người hâm mộ càng hóng ngày ra mắt của loạt phim Loki.</p><p>Tập đầu tiên của  Loki sẽ ra mắt vào ngày 11/6 năm 2021.</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/honeycam-2021-04-05-22-14-14-1617635702984434749926.gif.mp4\" ></video><div class=\"caption\">TVA trao cho Loki cơ hội sửa sai</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/5/honeycam-2021-04-05-22-16-34-1617636394925381501009.gif\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/honeycam-2021-04-05-22-17-06-16176363949321403896878.gif.mp4\" ></video><div class=\"caption\">Loki đi tới nhiều thế giới và dòng thời gian khác nhau</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/photo-1-16176366128251930298033.jpg\" ><figcaption class=\"caption\">Lady Loki vẫn chưa xuất hiện trong trailer mới của Loki</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/-1617636516140523842604.jpg\" ><figcaption class=\"caption\">Poster chính thức của Loki</figcaption></figure><p>Nguồn ảnh: Chụp màn hình</p>', NULL, 'thanh-lua-loki-tung-trailer-nong-he-lo-da-vu-tru-khon-luong-sau-avengers-endgame', 0, NULL, NULL),
(45, 7, 3, 1, 1, 'Nhã Phương đóng 10 phim khóc cả 10, thách bạn đoán được đây là \"nữ hoàng nước mắt\" ở phim nào!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617619476557-16176194767111374104316.png', '|', 'Chỉ với một bức hình bạn có đoán được đây là Nhã Phương ở bộ phim nào?', '<p></p><p>Được mệnh danh là nữ hoàng nước mắt của màn ảnh Việt, Nhã Phương đóng 10 phim thì cả 10 đều có cảnh khóc. Nếu tự tin mình là một fan cứng đã cày tất tần tật phim của bà xã Trường Giang, thử đoán xem những cảnh khóc này ở bộ phim nào nhé!</p><p>Nguồn ảnh: Tổng hợp</p>', NULL, 'nha-phuong-dong-10-phim-khoc-ca-10-thach-ban-doan-duoc-day-la-nu-hoang-nuoc-mat-o-phim-nao', 0, NULL, NULL),
(46, 7, 3, 1, 1, 'Em Của Thời Niên Thiếu có động thái \"chạy đua\" Oscar đầu tiên nhưng netizen vẫn cà khịa nát nước!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617596126171-16175961263721132574968.gif.png', '|', 'Em Của Thời Niên Thiếu vẫn tiếp tục quảng bá bất chấp ý kiến trái chiều.', '<p>Sau đề cử Oscar gây chấn động, Em Của Thời Niên Thiếu đã có động thái quảng bá lại. Bộ phim của Dịch Dương Thiên Tỉ - Châu Đông Vũ đã tung poster mới và tái phát hành tại thị trường Hàn Quốc. Đây có thể là một phần trong chiến dịch quảng bá hướng đến tượng vàng danh giá.</p><p>So với poster cũ, poster phát hành lại có vẻ vui vẻ, lạc quan hơn. Cặp đôi diễn viên trên nền xanh đậm chất \"thanh xuân vườn trường\", cùng với tiêu đề bắt mắt: \"Đề cử Oscar cho Phim quốc tế xuất sắc nhất\". Trái ngược với sự hào hứng của fan, nhiều cư dân mạng tiếp tục chỉ trích bộ phim \"thiếu tự trọng\" khi quảng bá bất chấp phốt đạo nhái.</p><p>Bình luận của cư dân mạng:</p><p>- Nghe đồn đây là đạo phẩm, chẳng hiểu sao đem công chiếu ở nhiều nước quá?</p><p>- \"Thời niên thiếu nợ tôi một ai đó bla bla\". Không, thời niên thiếu các bạn thiếu liêm sỉ đấy. Đạo phẩm mãi mãi là đạo phẩm, thế nhé.</p><p>- Lý lẽ của các bạn fans kiểu: \"Biết là đạo phẩm nhưng diễn hay nên chấp nhận\", \"Diễn viên đẹp thì coi thôi quan tâm chi đạo hay hổng đạo\"...  Hèn gì đạo phẩm mà được công chiếu nhiều nơi ghê. Tính tẩy trắng bằng Javen à ?</p><p>-  Diễn xuất cũng bình thường mà được tung hô phết.</p><p>- Có hay thế nào cũng là đạo, mình thích cả Châu Đông Vũ và Dịch Dương Thiên Tỉ nhưng đạo phẩm mà được tán thành như thế này là điều không nên.</p><p>Nguồn: Weibo Việt Nam</p><p>Lễ trao giải Oscar lần thứ 93 sẽ diễn ra vào ngày 25/4 tại nhiều địa điểm khác nhau qua hình thức trực tuyến. Liệu Em Của Thời Niên Thiếu có thể làm nên kì tích, hãy chờ đợi nhé!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/16785354342731123594054218228785380383178957n-16175948275051136206919.jpg\" ><figcaption class=\"caption\">Poster mới của Em Của Thời Niên Thiếu tại Hàn Quốc</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/1683575545168030963738382600365852222119991n-16175953219871441409413.jpg\" ><figcaption class=\"caption\">Trông vui vẻ hơn nhiều poster cũ</figcaption></figure><p>Nguồn ảnh: Weibo</p>', NULL, 'em-cua-thoi-nien-thieu-co-dong-thai-chay-dua-oscar-dau-tien-nhung-netizen-van-ca-khia-nat-nuoc', 0, NULL, NULL),
(47, 8, 3, 1, 1, 'Mạc Văn Khoa suýt chết vì bị lật xuồng, ngâm mình dưới nước lạnh suốt 2 tiếng trong Lật Mặt: 48H', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617764417123-1617764417371791930739.jpg', '|', 'Trong lần đầu tiên đóng phim hành động, Mạc Văn Khoa cũng trải qua nhiều cảm xúc lo lắng, hồi hộp và háo hức trước những thử thách mới đặt ra cho vai diễn của mình.', '<p>Lần thứ hai trở lại với chuỗi phim Lật Mặt, Mạc Văn Khoa tiếp tục đảm nhận tuyến hài trong Lật Mặt: 48H. Trong phần phim Lật Mặt thứ 5 này, anh nhập vai Lâm - một chàng trai gốc Bắc, bạn thân của Hiền (Võ Thành Tâm) - một cựu vận động viên võ thuật. Sau khi kết hôn với Thảo (Lê Hạ Anh), Lâm đã cùng vợ về sinh sống nơi miền Tây sông nước. Trong một lần mời gia đình Hiền về nhà chơi, Lâm đã bị cuốn vào hành trình rượt đuổi cam go giữa Hiền và ông trùm xảo quyệt A Dìn. </p><p>Ngay từ trong trailer, vai diễn người bạn thật thà, chân chất với những câu thoại tỉnh bơ, hóm hỉnh của Mạc Văn Khoa đã hứa hẹn sẽ mang tới những giây phút giải trí cho khán giả. Đặc biệt Lật Mặt: 48H lại chính là dự án điện ảnh hành động đầu tiên của anh. Nếu như phần 4 Lật Mặt: Nhà Có Khách kết hợp yếu tố hài hước và kinh dị, thì Lật Mặt 5 trở lại với phong cách hành động xen hài đã quen thuộc ở ba phần phim trước. Do vậy, vẫn là với series điện ảnh Lật Mặt, nhưng Mạc Văn Khoa lại có những trải nghiệm hoàn toàn mới mẻ với các cảnh quay hành động.</p><p>Trong lần đầu tiên đóng phim hành động, Mạc Văn Khoa cũng trải qua nhiều cảm xúc lo lắng, hồi hộp và háo hức trước những thử thách mới đặt ra cho vai diễn của mình. Dòng phim hành động vốn đòi hỏi diễn viên không chỉ chú trọng vào diễn xuất mà cần phải rèn luyện thể lực và kỹ năng phản xạ tốt. Để chuẩn bị cho các cảnh quay hành động, anh đã chăm chỉ tập luyện võ thuật với mong muốn có thể phối hợp với các bạn diễn thật chính xác, hiệu quả theo yêu cầu khắt khe của đạo diễn Lý Hải.</p><p>Với bối cảnh miền Tây, Lật Mặt: 48H có rất nhiều cảnh rượt đuổi trên sông nước, tạo ra kha khá thách thức cho ekip quay phim. Mạc Văn Khoa đã cùng đoàn làm phim dậy từ rất sớm để chuẩn bị cho những cảnh quay phức tạp và không ngần ngại thực hiện các cảnh hành động trên sông. Với các cảnh lái xuồng máy tốc độ cao, anh và bạn diễn Võ Thành Tâm thậm chí còn nhiều lần suýt chết khi xuồng máy không may bị lật úp.</p><p>Theo Mạc Văn Khoa chia sẻ, một trong những kỉ niệm đáng nhớ của anh khi quay Lật Mặt: 48H chính là khoảng thời gian 2 tiếng ngâm mình trong nước lạnh vào sáng sớm chỉ để quay một cảnh ngắn trên phim. Có một sự trùng hợp khi trong Lật Mặt: Nhà Có Khách, anh cũng phải  tắm và ở trần trong suốt 4 tiếng đồng hồ giữa nhiệt độ núi rừng dưới 20 độ, để có được vài phút lên hình. Trong cả hai lần đóng Lật Mặt, anh đều có cảnh nhảy xuống sông, bị hù dọa. </p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-5-1617763892356355302907.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-4-1617763892334141484619.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-2-1617763892176278687589.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-3-1617763892248579778463.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-7-16177638923871513128168.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-6-1617763892376147500960.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-8-16177638924741436124982.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/macvankhoa-1-1617763892497680904198.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Lật Mặt: 48H chính thức được khởi chiếu từ ngày 16/4/2021 trên toàn quốc.</p>', NULL, 'mac-van-khoa-suyt-chet-vi-bi-lat-xuong-ngam-minh-duoi-nuoc-lanh-suot-2-tieng-trong-lat-mat-48h', 0, NULL, NULL),
(48, 8, 3, 1, 1, 'Thu Trang: Hướng Dương Ngược Nắng không hề cổ xúy tiểu tam mà đứng về phía những người phụ nữ đáng thương', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617725055351-16177250557521392012151.png', '|', 'Không chỉ chia sẻ về vai diễn Minh HH ở Hướng Dương Ngược Nắng, Thu Trang còn có dịp nói lên những suy nghĩ của mình về cuộc sống của một single mom.', '<p>Vốn là một diễn viên đã chinh chiến ngót nghét 10 năm tại sân khấu kịch nhưng với sân chơi truyền hình, Thu Trang - nữ chính Hướng Dương Ngược Nắng vẫn là một người mới khi chỉ vừa bước sang năm thứ 3 làm quen với khán giả màn ảnh nhỏ. Với sức ảnh hưởng của Hướng Dương Ngược Nắng, tên tuổi Thu Trang thực sự bước lên một tầm cao mới và dù gây ra vô số tranh cãi về nhân vật nhưng không thể phủ nhận Thu Trang đang tiến bộ từng ngày ở bộ phim lần này.</p><p>Quá bận rộn với lịch trình quay phim và những dự án quảng cáo, chúng tôi phải mất khá lâu để có được một buổi hẹn với nữ chính Hướng Dương Ngược Nắng. Không hoàn hảo tới mức cực đoan như cô Minh HH, Thu Trang ngoài đời cũng vô cùng tự tin, trẻ trung, xinh đẹp nhưng suy nghĩ chín chắn, thấu đáo và đầy sự từng trải hơn.</p><p>Thu Trang chia sẻ về vai diễn ở Hướng Dương Ngược Nắng</p><p>Hơn 10 năm làm đào thương trên sân khấu kịch, 3 phim đầu cũng toàn đóng vai đáng yêu, sang Hướng Dướng Ngược Nắng lại nhận về không ít chỉ trích, bạn cảm thấy thế nào?</p><p>Đã xác định làm một nghệ sĩ thì phải chuẩn bị tâm lý cho tất cả những làn sóng ập đến với mình bất cứ lúc nào. Khán giả có khen, có chê, có góp ý và cả xúc phạm, nhưng nghệ sĩ thì ai cũng phải có lập trường thật vững vàng, chuẩn bị cho tất cả những trường hợp xấu nhất để đủ bản lĩnh, tỉnh táo làm nghề.</p><p>Điều mà tôi buồn nhất chính là có nhiều khán giả sống trong tâm lý và đi sâu vào nhân vật quá mức. Họ xem phim không giống với việc thưởng thức một bộ phim nữa mà đang đưa chính cuộc đời của họ gán vào nhân vật, từ đó sinh ra ức chế và bức xúc. Thậm chí lời bình luận của họ không chỉ đánh giá về nhân vật mà còn đánh giá luôn đời từ nghệ sĩ. Nhưng cũng không thể trách và tránh được đâu, vì bây giờ khán giả xem phim thì họ thường nhập tâm quá, đây cũng là điều mà các bộ phim và nghệ sĩ hướng tới.</p><p>Có khi nào bạn cảm thấy tự ức chế với nhân vật của mình không?</p><p>Chính bản thân tôi lúc đọc kịch bản còn cảm thấy có ức chế và tức thay cho các nhân vật vì những gì mà Minh đã gây ra, mặc dù bản chất cô ấy không phải là một cô gái xấu. Tình ngay lý gian, có những điều xảy đến mà Minh không chủ động nhưng lại gây ra tổn thương và khó chịu cho người xung quanh.</p><p>Cụ thể là phân đoạn nào?</p><p>Đó là đoạn Minh bị Kiên cưỡng hôn, vô tình lúc đó chị Châu lại xuất hiện. Minh vẫn đang thắc mắc là tại sao Châu chỉ nhìn thấy nụ hôn của Kiên thôi mà không thấy cái tát sau đó. Đó là điều khiến cho Minh trở nên tội nghiệp, và cô ấy lại là người không thích chạy theo người khác để giải thích, thế nên Minh luôn vướng phải những oan ức.</p><p>Có những vấn đề rõ ràng là Minh bị oan thì tại sao lại không dám đối diện, thẳng thắn nói ra sự thật? Theo tôi, đó cũng là chủ ý của biên kịch, để gây ra sự tranh luận hay tạo nên câu chuyện riêng cho khán giả bàn luận.</p><p>Phải chăng chính sự tranh cãi đó đã tạo nên thành công của Thu Trang và Minh HH ở thời điểm hiện tại?</p><p>Sự thành công của một nghệ sĩ là khi họ không bị khán giả chê một màu, nhạt nhòa. Ít ra trong bộ phim này, Thu Trang cũng có một lượng khán giả nhất định, vai Minh là một vai khá nặng nên bản thân tôi khi đảm nhận vai này cũng rất áp lực. Có một số khán giả nói Minh không giống với ngoài đời, đời thường không có người nào sở hữu siêu năng lực như Minh cả. Bản thân tôi cũng thắc mắc với biên kịch về chuyện này. Điều này chính là sợi chỉ khiến cho khán giả bức xúc, ức chế thậm chí là không ưa Minh. Nhưng thà rằng tôi nhận lại tất cả những gạch đá đó còn hơn là bị nói nhạt nhòa.</p><p>Chuyện bị nói rằng nhân vật của mình và NS Vân Dung cổ xúy cho tiểu tam, con riêng thì sao?</p><p>Nếu xem kỹ phim mọi người sẽ thấy biên kịch chỉ đang đứng về những người phụ nữ đáng thương chứ không hề cổ xúy tiểu tam. Để đánh giá được một bộ phim thì phải chờ đến cái kết. Cho đến tận bây giờ mẹ Diễm Loan (Vân Dung) - nhân tố gây ra nhiều sóng gió cho cuộc đời của rất nhiều người cũng không được sống bình yên. Tôi nghĩ mỗi người đều có một số phận riêng, không ai muốn mình làm tiểu tam cả. Hãy chờ đến cái kết của phim để thấy rằng phụ nữ nào cũng xứng đáng có được hạnh phúc.</p><p>Vậy nhân vật nữ nào gần với Thu Trang ngoài đời nhất, khiến bạn đồng cảm nhất?</p><p>Đó là Minh, tôi ở ngoài đời là người khá quyết liệt, đương nhiên không bao giờ ích kỷ hay áp đặt những mối quan hệ xung quanh mình. Tuy nhiên, phụ nữ ở thời nay ai cũng quyết liệt, mạnh mẽ và thông minh, đó cũng là điều khiến tôi sống với nhân vật Minh nhanh hơn.</p><p>Nhưng nghe nói bạn cũng mất khá lâu để nhập vai?</p><p>Mất giai đoạn đầu khoảng một tuần, tôi trăn trở và lo lắng. Khi nhận kịch bản tôi đã biết đây là một thử thách rất lớn cho bản thân mình. Khi đọc kịch bản tôi nắm rõ những điều sẽ đến với Minh, nên bị áp lực nhiều lắm, tôi tự dặn mình là phải cố gắng phải làm thật tốt vai diễn này. Ngay từ đầu tôi đã phải gồng mình lên rồi, giai đoạn bắt đầu bấm máy tâm lý đó cũng chi phối tôi nhiều quá, điều này trở thành rào cản buộc tôi phải vượt qua để hoàn thành nhân vật.</p><p>Còn áp lực về việc đóng chung với quá nhiều gương mặt đình đám, có nhiều kinh nghiệm ở lĩnh vực phim truyền hình thì sao?</p><p>Khi biết được đội ngũ ekip, diễn viên trong Hướng Dương Ngược Nắng tôi cũng có một chút hoang mang, lo lắng và áp lực, vì tôi mới bước chân vào truyền hình được ba năm nay. Nhưng đến khi bấm máy thì không còn áp lực đó nữa, thay vào đó là lo bản thân có thể tìm được Minh, sống được với cô ấy hay không. Các anh chị trong đoàn giúp đỡ, động viên tôi rất nhiều, đó cũng là động lực lớn giúp tôi làm tốt vai diễn như hôm nay.</p><p>Và người hỗ trợ bạn nhiều nhất là...</p><p>Đó chính là chị Vân Dung, người đóng cùng khá nhiều và đã chứng kiến giai đoạn mà tôi bị stress. Những ngày đầu bấm máy ở Bắc Sơn, tôi có cơ hội để chia sẻ với chị và cũng xin chị lời khuyên về cách diễn, cách vào nhân vật.</p><p>Thu Trang chia sẻ về Hồng Diễm và dàn diễn viên Hướng Dương Ngược Nắng</p><p>Phải chăng cùng xuất thân từ sân khấu kịch nên hai người dễ đồng cảm hơn?</p><p>Chị Dung cũng là một diễn viên công tác ở Nhà hát Tuổi trẻ với tôi, nên tôi không cảm thấy có rào cản hay bất kỳ sự xa cách nào. Chị lại còn đóng mẹ của tôi, đó là một sợi dây liên kết để tôi dám chia sẻ, thổ lộ và xin lời khuyên. Ở bên ngoài tôi và chị Vân Dung cũng rất thân nhau, tuy ít chia sẻ lên mạng xã hội nhưng bất kỳ lúc nào có chuyện buồn hay vui tôi đều tìm đến chị để chia sẻ và tìm lời khuyên.</p><p>Về Hồng Diễm thì sao, nhiều người nói rằng bạn là nữ chính nhưng lại bị lép vế trước Hồng Diễm.</p><p>Làm sao mà mình có thể so sánh được với một người mà đã cống hiến và gắn bó với phim nhiều năm. Chị Hồng Diễm là người có ảnh hưởng quá lớn đối với phim truyền hình, tôi còn rất vui khi được so sánh với một nghệ sĩ nổi tiếng như chị Diễm. Thời gian gần đây có rất nhiều báo giật tít rằng là “Thu Trang không sợ lép vế trước Hồng Diễm”, tôi nghĩ mọi người đang quan trọng vai chính hay thứ trong phim. Trong Hướng Dương Ngược Nắng, nhân vật nào cũng xuyên suốt, cũng liên quan đến đời sống của nhau, ai vai chính hay phụ thì còn tùy vào cảm nhận của khán giả. Riêng tôi thì không có quan điểm đã là vai chính phải là hơn các vai khác.</p><p>Nhưng thực sự không có chút chạnh lòng nào sao khi mà ngay từ đầu rất nhiều khán giả đã đến với phim chỉ vì Hồng Đăng - Hồng Diễm?</p><p>Không chút nào cả. Chị Diễm là người rất tâm lý, đến đoàn chị còn khuyên tôi cứ tập trung làm đi, đừng để những thứ bên ngoài chi phối mình quá nhiều. Tôi cũng nghĩ rằng tại sao mình phải chạnh lòng, vì bản thân chính là người sống trong nhân vật đó, hiểu quá rõ giá trị nhân vật và số phận của nó.</p><p>Về Minh, cô ấy là người sống bản năng và không được dạy dỗ từ nhỏ. Vì Minh không có tiền, quyền thế nên chỉ có thể dựa vào sức mạnh của mình, những điều mà trước giờ Minh dùng để tự vệ, cô ấy cũng đem ra bảo vệ cho gia đình. Đương nhiên là sẽ có nhiều khán giả đánh giá Minh hay động chân động tay, nhưng bản thân Minh là một cô gái bé nhỏ, rất bản năng, không hề suy nghĩ sâu xa hoặc tính toán từng đường đi nước bước.</p><p>Tiện nói về những cảnh động chân động tay, cụ thể là với Quỳnh Kool, hai bạn chủ yếu dùng kỹ thuật diễn hay đánh thật?</p><p>Đánh thật hết, tôi nhớ nhất cảnh giữ Ngọc (Quỳnh Kool) ở cầu thang và đánh vào mông. Lúc đó tôi đánh Quỳnh rất nhiều đến nỗi bàn tay của tôi đỏ và sưng hết lên. Nhưng Quỳnh lại bảo tôi cứ đánh mạnh nữa lên vì Quỳnh chưa cảm thấy đau. Sau khi cảnh đó lên sóng, Quỳnh bảo thương tôi quá vì tôi đánh Quỳnh suốt mấy tiếng đồng hồ, đánh đến rát hết cả tay mà chỉ lên hình có vài giây.</p><p>Có vẻ sau những cảnh động tay động chân đó, bạn và Quỳnh không bị ái ngại với đối phương?</p><p>Đúng vậy, mà ở đoàn tôi, mọi người rất thích phân cảnh đi đánh hoặc bị người khác đánh, mọi người phấn khích lắm.</p><p>Sắp tới sẽ còn nhiều cảnh tương tự chứ?</p><p>Hướng Dương Ngược Nắng cũng chuẩn bị đi đến hồi kết rồi, mà Minh vẫn còn đánh nhau nữa thì tôi sẽ không còn cơ hội được khán giả thông cảm. Giai đoạn cuối là giai đoạn để giải quyết tất cả những mâu thuẫn đã xảy ra từ thế hệ trước để lại cho thế hệ sau, có nhiều những cảnh yêu thương, lãng mạn hơn.</p><p>Đây cũng là giai đoạn mà Minh lột xác, cụ thể nhất là đi cắt tóc đúng không?</p><p>Đúng và đó là bước ngoặt lớn, là cú sốc lớn dành cho Minh. Lúc Minh đi cắt tóc là lúc Minh rất buồn và thực sự muốn gục ngã rồi, nên cô ấy đã thay đổi và tự động viên bản thân.</p><p>Ba năm gắn bó, 4 bộ phim, đến giờ chị nhận được và mất gì sau quyết định chuyển hướng từ diễn viên kịch nói sang diễn viên truyền hình?</p><p>Ai cũng vậy không thể một mình mà làm tốt được rất nhiều việc. Bản thân tôi cũng không thể nào vừa đóng phim tốt vừa làm tròn vai trò ở sân khấu kịch hay ở gia đình, tôi không thể phân thân mà buộc phải đánh đổi. Khi bước chân vào phim truyền hình thì tôi cũng lơ là rất nhiều việc ở sân khấu, mặc dù sân khấu là nơi đã tạo nên Thu Trang của ngày hôm nay. Hiện tại tôi đang cố gắng cân đối, sắp xếp để làm thế nào dung hòa được mọi việc.</p><p>Thu Trang chia sẻ về con trai và cuộc sống của single mom</p><p>Sự chuyển hướng phải chăng vì phim ảnh mang đến cho bạn nhiều danh tiếng hơn?</p><p>Phim truyền hình ảnh hưởng đến khán giả đại chúng, còn sân khấu luôn có một lượng khán giả nhất định. Một vé xem kịch 150 nghìn thì tại sao không lấy 150 nghìn đó lên rạp để xem phim thay vì xem một vở kịch vừa nặng đầu vừa ngột ngạt, đó cũng là lý do khiến sân khấu gặp khó khăn. Điều đầu tiên tôi nghĩ đến chuyển từ sân khấu sang làm phim truyền hình đó là tại sao mình cũng là một diễn viên nhưng không ai biết đến mình? Tôi cũng muốn bản thân có sự ảnh hưởng tới xã hội, cũng muốn những điều mình làm cho xã hội được mọi người ghi nhận. Chính vì thế khi bước chân sang truyền hình tôi muốn kết nối bản thân mình đến công chúng, để cho mọi người biết tôi là một diễn viên, đã và đang làm được gì cho nền điện ảnh nước nhà.</p><p>Vậy diễn sân khấu và truyền hình có khác nhau nhiều không, ở đâu khiến bạn áp lực hơn?</p><p>Làm điều gì tôi cũng thấy áp lực và phải đầu tư rất nhiều chất xám. Ở sân khấu thì cũng có những giai đoạn mình phải nghiên cứu kịch bản, phim truyền hình cũng vậy, cái nào cũng khó như nhau. Nhưng ở thời điểm hiện tại thì không quá khó khăn, vì tôi đã có kinh nghiệm trong nghề, gây dựng cho mình được một bản lĩnh nhất định.</p><p>Nhưng từ khi chuyển hướng sang truyền hình có vẻ chị bận rộn hơn hẳn?</p><p>Trước đây tôi là người sống cho bản thân rất nhiều nhưng hiện tại thì thời gian dành cho gia đình còn không có. Nhiều lúc tôi thấy mình đang xuề xòa với bản thân quá, thế nhưng thay vào đó tôi lại đạt được hiệu quả công việc rất tốt, đó là sự đánh đổi xứng đáng.</p><p>Về con trai thì sao, bé có bị thiệt thòi nhiều khi mẹ quá bận?</p><p>Bé nhà tôi rất thiệt thòi, mẹ thì cứ đi từ sáng đến tối và bé cũng quá quen với việc này. Đương nhiên là bé vẫn sẽ buồn và tủi thân, vẫn muốn được mẹ đưa đi chơi. Tôi chỉ có thể bù đắp bằng việc ở bên con nhiều hơn trong những dịp lễ Tết.</p><p>Với bộ phim lần này, bạn cho con xem phim không và bé có biết những gì mà cư dân mạng nói về mẹ mình?</p><p>Bé không biết vì bé còn nhỏ quá. Nhưng bây giờ trẻ con cũng rất thông minh, có thể nhận thức được vui buồn rồi, thậm chí bé nhà tôi xem phim xong thích chú Trí quá còn đi mua 3, 4 con chim về để nuôi. Trẻ con mà thích cái gì thì sẽ làm cái đó, nhưng cũng thật may là ở thời điểm bây giờ em bé nhà tôi còn quá nhỏ và chưa hiểu nên chưa bị dư luận chi phối nhiều.</p><p>Liệu sau này bạn có nghĩ tới chuyện sẽ chỉ nhận vai tốt để con không bị ảnh hưởng bởi dư luận?</p><p>Cũng có thể đấy vì em bé nhà tôi là bé trai nên ít nhiều tôi cũng không thể nắm bắt được tâm lý của con. Tôi rất sợ việc tôi đang phải nuôi con một mình và việc dạy dỗ con trở thành một người như thế nào, có giống tôi mong muốn hay không.</p><p>Tìm một người cùng san sẻ, chăm sóc, dạy dỗ con thì sao?</p><p>Tôi chưa nghĩ đến chuyện sẽ tìm cho mình một người đàn ông ở bên. Tôi rất hạnh phúc và cảm thấy hài lòng với cuộc sống hiện tại. Chỉ cần như vậy thôi, không nhất thiết cứ phải gắn bó cuộc đời mình với một người đàn ông mãi mãi, bởi vì cuộc sống trước đây của tôi cũng đã là một câu trả lời, một cái gương lớn để tôi nhìn nhận lại bản thân và biết được cuộc sống của mình cần điều gì hơn. Đến thời điểm hiện tại tôi cần công việc và một cuộc sống hạnh phúc bên gia đình hơn là một người đàn ông ở bên cạnh để bảo vệ, che chở cho mình.</p><p>Còn lời đồn về việc chị từng từ chối lời đề nghị \"bao nuôi cả mẹ lẫn con\" của một số người đàn ông giàu có?</p><p>Nhiều người nói cô này đầy đủ yếu tố mà các anh đại gia thích, thứ nhất cô ấy là một nghệ sĩ nổi tiếng, thứ hai là một single mom, đó là chuyện họ áp đặt cho tôi như vậy, đồng nghĩa với việc các đại gia sẽ ào ào ập vào và đi tìm những single mom. Ai cũng có một quan điểm sống khác nhau, bản thân của tôi cho đến tận bây giờ không cổ xúy việc đại gia bao nuôi, nhưng nếu chọn cho mình được một người đàn ông tốt, phù hợp mà họ lại có điều kiện thì còn gì tốt bằng. Dù vậy tiêu chí đầu tiên mà tôi vạch ra cho người đàn ông bên cạnh mình không phải là tiền, tôi cần họ yêu thương và hiểu được cho công việc của mình. Nghệ sĩ là những ánh hào quang mà họ muốn tìm hiểu và khám phá, để rồi khi đã khám phá được thì họ sẽ ra đi. Tôi không cần những mối quan hệ như thế, mà cần  mối quan hệ bình lặng, âm thầm nhưng lâu dài. Tôi không nói những mối quan hệ chớp nhoáng là không tốt, nhưng nó sẽ làm ảnh hưởng tới người phụ nữ rất nhiều.</p><p>Nghĩa là bạn cũng tự cảm thấy rằng nhiều người đang đặt nặng những thành kiến lên single mom khiến bạn mất đi nhiều cơ hội?</p><p>Nhiều khi mình rung động với một người, nhưng lại không dám đến với họ vì rào cản. Có những người quá giàu, họ muốn kết bạn với mình nhưng tôi lại sợ cảm giác bị người ngoài nói mình cặp với đại gia để có nhà và xe sang chảnh.</p><p>Tại sao mọi người lại nghĩ thế? Phụ nữ hiện đại rất giỏi, không cần sống dựa dẫm vào đàn ông mà vẫn có thể tự mua được cho mình những thứ mình thích. Thậm chí có những người đàn ông còn không có đủ điều kiện để đáp ứng được nhu cầu của phụ nữ. Tại sao mọi người không nhìn vào những điều người phụ nữ đã trải qua mà cứ thấy họ đang sống sang chảnh là nghĩ ngay rằng họ dùng tiền người khác. Như thế có quá bất công với những single mom như chúng tôi và những người phụ nữ tự lập không? Không phải đại gia nào cũng thích single mom mà cũng không phải single mom nào cũng chọn đại gia để dựa dẫm.</p><p>Từng chia sẻ mình là tuýp người ưa sở hữu, trải qua những đổ vỡ và làm một người mẹ đơn thân, hiện tại sự sở hữu đó đang ở mức độ nào?</p><p>Nó cũng dần mất đi rồi, vì cái đó không tốt và đem lại rất nhiều những điều mình không mong muốn. Chẳng một người đàn ông hay phụ nữ nào muốn đối phương sở hữu mình, càng bị sở hữu thì lại càng muốn rời đi, cho đến bây giờ tôi mới nhận ra chân lý đó. Những điều vốn dĩ thuộc về mình thì sẽ là của mình, còn những thứ không giữ được thì đừng nên giữ. Như chồng chẳng hạn, hãy giữ ở mức độ thôi, giữ quá thì lại mang đến những hậu quả khôn lường.</p><p>Có cảm thấy mình nhận ra hơi muộn không?</p><p>Tất cả những điều đã xảy ra trong quá khứ với tôi thì đó sẽ là bài học rất lớn và rất đắt giá cho mình. Đó cũng là trải nghiệm để có được một Thu Trang như hôm nay và tôi thấy không có gì là quá muộn cả. Bây giờ tôi mới 30 tuổi thôi, vẫn có thể làm lại, có thể gây dựng cho mình một mối quan hệ mới, kết hôn và bỏ tính sở hữu đi.</p><p>Và cuộc sống hiện tại của mình thì đang...</p><p>Đó là hài lòng và vui vẻ!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/5w2b3950-16177201217391088762938.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/artboard-1-2-16177203192061653315151.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/5w2b3941-16177201218191854536869.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/artboard-1-copy-1-16177203192581761118636.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/5w2b3857-1-1617720121881170268070.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/artboard-1-copy-2-1-1617720319189734444246.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/5w2b3889-16177201218342042209231.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/5w2b3923-16177201218441391865988.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/artboard-1-copy-3-16177203186671975618360.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/artboard-1-copy-4-16177203190701289014340.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/5w2b3875-16177201218551252495421.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/artboard-1-copy-5-16177203187211717186381.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/artboard-1-copy-6-3-16177251533202043544479.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/artboard-1-copy-7-2-1617720318794938707302.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/artboard-1-copy-8-2-161772031896399245031.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Cảm ơn Thu Trang về những chia sẻ thú vị. Chúc cho bạn sẽ có thêm nhiều những dự án phim thành công và đến gần hơn nữa với khán giả!</p>', NULL, 'thu-trang-huong-duong-nguoc-nang-khong-he-co-xuy-tieu-tam-ma-dung-ve-phia-nhung-nguoi-phu-nu-dang-thuong', 0, NULL, NULL),
(49, 8, 3, 1, 1, 'Châu (Hồng Diễm) vừa trở về đã lập mưu giật crush của em gái ở Hướng Dương Ngược Nắng 2?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617724787203-1617724787416220040819.gif.png', '|', 'Châu đã kịp trở lại và cứu Cao gia một màn thua đau ở Hướng Dương Ngược Nắng 2 tập 20.', '<p>Sau bao ngày chuẩn bị, cuối cùng cuộc họp cổ đông đã diễn ra ở Hướng Dương Ngược Nắng 2 tập 20. Ban đầu, Hoàng Đế của Hoàng (Việt Anh) đã chiếm ưu thế khi được nhiều người ủng hộ hơn, nhưng Châu (Hồng Diễm) đã xuất hiện ở phút thứ 90 khiến cho số cổ phần ngang bằng và buộc phải tìm ra một người muốn thay đổi quyết định. Ngạc nhiên chưa, người đó lại chính là ông Vụ (NSND Công Lý), từ ủng hộ Hoàng Đế, ông ta lại bất ngờ về phe Cao gia.</p><p>Chuyện phim tiếp tục với màn đoàn tụ của gia đình Châu sau hơn một tháng cô nàng trốn tránh sự đời, đi ở ẩn với Phúc (Doãn Quốc Đam). Đây cũng là lúc lý do ông Vụ bỗng dưng đổi ý được tiết lộ, hóa ra bà Cúc (NSND Thu Hà) đã ép buộc ông Vụ bằng cách khởi kiện con trai ông ta về tội cưỡng hiếp Châu.</p><p>Cuối tập phim là màn đoàn tụ của Minh (Thu Trang) và Phúc, Minh liên tục trách mắng người anh thân thiết vì đã mất tích quá lâu lại còn rất nghe lời chị Châu. Hoàng đã chứng kiến cảnh tượng này và ghen ra mặt. Ở một diễn biến khác, Kiên (Hồng Đăng) xin được nói chuyện với Châu nhưng đại tiểu thư Cao gia không đồng ý. Ngay sau đó, Châu cũng nhìn thấy cảnh Minh - Phúc thân thiết với nhau và biểu cảm của cô vô cùng kì lạ. Châu đang toan tính điều gì, chúc phúc cho em hay sẽ lên kế hoạch giành lấy Phúc để khiến Minh đau khổ?</p><p>Hướng Dương Ngược Nắng 2 phát sóng 21:40 thứ Hai đến thứ Tư hằng tuần trên VTV3.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-2-161768008600075164222.jpg\" ><figcaption class=\"caption\">Châu bất ngờ trở lại</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-1617723939886763406345.png\" ><figcaption class=\"caption\">Người giúp Cao gia lần này lại là ông Vụ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-16177239967072005006934.png\" ><figcaption class=\"caption\">Ông Vụ trước đó đã bị bà Cúc nắm thóp</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-16177240570371039685461.png\" ><figcaption class=\"caption\">Hoàng ghen ra mặt nhưng không dám làm gì...</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-16177240958991558877790.png\" ><figcaption class=\"caption\">Khi thấy Minh - Phúc thân thiết</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-16177241245452025703790.png\" ><figcaption class=\"caption\">Châu lại có biểu cảm rất lạ khi thấy cảnh tượng này</figcaption></figure><p>Nguồn ảnh: VTV</p>', NULL, 'chau-hong-diem-vua-tro-ve-da-lap-muu-giat-crush-cua-em-gai-o-huong-duong-nguoc-nang-2', 0, NULL, NULL),
(50, 8, 3, 1, 1, 'Tức ói máu với \"cô Báu\" Nhã Phương ở Cây Táo Nở Hoa, đám tang bố mà chỉ lo đàn đúm bạn bè', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617718527782-16177185279591004699096.gif.png', '|', 'Tập 2 Cây Táo Nở Hoa có lẽ nên đổi tên thành \"Hạnh phúc của một tang gia\".', '<p>Đúng như dự đoán, người đột ngột qua đời ở cuối tập 1 Cây Táo Nở Hoa chính là bố của anh em Ngọc (Thái Hòa). Dù là bố đẻ nhưng ngoài Ngọc ra thì không một anh em nào trong nhà tỏ vẻ tiếc thương, đặc biệt Báu (Nhã Phương) và Ngà (Trương Thế Vinh) còn hồn nhiên nói những lời chua chát, cho rằng không nên nhìn mặt bố lần cuối vì \"gặp người chết xui lắm\".</p><p>Đám tang bố thì vẫn phải tổ chức nhưng không một ai đến viếng, con cái cũng chẳng ai khóc thương. Báu và Châu (Thúy Ngân) thậm chí còn cà khịa nhau giữa đám tang bố xem ai có nhiều bạn bè đến viếng hơn. Báu sau đó còn tranh thủ chụp ảnh tự sướng để đăng lên mạng xã hội cho bạn bè biết đường tới thăm. Đám tang giống trò cười hơn khi bố của Hạnh (Hồng Ánh) tới viếng, giả giọng gào khóc nhưng thực chất là nói xấu vợ chồng Hạnh - Ngọc.</p><p>Đám tang càng giống trò cười hơn khi hội bạn của Báu tới. Không chỉ trang điểm lòe loẹt, hội chị em còn làm lố, tranh thủ diễn sâu chụp hình, cười nói ầm ĩ, ăn mặc hở hang (được che bằng một lớp áo khoác).</p><p>Ngọc vất vả ở đám tang, ăn uống không đủ thậm chí còn đau bao tử, trong khi đó anh em Ngà - Báu vẫn chẳng mảy may suy nghĩ gì, đã thế còn mua bia về nhậu và nhảy trên bàn ăn. Khi hỏi nhau về kỷ niệm với bố, các em của Ngọc không nhớ một điều gì ngoài bữa nhậu năm Báu học lớp 10.</p><p>Những vị khách không mời mà tới đã đến đám tang của bố Ngọc, đầu tiên là chủ nợ của Ngà, thứ hai là một đám giang hồ có quen biết Dư (Song Luân). Đám người này tới viếng và để lại một phong bì rất nhiều tiền, không biết trong quá khứ đã có chuyện gì xảy ra nhưng Ngọc khăng khăng không cho đám người này viếng bố cũng không cho Ngà nhận tiền vì đó là tiền \"mua mạng Dư\". Đây cũng là lúc Dư nhớ về quá khứ ngày nhỏ luôn bị bố ghét bỏ, thậm chí còn vì bố mà bị tai nạn gãy xương vai.</p><p>Cây Táo Nở Hoa phát sóng 20:00 thứ Hai đến thứ Tư hằng tuần trên kênh truyền hình HTV2.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/vie-channelcay-tao-no-hoa-tap-24-16177174652991844723633.jpg\" ><figcaption class=\"caption\">Ngọc nổi điên vì các em quá vô tâm</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/vie-channelcay-tao-no-hoa-tap-29-1617717478905156624232.jpg\" ><figcaption class=\"caption\">Báu tranh thủ diễn nét buồn để chụp hình gửi bạn bè</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/vie-channelcay-tao-no-hoa-tap-214-1617717346925289221321.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/vie-channelcay-tao-no-hoa-tap-215-1617717464285319008463.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/vie-channelcay-tao-no-hoa-tap-218-16177175791501595658925.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-1617717670869323167259.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/vie-channelcay-tao-no-hoa-tap-223--16177178639241087895256.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/vie-channelcay-tao-no-hoa-tap-225-1617717863771918252431.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn ảnh: HTV2</p>', NULL, 'tuc-oi-mau-voi-co-bau-nha-phuong-o-cay-tao-no-hoa-dam-tang-bo-ma-chi-lo-dan-dum-ban-be', 0, NULL, NULL),
(51, 8, 3, 1, 1, 'Cây Táo Nở Hoa vừa lên sóng đã bị tố nhái poster bom tấn Hàn, phim remake mà mọi người ơi!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617700019545-16177000198171232972529.jpg', '|', 'Không hề có chuyện Cây Táo Nở Hoa đạo nhái poster What’s Wrong Poong Sang.', '<p>Vốn từng bị nghi ngờ là phiên bản remake của Khi Hoa Trà Nở vì cái tên na ná nhau nhưng sau khi công bố những trích đoạn đầu tiên, Cây Táo Nở Hoa đã chứng minh được rằng mình không có liên quan gì đến bộ phim của chị đẹp Gong Hyo Jin. Tuy vậy sau khi tập 1 lên sóng, phim lại tiếp tục dính phải nghi án đạo nhái poster một bom tấn truyền hình khác có tên What’s Wrong Poong Sang.</p><p>Hai poster gần như giống hệt nhau</p><p>Thực chất không hề có chuyện đạo nhái, Cây Táo Nở Hoa chính là phiên bản remake nhưng dài hơi hơn của phim Hàn Quốc What’s Wrong Poong San. Ngay tại buổi họp báo ra mắt trước khi phim lên sóng, ekip cũng đã đưa ra thông báo chính thức về việc Cây Táo Nở Hoa là một bộ phim remake.</p><p>Phát sóng năm 2019, What’s Wrong Poong San được đông đảo khán giả yêu thích, đạt rating vượt ngưỡng 20%. Ekip Cây Táo Nở Hoa đã mua bản quyền Việt hóa và chuyện poster có giống với bản Hàn cũng là điều dễ hiểu. </p><p>Poster nhân vật ở hai phiên bản</p><p>Cây Táo Nở Hoa phát sóng 20:00 thứ Hai đến thứ Tư hằng tuần trên kênh truyền hình HTV2.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/cats-16177006517111930529165.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177015163091774146180.jpg\" ><figcaption class=\"caption\">What’s Wrong Poong San là bộ phim truyền hình khá đình đám tại Hàn Quốc</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/1640043318777154994721464673621028101514287n-16177016183611823197791.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn ảnh: Tổng hợp</p>', NULL, 'cay-tao-no-hoa-vua-len-song-da-bi-to-nhai-poster-bom-tan-han-phim-remake-ma-moi-nguoi-oi', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(52, 8, 3, 1, 1, 'Diễn viên lồng tiếng TVB hé lộ \"hậu trường\" quằn quại, vật vã để đọc thoại được cảm xúc, netizen thấy mê luôn', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617702883634-1617702885150870807060.gif.png', '|', 'Hóa ra, để nhập tâm được 100% và mang đến những câu thoại cảm xúc, các diễn viên lồng tiếng cũng phải lăn xả vô cùng!', '<p>Bấy lâu nay, nhiều khán giả vẫn thường \"bỏ quên\" những con người góp phần làm nên thành công của nhiều bộ phim châu Á, TVB tại Việt Nam - chính là các diễn viên lồng tiếng. Mặc dù nhiều người bây giờ ưa chuộng xem phim với phụ đề, tuy nhiên không thể phủ nhận những giọng đọc lồng tiếng các bộ phim chưởng, phim truyền hình đã lớn lên với nhiều thế hệ. Nhắc đến phim TVB, chắc vẫn nhiều người nhớ ngay đến câu nói bất hủ: \"Sài Gòn dịch và lồng tiếng, Fafilm Việt Nam phát hành. Bộ phim...\". Giờ đây, vẫn còn nhiều khán giả thích xem phim có lồng tiếng vì đã quá quen thuộc và yêu thích.</p><p>Tuy nhiên, đã bao giờ bạn tự hỏi \"hậu trường\" của những phân đoạn lồng tiếng phim thực sự ra làm sao chưa? Các diễn viên lồng tiếng đôi khi cũng cần phải có được cảm xúc giống như diễn viên trước ống kính, để có thể truyền tải các câu thoại một cách thuyết phục nhất chứ không hề \"giả trân\".</p><p>Diễn viên lồng tiếng Đạt Phi, hay còn được biết đến là thầy Đạt Phi, là một trong những gương mặt gạo cội và nổi tiếng nhất trong nghề lồng tiếng phim, là người đứng sau hàng loạt \"bom tấn Hollywood biết nói tiếng Việt\" hay những thước phim TVB kinh điển. Mới đây, ông đã chia sẻ một đoạn video trên trang TikTok cá nhân của mình, cho thấy hậu trường đầy cảm xúc của một cảnh phim đối với các diễn viên lồng tiếng.</p><p>Hậu trường cảnh lồng tiếng rất cảm xúc</p><p>Các diễn viên phải la hét, quằn quại ra với nhau</p><p>Sau khi chứng kiến cảnh lồng tiếng này, nhiều khán giả cũng bày tỏ thái độ bất ngờ. Vốn dĩ, nhiều người chỉ tưởng các diễn viên lồng tiếng sẽ đứng im trước mic để đọc thoại, chứ không phải vật vã, gào thét khổ sở đến thế này.</p><p>- Đây cũng là những nhân vật làm nên thành công cho bộ phim!</p><p>- Đúng là lồng tiếng phải diễn như thế này mới thật.</p><p>- Những người thầm lặng sau những thước phim...</p><p>- Nghề gì cũng phải có tâm, thật khâm phục.</p><p>- Phải như thế này chứ, giờ nhiều phim lồng tiếng chẳng có cảm xúc một chút nào, nghe \"tuột mood\" ngay lập tức.</p><p>- Hình như đang lồng tiếng phim Diên Hy Công Lược đúng không nè?</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177024105011754592649.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/chrome-capture-9-1617702688035510784385.gif\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-161770218968789906540.jpg\" ><figcaption class=\"caption\">Diễn viên lồng tiếng Đạt Phi, được nhiều người trong nghề kính trọng gọi là \"thầy\"</figcaption></figure><p>Nguồn: @datphinguyen</p>', NULL, 'dien-vien-long-tieng-tvb-he-lo-hau-truong-quan-quai-vat-va-de-doc-thoai-duoc-cam-xuc-netizen-thay-me-luon', 0, NULL, NULL),
(53, 8, 3, 1, 1, 'Phim cổ trang về nữ hoàng đế duy nhất của Việt Nam khiến netizen đứng ngồi không yên vì tạo hình \"chuẩn chỉ\"', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617686434065-16176864345231621222581.png', '|', 'Bộ phim cổ trang về nữ hoàng đế Lý Chiêu Hoàng công bố tạo hình đầu tiên đã khiến khán giả cực hào hứng.', '<p>Ở Việt Nam, đề tài phim cổ trang vốn luôn là một bài toán khó về cả nội dung lẫn sản xuất. Mặc dù sở hữu lịch sử phong phú và lâu đời, việc đưa những nhân vật, câu chuyện của quá khứ lên màn ảnh chưa bao giờ là điều dễ dàng. Mới đây, một dự án cổ trang Việt đã khiến nhiều khán giả phải chú ý, mang tên Lý Chiêu Hoàng - nữ hoàng đế duy nhất trong lịch sử Việt Nam, trị vì từ năm 1224 đến 1225.</p><p>Dự án cổ trang này hiện đã công bố ý tưởng tạo hình của các nhân vật, tuy nhiên vẫn đang trong thời kỳ kêu gọi nhà đầu tư. Trước mắt, các hình ảnh đầu tiên từ phim đều nhận được phản hồi tích cực của khán giả vì tính trung thực với lịch sử.</p><p>Bộ phim về Lý Chiêu Hoàng này được nhà sản xuất thông báo sẽ là limited series - phim truyền hình ngắn tập. Kịch bản được tóm tắt như sau: “Vị nữ đế bị ép nhường ngôi cho chồng, lui về làm hoàng hậu, một lần nữa phải đối mặt với âm mưu phế vị khi không còn giá trị lợi dụng. Chỉ còn ba ngày cuối cùng làm hoàng hậu, sẽ thế nào nếu nàng nắm trong tay thời cơ duy nhất để đảo nghịch tình thế, giành lại hoàng quyền?”</p><p>Ngoài ra, nhà sản xuất của phim cũng thông báo rằng kịch bản của phim sẽ được chắp bút bởi biên kịch Paek Seong Og. Ông sẽ là người chuyển thể câu chuyện lịch sử được bên sản xuất Vietnam Centre tổng hợp, từ đó thêm thắt các yếu tố nghệ thuật cũng như thu hút đại chúng.</p><p>Nhìn chung, khán giả Việt đều rất háo hức về dự án phim Lý Chiêu Hoàng. Cuộc đời của nữ hoàng đế này là một bi kịch và cũng vô cùng phức tạp, cũng là nguồn cảm hứng cho nhiều tác phẩm nghệ thuật đại chúng.</p><p>Vẫn còn đang được \"thai nghén\" và được gọi đầu tư bởi nhóm Vietnam Centre, dự án series Lý Chiêu Hoàng hẵng còn một chặng đường dài để đi. Mong rằng khán giả Việt sẽ sớm nhận về được một tác phẩm chỉn chu, hấp dẫn về cả nội dung lẫn phần nhìn. </p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617684813335766463436.jpg\" ><figcaption class=\"caption\">Nguyễn Hoàng Kiều Trinh đóng vai Lý Chiêu Hoàng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176848216471991965065.jpg\" ><figcaption class=\"caption\">NSƯT Mạnh Dung đóng vai Trần Thủ Độ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176848368971596100058.jpg\" ><figcaption class=\"caption\">Nguyễn Phúc Quý Quang đóng vai Trần Cảnh</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176848480471973424507.jpg\" ><figcaption class=\"caption\">Nông Hồng Sơn đóng vai Lê Phụ Trần</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176848564451147384086.jpg\" ><figcaption class=\"caption\">Nguyễn Thị Diệu Vân đóng vai Thuận Thiên công chúa</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617685406899273424914.jpg\" ><figcaption class=\"caption\">Nhiều người mong chờ mối tình giữa Lý Chiêu Hoàng và Trần Cảnh sẽ được đưa lên phim</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/screen-shot-2021-04-06-at-120418-1617685564981312919269.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/screen-shot-2021-04-06-at-120434-1617685565257224528385.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176853705011211785129.jpg\" ><figcaption class=\"caption\">Tên tiếng Anh của phim là Empress Lý (Nữ hoàng Lý)</figcaption></figure><p>Nguồn ảnh: Vietnam Centre, Chụp màn hình</p>', NULL, 'phim-co-trang-ve-nu-hoang-de-duy-nhat-cua-viet-nam-khien-netizen-dung-ngoi-khong-yen-vi-tao-hinh-chuan-chi', 0, NULL, NULL),
(54, 9, 3, 1, 1, 'Đóng thế của Địch Lệ Nhiệt Ba lộ mặt rõ mồn một ở Trường Ca Hành, netizen phản pháo: \"Có 2 giây làm gì căng?\"', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617766018973-1617766019168789094698.gif.png', '|', 'Trường Ca Hành lại bị chê cười vì cảnh phim lộ rõ diễn viên đóng thế của Địch Lệ Nhiệt Ba.', '<p>Sau cảnh hôn giả trân gây tranh cãi, Trường Ca Hành lại bị ném đá vì cẩu thả trong hậu kì. Trong một cảnh giao chiến giữa Lý Trường Ca và nhân vật phụ, gương mặt diễn viên đóng thế của Địch Lệ Nhiệt Ba xuất hiện cực kì lộ liễu trên màn ảnh.</p><p>Chuyện diễn viên sử dụng thế thân không có gì xa lạ, nhưng ekip đã thể hiện rõ sự cẩu thả khi cắt ghép, khiến cảnh phim trở nên thiếu thuyết phục. Đây cũng là cái cớ để nhiều cư dân mạng còn chỉ trích Địch Lệ Nhiệt Ba không \"kính nghiệp\", những cảnh đơn giản cũng phải dùng đóng thế. Dù vậy, một số cư dân mạng cho rằng đây rõ ràng là lỗi của đạo diễn và tổ hậu kì, không thể trách diễn viên. Bên cạnh đó, cảnh quay này chỉ lướt qua trên màn ảnh chưa đến 1 giây, khán giả bình thường đa số không chú ý.</p><p>Bình luận của cư dân mạng:</p><p>- Xem qua vài đoạn thì chủ yếu thấy phim này không hề được đầu tư nhiều. Cụt (tên gọi khác của Tencent) tiết kiệm kinh phí làm ăn qua loa chủ yếu có cái chiếu cho fan cô xem để kiếm tiền chứ chả thấy nó bỏ bao nhiêu tâm huyết hay kì vọng vào cái bộ này.</p><p>- Nghe bảo đầu tư cũng nhiều tiền mà sao đoàn phim làm ăn kì thế? Cứ như kiểu quay cho xong để chiếu á, ẩu kinh khủng.</p><p>- Quay rõ hơn xíu có khi thế thân còn diễn ổn hơn cô Ba. Tôi bị ám ảnh cái kiểu diễn nhăn mày của cô này quá, giả trân hết nói nổi.</p><p>- Kính nghiệp là đây. Cảnh nhảy cửa sổ thì lồng hoạt hình, cảnh hôn thì để chim quay, cảnh đánh nhau thì thế thân. Khúc nhẹ nhàng thì để chị kính nghiệp. Chưa kể đến cái nét diễn giả trân trên mặt chị nữa.</p><p>- Rồi mắng hậu kỳ chứ mắng diễn viên là sao? Anti đến mức không phân biệt được khái niệm à? Dù mình không xem phim cô Ba đóng mà còn thấy phản cảm giùm luôn á.</p><p>- Ủa này là lỗi đoàn phim mà. Phim cổ trang ai mà không dùng thế thân. Edit không tốt cái đổ lên đầu diễn viên mấy má. Táp vừa thôi tém tém lại cái đi.</p><p>- Chắc blogger mê phim quá nên tua chậm x 0,25 để soi từng chi tiết đấy, khán giả đa số đều không chú ý đến cảnh này vì nó lướt qua quá nhanh, vào miệng blogger lại thành “lộ liễu”.</p><p>- Cảnh người ta quay cẩn thận, hậu trường có. Nếu có video lộ thế thân thì quăng lên đây chứ đừng có để cái đoạn gif 2s cắt ghép dắt mũi.</p><p>Nguồn: Cbiz: Chuyện chưa kể</p><p></p><p>Trường Ca Hành phát sóng lúc 20h00 vào thứ Hai, thứ Ba, thứ Tư hàng tuần trên VieON.</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/ezgif-7-83f55339f0f0-1617762873803384293188.gif.mp4\" ></video><div class=\"caption\">Diễn viên đóng thế được \"hô biến\" thành Nhiệt Ba khá vụng về</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/ezgif-7-f904198cbf38-16177655775641275854487.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/ezgif-7-a67995b2be5c-16177655775661852149503.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/ezgif-7-ef58b8ac3054-16177655775672006220591.gif.mp4\" ></video><div class=\"caption\">Nhiệt Ba tự mình thực hiện nhiều cảnh hành động</figcaption></figure><p>Nguồn ảnh: Weibo</p>', NULL, 'dong-the-cua-dich-le-nhiet-ba-lo-mat-ro-mon-mot-o-truong-ca-hanh-netizen-phan-phao-co-2-giay-lam-gi-cang', 0, NULL, NULL),
(55, 9, 3, 1, 1, 'Joseon Exorcist \"quỵt\" tiền cát-xê diễn viên tận 12 tập phim, netizen \"thấy cũng tội mà thôi cũng kệ\"', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617769182935-16177691830961069789142.gif.png', '|', 'Thương thay cho dàn cast Joseon Exorcist, vật vã quay 14 tập phim mà chỉ nhận thù lao 2 tập.', '<p>Sau khi bị hủy chiếu vì sai lệch lịch sử, Joseon Exorcist (Pháp Sư Trừ Tà Triều Tiên) đang phải đối mặt với rất nhiều vấn đề. Ngày 6/4, truyền thông Hàn Quốc đưa tin các diễn viên của phim mới được trả thù lao cho 2 tập phát sóng, dù họ đã quay xong 14/16 tập. Trong khi đó, bình thường diễn viên sẽ được trả 80% cát-xê cho các tập phim đã quay, khi phim đã lên sóng.</p><p>Tuy nhiên, nhà sản xuất khó lòng có thể trả đủ thù lao cho các diễn viên, vì họ đang phải gánh khoản lỗ kinh phí lên tới  32 tỷ won. Toàn bộ nhà tài trợ đã rút khỏi bộ phim, khiến  Crave Works, Lotte Cultureworks và  YG Studioplex rơi vào cảnh điêu đứng.</p><p>Jang Dong Yoon - nam chính của bộ phim</p><p>Cư dân mạng có nhiều ý kiến trái chiều. Người cho rằng ekip  Joseon Exorcist phải thanh toán đủ tiền cho các diễn viên theo quy định, người lại thấy dàn cast phải chịu chung trách nhiệm vì họ đã nhận đóng một tác phẩm sai trái. </p><p>Bình luận của cư dân mạng Việt:</p><p>- Nghe nói Jang Dong Yoon vì quay phim này mà gãy cả tay, đi nhận giải nào đó còn phải bó bột cơ mà. Khá là quý anh, thôi thì coi như lần này đen vậy.</p><p>- Nên chịu trách nhiệm chung. Như vậy ok mà.</p><p>- Đâu phải ai cũng học và tìm hiểu kỹ về lịch sử nước nhà đâu. Mình không phản bác chuyện chính trị, nhưng cũng không nên đổ lên đầu diễn viên toàn bộ mọi chuyện như vậy.</p><p>- Chứ có được chiếu đâu mà có tiền trả hết 14 tập ngộ ha?</p><p>Nguồn: Netizenbuzz Vtrans</p><p>Joseon Exorcist kể về cuộc chiến của một gia đình hoàng tộc để bảo vệ người dân khỏi linh hồn ác quỷ. Trong hành trình đó, vua Taejong (Kam Woo Sung) cùng hai con trai của ông là hoàng tử Chung Nyung (Jang Dong Yoon) và hoàng tử Yang Nyeong (Park Sung Hoon) diệt trừ những xác sống không hồn, mang lại sự bình yên cho đất nước như trước đây.</p><p>Teaser 2 của Joseon Exorcist</p><p>Tuy nhiên, sau khi chiếu tập đầu tiên, phim bị tẩy chay dữ dội vì xây dựng vua Taejong là kẻ tàn nhẫn, giết người không ghê tay, đưa vào rất nhiều trang phục, đạo cụ, món ăn truyền thống của Trung Quốc. Sau khi họp bàn, SBS đã quyết định \"khai tử\" bộ phim.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo1308118-1617768289193242026616.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/photo1310117-16177684285561904044813.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1-16177676532791858728333.png\" ><figcaption class=\"caption\">Bình luận của cư dân mạng Hàn (Nguồn: Netizenbuzz Vtrans)</figcaption></figure><p>Nguồn: topstarnews, SBS</p>', NULL, 'joseon-exorcist-quyt-tien-cat-xe-dien-vien-tan-12-tap-phim-netizen-thay-cung-toi-ma-thoi-cung-ke', 0, NULL, NULL),
(56, 9, 3, 1, 1, 'Tiểu thuyết về chiến tranh Việt Nam thắng Pulitzer được \"trùm Oscar\" chuyển thể thành phim', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617767756958-16177677576071973813468.png', '|', 'Tiểu thuyết danh giá The Sympathizer từng đoạt giải Pulitzer sẽ được hãng phim nổi tiếng A24 chuyển thể thành phim truyền hình dưới sự chỉ đạo của một đạo diễn nổi tiếng Hàn Quốc.', '<p>Hãng phim danh giá A24 đã mua lại quyền chuyển thể cuốn sách The Sympathizer (Cảm Tình Viên) của nhà văn Nguyễn Thanh Việt. The Sympathizer là cuốn tiểu thuyết đầu tay của Nguyễn Thanh Việt, cũng là tác phẩm đã mang lại cho ông giải thưởng Pulitzer cao quý ở hạng mục tiểu thuyết.</p><p>The Sympathizer sẽ được hãng A24 chuyển thể thành series truyền hình. Cuốn sách ra mắt năm 2015, kể về một người mang dòng máu lai Pháp - Việt và góc nhìn của nhân vật này xuyên suốt những năm tháng chiến tranh Việt Nam. Nhân vật gián điệp này đã chuyển từ miền Nam Việt Nam đến Los Angeles sau khi chính quyền Sài Gòn sụp đổ, sau đó lại di chuyển khắp Đông Nam Á. The Sympathizer là một cuốn sách đề cập tới nhiều vấn đề phức tạp, từ việc người gián điệp có cảm tình với những người anh ta bị buộc theo dõi cho tới việc định cư ở Mỹ và sống như một người tị nạn.</p><p>Giáo sư Nguyễn Thanh Việt sinh ra tại Buôn Ma Thuột, theo gia đình di cư sang Mỹ từ 1975. Ông tốt nghiệp bằng danh dự ngành tiếng Anh và Nghiên cứu dân tộc học tại Đại học California. Hiện tại, ông là giáo sư tại Đại học Nam California và cũng là một nhà phê bình lớn cho tờ The New York Times.</p><p>Trước đây, tiểu thuyết The Sympathizer đã từng chiến thắng nhiều giải thưởng lớn như Center for Fiction 2015, huy chương Carnegie cho Tiểu thuyết xuất sắc của Hiệp hội Thư viện Mỹ...</p><p>Đạo diễn, biên kịch Park Chan Wook sẽ là người đảm nhiệm dự án The Sympathizer</p><p>Đạo diễn của series The Sympathizer được chính Nguyễn Thanh Việt thông báo là Park Chan Wook - người đứng sau nhiều tác phẩm điện ảnh nổi tiếng là The Handmaiden (Người Hầu Gái) và Oldboy (Báo Thù).</p><p>Hãng A24 cũng vốn nổi tiếng là một hãng phim với biệt tài lựa chọn đề tài, kịch bản thần sầu. Hãng sở hữu rất nhiều tác phẩm danh giá ở cả mảng điện ảnh và truyền hình, tiêu biểu bao gồm The Lighthouse (Ngọn Hải Đăng), Lady Bird (Tuổi Nổi Loạn), Hereditary (Di Truyền), Euphoria (Lâng Lâng). Hiện tại, hãng phim cũng đang chạy nước rút trên đường đua Oscar với bộ phim Minari cùng 6 đề cử.</p><p>Hiện tại, các thông tin khác về dự án phim truyền hình The Sympathizer vẫn chưa được công bố. Nhiều người kỳ vọng nhân vật chính sẽ được thủ vai bởi một diễn viên người Việt hoặc gốc Việt theo đúng nguyên tác.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617766922958960269158.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/-16177669631891785859454.jpg\" ><figcaption class=\"caption\">Tác giả Nguyễn Thanh Việt</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617767403371307724065.jpg\" ><figcaption class=\"caption\">Minh họa của trang The New York Times</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/park-chan-wook-portrait-1453194629-16177671015091254527463.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/a24-best-movies-2016-16177672128281829259862.jpg\" ><figcaption class=\"caption\">A24 có lẽ là hãng phim \"uy tín\" nhất Hollywood hiện giờ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617767254267682609924.jpg\" ><figcaption class=\"caption\">Cuốn sách The Sympathizer từng lọt vào list sách mà Bill Gates khuyên đọc</figcaption></figure><p>Nguồn ảnh: Tổng hợp</p>', NULL, 'tieu-thuyet-ve-chien-tranh-viet-nam-thang-pulitzer-duoc-trum-oscar-chuyen-the-thanh-phim', 0, NULL, NULL),
(57, 9, 3, 1, 1, 'Quên Kiên đi, chân ái của Châu ở Hướng Dương Ngược Nắng chính là Phúc phố núi!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617621285695-16176212858921393769886.jpg', '|', 'Bất đắc dĩ bên nhau, thế nhưng Châu (Hồng Diễm) và Phúc (Doãn Quốc Đam) lại rất tình cảm ở Hướng Dương Ngược Nắng 2.', '<p>Sau cú sốc lớn từ tình yêu và gia đình ở Hướng Dương Ngược Nắng, Châu (Hồng Diễm) đường cùng mà tự vẫn, may mắn thay cô được Phúc (Doãn Quốc Đam) đến cứu kịp thời . Một phần vì Phúc bị thương, phần vì Châu không muốn trở lại thành phố, cặp đôi quyết định \"đưa nhau đi trốn\" một thời gian. Tại đây, tuy không có \"ý\" với nhau nhưng tình cảm của cả hai cũng bắt đầu được nảy nở, bởi sự quan tâm và thấu hiểu từ cả hai phía.</p><p>Từ tình cờ chạm mặt, Châu - Phúc trở nên gắn kết nhờ mối duyên nợ và cả nhiệm vụ mà ông Quân giao phó cho Phúc. Không thân thiết nhưng giữa những lúc Châu gặp nạn hay rơi vào hoàn cảnh éo le nhất, Phúc đều là người có mặt để giải cứu cô. Bị cưỡng hiếp không ai hay biết, đi nhảy cầu tự tử vì quá tuyệt vọng, tất cả những điều xấu xảy đến với Châu thì đều có Phúc nhìn thấu và giúp đỡ.</p><p>Không ngoa khi nói sau Kiên (Hồng Đăng) thì Phúc là người hiểu Châu nhất. Anh chàng không những đọc vị được suy nghĩ, mà còn đưa ra nhiều lời khuyên hữu ích giúp gỡ rối cho Châu. Nếu Châu đang gồng mình chứng tỏ bản thân đã gây ra quá nhiều sai lầm, từ mất hết danh dự, công việc nên mới chạy trốn gia đình, thì Phúc lại nắm thóp được rằng Châu đang đau đớn vì tình yêu là chính. Anh chàng còn ví Châu như một bông hoa rất đẹp, nhưng phải mạnh mẽ và kiên cường hơn nữa.</p><p>Chạy trốn phố thị xô bồ, Châu và Phúc tìm về phố núi, cả hai bắt đầu những ngày tháng yên bình, chưa từng có trước đây. Lửa gần rơm lâu ngày cũng bén, Châu bắt đầu có cảm tình khi Phúc luôn là người quan tâm, gần gũi cô. Phúc cũng có cái nhìn khác với đại tiểu thư nhà họ Cao, khi quan sát và hiểu rõ hơn nội tâm của Châu. Hết lo Châu bị cảm lạnh, Phúc lại chiều ý đưa cô đi chợ, dỗ Châu đang dỗi bằng ly trà sữa bất ngờ và ngọt ngào. Chiều ý Châu nhưng cũng có giới hạn, Phúc quyết liệt đưa Châu về lại thành phố, và muốn bản thân Châu phải tự mình đối mặt với nghịch cảnh thì mới có thể sống và hạnh phúc tiếp được.</p><p>  Hướng Dương Ngược Nắng 2 phát sóng 21:40 thứ Hai đến thứ Tư hàng tuần trên VTV3.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/photo-1-1617621410172507487200.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/photo-1-1617620819223897626712-16176208679431940661393.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/photo-1-16176190798061386263769.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/honeycam-2021-04-05-17-17-37-1617619195835174690002.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/photo-1-1617619337810905302185.gif.mp4\" ></video><div class=\"caption\">Yêu nhau khoác mũ áo cho nhau?</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/photo-1-1617619348817224888935.png\" ><figcaption class=\"caption\">Mua trà sữa cho nè</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/honeycam-2021-04-05-17-40-13-16176192525401015402401.gif.mp4\" ></video><div class=\"caption\">Châu còn bạo hơn khi hỏi khéo Phúc: \"Lỡ đâu mai này mình yêu nhau?\"</figcaption></figure><p>Nguồn ảnh: VTV</p>', NULL, 'quen-kien-di-chan-ai-cua-chau-o-huong-duong-nguoc-nang-chinh-la-phuc-pho-nui', 0, NULL, NULL),
(58, 9, 3, 1, 1, '4 yếu tố tấu hài cực mạnh của Penthouse', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/18/photo1616009962326-1616009963018202949072.gif.png', '|', 'Bom tấn Penthouse ngập tràn drama nhưng không vì thế mà thiếu đi tình tiết hài hước.', '<p>Dàn nhân vật trong phim không ai hoàn toàn lương thiện chính là đặc điểm khiến  Penthouse trở thành cái tên hot nhất thời điểm hiện tại. Tuy nhiên, bên cạnh những pha “bẻ lái” cực gắt, biên kịch Kim Soon Ok vẫn “tâm lý” đan cài nhiều tình tiết giải trí hài hước, giúp người xem thư giãn sau thời gian dài xem phim căng thẳng.</p><p>1. “Ác nữ” Cheon Seo Jin với loạt biểu cảm \"khó đỡ\"</p><p>Mưu mô, ác độc nhất nhì hội Hera Palace, Cheon Seo Jin (Kim So Yeon) vẫn được khán giả yêu quý vì quá thần thái và sở hữu nhiều biểu cảm độc đáo. Từ bất ngờ, tức giận cho tới đau khổ, dù là cảm xúc nào chị đẹp cũng đều có thể khiến người xem phải phì cười. Rõ ràng thuộc phe phản diện mà vẫn được fan chế meme đều đều, chắc chỉ có mình cô Cheon mà thôi.</p><p>Từ khi “rước nàng về dinh”, trùm ác ma Ju Dan Tae (Uhm Ki Joon) cũng học được kha khá sự hài hước của cô giáo. Trên đời này chắc chẳng có ai cầm dao định thủ tiêu người khác mà lại hát nhạc thiếu nhi, gọi tên người ta thân mật như chủ tịch Ju đâu nhỉ?</p><p>2. Hội nhân vật “tấu hài” cực mạnh: Từ luật sư Lee đến thầy Ma Du Ki đều chuẩn “chúa hề”</p><p>Nhắc đến độ hài hước của dàn nhân vật Penthouse mà luật sư Lee (Bong Tae Gyu) đứng thứ 2 thì chắc chắn không ai dám giành hạng nhất. Luôn xuất hiện trong bộ trang phục Thom Browne lịch lãm, Lee Kyu Jin lại chẳng thể nghiêm túc nổi 1 phút bởi tính cách “tưng tửng”, có phần trẻ con của bản thân. Hình ảnh của tân nghị sĩ Quốc hội gắn liền với bộ đôi túi xách chó - thỏ sành điệu. Dù đã ở tuổi tứ tuần, anh này vẫn mãi là “con trai cưng của mẹ”, sống không có chính kiến, còn hay “bám đuôi” Ju Dan Tae để kiếm lời từ việc kinh doanh bất hợp pháp.</p><p>Tính tình trẻ con, không bất ngờ khi luật sư Lee cực kì ưa nịnh bợ. Nắm được điều này, Oh Yoon Hee (Eugene) đã nhiều lần lợi dụng thành công anh hàng xóm, nhằm đạt mục đích của mình. Hai lần xin sự đồng ý để bước chân vào Hera Palace, còn thêm âm mưu chia rẽ cặp “gian phu dâm phụ” Dan Tae - Seo Jin, nhờ có Kyu Jin mà kế hoạch của Yoon Hee mới trót lọt.</p><p>Bên cạnh luật sư Lee, Penthouse còn đem đến 1 nhân vật đầy nhiệt huyết khác - thầy giáo thanh nhạc Ma Du Ki. Đây có lẽ là nhân vật “ba phải” nhất phim khi vừa trợ giúp cô Cheon, lại vừa nghe lệnh chủ tịch Ju. Đối với thầy Ma, tiền quyển mới là thứ đáng trân trọng nhất, còn theo phe ai chỉ là điều thứ yếu.</p><p>3. Yếu tố “tâm linh” còn thêm hiệu ứng “giả trân” chẳng khác nào phim ma Thái Lan</p><p>“Con người ta chỉ chết đi về mặt thể xác, còn linh hồn sẽ sống mãi”, đây chắc hẳn là chân lý mà biên kịch Kim Soon Ok theo đuổi khi viết kịch bản cho Penthouse. Chẳng biết là do chủ ý hay tình cờ, hội nhân vật đã mất cứ “rủ nhau” xuất hiện trở lại, ám ảnh tâm lý của những người đang sống. Không chỉ thế, hiệu ứng của loạt cảnh phim “tâm linh” này lại được thiết kế “không hề giả trân”, khiến cho khán giả có cảm giác như đang lạc vào thế giới phim ma Thái Lan nhiều năm về trước vậy. Thật rùng rợn nhưng cũng không kém phần hài hước, gây cười.</p><p>4. Sự sáng tạo vô biên của khán giả - kho nội dung giải trí khổng lồ những ngày chờ chiếu phim</p><p>Một trong những yếu tố khiến drama tranh đấu như Penthouse trở nên thú vị và hài hước hơn lại nằm ở sự sáng tạo không ngừng nghỉ của khán giả. Chỉ với một bức ảnh biểu cảm của nhân vật trong phim, fan có thể chế ra hàng loạt meme khiến người xem phải bật cười. Thậm chí, tại Hàn Quốc còn có một fan chân chính đến nỗi đều đặn quay video diễn lại những cảnh đặc sắc trong tập phim vừa phát sóng, nhưng là phiên bản “nhiều muối” hơn.</p><p>Penthouse 3 sẽ lên sóng trong năm nay.</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/10/8-16153633356671950799548.gif.mp4\" ></video><div class=\"caption\">\"Xỉu ngang\" trong bệnh viện sau khi biết về cái chết của bố mình</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/2-16160087775782059913317.gif.mp4\" ></video><div class=\"caption\">Đang tổ chức đính hôn thì bị chồng cũ phá tan, tức ghê</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/3-1616008777636683163767.gif.mp4\" ></video><div class=\"caption\">Khi bạn đam mê làm ca sĩ nhưng cuộc đời bắt bạn phải kinh doanh</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008586316548036733.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008591218746095870.jpg\" ><figcaption class=\"caption\">Mỗi chiếc túi xách của luật sư Lee có giá lên tới gần 100 triệu đồng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008590146626109335.jpg\" ><figcaption class=\"caption\">Quần đùi gấu Pooh chuẩn con trai bé bỏng</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/7-16160089815781021754260.gif.mp4\" ></video><div class=\"caption\">Không hiểu nổi anh đã “cất liêm sỉ” ở chỗ nào rồi</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/6-161600877781580949127.gif.mp4\" ></video><div class=\"caption\">Chỉ cần hai tiếng \"oppa\", Yoon Hee đã trở thành em gái của luật sư Lee</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085906991488085159.jpg\" ><figcaption class=\"caption\">Khuôn mặt nhìn đã thấy “đáng tin” của Lee Kyu Jin</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/4-1616008981305623260830.gif.mp4\" ></video><div class=\"caption\">Lừa anh luật sư dễ dàng như ăn kẹo</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/5-1616008777772392411479.gif.mp4\" ></video><div class=\"caption\">Sự quan tâm đầy thân mật dành cho em gái Yoon Hee</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085908601633009733.jpg\" ><figcaption class=\"caption\">Nay là cánh tay phải đắc lực của cô giáo</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085917311872199613.jpg\" ><figcaption class=\"caption\">Mai lại dành cả con tim cho chủ tịch Ju</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085909451578514778.jpg\" ><figcaption class=\"caption\">Đoạn độc thoại hài hước của thầy Ma trong tập 8</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/1-1616008777385265652283.gif.mp4\" ></video><div class=\"caption\">Thầy đi dạy để kiếm sống, còn \"tấu hài\" vì đam mê</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085904841690185193.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085902591934498950.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085903642555676.jpg\" ><figcaption class=\"caption\">Min Seol Ah (Jo Soo Min) năm lần bảy lượt hiện về ám lấy hội Hera</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/honeycam-2021-03-18-02-00-05-16160087782081014474316.gif.mp4\" ></video><div class=\"caption\">Luôn chỉn chu về mặt hình ảnh, Penthouse khiến khán giả sốc nặng với hiệu ứng giả trân</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008589903502052393.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085915301169574871.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008591953754816617.jpg\" ><figcaption class=\"caption\">Bộ ảnh cưới chất ngất của cặp đôi Dan Tae - Seo Jin dưới bàn tay người hâm mộ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085906251943774967.jpg\" ><figcaption class=\"caption\">50 sắc thái của Cheon Seo Jin</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008591108546429152.jpg\" ><figcaption class=\"caption\">Màn cosplay “y hệt” bản gốc</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008591327961113099.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn ảnh: Tổng hợp</p>', NULL, '4-yeu-to-tau-hai-cuc-manh-cua-penthouse', 0, NULL, NULL),
(59, 9, 3, 1, 1, '\"Bóc\" trailer mới của Loki: Tháp Avengers sụp đổ và hàng loạt tình tiết dễ bỏ qua', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617681195425-16176812072911386069167.jpg', '|', 'Chỉ kéo dài 2 phút 17 giây, thế nhưng đoạn trailer mới của Loki vẫn hé lộ loạt tình tiết đáng chú ý.', '<p>Là một trong những nhân vật được yêu thích bậc nhất MCU, Loki (Tom Hiddleston) nhanh chóng được Marvel Studios phát triển một loạt phim độc quyền trên Disney+, theo chân hành trình của nhân vật này sau khi tẩu thoát thành công trong Avengers: Endgame. Sau lần đầu lộ diện tại sự kiện của Disney diễn ra vào tháng 11/2020, mới đây, một đoạn trailer của Loki đã được tung ra, hé lộ thêm cốt truyện của loạt phim.</p><p>Trailer Loki</p><p>Chỉ kéo dài 2 phút 17 giây, thế nhưng, đoạn giới thiệu mới của Loki hé lộ loạt tình tiết đáng chú ý, trong đó có cả những chi tiết mà rất có thể bạn đã bỏ qua nếu chỉ xem một lần. Cùng lật lại toàn bộ đoạn trailer, khám phá những gì mà Marvel Studios đã bí mật đưa vào trong nhé.</p><p>Loạt phim Loki lấy bối cảnh sau chuyến du hành thời gian của các siêu anh hùng trong Endgame, sau khi họ để lọt khối Tesseract vào tay Loki. Với việc đã làm quen với viên đá vô cực này từ trước, \"thần lừa lọc\" dễ dàng sử dụng nó để dịch chuyển tức thời và phá vỡ quy luật của đa vũ trụ. Thật không may, có vẻ như khoảng thời gian tự do tự tại của Loki sẽ nhanh chóng chấm dứt khi anh bị TVA - một tổ chức chuyên giám sát dòng thời gian, bắt tận tay. Điểm thú vị là Loki biết TVA là những ai và có thể trước đây, người Asgardia đã gặp tổ chức này.</p><p>Trong  Avengers: Endgame, Ancient One đã tiết lộ sơ qua về tầm quan trọng của các viên đá vô cực, thứ kiểm soát \"dòng chảy của thời gian\" và điều gì sẽ xảy ra nếu các viên đá không được đặt đúng chỗ. Tuy nhiên, dường như bậc thầy phù thủy này đã đơn giản hóa quá mức những hiểm nguy xảy ra nếu các quy tắc của dòng thời gian vô tình bị phá vỡ, bởi nếu không có gì nghiêm trọng xảy ra thì một tổ chức như TVA chắc chắn sẽ không cần tồn tại. Hơn nữa, Loki có thể không phải là tù nhân duy nhất bị TVA bắt giữ. Nếu nhìn kỹ, có một Skrull đã xuất hiện tại trụ sở của TVA, nhưng không rõ đây là tù nhân hay là một đặc vụ.</p><p>Trước đó, Marvel đã xác nhận Gugu Mbatha-Raw sẽ vào vai Thẩm phán Renslayer - một nhân vật rất quen thuộc với người hâm mộ của Marvel Comic. Trong truyện tranh, Ravonna là một công chúa sinh ra và lớn lên ở thế kỷ 40, người không may lọt vào mắt xanh của Kang the Conqueror - một trong những nhân vật phản diện tầm cỡ nhất Marvel. Sau này, Ravonna được đưa đến vương quốc Limbo (trong MCU là Vương quốc lượng tử) để du hành thời gian. </p><p>Được biết, Jonathan Majors đã được chọn vào vai Kang the Conqueror  trong  Ant-Man & the Wasp: Quantumania, vì vậy, rất có thể Renslayer sớm muộn cũng sẽ lộ diện thân phận thật sự trong MCU. Hãy để ý biểu tượng ba đầu đằng sau Renslayer, đây có thể là tượng trưng cho ba khía cạnh của Living Tribunal, người chịu trách nhiệm duy trì toàn bộ đa vũ trụ.</p><p>Như đã nói ở trên, Loki đã phá vỡ quy tắc của dòng thời gian sau khi đánh cắp khối Tesseract và tạo ra một dòng thời gian mới, mặc dù công bằng mà nói, đây chỉ là Loki đang tự tạo cơ hội trốn thoát cho mình. Có vẻ như hành động của \"thần lừa lọc\" đã tạo ra nhiều nhánh mới trong dòng thời gian và TVA đang cố gắng sửa chữa lại sự cố này. Từ nhánh thời gian vừa mới được tạo ra, nhiều phiên bản của Loki đã sinh ra đời và chính vì vậy, TVA đã đưa Loki vào kế hoạch: Để Loki tự đánh gục Loki.</p><p>Vì được tạo ra từ một dòng thời gian phân nhánh, Loki trong loạt phim mới được mô tả là một \"biến thể\". Cụm từ này cũng được dùng để miêu tả phiên bản Thanos đã tấn công Avengers ở hồi cuối Avengers: Endgame, vì hắn cũng xuất hiện từ một dòng thời gian nhánh được tạo ra khi Avengers du hành trở lại năm 2014.</p><p>Đoạn trailer mới của Loki đã tiết lộ em trai của Thần Sấm sẽ có cơ hội đi \"du lịch\" rất nhiều dòng thời gian khác nhau, nhưng trong đó, cảnh quay thú vị nhất là khi Loki ghé thăm thành phố New York hoang tàn - có thể là tàn tích của một vụ xâm chiếm nào đó, phía đằng sau là tòa tháp Avengers sụp đổ. Không rõ đây là dòng thời gian nào, nhưng có vẻ như trong loạt phim riêng, Loki sẽ hóa siêu anh hùng, giải cứu thế giới thoát khỏi tận thế.</p><p>Trong một dòng thời gian, Loki đã nhìn thấy một phiên bản khác của bản thân đang ngồi trên ngai vàng Asgard - điều mà anh đã từng ao ước kể từ khi mới xuất hiện trong MCU. Thế nhưng, nhiệm vụ lúc này của Loki là phải giải thích lý do tại sao hắn có thể làm được điều trên và liệu đó có phải là những gì hắn \"thu hoạch\" được sau khi hoàn thành nhiệm vụ của một kẻ phản diện nào đó?</p><p></p><p></p><p></p><p></p><p></p><p></p><p>Nguồn ảnh: Marvel</p><p></p><p></p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617682242788484412741.jpg\" ><figcaption class=\"caption\">Loki biết rõ TVA dù vừa mới bị bắt, chứng tỏ người Asgardia đã từng gặp tổ chức này</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/honeycam-2021-04-06-11-00-01-16176817773751439477279.gif.mp4\" ></video><div class=\"caption\">Skrull xuất hiện trong trailer của Loki (góc phải)</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/honeycam-2021-04-06-11-01-18-16176817776241321560281.gif.mp4\" ></video><div class=\"caption\">Judge Renslayer xuất hiện và là người phán tội cho Loki</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/honeycam-2021-04-05-22-14-14-16176363949051036720631.gif.mp4\" ></video><div class=\"caption\">Loki tự sửa lại những sai lầm trong \"quá khứ\"</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176823120571672795698.jpg\" ><figcaption class=\"caption\">Sau khi tẩu thoát, Loki đã được coi là một \"biến thể\"</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/honeycam-2021-04-06-10-50-40-16176815815881609055016.gif.mp4\" ></video><div class=\"caption\">Loki đến dòng thời gian nơi New York trở thành một bãi hoang tàn và tháp Avengers sụp đổ</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/honeycam-2021-04-05-22-17-06-16176363949321403896878.gif.mp4\" ></video><div class=\"caption\">Loki ngồi trên ngai vàng Asgard</figcaption></figure><p></p>', NULL, 'boc-trailer-moi-cua-loki-thap-avengers-sup-do-va-hang-loat-tinh-tiet-de-bo-qua', 0, NULL, NULL),
(60, 9, 3, 1, 1, 'Lộ clip Địch Lệ Nhiệt Ba khóc bằng tiếng mẹ đẻ ở Trường Ca Hành, netizen vẫn \"khui\" chi tiết diễn xuất dở tệ?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617710668810-16177106699531535707755.gif.png', '|', 'Có vẻ như Trường Ca Hành là bộ phim sóng gió nhất của Địch Lệ Nhiệt Ba.', '<p>Từ khi mới được công bố, Trường Ca Hành - bộ phim chuyển thể từ tác phẩm truyện tranh cùng tên của nữ họa sĩ đã được kỳ vọng sẽ trở thành một bước ngoặt đưa tên tuổi của Địch Lệ Nhiệt Ba vươn xa hơn, khẳng định tài năng của cô. Thế nhưng, ngay từ khi bộ phim này mới lên sóng, nữ diễn viên đã nhận về vô vàn chỉ trích, từ việc trở thành \"cái bóng\" cho nữ phụ đến lối diễn xuất giậm chân tại chỗ. Gần đây nhất, loạt cảnh khóc của Địch Lệ Nhiệt Ba tiếp tục trở thành tâm điểm chú ý.</p><p>Cụ thể, một đoạn clip trong đó Địch Lệ Nhiệt Ba sử dụng chất giọng gốc (thay vì lồng tiếng) đang lan truyền trên MXH. Thế nhưng dù có đài từ khá tốt, nữ chính Trường Ca Hành vẫn nhận về rất nhiều lời chỉ trích. Thay vì đau khổ từ trong nội tâm ra ngoài, nữ diễn viên cố gắng thể hiện sự đau xót của nhân vật bằng cách nhăn trán, nhướn mày, khiến cảnh quay trông khá \"giả trân\", không thật.</p><p>Cảnh khóc của Địch Lệ Nhiệt Ba không chỉ không toát lên được cảm xúc của nhân vật mà còn khiến khán giả cảm thấy mệt mỏi</p><p>Cư dân mạng cũng hết hồn về cảnh khóc của Địch Lệ Nhiệt Ba:</p><p>- Nói thật cô Ba mắc 2 điểm quan trọng khiến phim của cô thường không hot: diễn xuất tệ và không có cảm xúc với nam chính.</p><p>- Vấn đề là cô ấy không hoàn toàn nhập vai, cau mày là để nước mắt rơi, nếu như nhập vai sâu thì khóc sẽ có trình tự hơn, khiến khán giả bị cuốn theo...</p><p>-  Diễn nhìn gồng dữ vậy, cô này lên phim thật là... đúng là chỉ biết lừa người qua ảnh chỉnh sửa và trang điểm thật kĩ lưỡng.</p><p></p><p></p><p>- Tôi chỉ muốn hai cọng tóc hai bên biến mất.</p><p>- Nói thật chứ trình độ diễn của cô chưa tới mức gọi là tốt nữa thì tại sao lại không được chê vậy? Hỏi thật. Nói làm như phái thực lực, diễn xuất đỉnh cao lắm, càng ngày càng mất thiện cảm.</p><p>Nguồn: Chuồng gà Cbiz</p><p></p><p>Ngoài Địch Lệ Nhiệt Ba và Ngô Lỗi, Trường Ca Hành còn có sự tham gia diễn xuất của Lưu Vũ Ninh, Triệu Lộ Tư, Phương Dật Luân, Dương Minh Na, Cảnh Nhạc, Tào Hi Nguyệt, Tát Đỉnh Đỉnh, Lưu Hải Khoan,...</p><p>Clip khóc bằng giọng thật của Địch Lệ Nhiệt Ba ở Trường Ca Hành</p><figure class=\"image video\"><img src=\"https://pbs.twimg.com/media/EeE-HzYU0AEuW2R.jpg:large\" ><figcaption class=\"caption\">Địch Lệ Nhiệt Ba bị chê tới chê lui ngay khi Trường Ca Hành mới lên sóng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/honeycam-2021-04-06-18-28-03-16177085504411856374999.gif\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/1693032078895401016115938283843629122353107n-1617708900928660505293.jpg\" ><figcaption class=\"caption\">Loạt cảnh khóc của Địch Lệ Nhiệt Ba đều bị chê thảm thương</figcaption></figure><p>Nguồn ảnh: Tổng hợp</p>', NULL, 'lo-clip-dich-le-nhiet-ba-khoc-bang-tieng-me-de-o-truong-ca-hanh-netizen-van-khui-chi-tiet-dien-xuat-do-te', 0, NULL, NULL),
(61, 10, 3, 1, 1, 'Joseon Exorcist \"quỵt\" tiền cát-xê diễn viên tận 12 tập phim, netizen \"thấy cũng tội mà thôi cũng kệ\"', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617769182935-16177691830961069789142.gif.png', '|', 'Thương thay cho dàn cast Joseon Exorcist, vật vã quay 14 tập phim mà chỉ nhận thù lao 2 tập.', '<p>Sau khi bị hủy chiếu vì sai lệch lịch sử, Joseon Exorcist (Pháp Sư Trừ Tà Triều Tiên) đang phải đối mặt với rất nhiều vấn đề. Ngày 6/4, truyền thông Hàn Quốc đưa tin các diễn viên của phim mới được trả thù lao cho 2 tập phát sóng, dù họ đã quay xong 14/16 tập. Trong khi đó, bình thường diễn viên sẽ được trả 80% cát-xê cho các tập phim đã quay, khi phim đã lên sóng.</p><p>Tuy nhiên, nhà sản xuất khó lòng có thể trả đủ thù lao cho các diễn viên, vì họ đang phải gánh khoản lỗ kinh phí lên tới  32 tỷ won. Toàn bộ nhà tài trợ đã rút khỏi bộ phim, khiến  Crave Works, Lotte Cultureworks và  YG Studioplex rơi vào cảnh điêu đứng.</p><p>Jang Dong Yoon - nam chính của bộ phim</p><p>Cư dân mạng có nhiều ý kiến trái chiều. Người cho rằng ekip  Joseon Exorcist phải thanh toán đủ tiền cho các diễn viên theo quy định, người lại thấy dàn cast phải chịu chung trách nhiệm vì họ đã nhận đóng một tác phẩm sai trái. </p><p>Bình luận của cư dân mạng Việt:</p><p>- Nghe nói Jang Dong Yoon vì quay phim này mà gãy cả tay, đi nhận giải nào đó còn phải bó bột cơ mà. Khá là quý anh, thôi thì coi như lần này đen vậy.</p><p>- Nên chịu trách nhiệm chung. Như vậy ok mà.</p><p>- Đâu phải ai cũng học và tìm hiểu kỹ về lịch sử nước nhà đâu. Mình không phản bác chuyện chính trị, nhưng cũng không nên đổ lên đầu diễn viên toàn bộ mọi chuyện như vậy.</p><p>- Chứ có được chiếu đâu mà có tiền trả hết 14 tập ngộ ha?</p><p>Nguồn: Netizenbuzz Vtrans</p><p>Joseon Exorcist kể về cuộc chiến của một gia đình hoàng tộc để bảo vệ người dân khỏi linh hồn ác quỷ. Trong hành trình đó, vua Taejong (Kam Woo Sung) cùng hai con trai của ông là hoàng tử Chung Nyung (Jang Dong Yoon) và hoàng tử Yang Nyeong (Park Sung Hoon) diệt trừ những xác sống không hồn, mang lại sự bình yên cho đất nước như trước đây.</p><p>Teaser 2 của Joseon Exorcist</p><p>Tuy nhiên, sau khi chiếu tập đầu tiên, phim bị tẩy chay dữ dội vì xây dựng vua Taejong là kẻ tàn nhẫn, giết người không ghê tay, đưa vào rất nhiều trang phục, đạo cụ, món ăn truyền thống của Trung Quốc. Sau khi họp bàn, SBS đã quyết định \"khai tử\" bộ phim.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo1308118-1617768289193242026616.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/photo1310117-16177684285561904044813.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1-16177676532791858728333.png\" ><figcaption class=\"caption\">Bình luận của cư dân mạng Hàn (Nguồn: Netizenbuzz Vtrans)</figcaption></figure><p>Nguồn: topstarnews, SBS</p>', NULL, 'joseon-exorcist-quyt-tien-cat-xe-dien-vien-tan-12-tap-phim-netizen-thay-cung-toi-ma-thoi-cung-ke', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(62, 10, 3, 1, 1, '4 yếu tố tấu hài cực mạnh của Penthouse', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/18/photo1616009962326-1616009963018202949072.gif.png', '|', 'Bom tấn Penthouse ngập tràn drama nhưng không vì thế mà thiếu đi tình tiết hài hước.', '<p>Dàn nhân vật trong phim không ai hoàn toàn lương thiện chính là đặc điểm khiến  Penthouse trở thành cái tên hot nhất thời điểm hiện tại. Tuy nhiên, bên cạnh những pha “bẻ lái” cực gắt, biên kịch Kim Soon Ok vẫn “tâm lý” đan cài nhiều tình tiết giải trí hài hước, giúp người xem thư giãn sau thời gian dài xem phim căng thẳng.</p><p>1. “Ác nữ” Cheon Seo Jin với loạt biểu cảm \"khó đỡ\"</p><p>Mưu mô, ác độc nhất nhì hội Hera Palace, Cheon Seo Jin (Kim So Yeon) vẫn được khán giả yêu quý vì quá thần thái và sở hữu nhiều biểu cảm độc đáo. Từ bất ngờ, tức giận cho tới đau khổ, dù là cảm xúc nào chị đẹp cũng đều có thể khiến người xem phải phì cười. Rõ ràng thuộc phe phản diện mà vẫn được fan chế meme đều đều, chắc chỉ có mình cô Cheon mà thôi.</p><p>Từ khi “rước nàng về dinh”, trùm ác ma Ju Dan Tae (Uhm Ki Joon) cũng học được kha khá sự hài hước của cô giáo. Trên đời này chắc chẳng có ai cầm dao định thủ tiêu người khác mà lại hát nhạc thiếu nhi, gọi tên người ta thân mật như chủ tịch Ju đâu nhỉ?</p><p>2. Hội nhân vật “tấu hài” cực mạnh: Từ luật sư Lee đến thầy Ma Du Ki đều chuẩn “chúa hề”</p><p>Nhắc đến độ hài hước của dàn nhân vật Penthouse mà luật sư Lee (Bong Tae Gyu) đứng thứ 2 thì chắc chắn không ai dám giành hạng nhất. Luôn xuất hiện trong bộ trang phục Thom Browne lịch lãm, Lee Kyu Jin lại chẳng thể nghiêm túc nổi 1 phút bởi tính cách “tưng tửng”, có phần trẻ con của bản thân. Hình ảnh của tân nghị sĩ Quốc hội gắn liền với bộ đôi túi xách chó - thỏ sành điệu. Dù đã ở tuổi tứ tuần, anh này vẫn mãi là “con trai cưng của mẹ”, sống không có chính kiến, còn hay “bám đuôi” Ju Dan Tae để kiếm lời từ việc kinh doanh bất hợp pháp.</p><p>Tính tình trẻ con, không bất ngờ khi luật sư Lee cực kì ưa nịnh bợ. Nắm được điều này, Oh Yoon Hee (Eugene) đã nhiều lần lợi dụng thành công anh hàng xóm, nhằm đạt mục đích của mình. Hai lần xin sự đồng ý để bước chân vào Hera Palace, còn thêm âm mưu chia rẽ cặp “gian phu dâm phụ” Dan Tae - Seo Jin, nhờ có Kyu Jin mà kế hoạch của Yoon Hee mới trót lọt.</p><p>Bên cạnh luật sư Lee, Penthouse còn đem đến 1 nhân vật đầy nhiệt huyết khác - thầy giáo thanh nhạc Ma Du Ki. Đây có lẽ là nhân vật “ba phải” nhất phim khi vừa trợ giúp cô Cheon, lại vừa nghe lệnh chủ tịch Ju. Đối với thầy Ma, tiền quyển mới là thứ đáng trân trọng nhất, còn theo phe ai chỉ là điều thứ yếu.</p><p>3. Yếu tố “tâm linh” còn thêm hiệu ứng “giả trân” chẳng khác nào phim ma Thái Lan</p><p>“Con người ta chỉ chết đi về mặt thể xác, còn linh hồn sẽ sống mãi”, đây chắc hẳn là chân lý mà biên kịch Kim Soon Ok theo đuổi khi viết kịch bản cho Penthouse. Chẳng biết là do chủ ý hay tình cờ, hội nhân vật đã mất cứ “rủ nhau” xuất hiện trở lại, ám ảnh tâm lý của những người đang sống. Không chỉ thế, hiệu ứng của loạt cảnh phim “tâm linh” này lại được thiết kế “không hề giả trân”, khiến cho khán giả có cảm giác như đang lạc vào thế giới phim ma Thái Lan nhiều năm về trước vậy. Thật rùng rợn nhưng cũng không kém phần hài hước, gây cười.</p><p>4. Sự sáng tạo vô biên của khán giả - kho nội dung giải trí khổng lồ những ngày chờ chiếu phim</p><p>Một trong những yếu tố khiến drama tranh đấu như Penthouse trở nên thú vị và hài hước hơn lại nằm ở sự sáng tạo không ngừng nghỉ của khán giả. Chỉ với một bức ảnh biểu cảm của nhân vật trong phim, fan có thể chế ra hàng loạt meme khiến người xem phải bật cười. Thậm chí, tại Hàn Quốc còn có một fan chân chính đến nỗi đều đặn quay video diễn lại những cảnh đặc sắc trong tập phim vừa phát sóng, nhưng là phiên bản “nhiều muối” hơn.</p><p>Penthouse 3 sẽ lên sóng trong năm nay.</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/10/8-16153633356671950799548.gif.mp4\" ></video><div class=\"caption\">\"Xỉu ngang\" trong bệnh viện sau khi biết về cái chết của bố mình</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/2-16160087775782059913317.gif.mp4\" ></video><div class=\"caption\">Đang tổ chức đính hôn thì bị chồng cũ phá tan, tức ghê</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/3-1616008777636683163767.gif.mp4\" ></video><div class=\"caption\">Khi bạn đam mê làm ca sĩ nhưng cuộc đời bắt bạn phải kinh doanh</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008586316548036733.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008591218746095870.jpg\" ><figcaption class=\"caption\">Mỗi chiếc túi xách của luật sư Lee có giá lên tới gần 100 triệu đồng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008590146626109335.jpg\" ><figcaption class=\"caption\">Quần đùi gấu Pooh chuẩn con trai bé bỏng</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/7-16160089815781021754260.gif.mp4\" ></video><div class=\"caption\">Không hiểu nổi anh đã “cất liêm sỉ” ở chỗ nào rồi</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/6-161600877781580949127.gif.mp4\" ></video><div class=\"caption\">Chỉ cần hai tiếng \"oppa\", Yoon Hee đã trở thành em gái của luật sư Lee</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085906991488085159.jpg\" ><figcaption class=\"caption\">Khuôn mặt nhìn đã thấy “đáng tin” của Lee Kyu Jin</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/4-1616008981305623260830.gif.mp4\" ></video><div class=\"caption\">Lừa anh luật sư dễ dàng như ăn kẹo</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/5-1616008777772392411479.gif.mp4\" ></video><div class=\"caption\">Sự quan tâm đầy thân mật dành cho em gái Yoon Hee</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085908601633009733.jpg\" ><figcaption class=\"caption\">Nay là cánh tay phải đắc lực của cô giáo</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085917311872199613.jpg\" ><figcaption class=\"caption\">Mai lại dành cả con tim cho chủ tịch Ju</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085909451578514778.jpg\" ><figcaption class=\"caption\">Đoạn độc thoại hài hước của thầy Ma trong tập 8</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/1-1616008777385265652283.gif.mp4\" ></video><div class=\"caption\">Thầy đi dạy để kiếm sống, còn \"tấu hài\" vì đam mê</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085904841690185193.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085902591934498950.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085903642555676.jpg\" ><figcaption class=\"caption\">Min Seol Ah (Jo Soo Min) năm lần bảy lượt hiện về ám lấy hội Hera</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/18/honeycam-2021-03-18-02-00-05-16160087782081014474316.gif.mp4\" ></video><div class=\"caption\">Luôn chỉn chu về mặt hình ảnh, Penthouse khiến khán giả sốc nặng với hiệu ứng giả trân</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008589903502052393.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085915301169574871.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008591953754816617.jpg\" ><figcaption class=\"caption\">Bộ ảnh cưới chất ngất của cặp đôi Dan Tae - Seo Jin dưới bàn tay người hâm mộ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-16160085906251943774967.jpg\" ><figcaption class=\"caption\">50 sắc thái của Cheon Seo Jin</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008591108546429152.jpg\" ><figcaption class=\"caption\">Màn cosplay “y hệt” bản gốc</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/-1616008591327961113099.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn ảnh: Tổng hợp</p>', NULL, '4-yeu-to-tau-hai-cuc-manh-cua-penthouse', 0, NULL, NULL),
(63, 10, 3, 1, 1, 'Lộ clip Địch Lệ Nhiệt Ba khóc bằng tiếng mẹ đẻ ở Trường Ca Hành, netizen vẫn \"khui\" chi tiết diễn xuất dở tệ?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617710668810-16177106699531535707755.gif.png', '|', 'Có vẻ như Trường Ca Hành là bộ phim sóng gió nhất của Địch Lệ Nhiệt Ba.', '<p>Từ khi mới được công bố, Trường Ca Hành - bộ phim chuyển thể từ tác phẩm truyện tranh cùng tên của nữ họa sĩ đã được kỳ vọng sẽ trở thành một bước ngoặt đưa tên tuổi của Địch Lệ Nhiệt Ba vươn xa hơn, khẳng định tài năng của cô. Thế nhưng, ngay từ khi bộ phim này mới lên sóng, nữ diễn viên đã nhận về vô vàn chỉ trích, từ việc trở thành \"cái bóng\" cho nữ phụ đến lối diễn xuất giậm chân tại chỗ. Gần đây nhất, loạt cảnh khóc của Địch Lệ Nhiệt Ba tiếp tục trở thành tâm điểm chú ý.</p><p>Cụ thể, một đoạn clip trong đó Địch Lệ Nhiệt Ba sử dụng chất giọng gốc (thay vì lồng tiếng) đang lan truyền trên MXH. Thế nhưng dù có đài từ khá tốt, nữ chính Trường Ca Hành vẫn nhận về rất nhiều lời chỉ trích. Thay vì đau khổ từ trong nội tâm ra ngoài, nữ diễn viên cố gắng thể hiện sự đau xót của nhân vật bằng cách nhăn trán, nhướn mày, khiến cảnh quay trông khá \"giả trân\", không thật.</p><p>Cảnh khóc của Địch Lệ Nhiệt Ba không chỉ không toát lên được cảm xúc của nhân vật mà còn khiến khán giả cảm thấy mệt mỏi</p><p>Cư dân mạng cũng hết hồn về cảnh khóc của Địch Lệ Nhiệt Ba:</p><p>- Nói thật cô Ba mắc 2 điểm quan trọng khiến phim của cô thường không hot: diễn xuất tệ và không có cảm xúc với nam chính.</p><p>- Vấn đề là cô ấy không hoàn toàn nhập vai, cau mày là để nước mắt rơi, nếu như nhập vai sâu thì khóc sẽ có trình tự hơn, khiến khán giả bị cuốn theo...</p><p>-  Diễn nhìn gồng dữ vậy, cô này lên phim thật là... đúng là chỉ biết lừa người qua ảnh chỉnh sửa và trang điểm thật kĩ lưỡng.</p><p></p><p></p><p>- Tôi chỉ muốn hai cọng tóc hai bên biến mất.</p><p>- Nói thật chứ trình độ diễn của cô chưa tới mức gọi là tốt nữa thì tại sao lại không được chê vậy? Hỏi thật. Nói làm như phái thực lực, diễn xuất đỉnh cao lắm, càng ngày càng mất thiện cảm.</p><p>Nguồn: Chuồng gà Cbiz</p><p></p><p>Ngoài Địch Lệ Nhiệt Ba và Ngô Lỗi, Trường Ca Hành còn có sự tham gia diễn xuất của Lưu Vũ Ninh, Triệu Lộ Tư, Phương Dật Luân, Dương Minh Na, Cảnh Nhạc, Tào Hi Nguyệt, Tát Đỉnh Đỉnh, Lưu Hải Khoan,...</p><p>Clip khóc bằng giọng thật của Địch Lệ Nhiệt Ba ở Trường Ca Hành</p><figure class=\"image video\"><img src=\"https://pbs.twimg.com/media/EeE-HzYU0AEuW2R.jpg:large\" ><figcaption class=\"caption\">Địch Lệ Nhiệt Ba bị chê tới chê lui ngay khi Trường Ca Hành mới lên sóng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/honeycam-2021-04-06-18-28-03-16177085504411856374999.gif\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/1693032078895401016115938283843629122353107n-1617708900928660505293.jpg\" ><figcaption class=\"caption\">Loạt cảnh khóc của Địch Lệ Nhiệt Ba đều bị chê thảm thương</figcaption></figure><p>Nguồn ảnh: Tổng hợp</p>', NULL, 'lo-clip-dich-le-nhiet-ba-khoc-bang-tieng-me-de-o-truong-ca-hanh-netizen-van-khui-chi-tiet-dien-xuat-do-te', 0, NULL, NULL),
(64, 10, 3, 1, 1, 'Bắt gặp Vương Tuấn Khải ngồi bồn cầu ngoài phố cực dị, team qua đường thấy đẹp trai nên bỏ qua hết nha!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617699582502-16176995827882108314393.jpg', '|', 'Hình ảnh Vương Tuấn Khải mặc áo bệnh nhân, ngồi bồn cầu ngoài công cộng khiến fan suýt té xỉu.', '<p>Chiều 6/4, loạt ảnh trên phim trường Cánh Cửa Trùng Sinh của Vương Tuấn Khải được hé lộ. Khác xa với những hình ảnh thư sinh, boy ngầu trước đó, tạo hình của Vương Tuấn Khải lần này lại có phần hơi \"phèn\" và trông rất buồn... cười. Anh chàng diện nguyên set đồ bệnh nhân với họa tiết sọc kẻ xanh trắng, và ngồi bấm điện thoại trên bồn cầu được đặt ở ngoài trời. Tạo hình trớ trêu là thế, nhưng Vương Tuấn Khải vẫn được netizen ưu ái: \"Đẹp trai nên làm gì cũng thấy đẹp hết\".   </p><p></p><p></p><p>Một số hình ảnh \"bệnh nhân\" Vương Tuấn Khải ngồi bồn cầu ngay ngoài đường</p><p>Bất chấp tạo hình và bối cảnh trông hơi phèn của Vương Tuấn Khải trong phim mới, netizen vẫn \"nhảy\" vào nhận chồng ầm ầm, còn khen nức nở: \"Ngồi bồn cầu cũng không lu mờ được vẻ đẹp trai của anh\".</p><p>- Đẹp xỉu luôn, cái này là đẹp không góc chết mới đúng.</p><p>- Chồng em dù có ở trong tình thế nào cũng đẹp hết đó.</p><p>- Buồn cười quá, nhưng mà ngồi bồn cầu cũng không giảm đi sự đẹp trai của anh.</p><p>- Đẹp trai thì làm gì cũng đẹp!</p><p>- Ngồi bồn cũng nhưng phải soái ca nha mọi người.</p><p>- Ủa sao bồn cầu lại đặt ở ngoài trời dị?</p><p>- Mặc đồ bệnh nhân rồi ngồi bồn cầu phải chăng anh xuyên không?</p><p>Nguồn: Weibo Việt Nam</p><p>Trong  Cánh Cửa Trùng Sinh, Vương Tuấn Khải vào vai Trang Văn Kiệt, 22 tuổi là một sinh viên xuất sắc của khoa luật trường Đại học Qingcheng. Ông nội và cha của  Trang Văn Kiệt đều là những tên trộm chuyên nghiệp, môi trường sống như vậy đã khiến anh biết đủ loại kỹ thuật trộm cắp và tâm lý trộm cắp, vượt xa cả người thường. Anh chàng còn sở hữu khả năng quan sát và hành động cực đỉnh.</p><p>Cánh Cửa Trùng Sinh hiện vẫn đang trong quá trình quay.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/photo-1-16176958313461268471432.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/photo-1-16176958212451822774882.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176958532021756442091.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/photo-1-16176958692181303752347.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176972094381907324268.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617698187519682242664.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176974122381571462630.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176972162801858398311.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617698178041185587442.jpg\" ><figcaption class=\"caption\">Một số hình ảnh khác của Vương Tuấn Khải tại phim trường</figcaption></figure><p>Nguồn ảnh: Weibo</p>', NULL, 'bat-gap-vuong-tuan-khai-ngoi-bon-cau-ngoai-pho-cuc-di-team-qua-duong-thay-dep-trai-nen-bo-qua-het-nha', 0, NULL, NULL),
(65, 10, 3, 1, 1, 'Ngày hội việc làm ở Trường Ca Hành tập 15 - 16: Địch Lệ Nhiệt Ba bị bắt làm nô lệ, Triệu Lộ Tư thành thợ may?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617727012273-16177270129411470165904.gif.png', '|', 'Cả hai cô gái của Trường Ca Hành đều có khởi đầu mới ở tập 15 và 16 của bộ phim.', '<p>Trong tập 15 và 16 của Trường Ca Hành, thứ sử của Sóc Châu đã tự tay kết liễu mạng sống, cống nạp đầu của mình cho phe Ưng Sư mặc cho sự ngăn cản của Lý Trường Ca (Địch Lệ Nhiệt Ba) và thuộc hạ.</p><p>Lý Trường Ca đã tuân theo lời trăn trối của thứ sử, chủ động xin hàng trước Ưng Sư để bảo toàn mạng sống cho dân thành Sóc Châu. Trước đề nghị này, A Thi Lặc Chuẩn (Ngô Lỗi) đã đặt ra một điều kiện rằng sẽ chỉ thật sự miễn xá cho người dân Sóc Châu khi Lý Trường Ca chịu về làm tay chân, nô lệ cho anh.</p><p>Bên ngoài, điều kiện này nghe có vẻ như mang đầy tính hành hạ, bóc lột nữ chính Trường Ca Hành song thực chất ở bên trong, nguyên nhân sâu xa của nó là vì A Thi Lặc Chuẩn sớm biết Lý Trường Ca đã không còn chốn dung thân. Anh còn biết rằng nhân dân Sóc Châu sẽ chẳng tài nào hiểu được sự hi sinh của nữ tử họ Lý, thậm chí nảy sinh lòng thù hận nên đã dán thông báo Lý đô úy đã chết rồi sau đó dẫn cô theo mình trở về thảo nguyên.</p><p>Nam chính Trường Ca Hành cũng để ý rằng có người đi theo, theo dõi tình hình của Lý Trường Ca nên đã sai người diễn một màn kịch trước mặt kẻ bám đuôi, khiến hắn thật sự tưởng rằng Lý Trường Ca đã chết.</p><p>Bản thân nữ chính Trường Ca Hành từ sau khi bị cưỡng ép đầu quân cho phe Ưng Sư, chưa một ngày nào cô cảm thấy cuộc sống này có ý nghĩa, luôn từ chối tất cả các bữa ăn mà A Thi Lặc Chuẩn cho người chuẩn bị. Chỉ đến khi bị A Thi Lặc Chuẩn khiêu khích, Lý Trường Ca mới dần tìm lại được mục đích của mình.</p><p>Về phía Lý Lạc Yên (Triệu Lộ Tư), cô nàng đã thất bại trong việc chứng minh mình chính là nàng công chúa thất lạc ở Minh phủ nên phải lưu lạc ở khắp nơi trong thành, trải qua không biết bao nhiêu khó khăn, từ việc bị hiểu nhầm là kẻ giả mạo công chúa cho đến bị cướp đi những đồng tiền cuối cùng. May mắn thay, cô được một người phụ nữ tốt bụng nhận về làm công cho một xưởng dệt. Những tháng ngày phiêu bạt lang thang của Lý Lạc Yên cuối cùng  đã tạm thời dừng lại khi cô tìm được một chốn nghỉ ngơi trước khi được tìm thấy.</p><p>Trường Ca Hành phát sóng lúc 20h00 vào thứ Hai, thứ Ba, thứ Tư hằng tuần trên VieON. Xem ngay TẠI ĐÂY.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-16177172591461166608211.png\" ><figcaption class=\"caption\">Thứ sử đã hi sinh để bảo vệ tính mạng cho người dân Sóc Châu</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-1617717455663702930072.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-1617717584828505716554.png\" ><figcaption class=\"caption\">A Thi Lặc Chuẩn yêu cầu Lý Trường Ca làm nô lệ cho mình</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-1617717687536983669599.png\" ><figcaption class=\"caption\">Lý Trường Ca bị dân thành Sóc Châu ném rau vào người và sỉ nhục</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-1617717795681646557389.png\" ><figcaption class=\"caption\">Phe Ưng Sư đăng thông báo Lý đô uý đã chết</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-16177178805821907689349.png\" ><figcaption class=\"caption\">Nam chính Trường Ca Hành dựng lên một vở kịch để đánh lạc hướng kẻ bám đuôi</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-16177179906101322544193.png\" ><figcaption class=\"caption\">A Thi Lặc Chuẩn nhắc lại lý tưởng sống cho Lý Trường Ca</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-1617718095395238342317.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/untitled-1617718162536510360233.png\" ><figcaption class=\"caption\">Những ngày tháng sóng gió của Lý Lạc Yên bao giờ mới chấm dứt?</figcaption></figure><p>Nguồn ảnh: Tổng hợp</p>', NULL, 'ngay-hoi-viec-lam-o-truong-ca-hanh-tap-15---16-dich-le-nhiet-ba-bi-bat-lam-no-le-trieu-lo-tu-thanh-tho-may', 0, NULL, NULL),
(66, 10, 3, 1, 1, 'Phim mới của Song Hye Kyo kết nạp thêm Sehun (EXO), nội dung nghe qua đã hóng cực độ', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617705717755-16177057178551195712437.jpg', '|', 'Sehun (EXO) sẽ vào vai nhà thiết kế trong We Are Breaking Up cùng Jang Ji Yong và Song Hye Kyo.', '<p>Chiều 6/4, truyền thông Hàn đưa tin Sehun (EXO) đã xác nhận tham gia phim tình cảm mới mang tên We Are Breaking Up. Trong phim, Oh Sehun sẽ đảm nhận vai nhà thiết kế thời trang mới gia nhập công ty và làm việc dưới sự quản lý của Ha Young Eun (Song Hye Kyo thủ vai). Ngoài ra, nữ idol Yura (Girl’s Day) cũng tham gia phim với vai trò là một người nổi tiếng trên mạng xã hội tên Hye Rin. We Are Breaking Up là câu chuyện về tình yêu và những cuộc chia tay đầy xúc động.</p><p></p><p></p><p></p><p>Netizen vô cùng hào hứng trước thông tin nam idol đình đám lấn sân sang diễn xuất, lại còn đóng chung phim với chị đại Song Hye Kyo.</p><p>- Tài nguyên của phim này tốt quá, tui rất mong chờ sự kết hợp lần này của anh nhà và Song Hye Kyo.</p><p>- Oh Sehun thật sự có tố chất và triển vọng của một diễn viên đáng đầu tư và khai thác đấy.</p><p>- Oh Sehun visual đỉnh lắm luôn đó.</p><p>- Vào vai nhà thiết kế thời trang với cái chất quý tộc, sang chảnh của Oh Sehun là biết đỉnh lắm rồi.</p><p>- Trời, diễn chung với đàn chị Song luôn tự hào quá, chồng ơi!</p><p>- Đã có Jang Ky Yong rồi giờ lại thêm Sehun nữa ta nói nó nhức nách!</p><p>Nguồn: K Crush</p><p>Trước đó, cả Jang Ji Yong và Song Hye Kyo cũng xác nhận sẽ đảm nhận vai nam - nữ chính của phim. Song Hye Kyo sẽ vào vai Ha Young Eun, cô gái  thời thượng và rất thông minh, hiện đang làm  quản lý đội thiết kế của một thương hiệu thời trang nổi tiếng. Nam chính  Yoon Jae Guk do Jang Ji Yong thủ vai là một nhiếp ảnh gia tự do, giàu có và nổi tiếng, người vừa đẹp trai vừa thông minh.</p><p></p><p></p><p>Được đạo diễn bởi Lee Gil Bok - người từng làm nên những bộ phim vô cùng nổi tiếng như  Vì Sao Đưa Anh Tới,  Người Thầy Y Đức 2,  We Are Breaking Up  sẽ khởi quay vào tháng 4 này và dự kiến ​​phát sóng trên đài SBS vào nửa cuối năm nay.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/photo-1-16177017063062053106497.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617701806749345825620.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/photo-1-1617701715868384758454.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/photo-1-1617701994662900457267.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177021068932067610067.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/photo-1-1617702274693188055141.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177022375341224027268.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/photo-1-16177022462271822886888.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn: Soompi</p>', NULL, 'phim-moi-cua-song-hye-kyo-ket-nap-them-sehun-exo-noi-dung-nghe-qua-da-hong-cuc-do', 0, NULL, NULL),
(67, 10, 3, 1, 1, 'Fan khóc thét vì Lưu Hạo Nhiên lên cân, nhuộm tóc như \"trẻ nghé\" ở phim mới: Còn đâu Dư Hoài năm nào của chúng tôi!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617689455281-1617689455434940895673.gif.png', '|', 'Tạo hình mới của Lưu Hạo Nhiên khiến người hâm mộ không còn tin vào mắt mình.', '<p>Ngày 6/4, Lưu Hạo Nhiên đã khiến cho cộng đồng mạng tá hoả một phen khi tạo hình của nam diễn viên ở phim mới bị leak ra. Không phải là anh bạn Dư Hoài (Điều Tuyệt Vời Nhất Của Chúng Ta) hiền lành, ngoan ngoãn của 5 năm trước nữa, bây giờ người con trai ở trước mặt chúng ta đã là một người khác hoàn toàn.</p><p>Tạo hình gây giật mình của Lưu Hạo Nhiên ở hậu trường phim mới</p><p>Fan hâm mộ dường như không thể tin vào mắt mình khi Lưu Hạo Nhiên xuất hiện với một mái tóc vàng hoe khác lạ, làn da đen sạm cùng cơ thể có phần mũm mĩm hơn so với ngày trước. Mái tóc vàng tưởng chừng sẽ khiến người ta trông ngổ ngáo hơn thì đối với Lưu Hạo Nhiên, anh chàng lại trông \"ngố tàu\" hơn.</p><p>Ai trả lại Dư Hoài cho chúng tôi với!</p><p>Nhiều người nhận xét rằng nam diễn viên đã phải hi sinh rất nhiều cho vai diễn lần này. Không những thế, một số người còn cảm thán rằng anh chàng quá giống Nguyễn Kính Thiên. Tuy nhiên, dù sao đi nữa thì người hâm mộ cũng vẫn dành sự ủng hộ tích cực cho Lưu Hạo Nhiên.</p><p>Một số bình luận khác của cộng đồng mạng:</p><p>- Dư Hoài của tôi nay đã thành ông chú nào rồi huhu.</p><p>- Trái tim tôi đau quá...</p><p>- Từ đây ta rút ra kết luận, da không sáng lắm thì không nên nhuộm tóc vàng...</p><p>- Nhìn đáng yêu cảm giác giống như anh hàng xóm nhà bên, cưng ghê.</p><p>Tổng hợp Sạp Báo Hoa Ngữ</p><p>Hiện tại tên bộ phim mới của Lưu Hạo Nhiên vẫn chưa được hé lộ.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/6/16954102737754443125734655272131395468961556n-16176883976221756050515.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/screenshot-2021-04-06-125403-16176884578491191790535-16176886539771408333643.png\" ><figcaption class=\"caption\">Fan hâm mộ ngỡ ngàng trước màn F5 của Lưu Hạo Nhiên (Ảnh chụp màn hình)</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-7-1529979853476707333982-16176900512391640115987.jpg\" ><figcaption class=\"caption\">Tự nhiên mỹ nam Phù Dao Hoàng Hậu Nguyễn Kính Thiên bị réo tên?</figcaption></figure><p>Nguồn ảnh: Tổng hợp</p>', NULL, 'fan-khoc-thet-vi-luu-hao-nhien-len-can-nhuom-toc-nhu-tre-nghe-o-phim-moi-con-dau-du-hoai-nam-nao-cua-chung-toi', 0, NULL, NULL),
(68, 11, 4, 1, 1, 'Ngọc Trinh 10 năm trước từng hát vài câu \"chiếc ố\", Vũ Khắc Tiệp thấy hay quá nên \"đập đầu vào tường\"?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617763285787-1617763285955537943988.gif.png', '|', 'Bạn có tò mò giọng hát cách đây 10 năm của Ngọc Trinh nghe như thế nào không...', '<p>Những ngày vừa qua, các đoạn clip về chương trình Tam Sao Thất Bản, gameshow nổi tiếng cách đây 10 năm từng gây sốt trên sóng truyền hình bỗng viral trở lại trên cõi mạng, nguyên nhân đến từ phần thi của Ngọc Trinh và Vũ Khắc Tiệp.</p><p>Ngoài chuyện được ngắm lại nhan sắc thuở mới vào nghề của nữ hoàng nội y, dân mạng còn được chiêm ngưỡng dung nhan trước khi sửa mũi của Vũ Khắc Tiệp. Mới đây, một đoạn clip khác cũng trong chương trình Tam Sao Thất Bản đã được netizen đào lại trên TikTok, video ghi lại cảnh Ngọc Trinh... hát bài Mưa Ngâu. Vũ Khắc Tiệp nghe xong liền \"đập đầu vào tường\", muốn biết nguyên nhân đầu đuôi thế nào thì mời bạn xem qua đoạn clip bên dưới:</p><p>Clip: Ngọc Trinh khoe giọng hát \"chiếc ố\" làm Vũ Khắc Tiệp phải đập đầu vào tường</p><p>Cụ thể để vượt qua một thử thách giữ lại 1500 điểm cho cả đội, MC Thanh Thảo đã đề nghị Ngọc Trinh phải hát được 2 câu hát nổi tiếng trong bài Mưa Ngâu: Ngày xưa đôi ta bên nhau -  Chiếc ô xoe tròn là bóng lá. </p><p>Tuy nhiên, Ngọc Trinh đã hát thành: Ngày xưa đôi ta yêu nhau - Chiếc ô xoe tròn đầy bóng lá.</p><p>Sai 2 chữ hậu quả là cả nhóm mất trắng 1500 điểm, Vũ Khắc Tiệp vừa nghe Ngọc Trinh hát xong cũng... đập đầu vào tường luôn và ngay.</p><p>Sau khi xem xong đoạn clip, nhiều netizen đã tranh thủ gọi tên Chi Pu với bản live \"chiếc ố\" huyền thoại. Cùng là một \"chiếc ố\", cô chị 10 năm trước làm ông bầu Vũ Khắc Tiệp phải xỉu up xỉu down vì hát sai lời, còn cô em thân thiết của 10 năm sau lại... sai tông. Thật đúng là Ngọc Trinh và Chi Pu thật sự có duyên với nhau.</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/apr-07-2021-09-24-43-16177626034201278315879.gif.mp4\" ></video><div class=\"caption\">Ngọc Trinh hát say sưa...</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/apr-07-2021-09-25-05-161776260344881167083.gif.mp4\" ></video><div class=\"caption\">Nhưng rất tiếc là hát sai</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/anh-chup-man-hinh-2021-04-07-luc-092216-1617762414093151971116.png\" ><figcaption class=\"caption\">Ngọc Trinh lúc này còn mới vào nghề...</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/anh-chup-man-hinh-2021-04-07-luc-092236-1617762414153641079291.png\" ><figcaption class=\"caption\">Biểu cảm của Vũ Khắc Tiệp</figcaption></figure><p>Clip: Chi Pu và sự cố hát live \"chiếc ố\"</p>', NULL, 'ngoc-trinh-10-nam-truoc-tung-hat-vai-cau-chiec-o-vu-khac-tiep-thay-hay-qua-nen-dap-dau-vao-tuong', 0, NULL, NULL),
(69, 11, 4, 1, 1, 'Dương Hoàng Yến gây sốt với clip cover Let It Go, nhưng điểm chú ý là visual khác hẳn lúc tham gia Sao Nhập Ngũ', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617707915846-161770791620459952538.gif.png', '|', 'Dương Hoàng Yến là ca sĩ thuộc trường phái thực lực của Vpop là điều nhiều người đã biết, nhưng hãy tạm bỏ qua chuyện đó trong clip này vì visual của cô nàng mới đáng chú ý.', '<p>Kể từ khi tham gia chương trình Sao Nhập Ngũ, Dương Hoàng Yến đã nhận được rất nhiều tình cảm và sự quan tâm từ người hâm mộ. Các clip cover hay hình ảnh xuất hiện của Dương Hoàng Yến bên các nữ nghệ sĩ tham gia chương trình cũng thu hút lượt tương tác cao. Mới đây, Dương Hoàng Yến đã đăng tải đoạn clip cover ca khúc nhạc phim nổi tiếng Let It Go và nhanh chóng sở hữu đến 1,7 triệu lượt xem. </p><p>Clip Dương Hoàng Yến cover Let It Go</p><p>Là nữ giảng viên thanh nhạc trường Cao đẳng Nghệ thuật Hà Nội, ca khúc Let It Go không hề làm khó Dương Hoàng Yến. Những nốt cao chót vót của ca khúc đều được cô nàng \"xử đẹp\".</p><p>Tuy nhiên, visual của Dương Hoàng Yến mới lại là điều khiến các fan quan tâm hơn cả. So với ngoại hình thời điểm lúc tham gia Sao Nhập Ngũ thì nữ ca sĩ ở thời điểm hiện tại vừa trắng, mũi cao, đường nét khuôn mặt sắc lẹm được đánh giá \"Tây Tây\", xinh đẹp khó nhận ra khác hẳn mặt mộc xạm đen trong show thực tế.</p><p>Bên cạnh ngoại hình xinh đẹp thì một số cộng đồng mạng cũng thừa nhận, dù Dương Hoàng Yến từng ra mắt nhiều sản phẩm âm nhạc và thậm chí có cả bản hit nhưng từ sau Sao Nhập Ngũ, cô nàng mới được nhiều người biết tới. Nét tính cách dễ thương và nghiêm túc thực hiện các thử thách trong chương trình của Dương Hoàng Yến cũng chiếm trọn tình cảm của khán giả. </p><p>Một số bình luận của cộng đồng mạng: </p><p>- Mới đầu còn tưởng người nước ngoài. Nhìn mãi mới nhận ra là Dương Hoàng Yến. </p><p>- Nói thật hồi đầu không biết Dương Hoàng Yến là ai. Từ sau khi xem Sao Nhập Ngũ biết tới cô ấy luôn. Đúng là thời tới cản không kịp mà. </p><p>- Không nhận ra là Dương Hoàng Yến luôn ấy. </p><p>- Xinh đẹp, duyên dáng lại còn tài năng nữa. </p><p>- Nếu không nhìn tên thì sẽ không nhận ra là ai luôn. </p><p>- Những câu lên cao sao thấy cô ấy lên nhẹ nhàng thế nhỉ. </p><p>- Ước gì Yến tham gia Xuân Hạ Thu Đông Rồi Lại Xuân. </p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/screen-shot-2021-04-06-at-32057-pm-16177003973501009254245.png\" ><figcaption class=\"caption\">Đoạn clip cover Let It Go của Dương Hoàng Yến thu về 1,7 triệu lượt xem</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/idealistictallagouti-sizerestricted-1617707121497709934955.gif.mp4\" ></video><div class=\"caption\">Dương Hoàng Yến xuất hiện với gương mặt xinh đẹp tự tin cover Let It Go</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/oldfashionedselfassuredarizonaalligatorlizard-sizerestricted-16177071214071704811672.gif.mp4\" ></video><div class=\"caption\">Những nốt lên tông cao chót vót đều được nữ giảng viên thể hiện</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617707538384361250222.jpg\" ><figcaption class=\"caption\">Hình ảnh cô \"chiến sĩ\" Dương Hoàng Yến trong chương trình Sao Nhập Ngũ</figcaption></figure><p>Nguồn: TikTok </p>', NULL, 'duong-hoang-yen-gay-sot-voi-clip-cover-let-it-go-nhung-diem-chu-y-la-visual-khac-han-luc-tham-gia-sao-nhap-ngu', 0, NULL, NULL),
(70, 11, 4, 1, 1, 'Hoàng Dũng hát nhạc chế mà nghiêm túc dễ sợ, còn đóng nguyên bộ vest nhìn kiểu gì cũng thấy sai sai', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617683769260-16176837714831755430344.gif.png', '|', 'Đoạn clip Hoàng Dũng hát chế lời đang được cư dân mạng \"đào\" lại và chia sẻ đầy sự thích thú.', '<p>Mới đây cộng đồng mạng đã \"đào\" lại đoạn clip Hoàng Dũng trình diễn tại đêm nhạc phòng trà cuối năm 2018. Theo đó trong minishow này, nam ca sĩ đã mang tới bản cover ca khúc Tình Thôi Xót Xa dành tặng cho khán giả. Tuy nhiên, đang hát rất mượt ở verse 1, anh chàng bất ngờ khoe luôn màn chế lời với phiên bản \"tán tỉnh\" khiến khán giả vô cùng thích thú. </p><p>Hoàng Dũng cover Tình Thôi Xót Xa (Vertical Video)</p><p>Hoàng Dũng đã thể hiện màn cover ca khúc Tình Thôi Xót Xa với những câu hát chế lời như \"Nhà anh không to, tiền mẹ không hay cho nhưng anh sẽ kiếm em chớ lo\" hay \"Anh chịu khó, anh lại có duyên, soi gái, thích lăng nhăng không phải anh\". </p><p>Đáng chú ý, dù hát lời chế có phần khá \"lầy\" là thế nhưng Hoàng Dũng lại hát cực kì nghiêm túc, ai không nghe kĩ lại nghĩ đây là bản chính thức của ca khúc cũng nên. Đã thế, anh chàng còn \"đóng\" nguyên bộ vest rất chỉnh tề và nghiêm túc không hề hợp với hoàn cảnh chút nào. Thế nhưng chính sự \"ngược đời\" đó lại đem đến cảm xúc thú vị cho khán giả và khiến cho đoạn clip ngay khi được chia sẻ lại nhanh chóng thu hút sự quan tâm.</p><p>Một số bình luận của cộng đồng mạng: </p><p>- Bản chế này nghe nhiều rồi nhưng chưa bao giờ thấy nghiêm túc như bây giờ luôn á haha.</p><p>- Chế mà cũng dễ thương. Không thể không nghiện giọng Hoàng Dũng hơn được.</p><p>- Hic ăn gì hát hay thật luôn. Lời chế cũng ngọt.</p><p>- Anh hát chế nhưng vẫn không quên mặc vest nha quý vị. </p><p>- Hát live quá đỉnh, chế lời nghe cũng cưng xỉu. </p><p>- Hồi hộp nghe Dũng hát chế lời kkk. </p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/animation-16176834008502033084767.gif.mp4\" ></video><div class=\"caption\">Hoàng Dũng trình diễn Tình Thôi Xót Xa trong đêm nhạc</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/animation-1-16176834008311514984101.gif.mp4\" ></video><div class=\"caption\">Nhưng lại có màn hát chế lời trong nguyên cây vest vô cùng nghiêm túc</figcaption></figure><p>Nguồn: TikTok, YouTube </p>', NULL, 'hoang-dung-hat-nhac-che-ma-nghiem-tuc-de-so-con-dong-nguyen-bo-vest-nhin-kieu-gi-cung-thay-sai-sai', 0, NULL, NULL),
(71, 11, 4, 1, 1, 'Live Concert Rap Việt chốt ngày comeback 10/4, fan săn vé trước giờ G', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617767253648-1617767254200634410572.jpg', '|', 'Sau thời gian chờ đợi, fan Rap Việt đang nô nức lập team mua vé cùng nhau đi quẩy tại Live Concert Rap Việt All-Star vào thứ 7 tuần này - 10/4.', '<p>Ngay khi thông tin Live Concert Rap Việt All-Star trở lại được ban tổ chức ấn định diễn ra vào ngày 10/4/2021 đã thu hút được hàng trăm ngàn lượt tương tác phản hồi từ fan. Thông tin Live Concert Rap Việt All-Star khởi động lại không chỉ nhận sự đón nhận nồng nhiệt từ fan mà ngay cả những nghệ sĩ sẽ góp mặt tại Live Concert Rap Việt All-Star cũng không thể giấu được niềm phấn khích khi sẽ được cùng nhau một lần nữa cháy thật lớn tại sân khấu siêu hoành tráng của chương trình.</p><p>Những ngày qua chương trình đã khiến nhiều rap fan phải sôi sục chờ đợi 1 đêm nhạc đỉnh cao, bùng nổ vào thứ 7 tuần này - 10/4. Chưa dừng lại ở đó, Live Concert Rap Việt All-Star còn khiến nhiều người chưa kịp sở hữu vé vào tháng 1/2021 cực nóng lòng tậu ngay vé cho lần comeback của chương trình.</p><p>Theo thông tin chính thức từ ban tổ chức, vé mà khán giả đã mua từ tháng 1/2021 sẽ vẫn còn nguyên giá trị sử dụng cho sự kiện âm nhạc Rap Việt All-Star Concert 2021 vào ngày 10/04 tới. Ngoài ra, những khán giả chưa có vé hoặc có nhu cầu mua thêm vé thì có thể mua vé tại Ticketbox.vn hoặc thông qua hotline bán vé chính thức của chương trình: 1900 588 804, 0931 818 450, 0765 546 611. Be Group và ban tổ chức sẽ không chịu trách nhiệm giải quyết các trường hợp không sử dụng vé đã mua và đổi trả vé như \"Quy định Tham gia chương trình Rap Việt All-Star Concert 2021\" mà Be đã thông báo trên trang bán vé của Be Group từ tháng 12/2020.</p><p>Không chỉ quy tụ ê-kíp sản xuất hùng hậu, cùng sự cầm trịch của Giám đốc âm nhạc Touliver và 3 vị đạo diễn tài ba. Với Live Concert Rap Việt All-Star, lần đầu tiên tất cả các nghệ sĩ rap đình đám & những rapper thế hệ mới sẽ cùng đứng trên sân khấu cháy hết mình trước hàng ngàn khán giả. Đây có thể coi là sự kiện âm nhạc Rap - Hip hop có 1-0-2 và đỉnh nhất từ trước đến nay tại Việt Nam. Ê-kíp gồm hơn 1000 người cùng nhau làm việc ngày đêm cho dự án lớn lần này. Live Concert Rap Việt All-Stars sẽ có tổng cộng gần 50 bản rap được trình diễn. Ngoài những tiết mục nổi bật trong chương trình Rap Việt sẽ được dàn dựng hoàn toàn mới lạ và độc đáo với sự đầu tư hoành tráng, thì đây cũng là nơi ra mắt của những ca khúc mới và những sự kết hợp mới duy nhất cũng như những bản hit hàng đầu trong sự nghiệp của HLV và BGK sẽ được trình diễn vào đêm 10/4 tới đây. Chưa dừng lại ở đó, chương trình còn được \"bơm nhiệt\" cực mạnh bởi các Top DJ hàng đầu với set nhạc cực sung đậm chất hip hop.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617766986396322063618.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177670093041988828111.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-2-16177670081111199695201.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-3-1617767008472472364726.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-4-1617767008911238501693.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Live Concert Rap Việt All-Stars sẽ được tổ chức duy nhất vào ngày 10/4/2021 tại trung tâm SECC, Quận 7, TP.HCM. Chương trình chính diễn ra từ 20h, các hoạt động bên lề sẽ diễn ra từ 15h. Mọi thông tin về sự kiện Live Concert Rap Việt All-Star sẽ liên tục được cập nhật trên trang fanpage chính thức tại địa chỉ: Facebook.com/RapViet.VieChannel.</p>', NULL, 'live-concert-rap-viet-chot-ngay-comeback-104-fan-san-ve-truoc-gio-g', 0, NULL, NULL),
(72, 11, 4, 1, 1, 'Văn Mai Hương một mình hát Always Remember Us This Way liệu có hay hơn bản mash-up với Hoà Minzy?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617752937456-161775293782299939907.gif.png', '|', 'Từng gây \"bão\" là thế nhưng có gì khác biệt khi Văn Mai Hương một mình hát Always Remember Us This Way của Lady Gaga?', '<p>Văn Mai Hương vốn đã sở hữu giọng ca ấn tượng, đầy nội lực của mình nhưng gần đây, bản mash-up cover Ngày Chưa Giông Bão và Always Remember Us This Way hát cùng Hoà Minzy đã giúp tiếng tăm của nữ ca sĩ đi lên một tầm cao mới. Sau khi bản mash-up tại show Xuân Hạ Thu Đông Rồi Lại Xuân \"gây bão\", rất nhiều ý kiến trái chiều đã được nêu ra, đa số đều cho rằng Văn Mai Hương hát nhỉnh hơn Hoà Minzy và rất nhiều fan mong sẽ được thưởng thức riêng bài cover của Văn Mai Hương. Cho đến tối ngày 6/4, dân tình đã được chiêu đãi bản hát live acoustic chỉ một mình cô nàng rồi đây.</p><p>Always Remember Us This Way (A Star Is Born OST) - Văn Mai Hương cover</p><p>Một mình thể hiện Always Remember Us This Way, Văn Mai Hương hoàn toàn tự do khoe những kỹ thuật giọng hát ấn tượng mà không vướng bận điều gì. Dân tình không khỏi phát sốt với màn hát live nội lực và dạt dào cảm xúc từ Văn Mai Hương. Nhiều người thoả mãn khi được nghe trọn vẹn Always Remember Us This Way do Văn Mai Hương hát mà không cần phải \"tua\" nữa.</p><p>Thế nhưng, cũng không ít ý kiến trái chiều xung quanh bản cover này. Nhiều bình luận cho rằng bản mash-up cover Ngày Chưa Giông Bão và Always Remember Us This Way có sự góp giọng của Hoà Minzy hay và cảm xúc hơn. Band nhạc chơi tại chương trình Xuân Hạ Thu Đông Rồi Lại Xuân cũng được đánh giá cao, góp một phần lớn thành công cho bản mash-up.</p><p>Bản mash-up Ngày Chưa Giông Bão và Always Remember Us This Way với sự kết hợp giữa Hoà Minzy và Văn Mai Hương</p><p>Một vài ý kiến khác lại liên tục réo gọi cái tên Lady Gaga. Không phải vì muốn so sánh gì mà bởi lẽ, dân tình kêu gọi Văn Mai Hương hãy ghi tên Lady Gaga để thể hiện sự tôn trọng đối với tác giả ca khúc gốc.</p><p>Văn Mai Hương đã tiếp thu ý kiến từ netizen. 2 giờ sáng, Erik gọi ai không bắt máy thì không biết nhưng Văn Mai Hương vẫn còn thức, đã đồng loạt chỉnh sửa lại phần tiêu đề, caption clip trên YouTube và Facebook, bổ sung tên nghệ sĩ sáng tác Lady Gaga.</p><p>Có thể thấy, Văn Mai Hương là một nghệ sĩ rất biết lắng nghe và tiếp thu ý kiến của người hâm mộ. Một điểm cộng cho hành động này của Văn Mai Hương đúng không nào?</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/animation-14-1617752347200705081840.gif.mp4\" ></video><div class=\"caption\">Một mình thể hiện Always Remember Us This Way, Văn Mai Hương tiếp tục chinh phục người nghe bằng giọng hát nội lực của mình</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/animation-16-16177523473201188221218.gif.mp4\" ></video><div class=\"caption\">Phiên bản mới mẻ cùng ban nhạc acoustic</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070325-16177538376201153473340-161775406341435247471.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070332-16177538376531288160683-16177540845171798011518.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070328-1617753837638698130790-1617754103859352615804.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070319-1617753837305113669777-16177541217331113122332.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070322-1617753837317947299313-16177541341661196891498.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070311-1617753837222175779157.jpg\" ><figcaption class=\"caption\">Ban đầu, Văn Mai Hương không để tên nghệ sĩ sáng tác và thể hiện ca khúc gốc</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/anh-chup-man-hinh-2021-04-07-luc-070506-16177540073881840128504.png\" ><figcaption class=\"caption\">Sau đó, cô nàng đã bổ sung đầy đủ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070314-16177538372971379392304.jpg\" ><figcaption class=\"caption\">Ảnh chụp lúc 6 giờ sáng, tức là cô nàng đã chỉnh sửa caption từ 2 giờ</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/animation-15-16177523472172057013938.gif.mp4\" ></video><div class=\"caption\">Bạn thấy bản hát live acoustic một mình của Văn Mai Hương ra sao?</figcaption></figure><p>Nguồn & Clip: Văn Mai Hương - Ảnh: Chụp màn hình</p>', NULL, 'van-mai-huong-mot-minh-hat-always-remember-us-this-way-lieu-co-hay-hon-ban-mash-up-voi-hoa-minzy', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(73, 11, 4, 1, 1, 'Producer đào tạo Brave Girls 10 năm mới thành công là người đứng sau loạt hit của BIGBANG - Sunmi, từng cứu AOA, \"hồi sinh\" Park Bom', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617699717602-16176997180531964259690.png', '|', 'Brave Brothers quả thực \"mát tay\" khi cho ra các sản phẩm mang tính bước ngoặt của rất nhiều idol Hàn Quốc.', '<p>Thời gian vừa qua, cả Kpop phát sốt với một bản hit đã phát hành từ 4 năm trước mang tên Rollin\' của Brave Girls. Một bước lên luôn, Rollin\' đã giúp Brave Girls lập kỷ lục là nhóm nữ có số giờ Perfect All-kill nhiều nhất Kpop. Không chỉ thế, nhóm còn tiến hành quảng bá lại trên các show âm nhạc và Rollin\' đã mang về cúp âm nhạc đầu tiên cho Brave Girls sau 10 năm hoạt động. </p><p>Nhóm nữ đã rất chật vật với nhiều lần thay đổi đội hình, 10 năm nhưng không hề có một thành tích nổi bật nào và cũng không được biết đến nhiều. Thế nhưng, ít ai biết rằng, đứng sau Brave Girls chính là anh chàng producer cực tài năng, vừa quen mà cũng vừa lạ với fan Kpop tại Việt Nam - Brave Brothers.</p><p>Brave Brothers tên thật là Kang Dongchul, hiện là một rapper, nhà sản xuất thu âm và nhạc sĩ thuộc Brave Entertainment. Hãng thu âm Brave Entertainment cũng chính do Brave Brothers thành lập vào năm 2008. Brave Brothers sở hữu bốn nhóm dưới hãng thu âm của mình gồm: Brave Girls, Electroboyz, BIGSTAR và DKB.</p><p>Trong số đó, DKB là nhóm nhạc thế hệ mới nhất do Brave Entertainment đào tạo. Là nhóm nam đầu tiên được ra mắt sau 7 năm kể từ đàn anh BIGSTAR. Nhóm được kỳ vọng sẽ là thế hệ làm nên thành công cho Brave Entertainment. Tuy nhiên, hiện tại, ngoại trừ màn lội ngược dòng ấn tượng của Brave Girls, công ty này chưa đạt được nhiều thành công như mong đợi.</p><p>Công ty từng sở hữu ngôi sao sáng Kim Samuel - từng làm mưa làm gió tại Produce 101 mùa 2. Tuy nhiên, vào năm 2019, sau nhiều tranh chấp xảy ra thì Kim Samuel đã \"dứt áo ra đi\".</p><p>Quay ngược về quá khứ, Brave Brothers từng là producer đứng sau hàng loạt hit đình đám của Kpop. Anh chàng từng là nhà sản xuất và nhà soạn nhạc cho YG Entertainment từ năm 2004 đến năm 2008. Tính đến hiện tại, Brave Brothers đã từng sản xuất các bài hát hit cho After School, Sistar, 4Minute, T-ara, Son Dam Bi, BIGBANG, 2NE1, Brown Eyed Girls, AOA, Hello Venus và U-KISS. </p><p>Với câu hát nhận diện quen thuộc \"Brave Sound, drop it\", cứ nghe thấy là biết Brave Brothers sáng tác nhé! Nhắc đến thành công nổi bật nhất với vai trò sáng tác, chắc chắn phải nói về AOA. Brave Brothers đã \"cứu vớt\" AOA khi mang đến cho nhóm liên tục 4 hit lớn là Miniskirt, Short Hair, Like A Cat và Heart Attack. </p><p>MV Miniskirt - AOA</p><p>Brave Brothers cũng sáng tác những ca khúc làm nên thương hiệu \"nữ hoàng mùa hè\" của Sistar như Push Push, So Cool, Alone, Ma Boy, Gone Not Around Any Longer... Bài nào cũng vụt lên thành hit cả!</p><p>MV So Cool - Sistar</p><p>Chưa dừng lại ở đó, Brave Brothers còn sáng tác loạt ca khúc cho những nghệ sĩ đình đám khác như: Goodbye Baby và Last Fareweel (BIGBANG), Because of You (After School), Ice Cream (HyunA), Manmanhani (U-Kiss), Miss Right (Teen Top), Full Moon (Sunmi)...</p><p>Gần đây nhất, Brave Brothers đã góp phần không nhỏ cho màn tái xuất đại thành công của Park Bom sau ngày ở ẩn vì scandal thuốc cấm gián tiếp khiến 2NE1 tan rã. Anh chàng đã sáng tác ca khúc Spring, được Park Bom thể hiện thành công cùng màn góp giọng của Dara. Spring thực sự là một màn \"hồi sinh\" ấn tượng của Park Bom.</p><p>Spring - Park Bom ft. Dara</p><p>Brave Brothers được đánh giá là một trong những nhà sản xuất có đóng góp lớn nhất cho ngành giải trí Hàn tính đến hiện tại. Hy vọng rằng, những nhóm nhạc do Brave Brothers đào tạo sẽ sớm đạt được thành công như những gì mà \"quả ngọt\" Rollin\' đã làm được trong thời gian vừa qua.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617701143866259364131.jpeg\" ><figcaption class=\"caption\">Brave Girls có được thành công đầu tiên sau 10 năm hoạt động với Rollin\'</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617700869837676889037.png\" ><figcaption class=\"caption\">Brave Brothers đã đào tạo nên Brave Girls</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177008296751182797130.jpg\" ><figcaption class=\"caption\">Brave Girls</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617701193034458003081.jpg\" ><figcaption class=\"caption\">Electroboyz</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177012165501274691518.jpg\" ><figcaption class=\"caption\">BIGSTAR</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617701247968402060500.jpg\" ><figcaption class=\"caption\">DKB</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177022007972023319418.jpg\" ><figcaption class=\"caption\">Brave Entertainment từng sở hữu gà vàng Kim Samuel</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617703882327881901385.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-161770407726698295039.png\" ><figcaption class=\"caption\">Brave Brothers sáng tác những ca khúc làm nên thương hiệu \"nữ hoàng mùa hè\" của Sistar</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617704216946719056317.jpg\" ><figcaption class=\"caption\">Brave Brothers sở hữu kho tàng sáng tác đồ sộ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617701090766123052100.jpg\" ><figcaption class=\"caption\">Brave Brothers cùng Park Bom và Dara</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177683065471069804760.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Ảnh: Internet - Clip: YouTube</p>', NULL, 'producer-dao-tao-brave-girls-10-nam-moi-thanh-cong-la-nguoi-dung-sau-loat-hit-cua-bigbang---sunmi-tung-cuu-aoa-hoi-sinh-park-bom', 0, NULL, NULL),
(74, 11, 4, 1, 1, 'Bị hỏi thô lỗ \"Đang ở SNSD mà sao đi đóng nhạc kịch?\", Tiffany \"xử đẹp\" bằng câu trả lời gọn gàng!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617767885500-1617767885756239380617.gif.png', '|', 'Câu hỏi bị dân tình chê trách vì vô duyên còn pha trả lời của Tiffany (SNSD) lại cực kỳ \"đi vào lòng người\".', '<p>SNSD tuy thời gian gần đây không có hoạt động gì nhưng trên danh nghĩa vẫn là một nhóm nhạc 8 thành viên. Trong đó, Tiffany, Sooyoung và Seohyun đã rời khỏi công ty SM để có cho mình đường hướng phát triển riêng biệt. Mới đây, Tiffany đã tham gia vào một dự án nhạc kịch mang tên Chicago. </p><p>Tại buổi họp báo nằm trong khuôn khổ quảng bá vở nhạc kịch Chicago, bên cạnh biểu diễn giới thiệu, Tiffany còn giao lưu cùng giới truyền thông. Trong buổi phỏng vấn, một phóng viên đã hỏi nữ thần tượng: \"Tiffany tham gia vở nhạc kịch khi đang là thành viên nhóm nhạc nữ (SNSD). Tại sao bạn lại ở đây và bạn có kế hoạch quay trở lại hoạt động với nhóm của mình không?\".</p><p>Trước một câu hỏi hiểm hóc và có phần vô duyên này, Tiffany vô cùng bình tĩnh, đáp lại nhẹ nhàng: \"Em sẽ tiếp tục là thành viên của nhóm nhạc nữ và là diễn viên nhạc kịch trong tương lai ạ\". Câu trả lời được dân tình đánh giá cao về khả năng ứng xử của Tiffany. Bên cạnh đó, câu hỏi được đặt ra lại nhận về nhiều chỉ trích:</p><p>- Nguyên dàn vocal line nhà SNSD đều đi rồi nên mấy anh chị phóng viên bớt hỏi vô duyên giùm ạ. Nhóm có vocal tốt ngại gì không khoe.</p><p>- Tại vì giỏi nên làm thành viên nhóm nhạc, ca sĩ solo hay diễn viên nhạc kịch đều được vậy á. Chịu không chịu thì thôi.</p><p>- Trả lời lịch sự, nhẹ nhàng, đỉnh quá chị yêu.</p><p>- Đúng là gái nhà SNSD câu này có gì khó với chị.</p><p>- Hỏi câu gì mà thô lỗ vô duyên ghê.</p><p>- Chứ bắt người ta phải chọn một trong hai hả?</p><p>- Chị vẫn là thành viên của SNSD, nhưng hát nhạc kịch vì đam mê, được chưa?</p><p>Sân khấu biểu diễn Roxie của Tiffany tại buổi họp báo ra mắt Chicago</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/-16177296381361349779586.jpg\" ><figcaption class=\"caption\">Tiffany tại buổi họp báo quảng bá vở nhạc kịch Chicago</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177267734332143501878.jpg\" ><figcaption class=\"caption\">Tiffany gặp phải câu hỏi khó nhưng vẫn \"xử\" ngon ơ</figcaption></figure><p>Nguồn: Lạc Sâu Bích - Ảnh & Clip: Internet</p>', NULL, 'bi-hoi-tho-lo-dang-o-snsd-ma-sao-di-dong-nhac-kich-tiffany-xu-dep-bang-cau-tra-loi-gon-gang', 0, NULL, NULL),
(75, 12, 4, 1, 1, 'Cảnh nóng trong các MV của Taylor Swift: Lả lướt cùng mẫu nam nhưng vẫn chưa bằng cảnh quay \"trần như nhộng\" gây sốc', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/28/photo1616947379826-1616947380117868496515.gif.png', '|', 'Taylor Swift - \"bà chị\" vốn nổi tiếng với hình ảnh ngoan hiền cũng có lúc nóng bỏng hết nấc thế này đấy!', '<p>Tự hỏi, một bà chị vốn nổi tiếng với hình ảnh \"girl ngoan hiền\" như Taylor Swift có bao giờ thể hiện những góc quay nóng bỏng và quyến rũ chết người trong các MV của mình không nhỉ? Hỏi đúng hơn, có MV nào của Taylor Swift đã xuất hiện cảnh nóng khiến fan xem xong cũng \"mắt O miệng A\" đỏ cả mặt chưa?</p><p>Câu trả lời là có nhé! Tuy rằng không đến độ gắn mác 18+ thế nhưng những cảnh quay thân mật với bạn diễn hay \"mặc như không mặc\" trong các MV của Taylor Swift cũng khiến người xem phải hẫng đi vài nhịp vì nóng quá nóng!</p><p>Thời điểm Wildest Dream ra mắt, rất nhiều người đã không khỏi bất ngờ vì một nàng công chúa của nhạc đồng quê bỗng dưng \"thoát xác\", lần đầu hát về chuyện \"người lớn\" rõ ràng và thẳng thắn như thế. Và với một ca khúc mang nội dung \"người lớn\" đến thế kia thì hiển nhiên chiếc MV trình làng cũng nóng chẳng kém cạnh đâu các bạn ạ.</p><p>Trong MV Wildest Dream, Taylor Swift và mẫu nam Scott Eastwood đã có những phân cảnh tình tứ trên giường luôn mà.</p><p>Wildest Dreams - Taylor Swift</p><p>Cảnh nóng của Taylor Swift trong MV này chỉ có độ chừng mười giây ngắn ngủi, thế nhưng có thể nói đây chính là điểm nhấn của MV, bởi vì xem đến đâu, nóng đến đó! Taylor Swift thì quyến rũ hết nấc trong chiếc váy ngủ, còn nam diễn viên thì để lộ lưng trần cùng cơ bụng 6 múi. Cả hai anh chị tình tứ hôn nhau nồng nhiệt trên giường nên dù cho cảnh quay này trong MV ít thì cũng chất lượng vì bảo đảm, fan xem xong cũng nóng rực theo không kém!</p><p>Trong MV Style ra mắt cùng năm của Wildest Dream cũng có xuất hiện đôi ba góc quay đầy nóng bỏng. Với chiếc MV này, Taylor Swift lựa chọn mẫu nam đóng cùng mình là nam diễn viên người Anh Dominic Sherwood. Phần lớn cảnh quay trong MV là ở trong rừng và ngoài biển, tuy nhiên, riêng cảnh nóng của chàng và nàng thì được quay trong studio với phông nền tối.</p><p>Style - Taylor Swift</p><p>Cảnh nóng trong MV Style thì... tối thui! Mấy cảnh trong rừng ngoài biển thì rõ sáng nhưng đến lúc cảnh hai anh chị diễn thân mật với nhau thì lại là trong bóng tối. Chỉ đơn thuần là những cái chạm nhẹ vào da thịt rồi hôn nhau và ôm ghì lấy nhau lúc ẩn lúc hiện, thế nhưng hẳn cũng đủ để lột tả nội dung bài hát mà Taylor Swift muốn truyền tải: một mối tình đẹp với người yêu cũ Harry Styles!</p><p>Phân cảnh được cho là khá nhạy cảm trong MV ... Ready For It? chính là lúc Taylor Swift \"mặc như không mặc\", khoe trọn đường cong cơ thể. Thực chất là Taylor Swift vào vai nữ người máy và hình ảnh \"trần như nhộng\" kia là do kỹ xảo hình ảnh mà ra, chứ không có việc Taylor Swift nude 100%. Tuy nhiên, nếu chỉ thoáng nhìn qua và không tìm hiểu rõ thì việc người ta tin rằng cô nàng táo bạo khoe trọn cơ thể như thế là chuyện đương nhiên.</p><p>…Ready For It? - Taylor Swift</p><p>Được biết, trước khi ra mắt MV chính thức, ảnh cut Taylor Swift \"trần như nhộng\" đã được lan truyền trên MXH khiến fan không khỏi xôn xao về quyết định táo bạo này của cô nàng. Ngay sau đó, Taylor Swift cũng đã đăng tải trên MXH ảnh hậu trường đóng MV như một cách đính chính sự việc. À thì ra cô nàng có mặc bodysuit để quay MV!</p><p>À thì ra Taylor Swift đã mặc chiếc body suit màu da rồi thêm kỹ xảo hình ảnh mới ra cảnh quay nóng dễ gây sửng sốt như thế!</p><p>Babe - Sugarland ft. Taylor Swift</p><p>Nhìn chung, cảnh nóng trong MV của một bà chị giữ vững hình tượng ngoan hiền như Taylor Swift chỉ dừng lại ở những cái chạm trên da thịt tinh tế, những nụ hôn nồng nàn đến độ ngất ngây và có để lộ cơ thể từ trên xuống dưới thì cũng chỉ là kỹ xảo mà thôi. Nếu xét về mặt bằng chung các MV của những nghệ sĩ USUK khác thì cảnh nóng của Taylor Swift chỉ đạt độ \"sương sương\", nhấn nhá vài chỗ cho MV thêm nhiệt chứ chẳng \"thấm\" vào đâu so với các nghệ sĩ táo bạo khác. </p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/26/taylor-swift-bb29-2019-feat-billboard-wmfosjgrpajs-1240-800x1200-16167502292391183452181.jpg\" ><figcaption class=\"caption\">Cảnh nóng trong các MV của Taylor Swift sẽ nóng đến cỡ nào nhỉ?</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/26/ezgifcom-gif-maker-2-16167508723261943383026.gif.mp4\" ></video><div class=\"caption\">Taylor Swift gợi cảm trong chiếc váy ngủ hở trên trống dưới</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/26/ezgifcom-gif-maker-1-1616750872335447541442.gif.mp4\" ></video><div class=\"caption\">Taylor Swift và bạn diễn hôn nhau nồng nhiệt thế nhỉ!</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/26/ezgifcom-gif-maker-1616750872363147161723.gif.mp4\" ></video><div class=\"caption\">Phân cảnh thân mật của cả hai trong MV</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/26/540c13c8-9200-4a65-afc7-f434a89de3f8-16167500261191065129158.jpg\" ><figcaption class=\"caption\">Hình ảnh của Taylor Swift trong MV Wildest Dream</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/26/ezgifcom-gif-maker-3-1616751354477706284722.gif.mp4\" ></video><div class=\"caption\">Mẫu nam để lộ cơ bụng 6 múi \"mlem mlem\"</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/26/ezgifcom-gif-maker-4-16167513583551103026898.gif.mp4\" ></video><div class=\"caption\">Chàng và nàng ôm chặt nhau</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/26/ezgifcom-gif-maker-5-16167513622531750328450.gif.mp4\" ></video><div class=\"caption\">Cảnh hôn chớp nhoáng</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/26/ezgifcom-gif-maker-6-1616753814001222331419.gif.mp4\" ></video><div class=\"caption\">Taylor Swift phải chăng đã \"trần như nhộng\" trong MV ...Ready For It?</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/26/ezgifcom-gif-maker-7-16167538211592016099358.gif.mp4\" ></video><div class=\"caption\">Cô nàng khoe trọn đường cong cơ thể</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/26/ezgifcom-gif-maker-8-1616753824418204161552.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/26/1-16167538512231030041848.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/26/ezgifcom-gif-maker-10-16167538744481555736015.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/26/ezgifcom-gif-maker-9-1616753877476346776329.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Clip: YouTube - Ảnh: Internet</p>', NULL, 'canh-nong-trong-cac-mv-cua-taylor-swift-la-luot-cung-mau-nam-nhung-van-chua-bang-canh-quay-tran-nhu-nhong-gay-soc', 0, NULL, NULL),
(76, 12, 4, 1, 1, 'Ariana Grande hát được nửa bài rồi, fan bất ngờ hét lên một câu \"nghe vô lý nhưng rất thuyết phục\" làm nữ ca sĩ phải hát lại từ đầu', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/21/photo1616299069608-16162990701731412510290.gif.png', '|', 'Người ta đang hát mà tự nhiên hét làm mất tập trung ghê, lại phải hát lại!', '<p>Có thể nói, Ariana Grande là một trong những nghệ sĩ chiều fan hết mực nhất của làng nhạc USUK. Một trong những hành động nói lên điều đó chính là tại một buổi biểu diễn ở Los Angeles vào ngày 26/8/2018 nằm trong khuôn khổ Sweetener Sessions.</p><p>Tại đêm diễn này, Ariana Grande đã có hành động chiều fan đi vào lòng người: đang hát say sưa thì fan hét lên, bảo chị hát lại đi vì em chưa quay kịp, thế là nữ ca sĩ... hát lại luôn!</p><p>Ariana Grande bắt đầu lại bài hát vì fan chưa quay kịp</p><p>Cụ thể, Ariana Grande đã hát được gần nửa bài Raindrops rồi và đang chuẩn bị \"bắn\" nốt cao nhất trong bài thì phía dưới khán giả có người la lên: \"Hát lại đi chị ơi! Em chưa quay kịp\". </p><p>Bình thường thì hát tiếp chứ ai đang hát dở giữa bài dừng lại vì cái lý do cá nhân trớ trêu thế. Nhưng Ariana Grande thì khác, không nghe thấy thì thôi chứ nghe thấy rồi là không thể bỏ qua được. Thế là Ariana Grande đột ngột dừng hát và cất lời: \"Ai mới kêu \'chị hát lại đi, em chưa quay kịp\' đúng không? Ai đó nói với tôi vậy đó. Tôi có nghe, tôi có nghe mà. Và dĩ nhiên, tôi sẽ kiểu \'chắc chắn rồi, em đã sẵn sàng để quay chưa?\'. Tôi xin lỗi nhé! Ôi, vui quá đi!\".</p><p>Màn giao lưu ngắn này của Ariana Grande khiến khán giả bên dưới không khỏi phấn khích và vô cùng vui vẻ khi cô nàng đồng ý diễn lại Raindrops dù rằng đã hát được nửa bài rồi.</p><p>Đoạn clip này của Ariana Grande hiện đang được lan truyền trên MXH TikTok. Một số bình luận của netizen sau khi xem clip:</p><p>- Đang hát mà vẫn dừng lại lắng nghe ý kiến fan như vậy, cô ấy thật tử tế.</p><p>- Ariana Grande dễ thương quá!</p><p>- Chị này nói chuyện hài hước vậy. </p><p>- Đây là cách xử lý tình huống trên sân khấu của một ngôi sao thực thụ.</p><p>- Dù tôi không phải fan nhưng tôi vẫn thấy cô ấy dễ thương và tốt bụng nữa mà.</p><p>- Ariana Grande đáng yêu thật. Fan chưa quay kịp là lỗi của fan mà chị ấy vẫn xin lỗi rồi bắt đầu lại bài hát.</p><p>- Làm fan Ariana Grande quả không sai!</p><p>- Ngoài sự dễ thương kia thì mọi người cũng nên biết một điều là Ariana Grande không hề hát nhép.</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/21/ezgifcom-gif-maker-18-16163001833681340730260.gif.mp4\" ></video><div class=\"caption\">Đang hát thì Ariana Grande dừng đột ngột vì nghe có ai đó bảo cô hát lại từ đầu do chưa quay kịp</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/21/ezgifcom-gif-maker-19-16163001875931009420840.gif.mp4\" ></video><div class=\"caption\">Ariana Grande chiều fan nên trình diễn lại ca khúc</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/21/hqatqjvkj0451-16163009387371713597119.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Clip: YouTube, TikTok - Ảnh: Internet</p>', NULL, 'ariana-grande-hat-duoc-nua-bai-roi-fan-bat-ngo-het-len-mot-cau-nghe-vo-ly-nhung-rat-thuyet-phuc-lam-nu-ca-si-phai-hat-lai-tu-dau', 0, NULL, NULL),
(77, 12, 4, 1, 1, 'Xôn xao clip Justin Bieber định lao vào đánh antifan vì bị gọi là “cô gái không biết hát”?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/24/photo1616554656739-1616554657092438108103.gif.png', '|', 'Đoạn clip được cho là Justin Bieber có hành động gay gắt với antifan đang thu hút đến hơn 16 triệu lượt xem.', '<p>Mới đây cộng đồng mạng đang xôn xao trước đoạn clip ghi lại hình ảnh được cho là của Justin Bieber đăng tải vào ngày 19/2 vừa qua. Cụ thể trong clip, người được cho là Justin Bieber dù được hộ tống bởi 2 vệ sĩ nhưng antifan - cũng chính là người quay video vẫn cố gắng tiếp cận. </p><p>Khi đến gần, người này đặt câu hỏi cho Justin Bieber: \"What you call a girl from Canada that can\'t sing?\" (Tạm dịch: Bạn gọi cô gái ở Canada không biết hát là gì?). Trước câu hỏi này, \"hoàng tử\" nhạc Pop tỏ ra khá bối rối. </p><p>Clip Justin Bieber định lao vào đánh antifan vì màn cà khịa kém duyên</p><p>Tuy nhiên, khi nghe antifan trả lời rằng đó chính là \"Justin Bieber\", anh chàng đã lộ rõ sự cáu gắt và định lao vào đánh antifan. Rất may lúc này 2 vệ sĩ đã kịp thời cản Justin Bieber nếu không, chưa biết chuyện gì sẽ xảy ra với chàng trai kia. </p><p>Hiện đoạn clip đang nhận về hơn 2 triệu lượt \"bắn\" tim và 16,4 triệu views trên ứng dụng video. </p><p>Thế nhưng bên cạnh những bình luận xôn vao vì phản ứng hành hung antifan của Justin Bieber thì nhiều ý kiến đã chỉ ra rằng đây là clip dàn dựng, giả dạng nam ca sĩ để nhằm đánh lừa khán giả. </p><p>Bởi lẽ người đăng tải clip này trước đây cũng từng bị cộng đồng mạng lên án vì hoá trang thành Justin Bieber và làm video gây hiểu nhầm cho các fan. Không chỉ vậy, đối chiếu với hình ảnh Justin Bieber thời điểm tháng 2/2021, nhiều người cũng cho rằng có sự khác nhau giữa hai gương mặt. </p><p>Trước đó vào năm 2016, Justin Bieber cũng từng gây \"rúng động\" khi thẳng tay đấm fan chảy máu miệng. Cụ thể, khi đang trên đường đến buổi biểu diễn ở Barcelona, Tây Ban Nha bằng ô tô riêng, đã có rất nhiều người hâm mộ bao vây để được nhìn ngắm thần tượng trực tiếp. </p><p>Khi có fan quá khích thò tay qua kính xe để chạm vào người mình, Justin Bieber đã thẳng tay đấm vào mặt fan khiến người này chảy máu. Sau khi sự việc gây xôn xao, nguồn tin bên cạnh Justin Bieber đã thanh minh rằng hành động đó chỉ nhằm tự vệ bản thân trước fan cuồng quá khích. </p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/24/infatuatedcoordinatedbullmastiff-sizerestricted-1616553986970663349048.gif.mp4\" ></video><div class=\"caption\">Antifan tiếp cận Justin Bieber với cự ly gần</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/24/potableripebull-sizerestricted-16165539868791791751731.gif.mp4\" ></video><div class=\"caption\">Và dành câu hỏi: \"Bạn gọi cô gái ở Canada không biết hát là gì?\"</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/24/emptyoddballhoverfly-sizerestricted-16165539868611125972463.gif.mp4\" ></video><div class=\"caption\">Khi nghe câu trả lời là \"Justin Bieber\", nam ca sĩ đã có hành động định lao vào đánh antifan</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/24/screen-shot-2021-03-24-at-94414-am-1616553986398619924430.png\" ><figcaption class=\"caption\">Hiện đoạn clip này đang thu về 16,4 triệu lượt xem</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/24/2021-03-24-104337-16165574296171891610311.jpg\" ><figcaption class=\"caption\">Hình ảnh Justin Bieber thời điểm tháng 2/2021</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/24/screen-shot-2021-03-24-at-103606-am-1616557198673133760182.png\" ><figcaption class=\"caption\">Người đăng tải clip Justin Bieber định đánh antifan trước đây cũng từng hoá trang giả dạng nam ca sĩ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/24/photo-1-1616554164105599026816.jpg\" ><figcaption class=\"caption\">Justin Bieber đang gây xôn xao vì đoạn clip được cho là anh định hành hung antifan</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/24/animation-1616553986845605313013.gif.mp4\" ></video><div class=\"caption\">Khoảnh khắc Justin Bieber thẳng tay đấm vào mặt fan quá khích</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/24/animation-1-16165539864281825818777.gif.mp4\" ></video><div class=\"caption\">Khiến người này chảy máu miệng</figcaption></figure><p>Nguồn: TikTok, YouTube </p>', NULL, 'xon-xao-clip-justin-bieber-dinh-lao-vao-danh-antifan-vi-bi-goi-la-co-gai-khong-biet-hat', 0, NULL, NULL),
(78, 12, 4, 1, 1, 'Ai rồi cũng khác: Dua Lipa mới 3 năm trước bị fan chọc quê vì lắc hông như đi chợ, giờ tự tin nhảy nhót nhìn mê luôn!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/20/photo1616173329856-1616173330766563883598.gif.png', '|', 'Có cố gắng là có thành công nha các bạn mình ơi!', '<p>Câu \"ai rồi cũng khác\" sử dụng trong trường hợp này quá ư hợp tình hợp lý các bạn ạ.</p><p>Hẳn bạn còn nhớ vào lễ trao giải Grammy 2021 diễn ra vào mấy ngày trước (15/3), một trong những màn trình diễn xuất sắc nhất, nhận được nhiều lời khen ngợi và sự chú ý nhất chính là sân khấu Levitating và Don\'t Star Now của bộ đôi Dua Lipa - DaBaby. Đó là một sân khấu đậm sắc tím hồng và cực kỳ nóng bỏng theo từng nhịp nhạc. </p><p>Màn trình diễn của Dua Lipa và DaBaby tại Grammy 2021</p><p>Từ giọng hát cho đến vũ đạo, cặp đôi này (đặc biệt là Dua Lipa) đã nhận cơn mưa lời khen từ khán giả. Không những thế, sân khấu này cũng đã nhanh chóng lọt top trending YouTube của Mỹ với số view cứ tăng vèo vèo, bỏ xa sân khấu của loạt nghệ sĩ khác tại Grammy 2021 như Taylor Swift, Cardi B, Billie Eilish, Bruno Mars,... </p><p>Sân khấu của Dua Lipa tại Grammy 2021 xuất sắc thật. Nhìn lại 3 năm về trước của Dua Lipa thì ôi thôi, không ai tin là cô nàng lại từng \"gà mờ\" về vũ đạo đến vậy.</p><p>Vào 3 năm trước, thời điểm đó Dua Lipa đang là một \"tân binh\" của làng nhạc USUK, nhận được cũng khá nhiều sự chú ý của khán giả. Dù xinh xắn, body chuẩn không cần chỉnh và giọng hát đặc biệt nghe một lần là nhớ ngay, thế nhưng Dua Lipa vẫn có một điểm trừ lớn mà không ít người nhận thấy, đó là không giỏi trình diễn trên sân khấu. Nhạc thì rất hay nhưng vũ đạo của cô thì khá hời hợt và thần thái cũng chưa đạt. Đoạn clip Dua Lipa nhảy như tập thể dục còn bị dân mạng cắt gif ra trêu khắp nơi.</p><p>Nếu chưa thể mường tượng được sân khấu của Dua Lipa thuở còn là \"tân binh\" thì mời bạn xem chiếc clip sau. Đây là màn trình diễn One Kiss của nữ ca sĩ tại Jingle Ball Tour Show ở Philadelphia vào tháng 12/2018.</p><p>Màn trình diễn của Dua Lipa tại Jingle Ball Tour Show, Philadelphia</p><p>Dua Lipa trình diễn tại Jingle Ball Tour Show vào năm 2018</p><p>Màn trình diễn One Kiss này của Dua Lipa sau đó đã trở thành đề tài để mọi người trêu chọc. Ai nấy đều bày tỏ sự khó hiểu về màn vũ đạo như đi chợ. Thậm chí, một số fan còn ghép hẳn chiếc clip nhảy của Dua Lipa với ca khúc Sài Gòn Đẹp Lắm nữa cơ! Nghe cũng hợp lý lắm!</p><p>Fan chèn clip nhảy của Dua Lipa với ca khúc Sài Gòn Đẹp Lắm</p><p>Nhìn lại màn trình diễn như đi chợ của Dua Lipa vào 3 năm trước rồi nhìn màn trình diễn đầy nóng bỏng và cuốn hút của Dua Lipa vào Grammy vừa qua mới nhận thấy cô nàng đã có sự thay đổi lớn. Cô đã biết làm chủ sân khấu hơn, thần thái \"đỉnh của chóp\" và vũ đạo thì cũng cải thiện hơn rất nhiều. Ai rồi cũng khác, chỉ cần quyết tâm là thay đổi được hết các bạn ơi!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/19/9-16161708541161584754215.jpg\" ><figcaption class=\"caption\">Dua Lipa xuất hiện tại Grammy 2021 với bộ váy công chúa lộng lẫy</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/19/40470824-9362071-image-m-1001615771687958-16161708618231590344703.jpg\" ><figcaption class=\"caption\">Và sau đó, cô nàng cởi gần hết trang phục, chỉ còn bộ bikini cực nóng bỏng trên sân khấu khoe body cực \"mlem\"</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/19/ezgifcom-gif-maker-6-1616171054850998335409.gif.mp4\" ></video><div class=\"caption\">Dua Lipa xinh hết nấc trong chiếc váy công chúa</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/19/ezgifcom-gif-maker-8-16161710654001140977382.gif.mp4\" ></video><div class=\"caption\">Còn giờ thì quyến rũ vô cùng với bộ bikini khoe trọn những đường cong trên cơ thể</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/19/1111-1616172583918216376079.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/19/ezgifcom-gif-maker-12-1616172730114942920935.gif.mp4\" ></video><div class=\"caption\">Là nhảy hay tập thể dục vậy Dua Lipa?</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/19/ezgifcom-gif-maker-4-1616172740557806442711.gif.mp4\" ></video><div class=\"caption\">Đây là động tác chú chim gãy cánh đúng không nhỉ?</figcaption></figure><p>Clip: YouTube - Ảnh: Internet</p>', NULL, 'ai-roi-cung-khac-dua-lipa-moi-3-nam-truoc-bi-fan-choc-que-vi-lac-hong-nhu-di-cho-gio-tu-tin-nhay-nhot-nhin-me-luon', 0, NULL, NULL),
(79, 12, 4, 1, 1, 'Người cả đời chưa cầm cúp Grammy, có cô bé mới 9 tuổi đã sở hữu Grammy đầu tiên, không biết dùng làm gì nên lấy làm bình sữa?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/19/photo1616130349083-16161303495421039616492.png', '|', 'Kẻ ăn không hết, người lần không ra là có thật các bạn ạ. Cha mẹ của cô bé này gom lại \"sương sương\" 50 chiếc cúp Grammy thôi ấy mà...', '<p>Ông bà xưa có câu, \"Kẻ ăn không hết, người lần không ra\", mới đây lại có ví dụ chứng minh điều này là có thật các bạn ạ! Với rất nhiều người (không nói là Katy Perry đâu nhé) đã dành hết cả thanh xuân cống hiến cho Grammy: biểu diễn nhiệt tình, váy hoa xúng xính trên thảm đỏ, nộp đề cử cũng rất \"máu lửa\" nhưng rồi Viện Hàn lâm vẫn lắc đầu \"say no\", chưa bao giờ để họ sở hữu chiếc cúp Grammy danh giá, dù chỉ là một giải phụ, dù chỉ một lần...</p><p>Nhưng rồi có những cô cậu bé sinh ra đã ở vạch đích, có ba mẹ là \"ông trùm\" trong làng nhạc và đã mang về giải Grammy đầu tiên trong cuộc đời khi chỉ mới... 9 tuổi. Đó là cô bé Blue Ivy Carter, con gái đầu lòng của \"Ong Chúa\" Beyoncé và \"ông trùm\" Jay-Z. Gia đình này hiện đang có tổng cộng... 50 chiếc cúp Grammy, 28 đến từ Beyoncé, 22 đến từ Jay-Z, con số khiến ai nhìn vào cũng toát mồ hôi!</p><p>Trong lễ trao giải Grammy 2021 vừa kết thúc cách đây mấy ngày, MV Brown Skin Girl của Beyoncé đã giành chiến thắng ở hạng mục Best Music Video (Video âm nhạc xuất sắc nhất). Chiếc cúp Grammy này được chính thức đề tên và dành sự công nhận cho Beyoncé, nghệ sĩ góp mặt Wizkid, đạo diễn MV Jenn Nkiru và cô bé Blue Ivy Carter với vai trò đồng sáng tác Brown Skin Girl.</p><p>Thành tích của Blue Ivy Carter tại Grammy 2021 gây \"chấn động\" cộng đồng mạng thế giới khi khán giả bày tỏ sự ngưỡng mộ trước \"cái vạch đích\" quá đỉnh của cô bé này. Với việc giành chiến thắng Grammy khi mới 9 tuổi, Blue Ivy Carter cũng trở thành người trẻ thứ 2 trong lịch sử giành chiến thắng của Viện Hàn lâm. Kỉ lục hiện tại chưa ai vượt qua Leah Peasall khi chiến thắng Grammy 2001 lúc 8 tuổi.</p><p>Trên MXH những ngày qua thích thú chia sẻ bức ảnh Blue Ivy Carter đầu đội vương miện đầy quyền lực trông không khác gì mẹ, trên tay dùng chiếc cúp Grammy của riêng mình làm... bình đựng sữa. Hành động hút sữa của cô bé 9 tuổi từ chiếc cúp Grammy cũng rất ư quyền lực, quả thật \"con nhà tông không giống lông cũng giống cánh\"! Nhiều người không có Grammy dòm mà tức lắm đây...</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/photo-1-16160687131451394810077.jpg\" ><figcaption class=\"caption\">Cố tỏ ra mình ổn nhưng trong tâm như biển xô sóng trào...</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/photo-1-16160692977271296682969.jpg\" ><figcaption class=\"caption\">Gia đình quyền lực hàng đầu Hollywood: Beyoncé, Jay-Z và Blue Ivy Carter</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/16173087419342587267213884377357033812054059n-16160684432811573962488.jpg\" ><figcaption class=\"caption\">Từ bé đã toát ra \"thần thái\" không thua kém gì bố mẹ rồi nhé.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/18/16134509619342588733880401709103721297974200n-16160684431701325288399.jpg\" ><figcaption class=\"caption\">Blue Ivy Carter dùng chiếc cúp Grammy làm bình uống sữa các bạn ạ...</figcaption></figure><p>Ảnh: Twitter</p>', NULL, 'nguoi-ca-doi-chua-cam-cup-grammy-co-co-be-moi-9-tuoi-da-so-huu-grammy-dau-tien-khong-biet-dung-lam-gi-nen-lay-lam-binh-sua', 0, NULL, NULL),
(80, 12, 4, 1, 1, 'Màn trình diễn Grammy đang #1 trending Mỹ: View gấp 10 lần Taylor Swift, gấp 100 lần Cardi B và \"quá xinh đẹp, quá tuyệt vời, xin cảm ơn\"', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/16/photo1615906970050-1615906970601690443827.png', '|', 'Hiện tại, sân khấu tại Grammy 2021 của nữ nghệ sĩ này đang nhận được sự chú ý của đông đảo khán giả và lọt top 1 trending YouTube tại Mỹ.', '<p>Sáng sớm ngày 15/3 (theo giờ Việt Nam) vừa qua, lễ trao giải Grammy lần thứ 63 đã chính thức diễn ra. Bên cạnh những khoảnh khắc hồi hộp xướng tên người nhận giải tại các hạng mục thì các sân khấu trình diễn cũng luôn được khán giả quan tâm. Vậy, bạn có tò mò muốn biết sân khấu của nghệ sĩ nào được xem là xuất sắc nhất tại Grammy 2021 không?</p><p>Chẳng phải Taylor Swift, cũng chẳng phải Cardi B, Bruno Mars hay BTS, nghệ sĩ sở hữu sân khấu nổi bật nhất chính là bộ đôi Dua Lipa và DaBaby.</p><p>Chiếc clip trình diễn của Dua Lipa và DaBaby đã lọt top 1 trending YouTube tại Mỹ chỉ sau chưa đầy 1 ngày ra mắt. Trong số các màn trình diễn tại Grammy 2021, sân khấu của Dua Lipa kết hợp với DaBaby đã nhận về lượt view cao nhất với hơn 9,1 triệu views trên YouTube. Con số này hiện đang tăng \"vèo vèo\".</p><p>Sân khấu của Dua Lipa và DaBaby tại Grammy 2021 mang đậm sắc tím hồng. Cả hai đã trình diễn 2 ca khúc là Levitating và Don\'t Start Now. Từ giọng hát cho đến vũ đạo, sân khấu của cặp đôi này đầy nóng bỏng và sôi động, đã khuấy động được không khí của lễ trao giải. Khán giả không xem được trực tiếp nhưng qua màn hình lại rất đã con mắt!</p><p>Màn trình diễn của Dua Lipa được dàn dựng không quá nhiều màu sắc nhưng lại vô cùng hiệu quả về hiệu ứng sân khấu khi diễn. Giọng hát khỏi bàn mà body của Dua Lipa lại \"mlem\" hết sức, cô nàng sinh năm 1995 thay đồ 3 lần từ bộ váy công chúa sang xịn mịn sang mặc độc chiếc áo vest sequin lấp lánh và rồi... cởi gần hết khi chỉ còn bộ bikini trên sân khấu. Nhưng không hề dâm dục, Dua Lipa quá sexy và diễn đầy thần thái, khiến ai xem cũng bị hút mắt.</p><p>Màn trình diễn của Dua Lipa và DaBaby tại Grammy 2021</p><p>Thành tích #1 trending Mỹ cũng như viral và được khen ngợi khắp nơi của Dua Lipa là điều vô cùng bất ngờ. Bởi kể từ đợt dịch Covid-19 năm 2020 đến nay, tình hình showbiz quốc tế rất ảm đạm. Các màn trình diễn quay hình sẵn, không có khán giả thường không đủ \"nhiệt\" khiến dân tình có phản ứng bùng nổ. Grammy 2021 cũng... nhạt nốt, ratings giảm, Taylor Swift làm nên lịch sử như-mọi-lần, BTS xuất hiện cũng chỉ có thể hút thêm fan của BTS mà thôi nên nhìn chung sự kiện không có gì đáng chú ý.</p><p>May sao Dua Lipa lại sở hữu sân khấu toả sáng, cô nàng cũng đã khoe thành tích này trên MXH Facebook và gửi lời cảm ơn đến ekip của mình đã góp phần giúp cô làm được điều này.</p><p>Sự thật mà nói, trong danh sách các nghệ sĩ trình diễn tại Grammy 2021, người được kỳ vọng sẽ mang đến sự bùng nổ và cũng là điểm nhấn của chương trình chính là Taylor Swift. Tuy nhiên, nếu xét lượng tương tác trên YouTube tính đến thời điểm hiện tại thì sân khấu của nàng \"rắn chúa\" lại không nhận được sự chú ý của khán giả.</p><p>Sau 1 ngày ra mắt, nếu số view của sân khấu Dua Lipa hơn 9,1 triệu views thì sân khấu Taylor Swift chỉ hơn 984 nghìn views, tức thua xấp xỉ gấp 10 lần. Và màn hóa thân nàng công chúa trong rừng cổ tích của Taylor Swift cũng không hề lọt vào top trending của YouTube. Mặc dù màn hóa thân này cũng đầu tư không kém nhưng vẫn không đủ sức nhận được sự hưởng ứng nhiệt tình của khán giả.</p><p>Ngoài Taylor Swift, sân khấu WAP của bộ đôi Cardi B - Megan Thee Stallion và sân khấu Leave The Door Open của Silk Sonic (Bruno Mars - Anderson .Paak) cũng được kỳ vọng có thể mang đến những màn trình diễn ấn tượng cho khán giả tại Grammy năm nay. Ấy thế mà, cả hai sân khấu này cũng không đặc sắc là bao (vì vốn dĩ cũng nằm trong suy đoán của mọi người, không có gì bất ngờ).</p><p>Sau hơn 1 ngày ra mắt, sân khấu WAP chỉ có hơn 87 nghìn views, còn sân khấu Leave The Door Open chỉ có gần nửa triệu views. Hiển nhiên, cả hai màn trình diễn này cũng không lọt top trending YouTube tại Mỹ.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/16/0-1615888354634996245486.jpg\" ><figcaption class=\"caption\">Nàng Dua Lipa đã có sân khấu ấn tượng tại Grammy 2021</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/16/photo2021-03-1616-19-47-1615886641194116360081.jpg\" ><figcaption class=\"caption\">Sân khấu của Dua Lipa và DaBaby đang nắm giữ top 1 trending YouTube tại Mỹ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/16/1-16158885033431162630743.jpg\" ><figcaption class=\"caption\">Hiện tại, chiếc clip này nhận về hơn 9,1 triệu views</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/16/cats-1615886687173715425680.jpg\" ><figcaption class=\"caption\">Dua Lipa cũng đã nhanh chóng chia sẻ niềm vui này trên MXH: \"Hơn 7 triệu views cho màn trình diễn của chúng tôi tại Grammy chỉ sau chưa đầy 24 giờ ra mắt và lọt top 1 trending YouTube. Tôi muốn nói lời cảm ơn đến đội ngũ tuyệt vời của mình vì đã giúp đạt được điều đó...\"</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/16/2-16158902899691287575744.jpg\" ><figcaption class=\"caption\">Số view của sân khấu Taylor Swift tại Grammy 2021 tính đến thời điểm hiện tại chỉ hơn 984 nghìn</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/16/6-16159069346351602378537.jpg\" ><figcaption class=\"caption\">Số view của sân khấu WAP hiện chỉ có hơn 87 nghìn</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/16/5-1615906934618196143667.jpg\" ><figcaption class=\"caption\">Số view của sân khấu Leave The Door Open hiện chỉ có gần nửa triệu</figcaption></figure><p>Clip: YouTube - Ảnh: Grammy 2021</p>', NULL, 'man-trinh-dien-grammy-dang-1-trending-my-view-gap-10-lan-taylor-swift-gap-100-lan-cardi-b-va-qua-xinh-dep-qua-tuyet-voi-xin-cam-on', 0, NULL, NULL),
(81, 12, 4, 1, 1, 'Taylor Swift vừa nhận giải thưởng lớn của Grammy 2021, MXH lan truyền đoạn story cà khịa của Vũ: Lại drama gì thế này?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/15/photo1615789386427-1615789386958849912895.png', '|', 'Chỉ vài giờ sau khi Taylor Swift nhận giải thưởng Album của năm tại Grammy 2021, MXH bỗng lan truyền đoạn story của Vũ về vấn đề này. Nhưng sự thật là gì?', '<p>Mới đây, Taylor Swift vừa gây chú ý khi nhận được thêm một chiếc kèn Grammy trong sự nghiệp âm nhạc của mình với giải thưởng Album của năm. Như vậy, Taylor Swift trở thành nữ nghệ sĩ duy nhất trong lịch sử có 3 album chiến thắng 3 giải thưởng này. Một ngày quá tuyệt vời cho nữ ca sĩ sinh năm 1989 như chính chiếc váy hoa mà cô mặc vậy!</p><p>Chiến thắng này của Taylor Swift nhanh chóng nhận được sự quan tâm của netizen. Ai nấy đều vui mừng cho giải thưởng danh giá mà cô vừa nhận. Nhưng vui đâu chưa kịp thấy thì bất ngờ trên MXH cũng lan truyền một đoạn story của \"hoàng tử Indie\" Vũ với lời lẽ khá gắt dành cho Taylor Swift. </p><p>Chuyện gì vậy nhỉ?</p><p>Xem kỹ mới biết: à thì ra đây là một sản phẩm của netizen. Ai đó đã lấy đoạn story mà Vũ từng đăng tải trên Instagram bày tỏ sự bức xúc khi Taylor Swift nhận giải Album của năm vào năm 2019 rồi sửa lại nội dung cho phù hợp với Grammy 2021 chỉ để... gợi chuyện lại chút!</p><p>Quay lại thời điểm diễn ra vụ việc này, đó là vào khuya muộn ngày 31/8/2019, Vũ đã cho đăng tải một loạt story chia sẻ cảm nghĩ của anh về âm nhạc, trong đó có một story thẳng thắn chỉ trích Taylor Swift không xứng nhận giải Album của năm tại Grammy.</p><p>Cụ thể, Vũ đã bày tỏ thái độ không bằng lòng về việc album To Pimp A Butterfly của Kendrick Lamar đã thua giải Album của năm. Anh cho rằng album của Kendrick Lamar là một album chất lượng về cả khâu sản xuất lẫn nội dung và được giới chuyên môn đánh giá rất cao. Bên cạnh đó, anh đã bày tỏ sự không hài lòng của bản thân với album đã giành chiến thắng năm đó - dù không nêu đích danh, nhưng ai cũng hiểu: lễ trao giải Grammy năm đó, alum 1989 của Taylor Swift đã chiến thắng chiếc máy hát đĩa của Grammy.</p><p>Ở cuối bài đăng, anh tiếp tục lôi Kanye West cùng khoảnh khắc cướp micro Taylor Swift tại MTV Video Music Awards 2009 lên, dường như có ý đồng tình với việc Kanye West cướp diễn đàn để khẳng định video âm nhạc của Beyoncé đỉnh hơn năm xưa là hoàn toàn đúng?</p><p>Còn nhớ thời điểm đó, anh chàng này đã bị \"ném đá\" không ít bởi fan của Taylor Swift. Dù sau chuyện đó, Vũ không có thêm động thái gì liên quan đến Taylor Swift nữa thế nhưng fan của cô nàng lâu lâu vẫn đem vụ việc năm xưa ra để \"cà khịa\" Vũ. Và lần này cũng vậy.</p><p>Hiện tại, đoạn story chế này đang nhận được sự chú ý của nhiều netizen. Một số bình luận của netizen:</p><p>- Mỗi lần Taylor Swift được giải chắc Vũ cay lắm.</p><p>- Mình đã tạm biệt \"hoàng tử Indie\" từ story đấy.</p><p>- Vũ năm nay chắc vẫn viết story như thế nữa chứ nhỉ?</p><p>- Giờ nhớ lại còn thấy tức.</p><p>- Ký ức ùa về.</p><p>- Không biết năm nay anh Vũ có bức xúc nữa không ta?</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/15/saostar-1db38gozmvdcbs4r-16157931889221980198180.jpg\" ><figcaption class=\"caption\">Taylor Swift nhận giải Album của năm tại Grammy 2021</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/15/00-16157896228581940875000.jpg\" ><figcaption class=\"caption\">Đoạn story của Vũ bày tỏ bức xúc làm dân tình hoang mang (Ảnh: Hóng chuyện Hollywood)</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/15/0-1615789619642457915864.jpg\" ><figcaption class=\"caption\">Đoạn story gây tranh cãi của Vũ vào năm 2019</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/15/photo-1-1615803771737879364183.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn: Hóng chuyện Hollywood</p>', NULL, 'taylor-swift-vua-nhan-giai-thuong-lon-cua-grammy-2021-mxh-lan-truyen-doan-story-ca-khia-cua-vu-lai-drama-gi-the-nay', 0, NULL, NULL),
(82, 13, 4, 1, 1, 'Nửa đêm Sơn Tùng đi bình luận dạo, lập tức lọt top trending giữa loạt hashtag teaser MV của Jack', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617730824199-1617730824734727111330.png', '|', 'Đêm qua khó ngủ quá Tùng nhỉ?', '<p>Tối ngày 6/4, Jack đã chính thức tung teaser concept cho sản phẩm comeback mang tên LLL. Teaser MV dài khoảng 30 giây hé lộ những phân cảnh đậm chất \"giang hồ\" của Jack. Màn nhá hàng nhanh chóng nhận được sự quan tâm của đông đảo người hâm mộ, loạt hashtag liên quan về sản phẩm LLL đã leo lên những vị trí cao nhất top trending tại Việt Nam. </p><p>Teaser Concept LLL - Jack</p><p>Đáng chú ý, giữa một loạt hashtag về Jack lại xuất hiện 1 chiếc hashtag Sơn Tùng M-TP. Dân tình không khỏi thấy lạ với sự xuất hiện này. Điều tra lý do thì mới phát hiện ra một chi tiết cực đáng yêu của Sơn Tùng M-TP. Nam ca sĩ đã đi \"comment dạo\" bài viết của một người bạn. Nhưng mà fan lại đồng loạt nhận được thông báo, thế là ồ ạt vào xem Sơn Tùng M-TP đã bình luận gì.</p><p>Nhìn vào bình luận của Sơn Tùng M-TP, dân tình ùa vào \"thả haha\" ngay vì ngôn ngữ đậm chất Gen Z của nam ca sĩ. Đã thế, anh chàng còn xài chiếc icon cười ra nước mắt đang cực kỳ thông dụng trên Facebook nữa chứ! Thế là leo lên top trending ngồi luôn. </p><p>Tuy nhiên, một lý do khác khiến nhiều người sử dụng hashtag Sơn Tùng M-TP là tên của nam ca sĩ cùng fandom Sky là đại diện Việt Nam duy nhất xuất hiện trong một bảng xếp hạng những fandom nổi tiếng toàn thế giới của Wikipedia (tiếng Anh), bên cạnh rất nhiều fandom Kpop đình đám khác như: ARMY (của BTS), Blink (của BLACKPINK), VIP (của BIGBANG), EXO-L (của EXO), ELF (của Super Junior)...</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/anh-chup-man-hinh-2021-04-07-luc-000413-16177313376321188480452.png\" ><figcaption class=\"caption\">3 hashtag dẫn đầu top trending đều thuộc về Jack sau khi tung teaser concept LLL</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/-16177320679291271328112.jpg\" ><figcaption class=\"caption\">Jack thống trị top trending, chứng tỏ người hâm mộ đang cực kỳ mong chờ vào màn comeback của anh chàng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617730179121898091507-1617756432750404772559.jpg\" ><figcaption class=\"caption\">Fan đồng loạt nhận được thông báo về một động thái của Sơn Tùng M-TP trên Facebook</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/16177301894771160379555-1617756455873133563020.jpg\" ><figcaption class=\"caption\">Sơn Tùng M-TP để lại bình luận \"Zậyyyy hợ...???\" kèm icon cười ra nước mắt</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-161773190379068592709.png\" ><figcaption class=\"caption\">Thả một chiếc bình luận kiểu ngôn ngữ Gen Z thế là leo hẳn lên top trending</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177318324382018224247-1617731967066743329348.jpg\" ><figcaption class=\"caption\">Sky là fandom duy nhất của nghệ sĩ Việt Nam có mặt trong danh sách các fandom nổi tiếng toàn thế giới</figcaption></figure><p>Nguồn, Ảnh: Twitter - Clip: YouTube</p>', NULL, 'nua-dem-son-tung-di-binh-luan-dao-lap-tuc-lot-top-trending-giua-loat-hashtag-teaser-mv-cua-jack', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(83, 13, 4, 1, 1, 'BTS, Demi Lovato, Justin Bieber,... đồng loạt gửi lời chúc mừng đến thương vụ bạc tỉ của HYBE, nhưng Ariana Grande đâu rồi?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617685648467-16176856488301805559536.png', '|', 'Sự vắng mặt của Ariana Grande trong đoạn clip chúc mừng gây chú ý.', '<p>Tối 5/4, HYBE đã cho đăng tải đoạn clip xác nhận thương vụ sát nhập Ithaca Holdings vào HYBE. \"Thương vụ bạc tỉ\" này gây xôn xao toàn thế giới khi ông lớn của Kpop sẽ \"thâu tóm\" Ithaca Holdings, bao gồm cả SB Project (công ty quản lý của Ariana Grande, Justin Bieber, J Balvin và Demi Lovato) và hãng đĩa Big Machine Label Group.</p><p>Việc sát nhập này sẽ giúp HYBE trở thành một trong những công ty giải trí lớn nhất và có tầm ảnh hưởng lớn nhất thế giới. Tính đến thời điểm hiện tại, tính chung tất cả các công ty con nằm trong \"hệ sinh thái\" HYBE thì họ đang sở hữu những tên tuổi: BTS, TXT, SEVENTEEN, NU’EST, GFRIEND, ENHYPEN, ZICO, Justin Bieber, Ariana Grande, J Balvin, Demi Lovato, Thomas Rhett, Florida Georgia Line, Lady A,...</p><p>Bang Si-Hyuk, Scooter Braun và dàn nghệ sĩ gửi lời chúc mừng đến màn sát nhập của Ithaca với HYBE</p><p>Điểm nhấn của đoạn clip chính là khoảnh khắc hàng loạt nghệ sĩ \"chung nhà\" HYBE gửi lời chúc mừng đến sự kết hợp đầy ấn tượng này. Nhìn qua, khán giả toàn thế giới có thể thấy \"khí thế\" của HYBE khi tiếp nhận Ithaca vào cùng \"hệ sinh thái\", giúp công ty chủ quản của BTS \"bành trướng\" không chỉ ở Hàn Quốc mà còn ở phạm vi toàn cầu.</p><p>BTS cho biết: \"Tin tức này thực sự quá chấn động khiến chúng tôi cực kì phấn khích. Ariana Grande, Justin Bieber, J Balvin! Ekip đứng sau những nghệ sĩ tuyệt vời này đã cùng về nhà với HYBE. Chúng tôi thực sự hạnh phúc khi rất nhiều những nghệ sĩ mà chúng tôi quý mến nay lại trở thành thành viên của đại gia đình. </p><p>RM và SUGA nhấn mạnh: \"Có cảm giác như thế giới chúng ta ngày càng kết nối và cũng dần trở nên nhỏ bé hơn. Chúng ta đang chứng kiến những điều không tưởng nay trở thành sự thật, chẳng hạn như tin tức đáng kinh ngạc này!\"</p><p>J Balvin, nam nghệ sĩ chủ nhân bản hit Mi Gente cùng loạt ca khúc Latinh sôi động khác cũng cho biết màn kết hợp này sẽ đẩy mọi thứ lên một tầng cao mới. Anh cũng không quên gửi lời chúc mừng đến Scooter Braun và Chủ tịch Bang. </p><p>Demi Lovato và SEVENTEEN cũng bày tỏ sự hào hứng trước màn kết hợp này. Justin Bieber qua đó cũng bày tỏ cảm xúc ngắn gọn, chúc mừng Chủ tịch Bang và Scooter Braun, đồng thời bày tỏ sự lạc quan vào những sự kết hợp quốc tế sẽ diễn ra trong tương lai. </p><p>Đoạn clip có nội dung không mới vì hầu như công chúng đều đã biết khá rõ về màn sát nhập giữa Ithaca vào \"hệ sinh thái\" của HYBE, những cơ hội rộng mở cho dàn nghệ sĩ của hai bên khi về chung một nhà. Tuy nhiên, điều đáng chú ý chính là sự vắng mặt của Ariana Grande trong đoạn clip chúc mừng trên. </p><p>Có thể thấy, dù phía SB Project và Big Machine Label Group có khá nhiều nghệ sĩ trực thuộc nhưng 3 cái tên lớn nhất vẫn là Ariana Grande, Justin Bieber, Demi Lovato. Trong số 3 cái tên lớn nhất ấy, Ariana Grande vẫn có vị thế và giá trị lớn nhất trong thời điểm hiện tại, tương tự như BTS của phía Big Hit Music. Ở mặt bằng chung, Ariana Grande rõ ràng đang là một trong những nữ nghệ sĩ trẻ có tầm ảnh hưởng lớn nhất thế giới trong thời điểm hiện tại.</p><p>Chính vì thế, việc Ariana Grande vắng mặt trong đoạn clip chúc mừng màn sát nhập khiến công chúng không khỏi hoang mang. Dù vẫn có rất nhiều các nghệ sĩ trực thuộc cả 2 công ty chưa xuất hiện trong clip nhưng sự vắng mặt của \"tiểu diva\" vẫn vô cùng đáng tiếc.</p><p>Rất có thể do cô có lịch trình quá bận rộn nên không thể thực hiện đoạn clip chúc mừng. Tuy nhiên, nhìn sang Demi Lovato, Justin Bieber hay J Balvin, tất cả đều là những đoạn clip ghi hình bằng điện thoại rất nhanh chóng, thật khó để tin Ariana Grande lại không có thời gian chỉ để quay một đoạn clip ngắn bằng điện thoại để chúc mừng, đặc biệt là với cương vị nghệ sĩ có vị thế lớn nhất của SB Project!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/01-1617684626364200938851.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/02-1617685011194278353496.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/03-1617685302202974543814.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/04-16176853733821527245507.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/05-1617685404356991535916.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16176864141471271856026.png\" ><figcaption class=\"caption\">Scooter Braun bên 2 \"gà cưng\" Ariana Grande và Demi Lovato</figcaption></figure><p>Clip: HYBE</p>', NULL, 'bts-demi-lovato-justin-bieber-dong-loat-gui-loi-chuc-mung-den-thuong-vu-bac-ti-cua-hybe-nhung-ariana-grande-dau-roi', 0, NULL, NULL),
(84, 13, 4, 1, 1, 'HLV Kpop hé lộ câu hỏi casting idol: Không chỉ hỏi về hình mẫu và ước mơ mà còn đi sâu hơn vào vấn đề bạo lực học đường', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/4/photo1617515099599-16175150999721248159218.png', '|', 'Nếu có ý định casting cho một công ty giải trí Kpop, đừng bỏ qua bài viết này!', '<p>Vượt qua một buổi thử giọng Kpop không phụ thuộc hoàn toàn vào việc bạn hát, nhảy hay rap tốt. Để có được trên tay hợp đồng thực tập sinh với công ty mà mình chọn, bạn cần phải trả lời một số câu hỏi để gây ấn tượng với người tuyển chọn. Mới đây, trong một cuộc phỏng vấn, hai vị huấn luyện viên thần tượng Kpop đã tiết lộ tất cả những điều liên quan đến các câu hỏi phỏng vấn tại buổi thử giọng.</p><p>Trước đây là thành viên của nhóm nhạc nữ thế hệ thứ ba Playback, Lee Yun Ji hiện là hiệu trưởng của trường đào tạo thần tượng Making Academy. Trong khi đó, Kim Se Yeon đã đào tạo những thần tượng có sở trường về giọng hát 10 năm qua. Họ đã tiết lộ những câu hỏi mà các nhân viên tuyển chọn thường hỏi trong các buổi thử giọng Kpop.</p><p>Đầu tiên, Lee Yun Ji nói rằng trong vòng thử giọng đầu tiên, hầu như không có câu hỏi nào. Trên thực tế, dường như không có thời gian để đặt câu hỏi, bởi các buổi thử giọng thường kết thúc chỉ trong 30 giây. </p><p>Đôi khi, các giám khảo buổi thử giọng có thể nhận thấy được tố chất đặc biệt ở một trong những người thử giọng và quyết định thực hiện một bài kiểm tra camera. Như tên gọi, một bài kiểm tra camera bao gồm nói chuyện và biểu diễn trước máy quay. Trong các bài kiểm tra camera, Lee Yun Ji nói rằng các thực tập sinh có nguyện vọng có thể được hỏi những câu hỏi đơn giản. Nhưng các buổi thử giọng đầu tiên thì chủ yếu tập trung vào các kỹ năng trình diễn.</p><p>Tuy nhiên, để vượt qua được vòng thử giọng cuối cùng thì bạn phải trải qua quá trình kiểm tra khá gắt gao với loạt câu hỏi \"sâu sắc\". Lee Yun Ji giải thích, đối với những người mới bắt đầu, họ muốn nghe về âm nhạc yêu thích của bạn, ca sĩ hoặc hình mẫu mà bạn khao khát trở thành, và ước mơ của bạn cho tương lai. Đây là tất cả các câu hỏi sẽ định hình hình tượng mà một thí sinh thử giọng có thể trở thành.</p><p>Trên hết, Kim Se Yeon tiết lộ rằng gần đây, các nhân viên tuyển chọn cũng hỏi về bạo lực học đường. Có lẽ do các cáo buộc gia tăng từ cư dân mạng, những người cho rằng một số thần tượng đã bắt nạt họ trong quá khứ. Họ sẽ hỏi: \"Bạn đã từng là một người tốt đúng không?\".</p><p>Tuy nhiên, Lee Yun Ji đi đến kết luận rằng đáp án của những câu hỏi này không phải là thứ mà người thử giọng cần phải lo lắng. Thay vào đó, cô tin rằng điều quan trọng nhất là các thí sinh phải nói chuyện tự nhiên, tự tin trước ban giám khảo. Nếu quá rụt rè hoặc chuẩn bị quá nhiều từ trước thì các giám khảo cũng có thể nhận ra.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/4/unnameda-16175167513501890331339.jpeg\" ><figcaption class=\"caption\">Hai vị huấn luyện viên thần tượng Kpop đã tiết lộ những câu hỏi phỏng vấn tại buổi thử giọng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/4/unnamedb-16175167513821521021018.jpeg\" ><figcaption class=\"caption\">Lee Yun Ji nói rằng trong vòng thử giọng đầu tiên, hầu như không có câu hỏi nào</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/4/unnamedd-1617516751412623636573.jpeg\" ><figcaption class=\"caption\">Nayeon (TWICE) tại buổi thử giọng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/4/unnamedc-1617516751394294644465.jpeg\" ><figcaption class=\"caption\">Kim Se Yeon tiết lộ rằng gần đây, các nhân viên tuyển chọn cũng hỏi về bạo lực học đường</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/4/unnamed-16175167512641709730286.jpeg\" ><figcaption class=\"caption\">Jennie (BLACKPINK) thời chưa debut</figcaption></figure><p>Nguồn & Ảnh: Koreaboo</p>', NULL, 'hlv-kpop-he-lo-cau-hoi-casting-idol-khong-chi-hoi-ve-hinh-mau-va-uoc-mo-ma-con-di-sau-hon-vao-van-de-bao-luc-hoc-duong', 0, NULL, NULL),
(85, 13, 4, 1, 1, 'Nhìn Rosé nhảy WAP mà nhớ ngay sân khấu cực sexy của Lisa: \"Chị em sinh đôi\" mà thần thái khác hẳn nhau!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617637668099-1617637668425472924655.gif.png', '|', 'Cùng có động tác nằm ra sàn quyến rũ, Rosé hay Lisa nhảy ấn tượng hơn đây?', '<p>Hai ngày vừa qua, Rosé (BLACKPINK) đang gây sốt với bản cover WAP của Cardi B. Đây là lời hứa của Rosé với fan khi tài khoản TikTok của cô nàng cán mốc 10 triệu người theo dõi. Tuy nhiên, so với bản gốc cực \"bốc lửa\" và sexy, Rosé đã thể hiện vũ đạo WAP phiên bản ngây thơ và đầy ngại ngùng đến cả chính chủ Cardi B cũng phải nhận xét: \"Trông cô ấy vẫn thật ngây thơ… Xinh đẹp và đáng yêu quá đi mất\".</p><p>Rosé (BLACKPINK) cover vũ đạo WAP của Cardi B</p><p>Vũ đạo \"chà sàn\" mà Rosé cover từ WAP lại làm người hâm mộ BLACKPINK nhớ về sân khấu đình đám một thời của Lisa. Trong bài nhảy Swalla tại concert, Lisa cũng đã thực hiện động tác nằm xuống sàn cực sexy và quyến rũ. Động tác nằm sàn cực \"bạo\" chiếm trọn spotlight tại sân khấu này. Với thần thái tự tin của Lisa, sân khấu càng trở nên cuốn hút hơn bao giờ hết.</p><p>Swalla - Lisa (BLACKPINK In Your Area Manila)</p><p>Cùng với một động tác tương đối giống nhau, mà Rosé và Lisa lại thể hiện với hai thái cực khác hẳn nhau. Là hai \"chị em sinh đôi\" mà mỗi người một vẻ thế này à? Nhưng cũng phải nói thêm, Lisa là màn trình diễn solo trên sân khấu concert, còn Rosé thì nhảy chơi chơi \"trả bài\" cho fan thôi ấy mà nên xem cho vui chứ cũng không thể so sánh một cách nghiêm túc. Dù sao thì dân mạng cũng rất mong chờ ngày Rosé và Lisa cùng nhau nhảy WAP đấy!</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/ezgif-4-89221fd35c7a-161763746588424659146.gif.mp4\" ></video><div class=\"caption\">Rosé nhảy vũ điệu 18+ còn ngại ngùng...</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/ezgif-4-c278e2700b4a-16176374659141985481032.gif.mp4\" ></video><div class=\"caption\">... và \"chà sàn\" cực đáng yêu thế này</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/animation-13-1617637465708997053902.gif.mp4\" ></video><div class=\"caption\">Còn Lisa thì lại cực \"bạo\", có vũ đạo nằm sàn quyến rũ hết nấc</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/photo-1-16176371643251619599205.gif.mp4\" ></video><div class=\"caption\">Là hai \"chị em sinh đôi\" mà mỗi người một vẻ thế này à?</figcaption></figure><p>Nguồn & Clip: TikTok, YouTube - Ảnh: Cắt từ clip</p>', NULL, 'nhin-rose-nhay-wap-ma-nho-ngay-san-khau-cuc-sexy-cua-lisa-chi-em-sinh-doi-ma-than-thai-khac-han-nhau', 0, NULL, NULL),
(86, 13, 4, 1, 1, 'BLACKPINK mang tiếng \"nắm trùm\" YouTube nhưng tổng view tại Hàn Quốc năm 2021 thua IU, \"không có cửa\" đối đầu với BTS', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617634037057-1617634037290116619967.png', '|', 'BTS dẫn đầu về lượt xem YouTube tại Hàn Quốc trong năm 2021, tuy nhiên BLACKPINK không có vị trí cao như mong đợi.', '<p>Mới đây, cư dân mạng trên diễn đàn  Pann (Hàn Quốc) đã thống kê tổng view YouTube đến từ Hàn Quốc của các idol Kpop từ 1/1/2021 cho đến 1/4/2021. Lượt xem bao gồm MV và tất cả video của nghệ sĩ đó trong phạm vi Hàn Quốc, không tính view từ nước ngoài.</p><p>Xét hạng mục idol nam, BTS dẫn đầu với 157 triệu lượt xem bắt nguồn từ Hàn Quốc. Con số này còn giúp họ thống trị top 20 nghệ sĩ (cả nam lẫn nữ). Đáng chú ý, BTS mới phát hành duy nhất MV tiếng Nhật mang tên Film Out vào ngày 1/4 và chưa comeback tại quê nhà trong năm 2021.</p><p>Xếp sau BTS là SEVENTEEN (41,9 triệu views). Hạng 3 thuộc về BIGBANG. Nhóm nam nhà YG đã \"đóng băng\" hoạt động từ năm 2018, nhưng lượt xem các nội dung trên YouTube của họ vẫn tăng, chỉ riêng 4 tháng đầu năm 2021 đã thu về 29 triệu views.</p><p>Top 20 idol nam có tổng view YouTube cao nhất Hàn Quốc năm 2021</p><p>1. BTS: 157 triệu views</p><p>2. SEVENTEEN: 41,9 triệu views</p><p>3. BIGBANG: 29 triệu views</p><p>4. SHINee: 24,5 triệu views</p><p>5. NCT 127: 19,8 triệu views</p><p>6. EXO: 16,4 triệu views</p><p>7. NCT Dream: 15,5 triệu views</p><p>8. DAY6: 14,7 triệu views</p><p>9. NCT U: 13,9 triệu views</p><p>10. NCT: 13 triệu views</p><p>11. TXT: 12,9 triệu views</p><p>12. Super Junior: 10.9 triệu views</p><p>13. WINNER: 9,98 triệu views</p><p>14. Baekhyun (EXO): 9,83 triệu views</p><p>15. G-Dragon (BIGBANG): 9,72 triệu views</p><p>16. iKON: 9,48 triệu views</p><p>17. Stray Kids: 9,25 triệu views</p><p>18. Yang Yoseob (HIGHLIGHT): 9,04 triệu views</p><p>19. Kyuhyun (Super Junior): 8,97 triệu views</p><p>20. The Boyz: 8,9 triệu views</p><p>Ở mảng nữ, thật bất ngờ khi  IU mới là người giành vị trí Quán quân. Trong năm 2021, nữ ca sĩ thu về 148 triệu lượt xem từ Hàn Quốc, tổng view của cô còn vượt cả  BLACKPINK.  4 cô gái đến từ YG Entertainment được mệnh danh là \"nữ hoàng YouTube\", lượt đăng ký kênh chỉ xếp sau Justin Bieber trong giới nghệ sĩ toàn cầu. Thế nhưng lượt xem YouTube Hàn Quốc của BLACKPINK trong 4 tháng đầu năm chỉ đạt 108 triệu views.</p><p>Top 20 idol nữ có tổng view YouTube cao nhất Hàn Quốc năm 2021</p><p>1. IU: 148 triệu views</p><p>2. BLACKPINK: 108 triệu views</p><p>3. IZ*ONE: 50,7 triệu views</p><p>4. TWICE: 46,1 triệu views</p><p>5. (G)I-DLE: 44,2 triệu views</p><p>6. Brave Girls: 37,8 triệu views</p><p>7. Red Velvet: 27,9 triệu views</p><p>8. Oh My Girl: 27,5 triệu views</p><p>9. ITZY: 4,5 triệu views</p><p>10. Chungha: 24 triệu views</p><p>11. Taeyeon (SNSD): 23,9 triệu views</p><p>12. GFRIEND: 23,5 triệu views</p><p>13. MAMAMOO: 22,7 triệu views</p><p>14. Sunmi: 21,9 triệu views</p><p>15. HyunA: 21,1 triệu views</p><p>16. aespa: 16.1 triệu views</p><p>17. Eunji (Apink): 13,8 triệu views</p><p>18. Apink: 13,8 triệu views</p><p>19. SNSD: 13,7 triệu views</p><p>20. Rosé (BLACKPINK): 13,7 triệu views</p><p>Nhưng nếu xét top 20 idol Kpop (cả nam lẫn nữ) có tổng view YouTube cao nhất năm 2021 ở Hàn Quốc thì BLACKPINK tụt xuống vị trí thứ 3, kém cả IU và \"không có cửa\" cạnh tranh với người đứng đầu là BTS. SEVENTEEN tuy là Á quân mảng nhóm nam nhưng họ thua 3 girlgroup IZ*ONE, TWICE và (G)I-DLE trên BXH tổng hợp, ngậm ngùi đứng thứ 7 chung cuộc.</p><p>Top 20 idol có tổng view YouTube cao nhất Hàn Quốc năm 2021</p><p>1. BTS: 157 triệu views</p><p>2. IU: 148 triệu views</p><p>3. BLACKPINK: 108 triệu views</p><p>4. IZ*ONE: 50,7 triệu views</p><p>5. TWICE: 46,1 triệu view s</p><p>6. (G)I-DLE: 44,2 triệu views</p><p>7. SEVENTEEN: 41,9 triệu views</p><p>8. Brave Girls: 37,8 triệu views</p><p>9. BIGBANG: 29 triệu views</p><p>10. Red Velvet: 27,9 triệu views</p><p>11. Oh My Girl: 27,5 triệu views</p><p>12. SHINee: 24,5 triệu views</p><p>13. ITZY: 24,5 triệu views</p><p>14. Chungha: 24 triệu views</p><p>15. Taeyeon (SNSD): 23,9 triệu views</p><p>16. GFRIEND: 23,5 triệu views</p><p>17. MAMAMOO: 22,7 triệu views</p><p>18. Sunmi: 21,9 triệu views</p><p>19. HyunA 21.1 triệu views</p><p>20. NCT 127: 19,8 triệu views</p><p>Knet bình luận:</p><p>- Lúc đầu tôi tự hỏi sao ít thế và rồi nhận ra chỉ tính view nội địa mà thôi, haha. BTS điên rồ thật.</p><p>- BTS đi đâu cũng đứng thứ nhất.</p><p>- Wow, BTS và IU lúc nào cũng đứng đầu vậy chắc thấy chán lắm.</p><p>- Tôi luôn cảm thấy BLACKPINK có ít ca khúc, nhưng thật tuyệt khi thấy con số khả quan như vậy.</p><p>- Mọi người cứ không để ý là BIGBANG có thêm view.</p><p>- BTS và IU là 2 ca sĩ yêu thích của tôi. Họ là những lựa chọn hàng đầu.</p><p>- Không phải các nhóm mới comeback sẽ có lợi hơn à?</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/bts-24-1617632633297821465291.jpg\" ><figcaption class=\"caption\">BTS dẫn đầu BXH idol nam và danh sách tổng hợp các idol có tổng view cao nhất năm 2021 tại Hàn Quốc</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/seventeenanodeall1-1617632687438167899605.jpg\" ><figcaption class=\"caption\">SEVENTEEN xếp thứ 2 trong danh sách idol nam</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/bigbang-1617632815999428258440.jpg\" ><figcaption class=\"caption\">BIGBANG lâu không hoạt động nhưng vẫn đứng thứ 3</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/iu-1617632929341601841454.png\" ><figcaption class=\"caption\">IU dẫn đầu mảng view YouTube Hàn Quốc của ca sĩ nữ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/bp-10-16171161605861864161043.jpeg\" ><figcaption class=\"caption\">BLACKPINK được mệnh danh là \"nữ hoàng YouTube\" nhưng chỉ đứng thứ 2</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/bp-8-16176332141821820099186.jpeg\" ><figcaption class=\"caption\">BLACKPINK đứng thứ 3 trên BXH tổng hợp nam nữ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/iu-3-16176332497421096279466.jpeg\" ><figcaption class=\"caption\">IU là Á quân, cô chỉ chịu thua...</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/bts-24-16176332350812063744430.jpeg\" ><figcaption class=\"caption\">... BTS</figcaption></figure><p>Nguồn: Pann - Ảnh: Internet </p>', NULL, 'blackpink-mang-tieng-nam-trum-youtube-nhung-tong-view-tai-han-quoc-nam-2021-thua-iu-khong-co-cua-doi-dau-voi-bts', 0, NULL, NULL),
(87, 13, 4, 1, 1, 'Wendy gây sốc với gương mặt khác lạ trong MV solo: Biểu cảm gượng gạo, đường nét cứng đờ, là di chứng sau vụ tai nạn kinh hoàng?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617617674114-1617617674817996142878.gif.png', '|', 'Giọng hát của Wendy vẫn thật tuyệt vời như thường lệ nhưng nhan sắc rạng ngời, tươi tắn một thời giờ còn đâu.', '<p>Chiều ngày 5/4, MV Like Water đã chính thức được tung ra, đánh dấu sự khởi đầu cho màn debut solo của Wendy. Không hổ danh là main vocal có tiếng của gen 3, Wendy đã phô diễn trọn vẹn giọng hát điêu luyện, giàu cảm xúc cùng với những nốt cao chót vót đầy ấn tượng.</p><p>MV Like Water - Wendy</p><p>Tuy nhiên, phần nghe xuất sắc là thế mà phần nhìn của MV lại khiến cho khán giả có chút lấn cấn. Các cảnh quay được bài trí, sắp đặt hết sức công phu và đẹp mắt, thế nhưng vấn đề nằm ở ngoại hình của Wendy. Cô nàng gây sốc với gương mặt khác lạ, đơ cứng, các đường nét biểu cảm hết sức gượng gạo.</p><p>Đặc biệt là ở những góc nghiêng 3/4, gương mặt trông rất thiếu tự nhiên</p><p>Phần đông mọi người cho rằng, đây chính là di chứng do vụ tai nạn năm xưa để lại. Do sự thiếu trách nhiệm từ phía SBS mà Wendy đã bị ngã từ bàn nâng sân khấu với độ cao gần 2 mét. Cô nàng đã phải chịu không ít đau đớn khi bị rạn xương má phải, gãy xương chậu và cổ tay. Thậm chí còn phải nẹp chặt hàm (còn gọi là dây cung hàm, dùng để kết nối xương hàm dưới và hàm trên lại với nhau) để chữa trị. </p><p>Trải qua nhiều chấn thương như vậy, không dễ để gương mặt của Wendy trở lại hoàn toàn bình thường như khi xưa. Nghĩ vậy mà ai cũng không khỏi xót xa. Nhất là khi nhìn lại hình ảnh của Wendy trong những MV cũ. Dù là phong cách girl crush cool ngầu, hay những bài hát mùa hè sôi động, cô nàng luôn sở hữu những cảnh quay với biểu cảm đa dạng, đầy lôi cuốn, khác xa với hình ảnh đơ cứng trong MV solo.</p><p></p><p></p><p>Khuôn mặt rạng ngời bừng sáng cùng nụ cười tươi roi rói đặc trưng của Wendy trong những MV cũ</p><p>Hay khi đổi sang phong cách girl crush, cô nàng cũng thể hiện được hình ảnh cá tính đầy mê hoặc</p><p>Nhưng giờ đây lại là một khuôn mặt đơ cứng, lạ lẫm thế này...</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/mk-16176188336821743135135.png\" ><figcaption class=\"caption\">Vừa mở đầu MV, khán giả đã cảm thấy là lạ trước khuôn mặt của Wendy</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/chrome-capture-1617617738489292294555.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/tumblr440d1b56c3d233fa762cb49e8dd2c55013d6b8c3540-16176174689181329995227.gif.mp4\" ></video><div class=\"caption\">Liên tiếp những cảnh quay sau đó, gương mặt đều có dấu hiệu sưng phù, biểu cảm đơ cứng thiếu linh hoạt</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/5/mkds-16176188358321533716643.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/5/completeimperturbableflicker-sizerestricted-16176174605831654109506.gif\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/5/tumblrpwid7qvpbn1ysxh74o1540-16176175001181269549341.gif\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/5/unnamed-16176175204751438904143.gif\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/5/main-qimg-87b1a61b9c1db32e5fac951adc8b7efc-1617617461908600625086.gif\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/5/tumblrb67bdbf59c6eb75b599435213bf27592b2d87771640-1617617488058380066451.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Video: YouTube - Ảnh: Internet</p>', NULL, 'wendy-gay-soc-voi-guong-mat-khac-la-trong-mv-solo-bieu-cam-guong-gao-duong-net-cung-do-la-di-chung-sau-vu-tai-nan-kinh-hoang', 0, NULL, NULL),
(88, 13, 4, 1, 1, 'Wendy (Red Velvet) debut solo sau 7 năm mà MV như một \"cú lừa\" từ SM, hát nốt cao đẳng cấp hàng đầu Kpop!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617615119323-16176151203761731769669.gif.png', '|', 'Màn debut của Wendy (Red Velvet) là dấu mốc quan trọng sau khi cô nàng gặp phải tai nạn nghiêm trọng và \"đóng băng\" hoạt động trong một khoảng thời gian dài.', '<p>Đúng 16 giờ ngày 5/4 (giờ Việt Nam), Wendy (Red Velvet) đã chính thức tung MV debut solo của mình mang tên Like Water. Đây là màn debut đánh dấu cột mốc quan trọng khi cô nàng là người đầu tiên của nhóm Red Velvet sau 7 năm ra mắt với vai trò là ca sĩ solo. Bên cạnh đó, đây cũng chính là màn tái xuất của Wendy sau một khoảng thời gian dài \"đóng băng\" hoạt động vì gặp chấn thương.</p><p>MV Like Water - Wendy</p><p>Trong MV Like Water, visual của Wendy đã được khai thác triệt để với nhiều cảnh quay vô cùng đẹp mắt. Tuy nhiên, MV thực sự là một \"cú lừa\" của SM Entertainment khi quý công ty đã tung 1001 bức ảnh teaser ngoại cảnh vô cùng đẹp mắt, từ lên rừng cho đến xuống biển. Thế nhưng, Like Water lại tiếp tục là một sản phẩm \"đóng hộp\" hoàn toàn. </p><p>Bên cạnh visual, Wendy tiếp tục phát huy thế mạnh vocal thông qua ca khúc debut. Bản acoustic pop ballad được thể hiện giọng ca thuần khiết của Wendy, như đã \"đo ni đóng giày\" cho riêng cô nàng. Wendy cũng gây ấn tượng với những nốt cao chót vót trong bài hát.</p><p>Về nội dung, giống như con người không thể sống thiếu nước, Like Water so sánh sự tồn tại và ý nghĩa của con người với nhau, tựa như làn nước trong mát từ những hạt mưa nhỏ tụ lại để tạo thành biển lớn.</p><p>Album Like Water bao gồm 5 bài hát. Bên cạnh Like Water, còn có một ca khúc chủ đề khác là When This Rain Stops, là một bản ballad có tiết tấu chậm, kết hợp trình diễn cùng piano và giọng hát ấm áp của Wendy. </p><p>Wendy chia sẻ: \"Vì lần này là album solo nên mình muốn tất cả bài hát đều có sự kết nối và truyền tải thông điệp, cảm xúc của mình tới mọi người. Mình hy vọng mọi người có thể cảm nhận lời bài hát. Trái tim mọi người sẽ được sưởi ấm khi nghe toàn bộ album\".</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/photo-1-1617614688191623847372.jpg\" ><figcaption class=\"caption\">Khác xa với những tấm hình teaser lên rừng...</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/photo-1-16176147335601239632730.jpg\" ><figcaption class=\"caption\">... ra sông rồi xuống biển...</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/animation-9-16176139727881901580171.gif.mp4\" ></video><div class=\"caption\">... MV của Wendy được quay \"đóng hộp\" hoàn toàn</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/animation-8-16176139726961436901791.gif.mp4\" ></video><div class=\"caption\">Visual ấn tượng của Wendy trong MV</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/animation-11-161761397281549124203.gif.mp4\" ></video><div class=\"caption\">Thế mạnh giọng hát của Wendy được phát huy tối đa trong ca khúc mới</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/5/animation-10-16176139728021290389629.gif.mp4\" ></video><div class=\"caption\">Like Water là bài hát mang nhiều ý nghĩa</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/photo-1-1617614980505920755908.jpg\" ><figcaption class=\"caption\">Wendy cùng Joy tại buổi họp báo ra mắt solo</figcaption></figure><p>Ảnh & Clip: SM</p>', NULL, 'wendy-red-velvet-debut-solo-sau-7-nam-ma-mv-nhu-mot-cu-lua-tu-sm-hat-not-cao-dang-cap-hang-dau-kpop', 0, NULL, NULL),
(89, 14, 4, 1, 1, 'Dương Hoàng Yến gây sốt với clip cover Let It Go, nhưng điểm chú ý là visual khác hẳn lúc tham gia Sao Nhập Ngũ', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617707915846-161770791620459952538.gif.png', '|', 'Dương Hoàng Yến là ca sĩ thuộc trường phái thực lực của Vpop là điều nhiều người đã biết, nhưng hãy tạm bỏ qua chuyện đó trong clip này vì visual của cô nàng mới đáng chú ý.', '<p>Kể từ khi tham gia chương trình Sao Nhập Ngũ, Dương Hoàng Yến đã nhận được rất nhiều tình cảm và sự quan tâm từ người hâm mộ. Các clip cover hay hình ảnh xuất hiện của Dương Hoàng Yến bên các nữ nghệ sĩ tham gia chương trình cũng thu hút lượt tương tác cao. Mới đây, Dương Hoàng Yến đã đăng tải đoạn clip cover ca khúc nhạc phim nổi tiếng Let It Go và nhanh chóng sở hữu đến 1,7 triệu lượt xem. </p><p>Clip Dương Hoàng Yến cover Let It Go</p><p>Là nữ giảng viên thanh nhạc trường Cao đẳng Nghệ thuật Hà Nội, ca khúc Let It Go không hề làm khó Dương Hoàng Yến. Những nốt cao chót vót của ca khúc đều được cô nàng \"xử đẹp\".</p><p>Tuy nhiên, visual của Dương Hoàng Yến mới lại là điều khiến các fan quan tâm hơn cả. So với ngoại hình thời điểm lúc tham gia Sao Nhập Ngũ thì nữ ca sĩ ở thời điểm hiện tại vừa trắng, mũi cao, đường nét khuôn mặt sắc lẹm được đánh giá \"Tây Tây\", xinh đẹp khó nhận ra khác hẳn mặt mộc xạm đen trong show thực tế.</p><p>Bên cạnh ngoại hình xinh đẹp thì một số cộng đồng mạng cũng thừa nhận, dù Dương Hoàng Yến từng ra mắt nhiều sản phẩm âm nhạc và thậm chí có cả bản hit nhưng từ sau Sao Nhập Ngũ, cô nàng mới được nhiều người biết tới. Nét tính cách dễ thương và nghiêm túc thực hiện các thử thách trong chương trình của Dương Hoàng Yến cũng chiếm trọn tình cảm của khán giả. </p><p>Một số bình luận của cộng đồng mạng: </p><p>- Mới đầu còn tưởng người nước ngoài. Nhìn mãi mới nhận ra là Dương Hoàng Yến. </p><p>- Nói thật hồi đầu không biết Dương Hoàng Yến là ai. Từ sau khi xem Sao Nhập Ngũ biết tới cô ấy luôn. Đúng là thời tới cản không kịp mà. </p><p>- Không nhận ra là Dương Hoàng Yến luôn ấy. </p><p>- Xinh đẹp, duyên dáng lại còn tài năng nữa. </p><p>- Nếu không nhìn tên thì sẽ không nhận ra là ai luôn. </p><p>- Những câu lên cao sao thấy cô ấy lên nhẹ nhàng thế nhỉ. </p><p>- Ước gì Yến tham gia Xuân Hạ Thu Đông Rồi Lại Xuân. </p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/screen-shot-2021-04-06-at-32057-pm-16177003973501009254245.png\" ><figcaption class=\"caption\">Đoạn clip cover Let It Go của Dương Hoàng Yến thu về 1,7 triệu lượt xem</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/idealistictallagouti-sizerestricted-1617707121497709934955.gif.mp4\" ></video><div class=\"caption\">Dương Hoàng Yến xuất hiện với gương mặt xinh đẹp tự tin cover Let It Go</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/oldfashionedselfassuredarizonaalligatorlizard-sizerestricted-16177071214071704811672.gif.mp4\" ></video><div class=\"caption\">Những nốt lên tông cao chót vót đều được nữ giảng viên thể hiện</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-1617707538384361250222.jpg\" ><figcaption class=\"caption\">Hình ảnh cô \"chiến sĩ\" Dương Hoàng Yến trong chương trình Sao Nhập Ngũ</figcaption></figure><p>Nguồn: TikTok </p>', NULL, 'duong-hoang-yen-gay-sot-voi-clip-cover-let-it-go-nhung-diem-chu-y-la-visual-khac-han-luc-tham-gia-sao-nhap-ngu', 0, NULL, NULL),
(90, 14, 4, 1, 1, 'Hoàng Dũng hát nhạc chế mà nghiêm túc dễ sợ, còn đóng nguyên bộ vest nhìn kiểu gì cũng thấy sai sai', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617683769260-16176837714831755430344.gif.png', '|', 'Đoạn clip Hoàng Dũng hát chế lời đang được cư dân mạng \"đào\" lại và chia sẻ đầy sự thích thú.', '<p>Mới đây cộng đồng mạng đã \"đào\" lại đoạn clip Hoàng Dũng trình diễn tại đêm nhạc phòng trà cuối năm 2018. Theo đó trong minishow này, nam ca sĩ đã mang tới bản cover ca khúc Tình Thôi Xót Xa dành tặng cho khán giả. Tuy nhiên, đang hát rất mượt ở verse 1, anh chàng bất ngờ khoe luôn màn chế lời với phiên bản \"tán tỉnh\" khiến khán giả vô cùng thích thú. </p><p>Hoàng Dũng cover Tình Thôi Xót Xa (Vertical Video)</p><p>Hoàng Dũng đã thể hiện màn cover ca khúc Tình Thôi Xót Xa với những câu hát chế lời như \"Nhà anh không to, tiền mẹ không hay cho nhưng anh sẽ kiếm em chớ lo\" hay \"Anh chịu khó, anh lại có duyên, soi gái, thích lăng nhăng không phải anh\". </p><p>Đáng chú ý, dù hát lời chế có phần khá \"lầy\" là thế nhưng Hoàng Dũng lại hát cực kì nghiêm túc, ai không nghe kĩ lại nghĩ đây là bản chính thức của ca khúc cũng nên. Đã thế, anh chàng còn \"đóng\" nguyên bộ vest rất chỉnh tề và nghiêm túc không hề hợp với hoàn cảnh chút nào. Thế nhưng chính sự \"ngược đời\" đó lại đem đến cảm xúc thú vị cho khán giả và khiến cho đoạn clip ngay khi được chia sẻ lại nhanh chóng thu hút sự quan tâm.</p><p>Một số bình luận của cộng đồng mạng: </p><p>- Bản chế này nghe nhiều rồi nhưng chưa bao giờ thấy nghiêm túc như bây giờ luôn á haha.</p><p>- Chế mà cũng dễ thương. Không thể không nghiện giọng Hoàng Dũng hơn được.</p><p>- Hic ăn gì hát hay thật luôn. Lời chế cũng ngọt.</p><p>- Anh hát chế nhưng vẫn không quên mặc vest nha quý vị. </p><p>- Hát live quá đỉnh, chế lời nghe cũng cưng xỉu. </p><p>- Hồi hộp nghe Dũng hát chế lời kkk. </p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/animation-16176834008502033084767.gif.mp4\" ></video><div class=\"caption\">Hoàng Dũng trình diễn Tình Thôi Xót Xa trong đêm nhạc</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/animation-1-16176834008311514984101.gif.mp4\" ></video><div class=\"caption\">Nhưng lại có màn hát chế lời trong nguyên cây vest vô cùng nghiêm túc</figcaption></figure><p>Nguồn: TikTok, YouTube </p>', NULL, 'hoang-dung-hat-nhac-che-ma-nghiem-tuc-de-so-con-dong-nguyen-bo-vest-nhin-kieu-gi-cung-thay-sai-sai', 0, NULL, NULL),
(91, 14, 4, 1, 1, 'Văn Mai Hương một mình hát Always Remember Us This Way liệu có hay hơn bản mash-up với Hoà Minzy?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617752937456-161775293782299939907.gif.png', '|', 'Từng gây \"bão\" là thế nhưng có gì khác biệt khi Văn Mai Hương một mình hát Always Remember Us This Way của Lady Gaga?', '<p>Văn Mai Hương vốn đã sở hữu giọng ca ấn tượng, đầy nội lực của mình nhưng gần đây, bản mash-up cover Ngày Chưa Giông Bão và Always Remember Us This Way hát cùng Hoà Minzy đã giúp tiếng tăm của nữ ca sĩ đi lên một tầm cao mới. Sau khi bản mash-up tại show Xuân Hạ Thu Đông Rồi Lại Xuân \"gây bão\", rất nhiều ý kiến trái chiều đã được nêu ra, đa số đều cho rằng Văn Mai Hương hát nhỉnh hơn Hoà Minzy và rất nhiều fan mong sẽ được thưởng thức riêng bài cover của Văn Mai Hương. Cho đến tối ngày 6/4, dân tình đã được chiêu đãi bản hát live acoustic chỉ một mình cô nàng rồi đây.</p><p>Always Remember Us This Way (A Star Is Born OST) - Văn Mai Hương cover</p><p>Một mình thể hiện Always Remember Us This Way, Văn Mai Hương hoàn toàn tự do khoe những kỹ thuật giọng hát ấn tượng mà không vướng bận điều gì. Dân tình không khỏi phát sốt với màn hát live nội lực và dạt dào cảm xúc từ Văn Mai Hương. Nhiều người thoả mãn khi được nghe trọn vẹn Always Remember Us This Way do Văn Mai Hương hát mà không cần phải \"tua\" nữa.</p><p>Thế nhưng, cũng không ít ý kiến trái chiều xung quanh bản cover này. Nhiều bình luận cho rằng bản mash-up cover Ngày Chưa Giông Bão và Always Remember Us This Way có sự góp giọng của Hoà Minzy hay và cảm xúc hơn. Band nhạc chơi tại chương trình Xuân Hạ Thu Đông Rồi Lại Xuân cũng được đánh giá cao, góp một phần lớn thành công cho bản mash-up.</p><p>Bản mash-up Ngày Chưa Giông Bão và Always Remember Us This Way với sự kết hợp giữa Hoà Minzy và Văn Mai Hương</p><p>Một vài ý kiến khác lại liên tục réo gọi cái tên Lady Gaga. Không phải vì muốn so sánh gì mà bởi lẽ, dân tình kêu gọi Văn Mai Hương hãy ghi tên Lady Gaga để thể hiện sự tôn trọng đối với tác giả ca khúc gốc.</p><p>Văn Mai Hương đã tiếp thu ý kiến từ netizen. 2 giờ sáng, Erik gọi ai không bắt máy thì không biết nhưng Văn Mai Hương vẫn còn thức, đã đồng loạt chỉnh sửa lại phần tiêu đề, caption clip trên YouTube và Facebook, bổ sung tên nghệ sĩ sáng tác Lady Gaga.</p><p>Có thể thấy, Văn Mai Hương là một nghệ sĩ rất biết lắng nghe và tiếp thu ý kiến của người hâm mộ. Một điểm cộng cho hành động này của Văn Mai Hương đúng không nào?</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/animation-14-1617752347200705081840.gif.mp4\" ></video><div class=\"caption\">Một mình thể hiện Always Remember Us This Way, Văn Mai Hương tiếp tục chinh phục người nghe bằng giọng hát nội lực của mình</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/animation-16-16177523473201188221218.gif.mp4\" ></video><div class=\"caption\">Phiên bản mới mẻ cùng ban nhạc acoustic</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070325-16177538376201153473340-161775406341435247471.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070332-16177538376531288160683-16177540845171798011518.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070328-1617753837638698130790-1617754103859352615804.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070319-1617753837305113669777-16177541217331113122332.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070322-1617753837317947299313-16177541341661196891498.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070311-1617753837222175779157.jpg\" ><figcaption class=\"caption\">Ban đầu, Văn Mai Hương không để tên nghệ sĩ sáng tác và thể hiện ca khúc gốc</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/anh-chup-man-hinh-2021-04-07-luc-070506-16177540073881840128504.png\" ><figcaption class=\"caption\">Sau đó, cô nàng đã bổ sung đầy đủ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2021-04-07-070314-16177538372971379392304.jpg\" ><figcaption class=\"caption\">Ảnh chụp lúc 6 giờ sáng, tức là cô nàng đã chỉnh sửa caption từ 2 giờ</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/animation-15-16177523472172057013938.gif.mp4\" ></video><div class=\"caption\">Bạn thấy bản hát live acoustic một mình của Văn Mai Hương ra sao?</figcaption></figure><p>Nguồn & Clip: Văn Mai Hương - Ảnh: Chụp màn hình</p>', NULL, 'van-mai-huong-mot-minh-hat-always-remember-us-this-way-lieu-co-hay-hon-ban-mash-up-voi-hoa-minzy', 0, NULL, NULL),
(92, 14, 4, 1, 1, 'Mừng MV Lối Nhỏ 100 triệu view, Đen Vâu sáng tác luôn lyrics mới lại còn remix cho \"sang\"', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617732399664-1617732399953109547726.gif.png', '|', 'Phiên bản Lối Nhỏ hoàn toàn mới đã được Đen Vâu \"thả thính\" trên trang cá nhân và nhanh chóng thu hút sự quan tâm.', '<p>Ít ngày vừa qua, cộng đồng fan Đồng Âm của Đen Vâu đã vô cùng phấn khích khi MV Lối Nhỏ đã chính thức cán mốc 100 triệu lượt xem. Để ăn mừng thành tích này, tối 6/4 Đen Vâu đã dành tặng món quà nhỏ cho người hâm mộ bằng một bản rap live Lối Nhỏ phiên bản remix. </p><p>Clip Đen Vâu hé lộ đoạn rap Lối Nhỏ phiên bản remix</p><p>Trong đoạn clip, tưởng chừng như Đen Vâu biến tấu ca khúc gốc Lối Nhỏ thành phiên bản remix nhưng khi nghe kỹ, phần lyrics đã được nam rapper sáng tác mới hoàn toàn. Vẫn là những tiếng hát của Phương Anh Đào cất lên nhưng verse rap đã được Đen Vâu viết lại với nhiều câu rap chất lừ như \"Em vào đời từ phố đông, còn anh vào đời từ xóm nhỏ.  Đường em đi có biển báo, còn đường anh đi anh tự mò\". Bên cạnh đó, khi những giai điệu của ca khúc Lối Nhỏ được remix, người nghe bỗng thấy \"sang\" kỳ lạ.</p><p>Trên trang cá nhân, Đen Vâu cũng đăng tải lyrics mới để \"thả thính\" cho một phiên bản chưa từng có của Lối Nhỏ. Dù chỉ hé lộ khoảng 22 giây nhưng nhiều người đang rất mong chờ Đen Vâu sẽ sớm tung ra bản full của version đặc biệt.</p><p>Trước đó vào trưa ngày 3/4 vừa qua, MV Lối Nhỏ của Đen Vâu đã chính thức cán mốc 100 triệu views. Ra mắt ngày 21/10/2019, như vậy sau khoảng thời gian 1 năm 6 tháng, sản phẩm kết hợp giữa Đen Vâu và Phương Anh Đào đã xuất sắc góp mặt trong danh sách MV 100 triệu views của Vpop.</p><p>Với thành tích này, Đen Vâu cũng có cho mình MV thứ 2 đạt 100 triệu views, trước đó là Bài Này Chill Phết kết hợp cùng nữ ca sĩ Min.</p><p>Đen - Lối Nhỏ ft. Phương Anh Đào (M/V)</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/focusednervousasianporcupine-sizerestricted-16177320188691261958334.gif.mp4\" ></video><div class=\"caption\">Để ăn mừng thành tích 100 triệu views, Đen Vâu \"chơi lớn\" sáng tác luôn lyrics mới</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/screen-shot-2021-04-07-at-10510-am-16177323273691954514099.png\" ><figcaption class=\"caption\">Lyrics mới toanh của Lối Nhỏ được Đen Vâu chia sẻ trên trang cá nhân</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/screen-shot-2021-04-07-at-10251-am-1617732185661650528610.png\" ><figcaption class=\"caption\">Thành tích lượt xem hiện tại của ca khúc Lối Nhỏ</figcaption></figure><p>Nguồn: FB, YouTube </p>', NULL, 'mung-mv-loi-nho-100-trieu-view-den-vau-sang-tac-luon-lyrics-moi-lai-con-remix-cho-sang', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(93, 14, 4, 1, 1, 'Anh Tú: \"Hòa Minzy không hề bị lu mờ, chỉ là do chị Uyên Linh và Văn Mai Hương hát quá hay mà thôi\"', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617711771971-16177117722162049523057.jpg', '|', 'Anh Tú cũng cho biết, thời điểm anh rời khỏi cuộc thi Giọng Hát Việt, anh từng rơi vào trầm cảm và cảm thấy \"sợ hãi\" trước showbiz.', '<p>Rời khỏi Giọng Hát Việt với ngôi vị Á quân, Anh Tú dấn thân vào con đường ca hát chuyên nghiệp với một số bản hit được nhiều người biết đến như: Mạnh Mẽ Lên Cô Gái, Cuộc Sống Em Ổn Không,... Tuy nhiên, một vấn đề không chỉ Anh Tú mà khá nhiều nghệ sĩ gặp phải, đó là: Khán giả nhớ ca khúc, thuộc làu làu nhưng mặt mũi người trình diễn hỏi 10 người chắc chỉ 1-2 người biết.</p><p>Vận may mỉm cười khi Anh Tú xuất hiện tại Rap Việt và sau đó là Xuân Hạ Thu Đông Rồi Lại Xuân, 2 show ban đầu không ai nghĩ có thể sẽ \"bùng nổ\" song lại thành công rực rỡ, đưa tên tuổi những người tham dự lên một tầm cao mới. Anh Tú qua đó cũng có một bước ngoặt quan trọng trong sự nghiệp và anh đã tìm cách nắm bắt lấy cơ hội này để tiến lên.</p><p>Chúng tôi đã có cuộc trò chuyện thẳng thắn với Anh Tú để biết được hành trình khá trắc trở của anh kể từ sau khi rời cuộc thi Giọng Hát Việt, về hoài bão lớn với âm nhạc cũng như khát khao được trò chuyện với từng người hâm mộ.</p><p>Thời điểm chúng ta đang ngồi đây, show Xuân Hạ Thu Đông Rồi Lại Xuân cũng đã khép lại, để lại nhiều cảm xúc đẹp đẽ với công chúng. Với Anh Tú, bạn có nghĩ đây tiếp tục là một cột mốc đáng tự hào trong sự nghiệp của mình?</p><p>Hôm ấy tôi đang thực hiện một MV trên Đà Lạt thì nhận được mail từ một chương trình do ekip Hàn Quốc thực hiện. Ngay từ khi đọc format của chương trình, tôi đã cảm thấy thích thú và đồng ý ngay vì phía show yêu cầu hát nhạc nhẹ nhàng, đặc biệt là phải hát live hoàn toàn. Ngay sau đó là một cuộc gọi video call ngay trên xe ô tô với ekip chương trình vì mọi thứ khi ấy đang vô cùng gấp gáp. Tôi lúc ấy cũng đang có sự cộng hưởng từ chương trình Rap Việt nên được nhớ tới ngay khi họ muốn tìm một giọng nam đáp ứng tiêu chí của Xuân Hạ Thu Đông Rồi Lại Xuân.</p><p>Nhớ lại thời điểm ghi hình thì lịch trình rất căng, mất thời gian nhất ở khâu chọn bài để ca khúc phù hợp với chủ đề tập lên sóng, tôi cũng có không ít lần phải đổi bài hát ngay phút chót. Thời gian luyện tập sau đó rất ngắn, chỉ được 1-2 ngày nên khi lên hình rất nhiều lúc khán giả thấy chúng tôi phải nhìn lời. Cực nhưng đáng nhớ vì tình cảm giữa tôi với Hứa Kim Tuyền và Hoà Minzy thêm phần bền chặt đến cả trong cuộc sống. Tôi học hỏi được rất nhiều từ ekip Hàn Quốc, một ekip thực sự chỉn chu và quan tâm nghệ sĩ đến từng tí một. Bạn có tin là họ chuẩn bị sẵn quyển số ghi lời bài hát cho nghệ sĩ, được kẹp lại đàng hoàng. Bình nước cá nhân luôn được châm đầy mà không cần phải hỏi. Điều này giúp cho các nghệ sĩ thoải mái diễn, thoải mái chơi đùa và “phiêu” khi hát. Ekip Việt Nam đã rất ổn rồi nhưng rõ ràng những điều tốt của ekip Hàn Quốc thì vẫn phải nên học hỏi.</p><p>Xuân Hạ Thu Đông Rồi Lại Xuân tập trung hoàn toàn vào âm nhạc, khán giả đến với show không vì những “drama” xoay quanh. Tôi được hát, được truyền cảm hứng và trao động lực cho người nghe qua âm nhạc. Đó đã là niềm hạnh phúc lớn nhất của một người nghệ sĩ. Hiện tại, sau khi show khép lại, tôi có rất nhiều cảm xúc: Nhớ nhung, buồn bã, tiếc nuối và thực sự rất nhớ mọi người. Khoảnh khắc cả “gánh hát” cúi đầu chào các bạn sinh viên, khi các ánh đèn flash xuất hiện phía dưới, lúc ấy chỉ muốn cầm micro lên và hát tiếp thôi, chẳng muốn dừng lại. </p><p>Không thể không nhắc đến những áp lực khổng lồ mà Hoà Minzy đã phải trải qua xuyên suốt chương trình, đặc biệt là về giọng hát. Với tư cách một người anh trong nghề, bạn có thấy những nhận xét của netizen về Hoà Minzy có phần quá nặng nề?</p><p>Tôi biết Hoà Minzy được hơn 11 năm. Cô ấy là một cô gái rất tuyệt vời, thông minh, lanh lợi nhưng lại suy nghĩ quá nhiều. Tôi phần nào cũng hiểu cho em ấy khi không may dính phải khá nhiều scandal không đáng có.</p><p>Đối với tôi, giọng của Hoà như thế là đã hay rồi, mỗi người theo đuổi một cách hát khác nhau, không thể nào tự ép bản thân được. Hoà Minzy không hề bị lu mờ chỉ là do chị Uyên Linh và Văn Mai Hương hát quá hay mà thôi. Những năm trước, giọng của Hoà Minzy rất “chung chung”, khó phân biệt giữa muôn vàn giọng hát ở Vpop, nhưng giờ đây đã khác, có cái “signature” (dấu ấn) bộc lộ rõ nét, hát lên 1 tiếng là phân biệt được ngay, tương tự anh Lam Trường, Khánh Phương, Đạt G,... nghe qua là nhận ra ngay.</p><p>Nhưng có không ít những người vốn không có giọng hát bẩm sinh tốt vẫn quyết định đi theo con đường ca hát. Sự “liều lĩnh” này rất nhiều lần làm công chúng cảm thấy khó chịu, anh nghĩ thế nào về vấn đề này?</p><p>Trước hết, đó đã là một sự đam mê. Tôi rất ít khi so sánh cái này với cái kia vì tôi chỉ muốn thấy những người ấy tiến bộ mà thôi, như thế đã rất giỏi rồi. Một người có giọng hát sẵn, việc cầm micro và ngân nga một bài trở nên quá đơn giản. </p><p>Nhưng với những người giọng yếu hoặc không có điểm nhấn riêng cho cá nhân thì họ sẽ gặp không ít thì nhiều các vấn đề tâm lý, không thể nào thoải mái cầm micro thậm chí còn phải tính toán từng câu chữ khi hát. Với những người như thế, tôi nghĩ cứ tiếp tục sống với đam mê và nhiệt huyết của mình, việc tự đứng trên đôi chân đã là một sự cống hiến rồi. Một người muốn theo nghệ thuật thì chí ít phải có nền tảng căn bản. Còn những ai không hề có chút năng khiếu hay tố chất nghệ thuật nào nhưng vẫn “đâm đầu” thì tôi không ủng hộ. </p><p>Bạn nói Hoà đã có “signature” trong giọng hát, vậy với giọng hát của riêng Anh Tú đã có cái “signature” ấy chưa?</p><p>Tôi nghĩ là rồi. Giọng của tôi sẽ trầm trầm, hơi ấm, khào một chút và hơi xước giọng. Dòng nhạc chính theo đuổi sẽ vẫn là pop ballad nhưng có một chút funky và r&b. Đây cũng là câu hỏi tôi thường hỏi những người xung quanh: “Liệu giọng tôi nghe qua có nhận ra ngay được không?”. Vấn đề “signature” này không phải cứ cố gắng là có được mà nằm ở bản chất vốn dĩ của người nghệ sĩ, chỉ có thể tập luyện để cho hay hơn. Nếu bản chất nó vốn đã “đều đều” thì nghe chẳng nhận ra được đâu.</p><p>Trong showbiz Việt, tôi cực kì thích màu giọng của anh Noo Phước Thịnh và anh Trúc Nhân. Tôi nói rất chân tình: Chẳng biết có phải do anh Noo trải nghiệm cuộc sống nhiều hơn, học hành nhiều hơn hay bỗng “giác ngộ” về giọng hát mà lại hát hay quá, vừa kĩ thuật, vừa nhẹ nhàng, vừa tình cảm. Còn Trúc Nhân thì tôi thấy do anh ấy “chơi chơi” với âm nhạc thế thôi chứ thừa khả năng trở thành một ngôi sao rất lớn, nhấn mạnh là rất lớn. Một số giọng hát tôi đánh giá cao có anh Trung Quân Idol và Đức Phúc nữa.</p><p>Sau chương trình, với chất giọng và hình ảnh ấy, tôi nhận được rất nhiều lời mời đi diễn, thậm chí mời đầu quân về công ty quản lý nữa. Trước đó, tôi bị cuốn theo hình tượng “unisex” rất mạnh, đó là thời điểm tôi cảm thấy mình phải cố gồng lên và không được là chính mình. Ngay cả khi rời khỏi 6th Sense Entertainment, tôi vẫn cố theo đuổi phong cách unisex một thời gian và lầm tưởng rằng khán giả sẽ yêu thích. Cho đến một ngày, tôi tự đặt câu hỏi với chính mình: “Thực tâm, tôi muốn hình ảnh của mình như thế nào?”</p><p>Tôi cảm thấy mình đã có độ tuổi trưởng thành nhất định nên cần một thứ gì đó thể hiện rõ “cái tôi” hơn. Và “cái tôi” của Anh Tú rất đàn ông, tôi quyết định bộc lộ điều ấy bằng cách cắt tóc, trang phục mặc trên người cũng nhẹ nhàng hơn và rõ ràng khán giả dường như yêu hình tượng ấy của tôi hơn. Tôi nhận ra công chúng thích những thứ chân thật, không cần phải diễn và ngược lại, tôi càng muốn gần khán giả hơn để lắng nghe và tâm sự cùng họ. </p><p>Càng làm nghệ thuật, tôi càng sợ sự so sánh bản thân với một điều gì đó vì nó làm chết đi tâm hồn của từng người, khiến mood tuột xuống, cuộc sống trở nên trì trệ. Đó chính là cảm xúc của tôi sau khi tham dự cuộc thi Giọng Hát Việt, tôi không muốn nói chuyện hay giao du với bất kì ai và gần như bị trầm cảm. </p><p>Anh đã từng trải qua trầm cảm sau khi tham dự Giọng Hát Việt? </p><p>Trong đêm Chung kết, tôi đã từng bất ngờ lẫn hạnh phúc khi được xướng tên trở thành Á quân của chương trình, đó là năm mà Ali Hoàng Dương đăng quang ở ngôi vị cao nhất, tôi cùng Hiền Hồ ở vị trí thứ hai. Hiện tại tôi vẫn giữ quan hệ rất tốt với Ali lẫn Hiền Hồ và vẫn là những người bạn tốt của nhau.</p><p>Nhưng sau đó, khi tôi bước chân vào showbiz, tôi thấy nhiều thực tế ở thế giới này khiến tôi không thể tin được vào mắt mình, những điều mà từ trước đến nay trong cuộc sống tôi chưa bao giờ nghĩ mình sẽ chứng kiến.</p><p>Nói một cách nhẹ nhàng hết sức có thể thì có người nói “thương mình” nhưng sau lưng lại nói “không thương mình”. Tôi không muốn nói nặng lời nhưng phải thẳng thắn: Ít người sống thật lắm. Tôi sợ cảm giác ấy và ngày càng chìm sâu trong những suy nghĩ tiêu cực về showbiz, chỉ muốn ở nhà và chẳng muốn làm gì cả. Nhưng hiện tại, sau 4 năm làm việc thì tôi đã nhận ra bất kì môi trường nào cũng có người này, người kia. Nơi đâu cũng sẽ có những người hai mặt và tôi chấp nhận điều ấy. Những năm qua, tôi đã biết chọn lọc các mối quan hệ hơn, tôi biết thế nào là đủ và xử lí những luồng dư luận không tốt. </p><p>Tôi đã tự vượt qua chính giai đoạn đó nhờ những người anh em thân thiết. Đó là một ngày Tết, tôi đến thăm những anh em nghệ sĩ miền Bắc không về quê ăn Tết mà chọn ở lại Sài Gòn, một “truyền thống” giúp cho những người con xa quê vơi bớt nỗi nhớ. Tôi đến và nhìn một vòng, tự hỏi: “Sao mọi người lại vui thế nhỉ?”. Nhìn mọi người đàn hát với nhau vô cùng vui vẻ, tôi như tỉnh thức trở lại sau một cơn ác mộng dài. Tôi chăm sang quây quần với anh em hơn và đã có được niềm vui vực dậy sau quãng thời gian tăm tối. Bây giờ thì hết rồi, tôi giờ đã là một con người lạc quan và tôi nhìn nhận mọi thứ xung quanh một cách tích cực.</p><p>Nói về The Voice một chút, anh có thấy chất lượng thí sinh sau mùa thi của anh giảm sút? Bằng chứng là 2 Quán quân của 2 mùa gần nhất gần như “mất hút” trên bản đồ nhạc Việt thời gian gần đây với những sản phẩm không mấy thành công?</p><p>Đây là một guồng quay. Tôi thấy chương trình nào đã quá dài rồi cũng sẽ có sự mai một về ý tưởng. Khán giả có quyền chọn những chương trình khác phù hợp với họ hơn để xem. Nói một cách thẳng thắn thì tôi cũng khá tiếc cho Ngọc Ánh và Đức Thịnh khi các bạn ít xuất hiện tại các sự kiện. Nhưng tôi mong các bạn có thể nhận ra rằng: khi việc thắng một cuộc thi không còn là một bệ phóng lớn trong sự nghiệp thì hãy biết đứng vững trên đôi chân của chính mình. </p><p>Đừng than thở vì tôi không hề thích than thở và chẳng ai quan tâm những người mãi than thở đâu. Tôi nói rất thật, giả dụ đoạt giải rồi nhưng quay lại nói chuyện với tôi rồi than thở: “Đoạt giải rồi nhưng chả biết làm gì tiếp theo và cũng không biết làm sao để nổi tiếng”. Cái đấy hãy tự trả lời đi vì đó thậm chí là câu không nên hỏi luôn đấy chứ! Làm nghệ thuật là phải cống hiến không dừng lại, tự đứng trên chính đôi chân và bớt đi chơi lại. </p><p>Nhưng nói đi cũng phải nói lại, từ Giọng Hát Việt gần đây có nổi lên cái tên của bé Juky San đấy, cũng là Á quân tương tự tôi. Cô bé có giọng hát nhẹ nhàng, đặc trưng và có tư duy. Tôi đánh giá cô bé sẽ tiếp tục “gây bão” trong thời gian tới đấy. </p><p>Nhiều người đánh giá việc tham gia Rap Việt chính là bước ngoặt lớn trong sự nghiệp của Anh Tú, mở ra nhiều cơ hội và tạo nên chỗ đứng của anh trong thời điểm hiện tại?</p><p>Lúc đầu tôi tham gia đơn thuần chỉ nghĩ đến việc sẽ giúp đỡ các bạn rapper trẻ thôi, chả nghĩ gì sâu xa. Nhưng khi chương trình lên sóng thì hiệu ứng kinh khủng như mọi người đã thấy. Tôi nhận được rất nhiều từ Rap Việt: đi diễn nhiều, cát xê lên, độ phủ sóng nhiều hơn và sự độc lập về hình ảnh cao hơn. </p><p>Tất cả những màn tôi góp giọng đều mang lại những cảm xúc khác nhau nhưng ấn tượng nhất hẳn vẫn là Đôi Mắt. Tôi rất vui khi GDucky đã gửi cho tôi những lời cảm ơn chân tình trên chương trình Sóng 21 vì đã góp phần làm nên thành công cho màn trình diễn Đôi Mắt của cậu ấy. Tôi chẳng đặt quá nặng việc các bạn sẽ cảm ơn vì tôi nghĩ mình làm để hỗ trợ đàn em, cống hiến vì nghệ thuật mà thôi thế nên tôi rất trân trọng khi GDucky cảm ơn và ôm tôi ngay trên sóng truyền hình.</p><p>Khi tôi ra mắt Cuộc Sống Em Ổn Không, lượt view YouTube lên đến 155 triệu chứ không hề thấp nhưng rất ít người nhận ra mặt của tôi. Sau khi tham dự Rap Việt thì hình ảnh của tôi được phổ cập hơn, mọi người biết đến nhiều hơn. Đó là một sự may mắn quá lớn của tôi khi cả Rap Việt hay Xuân Hạ Thu Đông Rồi Lại Xuân thời điểm trước khi phát sóng, chẳng ai nghĩ cả 2 show sẽ “bùng nổ” đến như vậy. </p><p>Một thời gian khá lâu rồi, khán giả vẫn chưa có được một sản phẩm âm nhạc chính thức từ Anh Tú trong khi hiệu ứng từ Rap Việt và sau đó là Xuân Hạ Thu Đông Rồi Lại Xuân rất lớn? Chuyện gì đã xảy ra?</p><p>Kể từ sau Mạnh Mẽ Lên Cô Gái, tôi muốn âm nhạc của tôi không chỉ mang tính tự sự mà còn phải là một chỗ dựa tinh thần cho khán giả khi cần thiết. Lẽ ra hồi Tết sẽ có một sản phẩm rồi nhưng vì nhiều lí do mãi vẫn chưa thể ra mắt. Đến khi ra mắt được thì tôi lại thấy thời điểm không còn phù hợp nữa. Sau đó có ra mắt thêm ca khúc Nếu Ta Không May cùng với LyLy thì cũng... không may theo luôn. Nhưng nhìn chung, s ản phẩm sắp tới thì tôi đang cực kì tâm đắc, sẽ mang một chút màu sắc ma mị, cổ trang và tôi quyết định sẽ “chơi lớn”.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/ngang-16177117673921114672644.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a1564-16177133294001646317001.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a1522-16177133295681495325524.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a1864-16177131883041019704417.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/q1-161771279579960973954.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a1835-16177132077021547884949.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a1605-16177133720591925633997.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/q2-1617729266437631462987.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/q3-1617712826858373014192.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a1826-16177132326361648022209.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/350/203336854389633024/2021/4/6/q4-ben-16177128909431150444896.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/q5-1617712916820561277322.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/q6-1617712948044724434544.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/q7-16177129682451652370379.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a1816-1617713254923934214238.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a1736-1617713405608648319242.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/350/203336854389633024/2021/4/6/q8-1617712994641107730448.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a1781-16177132739681837100668.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a1769-16177132930921134467622.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/q100-16177130247372138743576.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Cảm ơn anh về cuộc trò chuyện này!</p>', NULL, 'anh-tu-hoa-minzy-khong-he-bi-lu-mo-chi-la-do-chi-uyen-linh-va-van-mai-huong-hat-qua-hay-ma-thoi', 0, NULL, NULL),
(94, 14, 4, 1, 1, 'Jack tung teaser MV đậm chất \"giang hồ\" kèm lời tuyên bố cực gắt: \"Em mong giống anh sao, sorry anh ở trên cao\"', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617720288468-1617720289944314096759.gif.png', '|', 'Teaser MV hé lộ những phân cảnh đầu tiên trong sản phẩm comeback của Jack đã chính thức lên sóng.', '<p>Chiều 6/4, Jack bất ngờ đăng dòng trạng thái bâng quơ hẹn fan vào thời điểm 8-9h tối để dành tặng món quà bí mật. Khi tất cả đã \"sửa soạn một tâm hồn đẹp\" thì vẫn không thấy bóng dáng của Jack đâu. Trong lúc cộng đồng fan Đom Đóm những tưởng đây là một \"cú lừa\" thì Jack đã tung ra teaser hé lộ concept cho sản phẩm âm nhạc có hashtag #LLL.</p><p>Jack - J97 | LLL | Teaser Concept</p><p>Teaser MV dài khoảng 30 giây hé lộ những phân cảnh đậm chất \"giang hồ\" của Jack. Chỉ ngay từ giây đầu tiên, nam ca sĩ đã bước xuống từ xe hơi hạng sang và không nói không rằng liền ra tay đấm một người khác.</p><p>Những phân cảnh sau đó cho thấy Jack có kha khá đàn em và luôn mang trên mình biểu cảm đầy sự bí ẩn. Đáng chú ý, câu nói của Jack trong teaser cũng cho thấy anh đang có những lời tuyên bố cực \"gắt\" như: \"Việc mà mỗi ngày các em cố vùi dập anh chỉ giúp anh giàu thêm và nhét đầy túi mà thôi\" hay \"Em mong em giống anh sao. Sorry anh ở trên cao\".</p><p>Cho đến thời điểm hiện tại, tựa đề của ca khúc lần này vẫn được Jack hoàn toàn giữ kín. Cùng chờ đón sản phẩm comeback của Jack sẽ được ra mắt vào lúc 20h09 ngày 12/4 tới đây.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo-1-16177205969572024554474.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/animation-4-1617720067794311303672.gif.mp4\" ></video><div class=\"caption\">Jack xuất hiện đậm chất \"giang hồ\" thẳng tay đấm đàn em</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/animation-5-1617720067771815769187.gif.mp4\" ></video><div class=\"caption\">Không chỉ có biểu cảm bí ẩn, Jack còn được cánh phóng viên luôn luôn săn đón</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/6/animation-6-1617720067760418128125.gif.mp4\" ></video><div class=\"caption\">Những giây phút ngông nghênh của Jack cuối teaser cùng lời tuyên bố \"Sorry anh ở trên cao\" gây nhiều sự chú ý</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/screen-shot-2021-04-06-at-93558-pm-16177200675081521500885.png\" ><figcaption class=\"caption\">Thời gian - địa điểm đã được Jack hé lộ ở những giây cuối teaser MV</figcaption></figure><p>Nguồn: YouTube</p>', NULL, 'jack-tung-teaser-mv-dam-chat-giang-ho-kem-loi-tuyen-bo-cuc-gat-em-mong-giong-anh-sao-sorry-anh-o-tren-cao', 0, NULL, NULL),
(95, 14, 4, 1, 1, 'Rap Việt Concert công bố poster dàn line-up: Chỉ có 8 thí sinh Chung kết, những cái tên hot như 16 Typh, Yuno Bigboi... sẽ vắng mặt?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617707160017-1617707160403531401281.png', '|', 'Chỉ còn chưa đầy 1 tuần nữa, Rap Việt Concert sẽ chính thức diễn ra song dàn line-up có khiến bạn lo lắng?', '<p>Như vậy, chỉ còn chưa đầy 1 tuần lễ nữa, Rap Việt All-Star Concert sẽ chính thức diễn ra sau khi bị hoãn vì tình hình dịch bệnh. Có thể nói, kể từ khi Rap Việt kết thúc vào tháng 11 năm ngoái, công chúng thực sự mong muốn có một sự kiện quy tụ đầy đủ những rapper các thế hệ được vô cùng yêu mến xuyên suốt thời gian vừa qua.</p><p>Tuy nhiên, khi \"soi\" tấm poster được Rap Việt cho đăng tải gần đây nhất trên các nền tảng MXH, có thể nhận ra đôi điều bất ổn, đặc biệt là về số lượng nghệ sĩ sẽ xuất hiện tại đêm nhạc diễn ra ngày 10/4 sắp tới.</p><p>Trên tấm poster được đăng tải vào tối 5/4 có thể thấy sự có mặt của Trấn Thành cùng những cái tên quen thuộc trên \"ghế nóng\" gồm Giám đốc Âm nhạc Touliver, 2 Giám khảo JustaTee - Rhymastic, 4 vị HLV Wowy - Karik - Binz - Suboi. Bên cạnh đó là 8 cái tên góp mặt tại đêm Chung kết Rap Việt gồm Quán quân Dế Choắt, Á quân GDucky và MCK, Tlinh, Lăng LD, Ricky Star, Gonzo và Thành Draw.</p><p>Bên cạnh đó, trong đoạn clip luyện tập cho  Rap Việt Concert vừa được phía chương trình cho đăng tải vào ngày 4/4, có thể thấy thêm 1 số nhân vật sẽ xuất hiện gồm Lor, Yuno Bigboi và Duy Andy. Nhiều cái tên đình đám khác như RTee, 16 Typh, Tony D, Nul, Hydra,... đều hoàn toàn vắng bóng trên poster lẫn buổi tổng duyệt.</p><p>Karik hội ngộ dàn rapper tăng động không phanh | Live Concert Rap Việt All-Star</p><p>Không loại trừ khả năng phía ekip đang \"ém hàng\", chưa giới thiệu hết dàn line-up để giành cho khán giả sự bất ngờ. Song, trong lúc chỉ còn 4-5 ngày nữa là sự kiện diễn ra, việc thiếu vắng những cái tên nói trên khiến công chúng vẫn thiên về khả năng họ sẽ không xuất hiện nhiều hơn.</p><p>Trước đó, thông tin về dàn line-up của Rap Việt Concert thực ra chưa bao giờ được phía ekip xác nhận đầy đủ cả, chỉ thông tin chung chung \"dàn sao Rap Việt hội ngộ\" dẫn đến lầm tưởng về việc toàn bộ các thí sinh của cả 4 đội sẽ cùng trở lại sân khấu. Tuy nhiên, trước tình hình hiện tại, có vẻ như Rap Việt All-Star Concert sẽ khó quy tụ đầy đủ tất cả thí sinh của cả 4 đội.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/photo2021-04-0613-12-14-1617704281485403790966-16177045780141519088081.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Ảnh: Rap Việt</p>', NULL, 'rap-viet-concert-cong-bo-poster-dan-line-up-chi-co-8-thi-sinh-chung-ket-nhung-cai-ten-hot-nhu-16-typh-yuno-bigboi-se-vang-mat', 1, NULL, NULL),
(96, 15, 4, 1, 1, 'HOT: Rap Việt All-Star Concert chính thức trở lại, ngày và giờ đã được ấn định!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/1/photo1617285677573-1617285677821115193850.jpg', '|', 'Rap Việt All-Star Concert vừa được phía BTC thông báo sẽ chính thức được tổ chức trong vài ngày tới.', '<p>Vài phút trước, trên fanpage của chương trình Rap Việt đã đăng tải chính thức đến khán giả dòng thông báo: Rap Việt All-Star Concert chính thức được tổ chức trở lại vào ngày thứ bảy - 10/4/2021. Như vậy, chỉ còn hơn 1 tuần nữa, đêm diễn hoành tráng hội tụ dàn rapper \"đỉnh của chóp\" từ Rap Việt sẽ chính thức được diễn ra.</p><p>Vốn dĩ, Rap Việt All-Star Concert đã được ấn định ngày tổ chức vào 30/1 vừa rồi. Thế nhưng vì tình hình dịch Covid-19 diễn biến phức tạp nên phía BTC buộc lòng phải đưa ra thông báo tạm hoãn đêm diễn vào sát giờ G. Thời điểm đó, thông tin hoãn này cũng nhận nhiều tranh cãi xôn xao.</p><p>Hiện tại, tình hình dịch ít nhiều đã được kiểm soát tại TP.HCM. Chính vì vậy, sau hơn 2 tháng mỏi mòn chờ đợi, Rap Việt All-Star Concert đã chính thức trở lại. Thông tin này đang nhận được sự quan tâm của đông đảo khán giả. Một số bình luận phía dưới bài đăng:</p><p>- Ôi bất ngờ quá nè!</p><p>- Vui quá! Vậy là Rap Việt Concert cũng đã trở lại.</p><p>- Shout down cho Rap Việt anh em ơi.</p><p>- Xúng xính quần áo đi xem concert hoành tráng của Rap Việt thôi. Hóng quá rồi!</p><p>- Ơ ra thông báo bất ngờ thế!</p><p>- Xin phép được lót dép hóng.</p><p>- Ủa sao lại đăng Cá tháng Tư vậy??? Thật hay giả vậy?</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/1/1-16172859929911293378705.jpg\" ><figcaption class=\"caption\">Thông báo mới nhất của chương trình vừa được đăng tải vài phút trước</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/1/1427494392333841117373751378668817379423982n-1617285919475492553654.jpg\" ><figcaption class=\"caption\">Hình ảnh sân khấu được dàn dựng trước khi Rap Việt All-Star Concert tạm hoãn lần trước</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/1/1428008252338565416901322794128856968819950n-16172859195521358073055.jpg\" ><figcaption class=\"caption\">Rất có khả năng sân khấu lần này của đêm diễn cũng sẽ giống thế này!</figcaption></figure><p>Nguồn: Rap Việt</p>', NULL, 'hot-rap-viet-all-star-concert-chinh-thuc-tro-lai-ngay-va-gio-da-duoc-an-dinh', 0, NULL, NULL),
(97, 15, 4, 1, 1, 'MCK lên tiếng về khả năng đầu quân công ty Sơn Tùng, thẳng thắn nhận xét về âm nhạc của đàn anh', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/29/photo1617031186220-16170311868941203836730.jpg', '|', 'MCK đã có những chia sẻ thẳng thắn trước câu hỏi liên quan đến Sơn Tùng M-TP và việc liệu tham gia Rap Việt mùa 2 hay không.', '<p>Kể từ sau khi đạt được dấu ấn tại cuộc thi Rap Việt, MCK đã trở thành nam rapper trẻ tài năng thu hút được nhiều đối tượng khán giả yêu mến. Việc sở hữu khả năng sáng tác và phong cách rap đặc trưng, một bộ phận fan của MCK còn mong muốn anh chàng đầu quân vào công ty riêng.</p><p>Mới đây trên Instagram, trước câu hỏi: \"Anh nghĩ sao nếu anh là talent (nghệ sĩ) thuộc M-TP Entertainment (công ty do Sơn Tùng đứng vai trò Chủ tịch)\", MCK cũng có câu trả lời thẳng thắn. Theo đó, nam rapper cho biết: \"Anh sẽ rất tự hào đó nhưng anh thích fly solo (tạm dịch: bay nhảy tự do một mình) hơn\". </p><p>Với câu trả lời này, các fan có thể khẳng định rằng dù trong bất kỳ trường hợp nào, MCK sẽ không đầu quân vào bất kỳ công ty chủ quản nào cho dù đó là M-TP Entertainmnet. </p><p>Bên cạnh đó, khi có fan hỏi \"Anh có thích âm nhạc của Sơn Tùng với cách rap của Sơn Tùng không\", MCK khẳng định: \"Cách rap thì không biết nhưng mà nhạc thì chắc chắn là mê\". </p><p>Nam rapper còn không quên đính kèm bản demo cover hit Chúng Ta Của Hiện Tại của Sơn Tùng M-TP làm ví dụ minh hoạ. Còn nhớ cách đây không lâu, sau khi bạn gái Tlinh tung demo lên story và được Sơn Tùng M-TP \"thả tim\", MCK cũng hé lộ verse cover và \"tag\" hẳn nam ca sĩ vào story của mình. Tuy nhiên, trái ngược với động thái dành cho Tlinh, Sơn Tùng lại \"im lặng\" trước story của MCK khiến cho anh chàng phải đăng dòng trạng thái \"Buồn\" lên trang cá nhân. </p><p>MCK rap trên phiên bản làm mới hoàn toàn Chúng Ta Của Hiện Tại</p><p>Đặc biệt, trước việc Rap Việt mùa 2 đang rục rịch trở lại, có fan đã thắc mắc rằng liệu không biết MCK có trở lại hay không. Đáp lại câu hỏi này, anh chàng cho biết: \"No (Không). Anh tham gia cũng có để làm gì nữa đâu. Để cơ hội cho các rapper trẻ khác\". </p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/photo-1-16170309610931955179956.jpg\" ><figcaption class=\"caption\">MCK ngày càng được khán giả trẻ yêu thích kể từ sau khi tham gia Rap Việt</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/2021-03-29-215112-161703038224722041947.jpg\" ><figcaption class=\"caption\">MCK cho biết sẽ rất tự hào nếu trở thành nghệ sĩ thuộc M-TP Entertainment nhưng anh chàng thích tự do hoạt động solo hơn</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/2021-03-29-215122-16170303822751589189983.jpg\" ><figcaption class=\"caption\">MCK khẳng định mê nhạc Sơn Tùng và bằng chứng là bản demo anh chàng cover hit Chúng Ta Của Hiện Tại</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/2021-03-29-215118-16170303823351314177093.jpg\" ><figcaption class=\"caption\">MCK chia sẻ mình sẽ không tham gia Rap Việt mùa 2 để cơ hội cho những người anh em rapper trẻ khác</figcaption></figure><p>Nguồn: Instagram NV </p>', NULL, 'mck-len-tieng-ve-kha-nang-dau-quan-cong-ty-son-tung-thang-than-nhan-xet-ve-am-nhac-cua-dan-anh', 1, NULL, NULL),
(98, 15, 4, 1, 1, 'Nửa đêm Dế Choắt chia sẻ bài đăng dài về việc có suy nghĩ tiêu cực sau khi đăng quang Rap Việt, chuyện gì thế này?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/29/photo1616982180543-16169821807511252861172.png', '|', 'Chia sẻ đầy chân thật sau khi đăng quang ngôi vị Quán quân của rapper Dế Choắt đã nhận được sự chú ý lớn từ mọi người.', '<p>Mới đây, Dế Choắt đã có bài đăng khá dài trên MXH Facebook nói về chủ đề \"Sự tỉnh thức của một Quán quân\", trong đó anh chia sẻ về những khó khăn mình gặp phải về tinh thần sau khi đăng quang ngôi vị Quán quân của Rap Việt mùa 1 và cuối cùng đã tìm ra được chân lý cuộc sống cho chính bản thân mình.</p><p>Hiện bài đăng này của Dế Choắt đang nhận được sự chú ý của đông đảo khán giả.</p><p>Nguyên văn bài đăng của Dế Choắt có nội dung như sau:</p><p>Sự tỉnh thức của một Quán quân:</p><p>Ta như cá đang bơi ngược dòng</p><p>Yêu thương hoá ra vơi/ phơi được lòng.</p><p>Tại sao mình đã làm tất cả, mà vẫn cảm thấy cô đơn?</p><p>Sau đêm Chung kết, mọi thứ khác đi và thay đổi rất nhiều, mình nhận được tình yêu thương của cộng đồng, gia đình, bạn bè, những người thân cận, cô bác họ hàng gần xa. Việc trở thành Quán quân khiến mình phải cố gắng trở thành một điều gì đó để tương đồng với cái danh hiệu mà mình đạt được. Giữ mình - đặt mục tiêu cao - chạy theo những thứ không thấy được mình. Mình rối bời, cô đơn: Tại sao đã cống hiến mà vẫn cảm thấy một mình? Hoá ra mình quên mất rằng: Có phải cộng đồng, gia đình, bạn bè, những người thân cận, cô bác họ hàng gần xa ấy đã thắp cho mình rất nhiều năng lượng, niềm tin, vậy mà mình lại nhốt mình trong phòng! Suy nghĩ tiêu cực, bế tắc.</p><p>Mình quyết định bỏ hết, không gồng nữa. Lắng nghe người bên cạnh hơn, rủ gia đình sum họp, rồi tập vui vẻ với những người bạn mới, tập chấp nhận sự xoay chuyển của cuộc sống. Mọi thứ dễ thở hơn thật các bạn à, rồi mình thấy mọi người yêu thương mình thật (vì mình mất đi khoảng thời gian được dạy dỗ, bảo bọc mà trở nên như vậy). Mình tham gia nhiều hơn vào các hoạt động cộng đồng, truyền cảm hứng cho các bạn hãy tích cực, yêu thương, đừng khép lòng như anh Dế nha!</p><p>Khi cộng đồng, các bạn trẻ, gia đình, thương mình vì họ nhìn được nỗi lo sợ của mình. Mình cảm thấy có động lực để đi tiếp. Các bạn có biết là đi đâu không? Đi tìm về cội nguồn, mình nợ bà nội lời xin lỗi, bà mình đã nằm đó đợi mình rất lâu. Mình gọi về thăm cô bác họ hàng xa gần, mình đi thăm những người mẹ đỡ đầu, mẹ thứ hai, mình được lắng nghe nỗi lo, sợ, khổ, nghịch cảnh của mỗi gia đình. Hoá ra mọi người muốn tâm sự với một Quán quân trong lòng của họ.</p><p>Mình nhận ra rằng, mình đã nhận được quá nhiều năng lượng, yêu thương, niềm tin, từ nơi mình sinh ra, nơi mình lớn lên, nơi mình ra đời, nơi mình bươn chải, nơi mình thất bại, nơi mình bắt đầu. Còn gì thiếu sót không nhỉ?</p><p>Sự tỉnh thức đưa mình đi, cảm nhận vạn vật, những tình tiết để mình tin rằng nó đang đúng và tồn tại. Cuối cùng mình cũng đã đặt đúng vị trí của danh hiệu Quán quân, đó là quán quân trong lòng khán giả, người xem, cộng đồng, gia đình, bạn bè, những người thân cận, cô bác họ hàng gần xa. Mình lại chợt nhận ra: Wow, cảm hứng của mình đây rồi! Mình đã hiểu được, tìm được, đặt được vị trí của chính mình, đó là cảm nhận từ bên trong. Chúc các bạn tìm được chính mình qua câu chuyện này của Dế.</p><p>Mở lòng, dễ thở, dễ chịu, chấp nhận, yêu thương một chút. Mọi thứ sẽ dễ dàng hơn. Hãy nhớ rằng, một ngày trong đời, còn người buồn bạn, giận bạn, hờn bạn, ghét bạn, đợi bạn. Thì bạn chưa đạt được ý nguyện, không khai thông, sáng suốt để đi tiếp con đường của mình đâu nhé. Tích cực lên, vũ trụ sẽ giúp bạn.</p><p>Có thể thấy, Rap Việt là một trong những chương trình nhận được sự quan tâm lớn nhất của khán giả Việt. Chính vì vậy, danh hiệu Quán quân của chương trình lại càng có sức nặng hơn bao giờ hết. Việc Dế Choắt có suy nghĩ tiêu cực như bế tắc, cô đơn, rối bời sau khi nhận danh hiệu này cũng dễ hiểu. Theo những gì anh chàng chia sẻ thì quãng thời gian sau Rap Việt anh đã có những trải nghiệm tồi tệ vì đặt áp lực cho chính mình. Thật may khi cuối cùng Dế Choắt cũng đã nhận ra được chân lý cuộc sống cho bản thân: \"Tích cực lên, vũ trụ sẽ giúp bạn\".</p><p>Bài đăng này không chỉ đơn thuần là những trải lòng của Dế Choắt sau quãng thời gian dài sống trong sự tiêu cực, mà còn là một câu chuyện với mong mỏi được truyền cảm hứng đến tất cả mọi người.</p><p>Một số bình luận tỏ bày sự đồng cảm và an ủi Dế Choắt của netizen dưới bài đăng này:</p><p>- Vui lên thôi, dù sao ngày mai cũng là một ngày mới. Anh đúng là một người truyền cảm hứng tài năng!</p><p>- Dù anh có là Quán quân hay không thì mọi người vẫn luôn yêu quý anh.</p><p>- Dế Choắt tỉnh thức rồi. Vậy là sắp ra bài mới chứ đâu!</p><p>- Quý Dế Choắt từ trước khi thi Rap Việt cơ. Bây giờ vẫn thế, không bao giờ thay đổi.</p><p>- Bài viết này rất cần cho những ai đang hoang mang vô định và có suy nghĩ bi quan về cuộc sống nè.</p><p>- Ai cũng sẽ có lúc rơi vào trạng thái tiêu cực như thế. Đừng lo Dế ơi!</p><p>- Cảm ơn Dế Choắt - một Quán quân lan tỏa những thông điệp tích cực và cần thiết đến tất cả mọi người.</p><p>- Hãy mở lòng ra và đón nhận những tình cảm của mọi người, Dế Choắt nhé!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/1-16169767514101217697045.jpg\" ><figcaption class=\"caption\">Dế Choắt trải lòng về những khó khăn mắc phải sau khi giành lấy ngôi vị Quán quân của Rap Việt</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/1651062652714793912803654166380260141677416o-16169784213081724423663.jpg\" ><figcaption class=\"caption\">Rapper Dế Choắt</figcaption></figure><p>Nguồn: FB nhân vật</p>', NULL, 'nua-dem-de-choat-chia-se-bai-dang-dai-ve-viec-co-suy-nghi-tieu-cuc-sau-khi-dang-quang-rap-viet-chuyen-gi-the-nay', 0, NULL, NULL),
(99, 15, 4, 1, 1, 'Đen Vâu tự gọi mình là \"Sơ-lốc Vâu\" rồi \"đồng quê như Taylor Swift\" nhưng lại bị fan kêu già chát, đồng thanh gọi chú ơi!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/29/photo1616951633393-16169516337801867320105.png', '|', 'Chàng rapper \"mặn mòi từ trong da dẻ\" lại khiến fan có dịp lác mắt trong bộ suit lịch lãm. Nhưng fan có phản ứng thế nào?', '<p>Đen Vâu lâu lâu thay đổi lại khiến các fan \"mắt chữ A mồm chữ O\" khi xuất hiện trong bộ suit nâu \"bảnh tỏn\" tại một sự kiện. Thành viên của \"BlackBinz Việt Nam\" quả không làm fan thất vọng với vẻ ngoài rất gì và này nọ, khác hẳn hình ảnh giản dị có phần xuề xoà thường thấy trước đó. Vóc dáng cao ráo, gầy, tỉ lệ cơ thể rất là\"okla\" nên Đen Vâu ưng outfit của mình trong buổi diễn này lắm.</p><p>Có ai nhìn thấy anh không ạ? Có ai nhận ra anh không?</p><p>Thế nhưng không ai ngờ được rằng, nam rapper tự hào về bản thân đến thế mà lại bị fan vào phũ toàn tập. Cụ thể, chàng rapper than với một chất giọng hơi \"thảo mai\": \"Cũng hồi hộp mà được cái mọi người vỗ tay ủng hộ mừng gheeeee\" trong khi xem ảnh trông điệu bộ không giống đang hồi hộp cho lắm. Đã thế còn tự nhận mình giống Sherlock Vâu - anh em song sinh khác cha khác mẹ với Sherlock Holmes hay gì? Và chốt lại là lại \"dìa diaaa\" đã thành thương hiệu. </p><p>Tuy nhiên, anh chàng rapper đông fan bậc nhất Việt Nam vẫn chưa ngừng sự tếu táo lại đâu ạ. Sau khi nhận mình là em thất lạc của thám tử, anh lại nghĩ mình chính là... Thuý Loan, à nhầm, Taylor Swift??? Và anh không ngừng hỏi han các fan: \"Có đồng quê không ợoooooo? Có Tai-lo Xờ-Guýp không ợoooooo?\".</p><p>Thế nhưng, đen cho anh Đen, các fan chẳng ai thấy anh giống Sơ-lốc Hôm hay là Thuý Loan Tai-lo Xờ-Guýp, các fan chỉ thi nhau kêu... già chát và đồng thanh gọi \"Chú ơi!\". Có fan còn khuyên chú nên chuyển nghiệp sang làm người mẫu vì chất quá chú Đen ơi!</p><p>- Dạ hong ợơơơơ</p><p>- Mlem chú chỉnh màu đẹp quá đi ạ</p><p>- Nhìn chất quá chú</p><p>- Chú nên bỏ rap để đi làm model thì hợp lý hơn</p><p>- Rấccc đồng quê và rất taii lơ sờ guýt nhaaa chú ơiiii</p><p>- Tưởng chú làm vơ đét</p><p>- Chú mình hôm nay lịch lãm quá</p><p>- Chú mình đẹp quá đi, style quá điiiii</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/28/base64-16169446178171611354406.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/base64-16169446009851141586543.png\" ><figcaption class=\"caption\">Ủa ủa? Alo Sherlock Holmes có phải không ạ?</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/base64-16169443772661489101865-16169504985601801369549.png\" ><figcaption class=\"caption\">\"Sơ-lốc Vâu\" - em trai xém ruột của bác \"Sơ-lốc Hôm\"</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/den-16169365811501051417162.png\" ><figcaption class=\"caption\">Rồi sao ạ? Có giống không ạ?</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-1-16169345009601926369895.jpg\" ><figcaption class=\"caption\">Các fan chế cháo ảnh của Binz và Đen Vâu thành Blackpink Việt Nam</figcaption></figure><p>Đen Vâu luôn được lòng dân tình bởi những bản rap đậm chất đời, phong cách giản dị gần gũi và lối chơi chữ mặn mòi từ rap ra tới tận status Facebook. Gần đây, anh và Binz mới kết hợp và cho ra bản rap love Cho Mình Em làm bao em gái đổ gục. Anh còn tếu táo khi tự nhận là \"Hồng đen trong khu vực bạn đóooooo\"  khiến các fan  vô cùng thích thú. </p>', NULL, 'den-vau-tu-goi-minh-la-so-loc-vau-roi-dong-que-nhu-taylor-swift-nhung-lai-bi-fan-keu-gia-chat-dong-thanh-goi-chu-oi', 0, NULL, NULL),
(100, 15, 4, 1, 1, 'Giọng Hát Việt Nhí: Hưng Cao - Vũ Cát Tường gần như còn nguyên, BigDaddy - Emily bay màu nửa đội', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/28/photo1616948108558-1616948108825881689861.jpg', '|', 'Số lượng thành viên nhà BigFamily tại Giọng Hát Việt Nhí tính đến thời điểm hiện tại chỉ còn nửa đội.', '<p>Giọng Hát Việt Nhí New Generation 2021 đã đến tập 11, đối với các thí sinh và HLV thì chặng đường giờ đây chỉ còn một nửa. Trong tập 11 này, những thí sinh cuối cùng của các đội HLV sẽ tranh tài để chuẩn bị cho đêm Đột phá ở vòng sau.</p><p>Mở màn tập 12 là cặp đấu của Anh Thư và Minh Hằng. Mỗi bé đều mắc lỗi lớn khiến phần trình diễn mất đi một phần cảm xúc. Anh Thư làm mới một tiết mục dân ca Nam Bộ mang tên Lời Ru Của Mẹ. Cách viết rap và trau chuốt ca từ của cô bé là điều không mấy khi khiến ban giám khảo lo lắng hay phải góp ý. Thế nhưng đáng tiếc thay, có lẽ do quá căng thẳng, thời gian ghi hình giữa các vòng quá gấp rút đã khiến Anh Thư vấp lời đến 3-4 lần, ngay cả HLV Vũ Cát Tường cũng phải tiếc thay cho cô bé. Dương Khắc Linh lại hài lòng về phần rap của Anh Thư nhưng chưa ưng ý với giọng hát vì giọng hát chênh phô khá nhiều.</p><p>Trong khi đó, Minh Hằng rất cố gắng để truyền tải tinh thần yêu dân tộc, quê hương qua ca khúc Người Việt Phải Biết Sử Việt do Hồ Hoài Anh sáng tác. Dù cô bé đã rất cố gắng gượng và hết mình để có một phần thi tròn trịa nhưng vẫn bị sức khỏe không tốt làm cản trở nội lực trong giọng hát, khiến tiết mục chưa thực sự thăng hoa.</p><p>Kết quả Anh Thư nhận được cơ hội đi tiếp. Các HLV và giám khảo đều hy vọng Anh Thư sẽ giữ vững phong độ và toả sáng hơn trong vòng thi sau.</p><p>Ở tiết mục thứ hai, người thi trước là Kelvin Huỳnh đến từ đội Hưng Cao và Vũ Cát Tường. Trong vòng trước khi bắt cặp cùng Mỹ Anh, phần thể hiện cháy hết mình của Kelvin đã khiến cậu bé bị hao hụt năng lượng khá nhiều. Hưng Cao và Vũ Cát Tường cho Kelvin chậm lại, đằm hơn vòng trước bằng một sáng tác lấy cảm hứng từ chiếc đồng hồ báo thức và việc dậy trễ mỗi sáng. Với Vũ Cát Tường đây sẽ là cơ hội để Kelvin chứng tỏ mình là một thí sinh đa dạng, còn Hưng Cao lại nhắm vào việc cải thiện hơi thở và tiết chế năng lượng của Kelvin, anh còn căn dặn học trò: \"Rap nhanh rất dễ hay, nhưng rap chậm mà hay mới là khó\".</p><p>Đội BigFamily chuẩn bị cho Yến Nhi một tiết mục \"bánh bèo\" xuất sắc, làm mới sample câu đồng dao dân gian của các thế hệ trước: \"Dung dăng dung dẻ, năm mới năm mẻ…\". Phần trình diễn của Yến Nhi mang nét nhẹ nhàng, tươi tắn, hồn nhiên cùng bầu không khí thư thái của ngày Tết cổ truyền. Emily còn dàn dựng cho cô bé màn vũ đạo yểu điệu dễ thương, kết hợp với trang phục áo dài xinh xắn.</p><p>Kết quả, Kelvin chiến thắng và giành được vé đi tiếp, nhưng BigDaddy đã tự mình lên sân khấu đón Yến Nhi về vì cô bé xứng đáng có cơ hội thứ hai sau phần thể hiện dễ thương và rất giải trí của mình. Cả hai tiếp tục vào vòng trong - vòng Đột phá.</p><p>Cặp đấu cuối cùng là màn hội ngộ giữa 2 cái tên từng được 3 đội HLV tranh giành nhiều nhất Hà Anh và Duy Long. Một bên là cô bé Hà Anh nhẹ nhàng, thông minh, đậm chất con nhà người ta khi sở hữu quá nhiều tài năng. Đối đầu là Duy Long - cậu bé 15 tuổi, mang cá tính nổi bật, táo bạo và nghệ sĩ tính mạnh mẽ trên sân khấu. </p><p>Hà Anh hóa thân thành người kể chuyện, hát cho những người vô gia cư đã có tuổi. Lời ca văng vẳng, giọng hát có phần ma mị khiến cả khán phòng chìm đắm trong câu chuyện được kể bởi Hà Anh.</p><p>Về phía Duy Long, cậu bé đã đưa cả 6 thành viên Kidneeland lên sân khấu để dành cho họ một \"cái ôm siêu to bự\" với ca khúc Ra Đây Ôm Cái Nào. Ca khúc với tiết tấu vui vẻ và sôi động cùng với sự xuất hiện của Hưng Cao và Vũ Cát Tường đã là màn chào kết cao trào không thể hợp lý hơn của vòng Thách đấu.</p><p>Vợ chồng giám khảo BigDaddy và Emily ngồi trên ghế nóng cũng hạnh phúc trao nhau cái ôm nồng ấm, còn hai vị \"phụ mẫu\" nhà Mimimama phải tán dương đối thủ rằng phần trình diễn quá đầu tư. Kết thúc cặp đấu cuối cùng tuy Hà Anh được chọn để đi tiếp nhưng với mọi sự cố gắng của Duy Long, Hưng Cao và Vũ Cát Tường cũng hoàn toàn tin tưởng và trao cho cậu bé quyền cứu thí sinh.</p><p>Kết thúc vòng Thách đấu, tập 12 tuần sau sẽ mở ra với luật chơi cam go hơn khi cơ hội đi tiếp giữa các cặp đấu duy nhất chỉ có một, không thể cứu thí sinh bị loại. Bước vào chặng cuối, các thí sinh sẽ phải nghiêm túc và tập trung cao độ hơn, các HLV và nhà sản xuất sẽ phải làm việc hết công suất để dành cho học trò những tấm vé xứng đáng vào Bán kết. </p><p>Tập đầu tiên của vòng Đột phá sẽ được phát sóng vào ngày 4/4/2021.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-1-1616945396987878304222.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-1-1616945463540788086267.jpg\" ><figcaption class=\"caption\">Anh Thư</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-2-16169454642741937189504.jpg\" ><figcaption class=\"caption\">Minh Hằng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-3-1616945463998830698291.jpg\" ><figcaption class=\"caption\">Nhờ mang trong mình hơi thở và dòng máu của một cầu thủ bóng đá, âm nhạc latin chảy trong máu, cách Kelvin trình diễn toát lên thần thái rạng rỡ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-4-16169454643442034983107.jpg\" ><figcaption class=\"caption\">Yến Nhi</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-5-16169454636811936617495.jpg\" ><figcaption class=\"caption\">Hà Anh</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-6-16169454640171333695433.jpg\" ><figcaption class=\"caption\">Duy Long</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-7-16169454633031468414927.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Ảnh: Cát Tiên Sa</p>', NULL, 'giong-hat-viet-nhi-hung-cao---vu-cat-tuong-gan-nhu-con-nguyen-bigdaddy---emily-bay-mau-nua-doi', 1, NULL, NULL),
(101, 15, 4, 1, 1, 'Suboi không làm HLV Rap Việt mùa 2? Yên tâm, có một sao nữ từng tự đề cử cho vai trò này!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/28/photo1616916779698-1616916780288891283873.jpg', '|', 'Nghệ sĩ nữ này từng chia sẻ nếu làm giám khảo Rap Việt thì cô cũng có thể đóng góp ý kiến.', '<p>Sau thành công vang dội từ mùa giải đầu tiên, Rap Việt đang rục rịch trở lại với mùa giải mới trong năm 2021. Nhiều khán giả hi vọng sẽ được gặp lại bộ sậu mùa 1 gồm MC Trấn Thành, 4 HLV Wowy - Binz - Karik - Suboi cùng 2 giám khảo Rhymastic - JustaTee sẽ cùng hội ngộ trong mùa 2.</p><p>Tuy nhiên, nhiều khả năng HLV nữ duy nhất là Suboi không thể tiếp tục đồng hành cùng chương trình vì nữ rapper từng chia sẻ rằng mình muốn trở lại với vai trò làm mẹ, làm vợ sau khi kết thúc Rap Việt mùa 1.</p><p>Nếu như Suboi không tiếp tục đồng hành cùng Rap Việt thì gương mặt nào sẽ được chọn để thay thế cô? Một số cái tên được khán giả đề xuất như Đen Vâu, Datmaniac, LK, BigDaddy...</p><p>Ngoài ra, một nhân vật khác cũng nằm trong bảng đề cử và đó chính là... nữ ca sĩ Lệ Quyên. Cụ thể, trong buổi họp báo concert riêng, bên cạnh những chia sẻ về công việc cũng như đời sống cá nhân của mình, nữ ca sĩ đã cho biết: \" Chắc mọi người không biết những khi thư giãn Quyên thường nghe Rap. Quyên rất thích xem chương trình Rap. Ngoài việc nghe nhạc Quốc tế, Cổ điển, Bolero thì Quyên rất thích Rap/ Hip-hop. Quyên rất thích chương trình Rap Việt và nếu như cho Quyên làm giám khảo thì Quyên cũng có thể đóng góp ý kiến vì bản thân Quyên là một người rất đa dạng nhiều dòng nhạc\".</p><p>Dễ dàng nhận thấy, lời chia sẻ của Lệ Quyên khá khiêm tốn và không khẳng định chắc nịch \"đủ khả năng để làm giám khảo Rap Việt mùa 2\" nhưng nếu cô ngồi ghế nóng thì cũng là một sự lựa chọn thú vị đấy.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/fc4b7b5092137b4d2202-1616917064318866076067.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/batch5w2b2062-1610017078854312555527-16102929080271654703010-1616917353061509257460.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Ảnh: Internet</p>', NULL, 'suboi-khong-lam-hlv-rap-viet-mua-2-yen-tam-co-mot-sao-nu-tung-tu-de-cu-cho-vai-tro-nay', 1, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(102, 16, 5, 1, 1, '38 tuổi - Minh Nhựa có 5 con với 2 vợ: Để nhớ mặt và gọi tên các rich kid đã là thách thức với netizen!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617774688127-16177746885381801953250.png', '|', 'Sinh ra vượt vạch đích, 5 tiểu thư và thiếu gia nhà Minh Nhựa có cuộc sống khiến netizen ghen tị.', '<p>Với vị trí người kế thừa tài sản của ông trùm nhựa Long Thành kiêm tay chơi siêu xe khét tiếng, Minh Nhựa là nhân vật đình đám cỡ nào trên cõi mạng thì chẳng cần bàn nhiều. </p><p>Đó cũng chính là lý do mà các ái nữ và quý tử nhà Minh Nhựa cũng nhận được sự quan tâm không nhỏ từ netizen.  Trải qua 2 lần kết hôn, đại gia Sài thành có 5 người con gồm Joyce Phạm, Susie, Suri là con của người vợ đầu tên Phương Thuý và Nobi và Nana là con của Mina Phạm. </p><p>Tất nhiên, đây cũng chính là những cái tên có \"số má\" trong nhóm rich kid. Nhưng ngoại trừ Joyce Phạm được biết đến rộng rãi trên cõi mạng, 4 rich kid còn lại nhà Minh Nhựa đều nhỏ tuổi và kín tiếng nên đôi khi dân tình không nhớ mặt hết.</p><p>Joyce Phạm tên thật Phạm Trần Nguyễn Minh Anh, sinh năm 1999. Cô nàng là con gái trưởng và cũng nổi tiếng nhất trong số những người con của Minh Nhựa. Ngoài việc là rich kid quen mặt, Joyce Phạm còn gây chú ý vì tính cách thẳng thắn, sẵn sàng đáp trả anti-fan. </p><p>Năm 2019, Joyce Phạm đã kết hôn với Tâm Nguyễn bằng 1 đám cưới vô cùng xa xỉ với  15 siêu xe gần 100 tỷ hộ tống, cô dâu đeo sính lễ vàng và kim cương mỏi tay, tổng chi phí bữa tiệc khoảng 20 tỷ. </p><p>Sau khi kết hôn, vợ chồng Joyce Phạm vẫn ở chung với ông bà nội của Joyce vì ông trùm nhựa Long Thành không muốn xa cháu gái cưng. Và 6 tháng sau đám cưới, Joyce Phạm hạ sinh con trai đầu lòng, đặt tên là Jayden Nguyễn.</p><p>Con gái thứ 2 của Minh Nhựa là Susie Phạm và có phần kín tiếng hơn so với chị gái Joyce Phạm. Trang cá nhân của cô bạn không chia sẻ quá nhiều về bản thân, chỉ biết Susie đang học cấp 3 và đã có bạn trai.</p><p>Ngôi trường mà Susie đang theo học là BIS (British International School) - một ngôi trường quốc tế đình đám tại TP.HCM. Theo tìm hiểu, học phí một năm tại ngôi trường này lên tới hơn 700 triệu và đây là một trong những trường có học phí đắt đỏ bậc nhất cả nước.</p><p>Con gái út của Minh Nhựa và Phương Thuý là Suri Phạm. Sau khi bố mẹ đường ai nấy bước, Susie và Suri  ở với mẹ, được mẹ nuôi dạy từ bé. Vì vậy mà Suri rất ít khi xuất hiện trên trang cá nhân của đại gia, cô bé chỉ có mặt khi gia đình có dịp đặc biệt hoặc cùng nhau đi du lịch. </p><p>Suri với bố và mẹ</p><p>Dù còn nhỏ và ít khi \"lên sóng\" nhưng lần nào xuất hiện, Suri đều khiến netizen thích thú và trầm trồ vì quá xinh xắn. Cô bé thừa hưởng  trọn vẹn những điểm nổi bật từ ba mẹ mình. Vậy nên dân tình không ngần ngại dự đoán rằng chỉ vài năm nữa thôi, Suri hẳn sẽ vượt mặt 2 chị gái trên đường đua rich kid vừa giàu vừa xinh.</p><p>Nobi (tên thật  Gia Minh ) là con trai đầu của Minh Nhựa và vợ 2 - Mina Phạm. Nobi cũng là con trai duy nhất của đại gia nên  được cho rằng sẽ là người thừa kế gia sản nhà họ Phạm.</p><p>Chưa có trang cá nhân riêng nhưng Nobi thường xuyên được bố mẹ cho xuất hiện trên MXH. Thông qua những hình ảnh này, nhiều người không khỏi trầm trồ vì ngoại hình của thiếu gia quá giống bố, đúng chuẩn cha nào con nấy.</p><p>Minh Nhựa và con trai duy nhất - Nobi</p><p>Nana là công chúa út nhà Minh Nhựa sinh năm 2016, năm nay tròn 5 tuổi. Cùng với anh trai, Nana cũng thường xuyên xuất hiện trên trang cá nhân của bố mẹ. </p><p>Minh Nhựa và con gái út</p><p>Đặc biệt, Mina Phạm không chỉ mặc đẹp một mình mà còn chịu khó tút tát cho con gái nhỏ sành điệu như fashionista. Cứ đà này chẳng mấy chốc mà Nana sẽ thành tiểu thư xinh đẹp nức nở cho mà xem.</p><p>Mina Phạm cực chăm đầu tư chụp ảnh theo concept mẹ và con gái</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/2-16177746745441981191867.jpg\" ><figcaption class=\"caption\">Minh Nhựa và 3 cô con gái với Phương Thuý: Joyce, Susie và Suri...</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/7-16177746747902115690377.jpg\" ><figcaption class=\"caption\">... và 2 con cùng với Mina Phạm: Nobi, Nana</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/6-16177746747681493640760.jpg\" ><figcaption class=\"caption\">Minh Nhựa và Joyce Phạm</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/joycepham110616482275713274568776370304320397235820463862n-1617777149575800058657.jpg\" ><figcaption class=\"caption\">Gia đình nhỏ của Joyce Phạm - Tâm Nguyễn</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/4-1617774674739974906347.jpg\" ><figcaption class=\"caption\">Minh Nhựa và Susie</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/susieeieie941037912545675022364077824266944306604858n-16177773778511459686505.jpg\" ><figcaption class=\"caption\">Susie và mẹ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/5-1617777471599690541363.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/10-16177777837361463834294.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/8-1617777783701247557522.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/12-1617778232013261664967.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Ảnh: Tổng hợp</p>', NULL, '38-tuoi---minh-nhua-co-5-con-voi-2-vo-de-nho-mat-va-goi-ten-cac-rich-kid-da-la-thach-thuc-voi-netizen', 0, NULL, NULL),
(103, 16, 5, 1, 1, 'Phí Phương Anh đưa bồ thiếu gia đi thăm Hà Nội, chuẩn bị ra mắt gia đình ư?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617772430442-16177724308171997893092.jpg', '|', 'Cặp đôi này thường xuyên đưa nhau đi vi vu khắp nơi và địa điểm của chuyến đi lần này là Hà Nội - nơi gia đình Phí Phương Anh đang sinh sống.', '<p>Người ta hay nói được cái này thì mất cái kia, với Phí Phương Anh cũng vậy. Tuy sự nghiệp ca hát gặp nhiều trắc trở nhưng bù lại, đường tình của cô nàng dạo này lại thuận buồm xuôi gió hơn rất nhiều. Cô nàng được cho là đang hẹn hò với Đ.M.Q - một  thiếu gia kiêm bạn thân Fabo Nguyễn, con trai ông trùm điện tử Sài thành đình đám.</p><p>Phí Phương Anh được cho là đang hẹn hò thiếu gia Sài thành tên Đ.M.Q</p><p>Chưa có một lời xác nhận chính thức nào từ chính chủ nhưng bằng 1001 hint rõ rành rành mà Phí Phương Anh và bạn trai thường xuyên tung lên MXH, dân tình có thể chắc chắn gần như 100% về chuyện tình này. Mới đây, cả hai tiếp tục khoe loạt khoảnh khắc hẹn hò giấu mặt, thậm chí còn làm dấy lên nghi vấn về ra mắt gia đình.</p><p>Theo đó, từ hôm qua đến hôm nay, người ta liên tục thấy M.Q và Phí Phương Anh check-in tại các hàng quán tại Hà Nội, đương nhiên là cùng một thời điểm, cùng một địa điểm luôn. Có vẻ như Phí Phương Anh đang đưa bạn trai người Sài Gòn của mình đi ăn hết một lượt các đặc sản nổi tiếng Hà thành thì phải, từ bún chả đến dê áp chảo, rồi còn có cả món bún ngan thần thánh...</p><p>Từ hôm qua, bạn trai tin đồn của Phí Phương Anh đã liên tục check-in tại các hàng quán Hà Nội</p><p>Đến hôm nay thì cả hai công khai check-in cùng một nơi</p><p>Ngoài xuýt xoa vì độ chăm đi vi vu cùng nhau của cặp đôi, nhiều người còn tự hỏi phải chăng chuyến đi ra Hà Nội lần này của M.Q còn có mục đích ra mắt gia đình bạn gái. Bởi như các bạn cũng đã biết, dù đang hoạt động và làm việc tại Sài Gòn nhưng Phí Phương Anh vốn sinh ra và lớn lên tại Hà Nội. Dịp Tết vừa rồi, cô nàng cũng về nhà ăn Tết chứ không chọn ở lại Sài Gòn.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/base64-16177758914761310528110.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/base64-16177763863651831924157.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/base64-16177760878781480316264.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1487029861340201619042972344811237888735541n-16177764918721959575088.jpg\" ><figcaption class=\"caption\">Không biết cả hai chỉ đang đi chơi đơn thuần hay kết hợp cả việc về ra mắt gia đình đàng gái nữa</figcaption></figure><p>Ảnh: Tổng hợp</p>', NULL, 'phi-phuong-anh-dua-bo-thieu-gia-di-tham-ha-noi-chuan-bi-ra-mat-gia-dinh-u', 0, NULL, NULL),
(104, 16, 5, 1, 1, 'Hậu Hoàng gia nhập hội gái đoảng, Khánh Vân đòi \"pass\" gấp nhưng biết đâu có ai đó lại ưng thì sao!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617768147169-161776814751439886401.jpg', '|', 'Sở đoản của Hậu Hoàng mới đây đã bị Khánh Vân tiết lộ trên sóng Instagram.', '<p>Hậu Hoàng nổi tiếng thế nào thì chắc ai cũng biết. Cô nàng sở hữu kênh YouTube lên tới 7,2 triệu lượt theo dõi, fanpage cá nhân cũng \"hòm hòm\" 3,6 triệu người follow. Hậu Hoàng xinh xắn, dễ thương, lầy lội, hát hay..., nói chung là rất đa tài.</p><p>Thế nhưng mới đây, cô nàng lại bị bạn thân Khánh Vân \"bóc phốt\" một sở đoản khiến nhiều người cười lăn. Cụ thể, Khánh Vân đã đăng một loạt story trên Instagram với nội dung đòi... pass Hậu Hoàng.</p><p>\"Góc pass vợ: Tao đi học về mà mày ở nhà nấu cháo trứng cả vỏ như thế này thì chết cái niềng răng của tao\", Khánh Vân tố.</p><p>Hậu Hoàng bị bạn thân Khánh Vân \"bóc phốt\"</p><p>Có thể thấy trong clip đăng kèm, Hậu Hoàng tỏ ra rất chăm chú với công cuộc nấu cháo. Tuy nhiên, vì một chút bất cẩn mà lúc đập trứng cô nàng đã để cả mảng vỏ to đùng rơi vào bát. Khi bị Khánh Vân \"hỏi tội\", nàng \"diu tu bê\" vẫn còn mạnh miệng biện minh: \"Vỏ trứng có canxi, ăn vỏ trứng mới tốt!\".</p><p>Mảnh vỏ trứng \"tai hại\" khiến Hậu Hoàng bại lộ sở đoản nấu nướng</p><p>Tai nạn nấu nướng tuy khá nhỏ này của Hậu Hoàng vẫn đủ khiến mọi người cười lăn. </p><p>Thậm chí nhiều người còn nói đùa, Hậu Hoàng giỏi thật, sau này không làm YouTuber thì vẫn có thể làm cả đống nghề khác nhưng riêng nghề đầu bếp thì không. Lý do thì bởi nấu mà vụng về thế này có ngày bị khách \"bóc phốt\" tận mặt như cách Khánh Vân đã làm cho mà xem.</p><p> Nhưng hội đẩy thuyền \"Hậu Hoàng - Mũi trưởng Long\" lại nghĩ khác, vụng thì vụng thật nhưng vì quá dễ thương nên biết đâu Khánh Vân vừa pass thì lại có người \"hốt\" về thì sao? Cũng không chắc \"mặt hàng\" này còn đâu à nha!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1663888631053029421889608838160313782840473n-1617769398517909712703.jpg\" ><figcaption class=\"caption\">Hậu Hoàng là \"diu tu bê\" sở hữu lượng fan cực kì đông đảo</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/img7729-1617769230584355626767.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Ảnh: Tổng hợp</p>', NULL, 'hau-hoang-gia-nhap-hoi-gai-doang-khanh-van-doi-pass-gap-nhung-biet-dau-co-ai-do-lai-ung-thi-sao', 0, NULL, NULL),
(105, 16, 5, 1, 1, '\"Chị cả\" giới con nhà giàu Việt khẳng định đẳng cấp sang chảnh khi sắm đồ cho con', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617766172752-16177661729911447397940.jpg', '|', 'Toàn Hermes, Versace, Dolce & Gabbana, Gucci, Fendi... thôi mọi người ơi, nhìn hơi chóng mặt đó!', '<p>Nếu là người chăm chỉ cập nhật nhất cử nhất động của hội rich kid Việt thì hẳn bạn sẽ chẳng còn xa lạ với Tiểu Giang (tên đầy đủ là Nguyễn Đặng Tiểu Giang, sinh năm 1993) - nàng tiểu thư con gái một đại gia buôn xe hơi thường và siêu xe tại Việt Nam. Sau khi tốt nghiệp đại học ở Singapore, Tiểu Giang sang Mỹ định cư và kết hôn, sinh con. </p><p>Vì khá kín tiếng lại sở hữu nhan sắc hack tuổi nên nhiều người vẫn lầm tưởng cô chưa làm mẹ. Nhưng có vẻ như càng ngày, Tiểu Giang càng chăm khoe về gia đình hơn.</p><p>Mới đây, nàng rich kid đã khiến nhiều người trầm trồ khi khoe nhẹ thành quả cuộc shopping đồ cho con. Có thể thấy thương hiệu mà Tiểu Giang chọn mua đều là các brand đắt đỏ bậc nhất như: Hermes, Versace, Dolce & Gabbana, Gucci, Fendi... Từ quần đến áo, thậm chí đồ chơi cô cũng đã sắm đầy đủ.</p><p>Công cuộc sắm đồ cho con của Tiểu Giang toàn đồ hiệu</p><p>Không rõ tổng số tiền Tiểu Giang đã bỏ ra là bao nhiêu nhưng người ta tìm thử thì riêng con ngựa bông Hermes đã có giá nghe đâu vào tầm (khoảng 17,5 triệu VNĐ (hơn 760 đô), còn 2 chiếc áo kia cũng tầm 5,5 đến 7,5 triệu. Kết luận lại là... rất nhiều tiền!</p><p>Cô nàng mua từ quần áo đến đồ chơi</p><p>Đương nhiên điều này không khó hiểu cho lắm bởi danh xưng chị cả giới con nhà giàu của Tiểu Giang đâu phải hư danh. Xuất thân trong một gia đình buôn xe, Tiểu Giang đã có cơ hội trải nghiệm hơn 50 dòng siêu xe từ Huracan, Bentley, Rolls Royce đến Maserati... </p><p>Ngoài ra Tiểu Giang cũng sở hữu hầu hết các thương hiệu túi nổi tiếng thế giới mà cô gái nào cũng ao ước như Dior, Hermes, Gucci, Chanel, Fendi, Louis Vuitton... Những hình ảnh chia sẻ công khai trên Instagram hay Facebook của Tiểu Giang chỉ là một góc nhỏ trong cuộc sống xa hoa của cô.</p><p>Cuộc sống sang chảnh của Tiểu Giang khiến nhiều người trầm trồ</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1498884677945305347478416442774289472952292n-1617765793209359506984.jpg\" ><figcaption class=\"caption\">Tiểu Giang và con trai lớn</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/base64-16177658828041786405773.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/base64-1617765995927650007439.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/img7724-16177659612611474086925.png\" ><figcaption class=\"caption\">Chú ngựa bông này có giá sương sương 17,5 triệu VNĐ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/1237399484776632365392007548669099324777358n-1617766075971497446435.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Ảnh: Tổng hợp</p>', NULL, 'chi-ca-gioi-con-nha-giau-viet-khang-dinh-dang-cap-sang-chanh-khi-sam-do-cho-con', 0, NULL, NULL),
(106, 16, 5, 1, 1, 'Biệt đội cano 0 đồng: Bản lĩnh của tập thể không ai muốn một cái tên riêng, sẵn sàng hy sinh và đánh đổi vì nghĩa cử cộng đồng', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617760225052-16177602253661669755120.jpg', '|', 'Trong khó khăn, tình người là thứ dường như chưa bao giờ cạn. Và cũng trong khó khăn, câu chuyện về những cá nhân, tập thể dám quên đi hạnh phúc riêng, một lòng chung tay góp sức vì cộng đồng lại càng tỏa sáng. \"Biệt đội cano 0 đồng\" là một trong những từ khóa quy tụ được hết tinh thần ấy trong năm 2020 vừa qua.', '<p>Mưa lũ lịch sử ở miền Trung và những chuyến cano 0 đồng băng băng rẽ nước ứng cứu đồng bào</p><p>Tháng 10/2020 là khoảng thời gian muốn quên đi nhưng không thể quên của hàng triệu người dân miền Trung khi suốt gần một tháng ròng, khúc ruột của đất nước phải oằn mình chống lại cơn lũ lịch sử gây thiệt hại nghiêm trọng về cả người và của. Bất lực, hoang mang, người dân vùng lũ thậm chí còn phải kêu cứu trên MXH do nước lũ dâng quá nhanh, mọi công tác cứu trợ ở địa phương không thể theo kịp.</p><p>Và rồi chính những tiếng kêu cứu xé màn đêm ấy đã trở thành nguồn động lực lớn nhất khiến một nhóm người không thể ngồi yên. Với kinh nghiệm trong công tác cứu trợ bão lũ nhiều năm trước, nhóm người này hiểu được cái mà người dân thực sự cần trong lúc này.</p><p>\"Thử tưởng tượng nước lũ dâng lên đến nóc nhà thì làm sao họ có thể chịu đựng được? Hay có những ngôi nhà nằm sâu trong ngóc ngách gần 1 tuần chưa được tiếp cận hỗ trợ, rồi cả những khu vực bị cô lập bởi nước đến nỗi thuyền ghe bình thường cũng không di chuyển được?\".</p><p>Từ đây, suy nghĩ sử dụng thuyền bơm hơi - phương tiện nhẹ, nhỏ, dễ len lỏi xuất hiện. Ngay cả ở những nơi nước lớn nhất, phương tiện này vẫn có thể hoạt động. Vậy là ngay đêm 8/10, những chuyến xe bán tải đầu tiên mang theo thuyền hơi và cano nhằm ứng cứu đồng bào đã có mặt tại Quảng Trị. Ý tưởng về một Biệt đội cano 0 đồng cũng chính thức nhen nhóm từ đây.</p><p>Biệt đội là tập hợp của rất nhiều anh em đến từ nhiều tỉnh thành trong cả nước, họ không mong muốn được nhắc tên riêng, bởi mọi hoạt động đều mang ý nghĩa tập thể và hướng tới lợi ích chung.</p><p>\"Khẩn trương và hiệu quả\" là phương châm hoạt động của anh em trong Biệt đội cano 0 đồng ngay tại thời điểm đó. Chỉ trong một thời gian ngắn, từ một vài lời kêu gọi, dần dần nó hóa thành lời \"hiệu triệu\", nguồn lực thuyền hơi và cano 0 đồng được huy động từ cả nước chứ không còn loanh quanh khu vực Đà Nẵng, Huế… Ngay cả các doanh nghiệp cũng như các CLB lớn về thuyền hơi, xuồng hơi… cũng không chịu đứng ngoài cuộc. Chỉ sau một đêm, 20 chiếc thuyền bơm hơi lớn nhỏ, 3 cano lớn sức chứa hơn 20 người đã được tập trung.</p><p>\"Biệt đội cano 0 đồng là một cái tên lột tả được đúng tất cả những sự đóng góp và hi sinh của nó, bởi các anh em trong đội lúc đó thật sự là những người hùng thầm lặng, bản lĩnh, dám đương đầu với khó khăn, nguy hiểm, giúp người hơn cả giúp mình. Chúng tôi không riêng lẻ, chúng tôi là biệt đội bởi chỉ cần khó khăn, anh em Bắc - Trung - Nam đều sẽ hội tụ để cùng nhau vượt qua\", đại diện Biệt đội chia sẻ.</p><p>Sự lên ngôi của những bản lĩnh thầm lặng và những kỷ niệm không thể nào quên</p><p>Ở thời điểm ấy, các thành viên của Biệt đội cano 0 đồng - vốn phần lớn đều là những người con của đất mẹ miền Trung biết rằng mình phải làm điều gì đó. Tinh thần tương thân tương ái không thể chỉ nằm trên con chữ, mà phải là hành động để tạo ra kết quả tốt đẹp cho cuộc sống chung. Mỗi người mỗi tay, hợp sức lại, chúng ta sẽ làm ra những điều kỳ diệu.</p><p>Giờ nhắc lại thì thấy dễ nhưng phải hiểu rằng đây là công việc hết sức vất vả và nguy hiểm. Giữa mênh mông biển nước, không biết đâu là bờ, ngay cả cano có trọng tải lớn 20 chỗ còn suýt gặp nạn, huống chi là những chiếc thuyền hơi nhỏ bé. Tuy nhiên, với bản lĩnh, sự dũng cảm, tinh thần tương thân tương ái dành cho đồng bào miền Trung ruột thịt, Biệt đội vẫn miệt mài công việc của mình, không hề có ý định kết thúc cho đến khi người dân được ổn định và quay lại quỹ đạo vốn dĩ.</p><p>Trên chuyến hành trình của mình, các anh em trong Biệt đội cano 0 đồng đã có rất nhiều kỉ niệm. Vui có, buồn có, đau đớn có, ý nghĩa có nhưng tất cả đều trở thành một phần kí ức còn mãi trong tâm trí của anh em.</p><p>Đó là câu chuyện về một bà cụ 61 tuổi sống cùng cha 85 tuổi. Bà bị té ngã và ngâm mình trong nước ngập rồi qua đời. 7 ngày con nước lên nhà lại nằm sâu bên trong biển nước bao la, cano phải chạy rất lâu mới tiếp cận được. Đó còn là câu chuyện về một cụ bà 91 tuổi đứng ngóng ở cửa chỉ vì nhờ người mở dùm cái nắp chai. Đó cũng là câu chuyện về cụ ông đã 67 tuổi vẫn phải lội nước từ xa đến với đoàn thuyền cứu trợ, lý do âu cũng bởi mấy ngày liền nhà ông đã không có điện, không có ánh sáng, không đồ ăn thức uống</p><p>Những cung bậc cảm xúc đã có suốt hành trình cứu lũ ở miền Trung đã khiến anh em của Biệt đội 0 đồng củng cố thêm niềm tin rằng: Cứ cho đi và chúng ta sẽ nhận lại những gì chúng ta đã đánh đổi. Có thể là phần tiền hỗ trợ học phí cho con cái của chính các thành viên trong Biệt đội, có thể là một cái nắm tay, một cái ôm, một nụ cười hy vọng, một lời cảm ơn chân thành:</p><p>\"Chúng tôi luôn tâm niệm rằng nếu chỉ có lòng tốt thôi thì vẫn chưa trọn vẹn, nó cần kết hợp với sự thương người cũng như thông điệp: Trong cơn bão lũ, không để ai rơi lại phía sau\".</p><p>Những hành trình mới, những tình yêu thương lan rộng theo cách mới</p><p>Cuộc sống vốn là thế, bạn không thể biết được ngày mai và những điều bất ngờ cái nào đến trước. Cách duy nhất giúp chúng ta vượt qua nó chính là học cách thích nghi. Năm 2020 đã qua chính là một thử thách như vậy, không chỉ với Biệt đội cano 0 đồng mà với tất cả mọi người. Thế nhưng hành trình \"từ không đến có\" vẫn luôn tồn tại và có thể dễ dàng đạt được nếu bạn luôn ý thức được bản lĩnh tiềm tàng trong mình.</p><p>Bỏ lại sau lưng những trăn trở của một năm mưa lũ, dịch bệnh, kinh tế đình trệ… bủa vây, tập thể Biệt đội cano 0 đồng hướng tới một năm 2021 với hàng loạt các kế hoạch, dự định hướng tới cộng đồng đúng như tôn chỉ ban đầu đã đặt ra, thậm chí là nhiều hơn thế.</p><p>\"Những gì làm từ tâm thì sẽ chạm đến tâm\", đó là thông điệp mà trong suốt quá trình đồng hành, các anh em trong đội luôn nhắc nhở nhau. Không chỉ dừng lại ở mặt ý tưởng, các hành động thiết thực hơn cũng đang được triển khai, trong đó có chương trình Du thuyền tham quan bán đảo Sơn Trà cho các bạn nhỏ có điều kiện khó khăn hoặc mồ côi cha mẹ, hoặc những chương trình tour du lịch thiện nguyện nhằm quyên quỹ cho Biệt đội. Ngoài ra, Biệt đội cano 0 đồng luôn luôn sẵn sàng trong tư thế chuẩn bị để phối hợp cùng chính quyền địa phương trong công tác cứu hộ cứu trợ trong những lúc khẩn cấp.</p><p>\"Nếu có ai hỏi chúng tôi rằng: Bạn có thấy đánh đổi và hi sinh của bạn ở Biệt đội cano 0 đồng là xứng đáng? Chúng tôi sẽ dùng dấu ba chấm như một câu trả lời mở với nhiều cung bậc cảm xúc khác nhau, nhiều góc nhìn khác nhau để mỗi khi nhìn lại, mỗi mảnh ghép tạo nên bức tranh của Biệt đội cano 0 đồng ngày đó sẽ luôn đầy tự hào và xứng đáng\".</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177600785441641304274.jpg\" ><figcaption class=\"caption\">Những cuộc giải cứu diễn ra ngay trong đêm</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-2-16177600790461353360232.jpg\" ><figcaption class=\"caption\">Chỉ trong một thời gian ngắn, lời hiệu triệu đã được lan tỏa rộng khắp</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-3-16177600795991321757970.jpg\" ><figcaption class=\"caption\">Những chuyến cano, thuyền hơi rẽ lũ mà đi</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-4-16177600785791229381240.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-5-16177600790911137116378.jpg\" ><figcaption class=\"caption\">Những hình ảnh mà anh em Biệt đội không bao giờ quên</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-6-1617760078634321303484.jpg\" ><figcaption class=\"caption\">Năm 2021, Biệt đội có nhiều dự định hơn</figcaption></figure><p>Nắm bắt mọi thời cơ, quyết liệt tiến lên để chinh phục những đỉnh cao mới, Tiger Beer tin rằng: Chỉ cần có bản lĩnh, sẽ luôn có cách để bạn chinh phục những đỉnh cao mới. Khi đã chọn bắt đầu, hãy bắt đầu thật quyết liệt, hăng say để cùng thế hệ của mình vươn mình bứt phá từ hôm nay.</p>', NULL, 'biet-doi-cano-0-dong-ban-linh-cua-tap-the-khong-ai-muon-mot-cai-ten-rieng-san-sang-hy-sinh-va-danh-doi-vi-nghia-cu-cong-dong', 0, NULL, NULL),
(107, 16, 5, 1, 1, 'Cực phẩm trai đẹp xuất hiện trên sóng VTV, ảnh chất lượng thấp nhưng độ mlem thì chất lượng cao', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617759729063-16177597292981413738433.jpg', '|', 'Phải chăm xem thời sự hơn đi các bác ôi!', '<p>Không phải ai cũng thường xuyên xem TV nhưng nếu bạn quan tâm, các chương trình trên truyền hình cũng có nhiều nội dung thú vị, hữu ích và đang ngày càng hấp dẫn hơn. Và đặc biệt, nếu bạn là một thành viên của hội thích ngắm trai đẹp thì có lẽ bạn nên tick ngay việc xem truyền hình vào list sở thích. Bởi lẽ trai đẹp bây giờ có thể xuất hiện ở bất kì đâu, kể cả các bản tin... thời sự những tưởng khô khan, nhàm chán.</p><p>Mới đây, cộng đồng mạng đã like, share điên đảo hình ảnh về một trai đẹp trên sóng thời sự VTV như thế. Chỉ 2 bức ảnh chụp màn hình với chất lượng mờ căm nhưng vẫn không thể ngăn được các cô gái phát cuồng. Bởi lẽ anh chàng xuất hiện trong hình thực sự \"cực phẩm\" với vóc dáng cao ráo, đôi kính thư sinh cùng bộ vest chỉn chu.</p><p>Đến thời điểm, mọi thông tin đến trai đẹp này vẫn nằm trong vòng bí ẩn. Không rõ anh chàng này là phiên dịch viên hay thư ký Quốc hội, lên sóng trong bản tin vào ngày nào giờ nào. Và đương nhiên, tên tuổi của anh chàng cũng đang là ẩn số. Dẫu vậy, bằng sự đam mê cũng như tài năng tìm kiếm đỉnh cao của mình, hẳn info của anh chàng sẽ sớm bị \"nổ\" thôi.</p><p>Bên dưới hình, dân tình đã để lại rất nhiều bình luận thể hiện sự yêu mến dành cho trai đẹp thời sự này:</p><p>- Thời sự hôm nay có nội dung xịn quá ha. Từ nay phải chăm xem thời sự hơn mới được.</p><p>- Mèn đét ơi ta nói cực phẩm gì đâu. Mà không chỉ đẹp, chắc chắn anh này phải rất giỏi nữa.</p><p>- Anh ấy là thư ký nha mọi người, ký tên vào bản đăng ký kết hôn với mình nè!</p><p>- Dù out nét nhưng anh vẫn \"bén\" quá!</p><p>- Ngày nào cũng coi thời sự với ba, đúng hôm không coi thì có cực phẩm xuất hiện, chúng mình đúng là không có duyên rồi. \"Chằm Zn\" ghê!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/img7723-1617759601494653010914.jpg\" ><figcaption class=\"caption\">Dù ngồi ở góc xa nhất nhưng anh chàng vẫn lọt vào tầm ngắm của cư dân mạng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/16757255917706656531441781800112906757703822n-1617759659378550725251.jpg\" ><figcaption class=\"caption\">Ảnh thì mờ căm nhưng chất lượng của \"ảnh\" thì không mờ chút nào nha</figcaption></figure><p>Ảnh: Tổng hợp</p>', NULL, 'cuc-pham-trai-dep-xuat-hien-tren-song-vtv-anh-chat-luong-thap-nhung-do-mlem-thi-chat-luong-cao', 0, NULL, NULL),
(108, 16, 5, 1, 1, 'Dân chơi hệ trượt ván ở Sài Gòn \"tám chuyện\": Bố mẹ nói tự chơi tự chịu, bạn gái lúc đầu thấy ngầu sau cũng đòi bỏ!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/6/photo1617728267441-1617728269065186573204.jpg', '|', 'Đây sẽ là lần đầu tiên những người chơi hệ Skateboard - trượt ván kể về thú vui này của mình.', '<p>*Ngoài những hình ảnh \"mlem\" nóng bỏng của những chàng trai cô gái chơi trượt ván, bài viết có nhiều nội dung mà độc giả tuyệt đối không được làm theo tại nhà nếu không có người chơi hệ Skateboard (trượt ván) chuyên nghiệp hướng dẫn.</p><p>\"Tôi không thích con mình chơi trò này, nó quá mạo hiểm.\"</p><p>Một phụ huynh đang dẫn con nhỏ đi dạo ở phố đi bộ vừa trả lời xong câu hỏi của tôi thì nghe một cái \"ĐÙNG\". Một cậu thanh niên vừa làm cú Backside (trượt ván rồi xoay một vòng) thất bại, cú ngã rất đau vì anh bạn liên tục nhăn mặt. Lập tức cô nói tiếp:</p><p>\"Đó, cậu thấy chưa, chơi như vậy ba mẹ nào chịu nổi?\"</p><p>Khi được sếp giao cho đề tài này, tôi đã thử xem YouTube và thấy những người chơi hệ Skateboard (tên tiếng Anh của trượt ván) họ thật ngầu. Tuy nhiên ra ngoài hiện trường, cảm nhận của tôi về bộ môn này thật khác, nghiêm túc mà nói: Nó đầy nguy hiểm!</p><p>Nhưng chẳng phải ngày xưa Rap cũng từng có định kiến là kiểu âm nhạc \"tục\", không nghiêm túc đó sao? Bạn phải thật sự ngồi, chăm chú theo dõi những bạn trẻ chơi Skateboard để thấy ngo ài việc nhìn họ cool cool ra, trượt ván thực sự là một môn thể thao mạo hiểm đường phố thú vị.</p><p>Gặp những người chơi trượt ván \"mlem mlem\" ở Sài Gòn: Bố mẹ nói tự chơi tự chịu, bạn gái lúc đầu thấy ngầu sau cũng đòi bỏ!</p><p>Nói trắng ra, bộ môn này... đẹp trai lắm! Và câu chuyện của những người chơi nghe cũng thú vị nữa.</p><p>Vì sao \"u mê\" Skateboard, họ trả lời: Tôi biết rất nhiều người sợ ma nhưng thích xem phim kinh dị, chơi trò này cũng thế!</p><p>Khánh (2k1 - hiện đang là sinh viên RMIT), mỗi ngày lại chọn cho mình một chỗ chơi khác nhau, có khi là đối diện Vincom Lê Thánh Tôn (Q1), phố đi bộ Nguyễn Huệ, hoặc Crescent Mall (Q7)... Mỗi nơi lại có một địa hình, không khí chơi khác nhau. Khánh kể nhờ Skateboard, cậu bạn từ người nhút nhát mà trở nên tự tin và \"lì đòn\" hơn.</p><p>\"Chơi Skateboard, bạn phải chấp nhận việc ngã rất nhiều, thậm chí rất đau. Nhưng mỗi lần ngã xong, bạn khóc huhu thì coi như bỏ. Ở đây ngã xong ai cũng phải đứng dậy trượt tiếp, cuộc sống mình cũng vậy thôi\".</p><p>Khi tôi và Khánh đang trò chuyện lúc anh bạn nghỉ ngơi, một bạn nữ khác chêm vào: \"Cũng giống như nhiều người sợ ma nhưng thích coi phim kinh dị vậy đó. Ở đây toàn những vua lì đòn, ngã mãi mà chẳng biết sợ là gì\".</p><p></p><p>*Khánh đề nghị không chụp hình vì sợ... bố mẹ thấy.</p><p>Các bạn nói rất nhiều về sự không an toàn của Skateboard, vậy nó có thể nguy hiểm đến mức nào: À, nhẹ thì ê mông, nặng thì chấn thương sọ não</p><p>Chuyện những người chơi trượt ván bị chấn thương nó đều như chúng ta hít thở và ăn cơm mỗi ngày vậy. Có thể bạn đến với trò chơi này vì nhìn nó \"trend\", lướt trên tấm ván làm vài cú xoay 360 độ (dân trượt ván gọi nó là Caballeria) trông chất chất. Nhưng thật sự khi đã bắt đầu vào game, nếu không đủ đam mê thì bạn có thể nghỉ luôn sau 5 phút đứng lên tấm ván trượt.</p><p>\"Nhiều người thấy tụi mình chơi cũng hào hứng mua thử ván trượt để tham gia cùng, nhưng họ bỏ cuộc sau 5 phút đứng lên tấm ván là chuyện bình thường. Chơi được trò này thích thôi chưa đủ, bạn phải biết chấp nhận những cú ngã đau của nó nữa cơ\" - Hà, cô gái 2k3 hiện đang là sinh viên trường Luật chia sẻ.</p><p>Lộc (2k3, đang là sinh viên) thường chơi ở Crescent Mall quận 7 nói thêm: \"Mình từng thấy một người khi chơi trượt ván, vì chống tay hơi mạnh mà gãy trẹo xương, đi cấp cứu không kịp. Skateboard luôn tiềm ẩn những rủi ro nhất định nếu bạn không thật sự nghiêm túc tập trung vì ngã khi chơi là chuyện... bình thường\".</p><p>Có ai phản đối các bạn chơi Skateboard không: Nhiều chứ, bạn gái ban đầu còn thấy ngầu ngầu, sau thấy ngã nhiều quá còn đòi bỏ luôn!</p><p>Trong số những nhân vật tôi trò chuyện, Hùng (2k1) là chàng trai thú vị nhất. Thường xuyên \"cắm cọc\" ở Crescent Mall, Hùng rủ thêm bạn mình gia nhập bộ môn này. Mỗi lần chơi đều dẫn theo bạn gái. Bạn của Hùng tranh thủ lúc anh chàng đang làm việc riêng mà kể xấu: \"Nó chơi hay nên có bạn gái, em mới chơi còn dở nên... chưa kiếm được ai!\"</p><p>Bạn gái của Hùng ngồi một lát rồi bắt Grab về, Hùng lại kể chuyện bạn gái mình không thích Skateboard như thế nào: \"Hồi mới quen nó ủng hộ mình dữ lắm, khen ngầu, được thời gian thì cứ luyên thuyên mãi không cho chơi nữa vì thấy mình té nhiều quá\".</p><p>Một cô bé khác cũng chỉ tầm học cấp 2 kể với tôi: \"Bố mẹ em cho chơi chứ, nhưng cũng nói luôn là tự chơi tự chịu, đau thì đừng có về nhà than là được\".</p><p>Nhìn chung, phụ huynh hay bạn trai bạn gái của những bạn trẻ chơi Skateboard đều luôn chuẩn bị sẵn tâm thế là người thân của mình chơi trò này, kiểu gì cũng bị ngã, chấn thương từ nặng tới nhẹ. Nhưng biết sao được, mê quá rồi.</p><p>Skateboard là môn thể thao mạo hiểm, nhưng chúng ta có cách để chơi nó thật an toàn</p><p>Cũng như các môn thể thao vận động khác, những bạn trẻ chơi Skateboard đều khuyên những người mới chơi đều phải khởi động, làm nóng cơ thể trước khi chính thức đặt chân lên ván trượt.</p><p>Đừng thấy những đàn anh, người chơi Skateboard lâu năm làm những động tác khó mà vội vàng học theo. Skateboard muốn giỏi bạn phải biết... từ từ, phải làm quen cảm giác chân với ván trước, sau đó mới đi từ những động tác dễ đến khó.</p><p>Đặc biệt, dù không phải là môn thể thao đồng đội nhưng Skateboard buộc bạn phải chơi ở nơi thật đông người. Lý do là để những khi xảy ra tình huống nguy cấp, luôn có người bên cạnh, sẵn sàng giúp đỡ đưa bạn đến phòng khám gần nhất.</p><p>Về độ tuổi, hầu như ai cũng có thể chơi Skateboard.</p><p>Trong số những người chơi trượt ván tôi từng trò chuyện, Gia Khiêm là người ít tuổi nhất khi sinh năm 2012. Chú bé kể mình biết chơi trò này từ năm 5 tuổi, nghĩa là đã có đến 4 năm gắn bó với Skateboard. Gia Khiêm được bố mẹ chuẩn bị đầy đủ từ ván trượt, đồ bảo hộ tay chân, mũ bảo hiểm.</p><p>Cậu bé kể: \"Bố mẹ không cấm em chơi trò này đâu, còn mua cả đồ bảo hộ. Nhưng chỉ được chơi vào những ngày cuối tuần để không ảnh hưởng việc học\".</p><p>Ngoài ra, chú bé cũng tỏ ra ghen tị với một cậu bé 5 tuổi khác vì bạn nhỏ ấy biết làm cú xoay mà 4 năm nay Gia Khiêm vẫn chưa làm được: \"Bình thường nó cũng hay ra đây chơi giờ này nè, nay không thấy đâu\".</p><p>Dành riêng cho team khán giả: Những bạn chơi Skateboard rất \"mlem mlem\"</p><p>Trai đẹp là một tài nguyên, không ngắm người ta nói đó là lãng phí!</p><p>Và những chàng trai chơi Skateboard, nghiêm túc mà nói trông họ rất đẹp. Không phải kiểu baby cute, soái ca nam thần Hàn Quốc, những người chơi trượt ván trông vô cùng nam tính. Nói theo lời một bạn nữ khán giả ở Crescent Mall nhận xét đó là: \"Thanh xuân vô cùng hừng hực!\"</p><p>Muốn biết vì sao cô nàng lại gọi nó là như thế thì mời xem qua chùm ảnh bên dưới sẽ rõ:</p><p>Một vài địa chỉ bạn có thể ghé đến để xem (hoặc chơi) Skateboard tại Sài Gòn:</p><p>+ Trước Vincom mặt đường Lê Thánh Tôn (Quận 1)</p><p>+ Phố đi bộ Nguyễn Huệ, đoạn gần đường Tôn Đức Thắng (Quận 1)</p><p>+ Crescent Mall trước Phúc Long (Quận 7)</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a8114-1-16177270645991528421416.jpg\" ><figcaption class=\"caption\">Skateboard được nhiều người lớn cho là môn thể thao quá nguy hiểm</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a8318-161772733172322863554.jpg\" ><figcaption class=\"caption\">Phải thật sự đam mê thì mới chơi được Skateboard</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a8238-1617727331775642382073.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a8421-1617727389771142623761.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/dsc08783-1617727466905385317320.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/dsc08675-16177275295931174854024.jpg\" ><figcaption class=\"caption\">Quần của Hùng hằn rõ những vết bẩn vì ngã nhiều lần</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a8036-16177276316482027345063.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/413a8039-1617729488600362677848.jpg\" ><figcaption class=\"caption\">Bạn phải đảm bảo chuyện luôn có người hỗ trợ mình khi có sự cố xảy ra</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/413a8109-16177295565711171750607.jpg\" ><figcaption class=\"caption\">Bạn nhớ chơi trượt ván ở nơi đông người nhé!</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/dsc08805-1617727671956523404561.jpg\" ><figcaption class=\"caption\">Gia Khiêm đã chơi bộ môn này được 4 năm</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a8139-16177277620012023466807.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a8360-1617727762050888321787.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a8175-1-16177277620851721022982.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a8058-1-1617727762091426032766.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/6/413a8042-1-16177277621331245920219.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/artboard-1-16177296019341710550229.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Thời gian chơi từ 17:00 đến 22:00.</p>', NULL, 'dan-choi-he-truot-van-o-sai-gon-tam-chuyen-bo-me-noi-tu-choi-tu-chiu-ban-gai-luc-dau-thay-ngau-sau-cung-doi-bo', 0, NULL, NULL),
(109, 17, 5, 1, 1, 'Cô gái Hà Nội giấu bố mẹ đi kéo chân: Không mua quần áo suốt nhiều năm để tiết kiệm, leo lên bàn mổ với ước mơ cao thêm 7cm', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/2/photo1617299450550-16172994508861264336137.png', '|', 'Với chiều cao ban đầu là 1m48, Tâm muốn cao thêm 7cm để tự tin hơn trong cuộc sống. Để có sự thay đổi, Tâm đã trải qua một hành trình dài...', '<p>Yêu cầu công việc: Nữ, cao từ 1mxx trở lên.</p><p>Chiều cao: Trên 1mxx.</p><p>Cho mình hỏi chiều cao, cân nặng của bạn với ạ?</p><p>Bạn cao 1m bao nhiêu? Hình như bạn lùn đúng không?</p><p>…</p><p>Chúng ta có thể bắt gặp những mẫu câu như trên ở bất cứ đâu trên MXH, trong các thông tin tuyển dụng, câu hỏi dành cho các KOL trong phần \"ask\" Instagram. Chiều cao thật sự là một nỗi ám ảnh về ngoại hình với không ít người, đặc biệt là khi Gen Z ngày càng sở hữu chiều cao vượt trội. Những công việc hấp dẫn, xịn xò hiện nay đều đưa yếu tố chiều cao vào như là một điều kiện cần trong tuyển dụng.</p><p>Tâm (nickname hyanna115, sinh năm 1996, sống tại Hà Nội) vừa  phẫu thuật kéo chân được 30 ngày. Cô bạn có chiều cao ban đầu là 1m48 và hy vọng mình có thể cao thêm 7cm sau khi kéo chân. Mỗi ngày, Tâm kéo khung 3 lần, mỗi lần cách nhau 8 tiếng. Khung có 6 mặt, cứ 3 mặt tương ứng 1mm. Đó là một hành trình gian nan, cần tâm lý vững và chịu đau giỏi.</p><p>Theo như chia sẻ của một số người dùng trên Reddit, cơn đau khi tập rướn kéo khung trong giai đoạn 2 của quá trình kéo chân này lớn hơn toàn bộ những gì mà một người có thể trải qua từ nhỏ đến lớn, giống như bị tra tấn vậy. Thậm chí, nó khiến bệnh nhân... hóa điên, phải điều trị tâm lý. Và thống kê thực tế cũng cho thấy đa số đều hối hận vì đã trải qua những cơn đau này.</p><p>Tâm tập những bài tập chân mỗi ngày sau khi phẫu thuật</p><p>“Mình ý thức được chiều cao quan trọng trong cuộc sống như thế nào từ năm cấp 3. Mặc dù đã tìm đủ mọi cách để cao lên từ uống thuốc, tập thể dục… nhưng đều không có tác dụng. Và từ đó, mình đã tìm đến các biện pháp khác và cuối cùng là tìm hiểu về thông tin phẫu thuật kéo chân trong một bài báo trên mạng”, quyết định liều mình để cao hơn đến với Tâm như thế.</p><p>Cô gái 25 tuổi vào viện kéo chân một mình và trở về phòng trọ sau 10 ngày.</p><p>Trên MXH có một hội kín có đến 28K thành viên những người từng kéo chân hoặc có ý định kéo chân. Tâm join vào group và được giới thiệu đến bác sĩ Đ. - người từng thực hiện nhiều ca phẫu thuật kéo chân ở Hà Nội. Năm đó, Tâm đang học Đại học. Tâm nhận được câu trả lời: “Em cứ học xong đi rồi hẵng kéo\".</p><p>Sau khi tốt nghiệp ĐH, để có số tiền lớn thực hiện ước mơ của mình, Tâm làm 3-4 công việc một lúc gồm: Phiên dịch tiếng Hàn, bán quần áo order và dịch truyện tranh cho một số trang web.</p><p>“Mình đã không mua quần áo và mỹ phẩm trong nhiều năm liền để có tiền kéo chân. Chi phí phẫu thuật hiện tại là 175 triệu cho 2 lần phẫu thuật lắp khung chân và tháo khung. Nhưng ấy chỉ là chi phí cho 2 lần phẫu thuật, một lần nằm viện 10 ngày và một lần tháo khung 3 ngày trọn gói khi ở viện. Những cái khác mình vẫn phải tự lo như vật lý trị liệu khoảng 400-500k/lần, tiền ăn uống, chăm sóc… Mình đang dự tính phải vào khoảng 220 triệu đồng tất cả vì còn mua sữa, canxi và quan trọng nhất vẫn là ăn uống đủ chất\", Tâm nói.</p><p>Những bữa ăn rất nhiều dinh dưỡng sau khi Tâm phẫu thuật kéo chân</p><p>Tâm phải đặt hẹn trước vài tháng để được gặp bác sĩ mổ kéo chân. Cô chọn nằm ở Bệnh viện Đa Khoa Quốc Tế Bắc Hà (Hà Nội). Tâm giấu gia đình trong suốt quá trình thực hiện nên cô vào viện một mình và suốt 3 ngày đầu tiên sau mổ, Tâm được điều dưỡng bệnh viện chăm sóc.</p><p>“Trong lúc mổ, mình được gây tê màng cứng nên sau đó phải nằm một chỗ và ăn uống tại giường. Mình chỉ nhờ điều dưỡng chăm sóc khi ấy. Những ngày sau, mình cử động được thì mọi sinh hoạt cá nhân cơ bản mình đều tự làm. Hiện tại đã 1 tháng sau phẫu thuật, mình trở về nhà trọ sống với bạn mình. Thỉnh thoảng bạn có nấu ăn cho, còn lại mình vẫn tự lo được dù đang ngồi xe lăn”, cô gái chia sẻ.</p><p>Đến ngày thứ 7 sau khi mổ, quá trình kéo giãn tách xương bắt đầu. Tâm tự kéo giãn  chân theo chỉ định của bác sĩ: Một ngày 3 lần, mỗi ngày tăng thêm 1mm. Hiện tại, Tâm vẫn chưa biết mình có thể kéo tối đa bao nhiêu cm vì điều đó phụ thuộc vào cơ địa mỗi người. Có người kéo lên được tối đa 10,4cm, có người ít hơn.</p><p>Tâm chọn kéo khoảng 7cm vì đó là trong khoảng an toàn để cơ thể có thể hồi phục nhanh nhất và tốt nhất. Cô cũng được bác sĩ khuyến cáo rằng không đau thì cũng không được kéo giãn chân nhiều hơn 3 lần/ngày vì điều đó sẽ ảnh hưởng đến quá trình hình thành can xương.</p><p>“Mình đang ở ngày thứ 30 sau phẫu thuật, sức khỏe tốt, ăn uống tốt chỉ có giấc ngủ không được ổn lắm vì mình ngủ phải nằm ngửa, không xoay người được nên đêm có thể bị tỉnh nhiều lần”, Tâm là người có cơ địa cũng khá lành.</p><p>Nhật ký kéo chân của Tâm trên MXH</p><p>Hiện tại, Tâm đã cao thêm 2cm. Khoảng 2 tháng nữa, cô sẽ gặp bác sĩ để tháo khung nẹp ở chân.</p><p>Tâm chia sẻ clip trong những ngày nằm viện trên MXH nhưng xin được giữ kín danh tính vì… gia đình vẫn chưa biết quyết định này của cô. Cô giải thích, mình muốn kể về quá trình này vì trong quá trình tìm hiểu để làm phẫu thuật này, Tâm có tìm đến một vài anh chị đã xuất hiện trên những bài phỏng vấn để chia sẻ bí quyết kéo chân, nhưng mà cô không thể liên lạc được với họ. Cảm giác lúc đó khá hụt hẫng.</p><p>Tâm hy vọng mình công khai quá trình và thông tin phẫu thuật có thể tạo động lực và thêm niềm tin cho những bạn đang tự ti về chiều cao giống như mình.</p><p>Về phía gia đình, để giảm bớt lo lắng cho người thân, Tâm chọn giữ kín. Suy cho cùng, khi thấy mình cao lên cô sẽ tự tin hơn và có thể mạnh dạn làm những việc mình thích. Gia đình có thể không biết quá trình, chỉ cần nhìn vào kết quả cô hạnh phúc với những điều đã chọn là được.</p><p>\"Mình và bố mẹ ở cách nhau khoảng 40km. Bình thường, mình đã sống và làm việc xa nhà nên khoảng vài tháng không về nhà cũng không có gì lạ. Có anh phẫu thuật trước mình vài tháng bây giờ đã có thể chạy nhảy được rồi nên mình cũng hy vọng sớm phục hồi để về nhà và tìm kiếm cơ hội làm việc”, Tâm như chợt nhớ ra một chuyện, “À, khi kéo chân mình cũng hỏi bác sĩ là sau này em có mang giày cao gót được không? Bác sĩ bảo kéo chân xong mang giày cao gót có khi còn thoải mái hơn những giày khác do gân gót bị co, bàn chân có khả năng không để bằng phẳng được. Nhưng bác vẫn khuyên nên đi đế bằng để tập giãn gân gót, như vậy chân mới có thể sớm ổn định”.</p><p>Khi trải qua hành trình kéo chân, Tâm mới biết đang có rất nhiều bạn trẻ có mong ước giống mình và họ cũng tự để dành tiền để bước vào cuộc đại phẫu. Một tuần không cao điểm có từ 4-6 ca phẫu thuật, những tháng cuối năm có thể còn đông hơn. Có lẽ nhờ vậy, Tâm thấy quyết định của mình chỉ táo bạo thôi chứ không hề chấn động.</p><p>Trong những ngày vật lý trị liệu tại nhà, cô bạn đang cố gắng trau dồi nhiều kiến thức cho bản thân để đến khi hoàn thành việc kéo chân, sẽ có một cuộc sống tốt và tự tin hơn.</p><p>*Tên nhân vật đã được thay đổi.</p><p>Vào năm 2017, Hoàng Tùng Anh (sinh năm 1993, sống tại Hà Nội) cũng đã công khai quá trình kéo chân của mình trên Facebook, hút hàng chục ngàn lượt like và nhiều bình luận chia sẻ.</p><p>Người yêu của Tùng Anh đã rất sốc khi biết quá khứ kéo chân của bạn trai. Từ 1m67, Tùng Anh cao lên 1m76 sau khi kéo chân. Anh chia sẻ mình có thể khiêng bình nước nặng 20 lít và cõng người yêu như thường.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/1-16171083183371365977932.png\" ><figcaption class=\"caption\">Tâm đang chịu những cơn đau khủng khiếp nhưng cô có đủ động lực để vượt qua</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/unnamed-2-1-1617115235962348550044.jpg\" ><figcaption class=\"caption\">Chân của Tâm sau ca mổ, có thể thấy rõ từng đoạn gãy rời</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/30/unnamed-4-16171152674601583960301.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/unnamed-3-1617115340679962001624.jpg\" ><figcaption class=\"caption\">Tâm phải trả 400-500k/lần cho một buổi vật lý trị liệu</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/unnamed-1617115379868318862997.jpg\" ><figcaption class=\"caption\">Tâm chụp ảnh kỷ niệm trong những ngày còn ở trong viện</figcaption></figure><p>Quá trình kéo dài chân sẽ bao gồm 3 giai đoạn: mổ trực tiếp, kéo dài và phục hồi. Trong đó, 2 giai đoạn đầu tiên cơ thể phải chịu đựng những cơn đau cực kỳ khủng khiếp.</p>', NULL, 'co-gai-ha-noi-giau-bo-me-di-keo-chan-khong-mua-quan-ao-suot-nhieu-nam-de-tiet-kiem-leo-len-ban-mo-voi-uoc-mo-cao-them-7cm', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(110, 17, 5, 1, 1, 'Jsol - chàng ca sĩ \"nhai đầu mèo\" cực hot trên TikTok: Mỗi tháng tốn 20 triệu nuôi 14 con thú cưng, stress lắm nhưng không bỏ được!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/30/photo1617119652828-16171196532941131421500.png', '|', 'Nuôi 1 con mèo hay 1 con chó thôi đã làm bạn mệt rồi, đằng này Jsol nuôi tận... 14 con chó mèo ở nhà. Bạn có tưởng tượng nổi không?', '<p>Nhắc đến người chơi hệ \"chó mèo\" nổi tiếng nhất nhì trên TikTok Việt Nam, nhiều người sẽ nghĩ ngay đến Jsol - chàng ca sĩ điển trai sinh năm 1997 này có một kênh TikTok 4,4 triệu lượt follow với hơn 71,9 triệu lượt thả tim. Những con số tương tác \"khủng\" này phần lớn đều đến từ các clip \"nhai đầu mèo\" cực kì viral.</p><p>Lúc vừa gặp Jsol, tôi đã phải hỏi ngay: Nhai đầu mèo nhiều thế... miệng có 1 bụm đầy lông không? Cậu bạn trả lời thật thà là dính nhiều đến mức đánh răng cũng cảm giác còn lông mèo trên miệng mình. Nhưng được cái vui, mọi người cũng thích.</p><p>Còn riêng tôi trong suốt buổi nói chuyện, đôi khi còn trộm nghĩ hay nghề chính của anh bạn này là chăm chó mèo, ca sĩ chỉ là nghề tay trái thôi nhỉ chứ nuôi 14 con lận mà... Làm gì còn thời gian nữa. Tuy nhiên, công cuộc nuôi thú cưng bây giờ hiện đại hơn nhiều rồi, Jsol kể nhà có đủ \"máy móc\" hiện đại, tự động dọn phân chó mèo, có máy cho mèo uống nước. Chó được dạy đi vệ sinh... Nói chung cũng tiện lợi đầy đủ, không quá mệt nhưng vẫn đủ có những sự cố phát sinh làm cậu bạn stress. </p><p>Clip: Jsol - chàng ca sĩ \"nhai đầu mèo\" cực hot trên TikTok kể chuyện mỗi tháng tốn 20 triệu nuôi 14 con thú cưng, stress lắm nhưng không bỏ được!</p><p>Nuôi chó mèo, hãy chuẩn bị sẵn cho mình tâm lý chán nản</p><p>14 con chó mèo, tôi nghe con số thôi đã thấy cả một bầu trời... suy nhược rồi đấy Jsol ạ!</p><p>Tâm lý của những người yêu động vật nói chung và \"con sen\" nói riêng nó thế bạn ơi... Ban đầu mình nuôi đứa này, xong lướt Facebook lại thấy người ta nuôi giống khác, mê quá thế là phải đi tìm xem rốt cuộc con mèo của họ tên gì. Ban đầu không có ý định mua đâu, nhưng chẳng hiểu sao nhà có 14 đứa...</p><p>Tôi thường nghe người ta bảo nuôi chó mèo rất chill, rốt cuộc cảm giác \"chill\" đó nó như thế nào vậy?</p><p>Chill trong sự hạnh phúc đó bạn!</p><p>Tôi tin gia đình nào nuôi chó mèo cũng xem chúng như con của mình. Đi xa mà ngắm ảnh chó mèo của bạn bè hay thấy ngoài đường chạy nhong nhong là nhớ bầy con ở nhà của mình kinh khủng. Hơn nữa, với một người độc thân như mình, có mấy đứa này sẽ không còn cảm thấy cô đơn, ở nhà thì cũng có đứa chạy ra chạy vào. Thấy chúng cắn nhau trong bụng cũng vui nữa.</p><p>Người ta nuôi 1 con mèo thôi đã thấy stress, bạn nuôi 14 con cả chó lẫn mèo lại thấy hạnh phúc... bạn nhiều năng lượng đấy chứ?</p><p>Làm gì có! Này nhé, nuôi chó mèo với số lượng thế này bạn cần chuẩn bị tâm lý... chán nản. Ví dụ đi làm về một ngày rất mệt, mở cửa ra thấy mọi thứ tung tóe tất cả lên. Nghĩ lại những khoảnh khắc như thế thôi mình đã stress lắm rồi. Có những đứa nó không chịu đi vệ sinh đúng chỗ, cái đó phải dạy từ từ. Nhưng thấy cảnh đó, tôi tin 99,99% người nuôi đều sẽ lóe lên suy nghĩ: Hay cho người thân, không thì bán quách cho xong chứ như này nuôi sao nổi?</p><p>Cuối cùng vẫn thấy tụi nó đáng yêu, vẫn tiếp tục yêu thương. Bây giờ đang gửi nhờ 3 đứa cho người thân vì chúng nó cắn nhau dữ dội quá, vậy đó mà cũng thấy rất nhớ rồi, nếu cho thật thì chắc chịu không nổi.</p><p>Mỗi cây mỗi hoa - mỗi nhà mỗi con mèo có tính tình khác nhau</p><p>Nếu người yêu bắt bạn lựa chọn giữa chó mèo và cô ấy thì sao?</p><p>Tôi có \"luật\" là phải tìm 1 người, mà cô gái ấy phải yêu chó mèo.</p><p>Từ trước đến giờ nếu có quen hay yêu ai thì họ cũng đều là những người yêu chó mèo. Về cơ bản tôi cảm thấy những người ghét động vật thì khó tạo được thiện cảm với bản thân mình ngay từ đầu.</p><p>Ok vậy cô gái bạn yêu cũng thích chó mèo đi, nhưng quen một thời gian cô nàng lại bị dị ứng với chúng thì sao?</p><p>Ca này căng nhỉ? Nhưng dị ứng chó mèo thì cũng có cách: Cho bọn trẻ ở phòng riêng, không thì nuôi mấy con không lông như mèo Sphynx không có lông.</p><p></p><p>Tôi nghe người ta bảo, chó mèo cũng biết tị nạnh với nhau, bạn thường giải quyết mâu thuẫn của đàn con mình kiểu nào?</p><p>Ôi có chứ! Nhà có 6 con mèo, chia làm 2 gia đình, cả bọn nó là 2 hàng xóm \"xấu tính\" khi thường xuyên đánh nhau không nể nang con nào. Gần đây con mèo to nhà A có cắn lộn với con mèo mới đẻ nhà B. Giải pháp của tôi đó là đem con mèo to \"du côn\" kia nhờ người ta trông hộ rồi. Cái này mỗi cây mỗi hoa mỗi nhà mỗi con mèo có tính tình khác nhau, có người nuôi hòa hợp được, còn đã đánh nhau thì đánh mãi... Thế nên cho đi \"giáo dưỡng cách li\" là tốt nhất.</p><p>Bị bà hàng xóm \"chửi\" everyday vì nuôi chó mèo</p><p>Đã bao giờ bạn bị chó mèo cắn đồ hiệu chưa?</p><p>Chưa... Nhưng nghĩ tới thôi đã thấy tức lắm rồi đây. Bình thường đôi dép đi chợ để dưới nhà thấy nó cắn tan nát là đã bực bội trong người. Này cắn đồ hiệu chắc phải cho trận đòn (cười lớn). Đùa thôi, may mà nó chỉ suýt cắn thôi, mình phát hiện kịp.</p><p>Thế thiệt hại lớn nhất chó mèo từng gây ra cho bạn là gì?</p><p>Hì, nó từng cắn nát cái yếm xe máy của mình.</p><p>Đã bao giờ bạn trải qua cảm giác kinh khủng nhất là để bọn nhỏ chạy đi mất chưa?</p><p>Mình từng mất con Corgi, phải gần một tháng mới tìm được. Trời ơi trong suốt thời gian đó, ăn không ngon ngủ không yên, chiều nào cũng ra đứng trước ngõ xem xem có thấy được cái xe bắt con mình đi hay không. May mà nhờ đăng lên Facebook, có người thấy sau đó tìm được.</p><p>Hàng xóm có phàn nàn về việc nhà bạn nuôi quá nhiều thú cưng không?</p><p>Biết rồi, khổ lắm... nói mãi, lúc nào cũng nói luôn ấy. Bởi vì nhà hàng xóm cũng nuôi chó, mỗi khi bà ấy thả chó ra ngoài cho đi vệ sinh, mình cũng thả ra ngoài cho đi vệ sinh là hai con lao vào nhau như có duyên âm từ kiếp trước.</p><p>Có hôm mình dẫn chó đi vệ sinh, mình thì hay dẫn đi khuya, thả nó ra là con chó nhà bà ấy \"auto\" sủa to cho cả làng nghe rồi làm bà mất ngủ. Có lần dẫn chó đi vệ sinh lúc 2 giờ sáng, mình quay qua quay lại thấy nó đi tè ngay trước nhà, ngẩng đầu lên thì bác đang đứng trên lầu nhìn xuống và... chửi. Nhục vì con là có thật, hôm sau phải qua xin lỗi luôn và ngay.</p><p>Không nuôi 14 đứa nhỏ chắc cũng đủ tiền mua... 1 căn nhà nhỏ nhỏ</p><p>Jsol có nhớ đã chi bao nhiêu tiền cho bọn nhỏ chưa?</p><p>Nhiều... rất nhiều. Tiền mua bọn nó đã là một phần, nhưng cái công mình bỏ ra mới đáng nói. Sương sương: Bị bệnh là chi tiền, đồ chơi - đồ ăn - đồ uống là chi tiền, chưa kể cát chó mèo, đi spa các kiểu.</p><p>Chưa kể phải chăm từng đứa một, tính tình từng đứa đâu có giống nhau đâu? Đứa thích ăn hạt, đứa thích ăn thịt, đứa thích ăn pate. Còn xem chúng nó thích ăn gì, đứa nào thích ăn gì mua cái đó. Nói chung là... stress! Tháng tốn ít nhất cũng trên dưới 20 triệu.</p><p>Nếu không nuôi chó mèo như thế này bạn có đủ tiền mua thêm căn nhà nữa không?</p><p>Chắc đủ tiền mua một căn nhà nhỏ...</p><p>Có bao giờ chó mèo ốm bạn phải hủy show để ở nhà chăm nó chưa?</p><p>Hủy show thì chưa nhưng tụt mood làm việc thì có. Có hôm phải ra Hà Nội, 5 giờ sáng bay thì hay tin 3 con chó trong nhà chạy đi đâu mất tiêu. Mình phải lao một mạch đi về nhà xong đi tìm, nói luôn với anh quản lý là nếu không tìm thấy thì không đi đâu nữa, vì mình rất buồn. May mà tìm được... hú hồn các con ơi là các con.</p><p>Jsol kể về một số dịch vụ bạn đã từng chi cho 14 đứa con của mình được không?</p><p>Nhiều lắm, chó mèo bây giờ sướng hơn cả người.</p><p>Mình với bạn còn có ý định tự mở khách sạn cho bọn nó vì giá đắt quá đi. Gửi hai đứa ra ngoài 1 ngày thôi đã tốn 2 triệu. Chưa kể thì chó mèo còn được cắt tỉa lông, làm nail, xịt nước hoa, tất cả các thể loại vệ sinh, nói chung rất là đắt.</p><p>Người ta làm đâu tính theo con, nó tính theo kg. Con nào càng mập, càng to là càng tốn tiền.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/413a0046-16171201798892133001503.jpg\" ><figcaption class=\"caption\">Nhà Jsol bước vào... mèo everywhere</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/413a0034-16171202478671381064471.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/413a0010-1617120848563239967291.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/413a0072-1-16171208104551691962689.jpg\" ><figcaption class=\"caption\">Đây là mèo Sphynx, nhà Jsol có 3 con giống này</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/413a0061-1-1617120786059878037300.jpg\" ><figcaption class=\"caption\">Bé Phốc sóc nhà Jsol hay được ưu ái là \"thảo mai\" vì thấy người là auto nịnh</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/30/413a0088-1-1617120690691870112252.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/413a0114-2-1617120660006756159480.jpg\" ><figcaption class=\"caption\">Bé mèo này nhìn vô hại vậy chứ cũng \"du côn\" với các con mèo khác lắm nha</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/413a0145-1-1617120637685515422686.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/413a0134-1617120889976673198773.jpg\" ><figcaption class=\"caption\">Chân dung con chó tè bậy trước nhà bà hàng xóm đây</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/31/413a0154-1-16171236852322017650769.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/413a0067-1617120957402511033671.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Cám ơn Jsol và 14 \"đứa nhỏ\" về buổi phỏng vấn thú vị này nhé!</p>', NULL, 'jsol---chang-ca-si-nhai-dau-meo-cuc-hot-tren-tiktok-moi-thang-ton-20-trieu-nuoi-14-con-thu-cung-stress-lam-nhung-khong-bo-duoc', 0, NULL, NULL),
(111, 17, 5, 1, 1, 'Gái xinh 2k kể chuyện yêu \"ông chú\" người Nhật hơn 16 tuổi: Mình không sống dựa tiền đàn ông, mây tầng nào gặp mây tầng đó', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/30/photo1617121387031-16171213871771640024902.jpg', '|', 'Dù yêu xa nhưng ngày nào cũng như ngày nào, Trang và \"chú\" người yêu vẫn gọi điện video, nhắn tin quan tâm nhau từng chút một.', '<p>Ở tuổi 18 - 20, con gái rất dễ rung động trước một người. Đó có thể là đàn anh hoạt động cùng trong hội sinh viên, là cậu bạn làm thêm cùng tiệm trà sữa, là anh quản lý điển trai ở nhà sách khu trung tâm,... Còn với gái xinh Phan Thị Thùy Trang (SN 2000), đến từ Bà Rịa - Vũng Tàu, người \"cướp\" đi trái tim của cô nàng lại là Jin - một \"ông chú\" người Nhật (SN 1984) hơn Trang tới 16 tuổi. </p><p>Được biết, Thùy Trang đang là sinh viên năm 3 khoa Mỹ thuật công nghiệp của một trường đại học tại Sài Gòn. Trong khi đó, người yêu Trang làm trong lĩnh vực kinh doanh và đầu tư, anh thường di chuyển qua lại giữa Nhật Bản - Singapore và Việt Nam. </p><p>Chuyện tình giữa Trang và người yêu hơn tuổi là một chuyện tình có bắt đầu rất tình cờ, một chuyện tình với nhiều khoảng cách, một chuyện tình vượt qua bao khó khăn nhưng đồng thời cũng là một chuyện tình hết sức dễ thương. </p><p>Việt Nam và Nhật Bản, 19 tuổi và 35 tuổi </p><p>Sài Gòn, một ngày của những tháng cuối năm 2019, Trang đi ăn tại một nhà hàng ở Quận 1. Trên đường đi vệ sinh, Trang đi ngang qua bàn của người khác và vô tình đánh rơi đồ, nhặt lên giúp cô là một người đàn ông Nhật Bản - người sau này đã trở thành bạn trai của Trang. Khi đó, Trang 19 tuổi còn nửa kia của cô 35 tuổi.</p><p>Mọi việc diễn ra hết sức tự nhiên, \"ông chú\" Jin xin số Trang để làm quen, cả hai giữ liên lạc rồi từ từ tìm hiểu. Lần thứ hai gặp mặt tại Việt Nam, \"ông chú\" hẹn Trang đi ăn và bất ngờ tỏ tình. Không phô trương giữa đám đông như trong phim, chỉ đơn giản là bữa tối nhẹ nhàng, yên tĩnh bên bờ sông với 2 con người, có hoa, có nến, có nhẫn và một lời thổ lộ. Cảm nhận được sự chân thành, lãng mạn từ người đàn ông ngoại quốc hơn mình cả chục tuổi, Trang gật đầu đồng ý. Hai người chính thức yêu nhau, tính đến nay đã hơn 1 năm.</p><p>Chuyện tình cô gái 2k và \"ông chú\" người Nhật hơn 16 tuổi</p><p>Gần 16 tháng hẹn hò, nhưng chính xác Trang và người yêu chỉ được ở gần nhau 3 tháng. Lần cuối cùng cả hai gặp gỡ là tháng 2/2020. Kể từ đó, tình hình dịch bệnh phức tạp cả trong nước lẫn ở Nhật khiến mọi chuyến bay bị đình trệ, Trang cùng \"ông chú\" của mình cũng bất đắc dĩ phải yêu xa. </p><p>Người ta nói khoảng cách trong tình yêu chỉ là một con số. Trang đã vượt qua được khoảng cách 16 tuổi để ở bên Jin thì cô cũng có thể vượt qua được cả năm trời yêu xa cùng hàng nghìn kilomet để duy trì mối quan hệ với người mình thương. </p><p>Đầu tiên phải nói về khoảng cách tuổi tác, Trang cảm thấy hoàn toàn hạnh phúc khi yêu một người hơn tuổi, thậm chí là hơn rất nhiều tuổi. Bởi lẽ do hơn Trang tới 16 tuổi nên \"ông chú\" Jin hiểu biết nhiều hơn, giúp Trang học hỏi được nhiều thứ hữu ích trong công việc và học tập. Sự trải đời cũng giúp \"ông chú\" chăm sóc Trang chu đáo hơn, tâm lí hơn, bản thân Trang được cảm nhận sự cưng chiều nhiều hơn.</p><p>Về phần yêu xa, sợ hãi, lo lắng là điều không thể tránh khỏi nhưng Trang luôn có cách để tự động viên mình. Cô nàng nói: \"Có những cặp đôi luôn luôn ở cạnh nhau mà vẫn bị cắm sừng đó thôi, nên yêu xa như mình càng không thể kiểm soát được. Nên là thà rằng đừng lo, cứ tin tưởng người yêu mình thôi. Nói chung, yêu xa lâu lâu ghen cho vui chứ đàn ông trên 30 tuổi ngoài tình yêu họ còn rất bận rộn công việc, cuộc sống ngoài kia đã quá bộn bề rồi, nên để tình yêu đối với họ là một nơi bình yên thì tốt hơn\".</p><p>Trang sở hữu ngoại hình khá xinh xắn</p><p>Còn \"ông chú\" của Trang trông cũng rất phong độ</p><p>Một người nói tiếng Nhật, một người nói tiếng Việt, sợ giao tiếp không thông, cả hai quyết định chọn tiếng Anh làm ngôn ngữ chung. Ấy vậy mà sự ngọt ngào chẳng giảm đi chút nào so với những cặp đôi khác. Để giảm bớt sợ nhớ nhung, Trang và người yêu thường gọi video cho nhau bất cứ lúc nào có thể. Từ những câu dặn dò ngủ sớm, dậy sớm đến lời nhắc nhở khi về khuya, từ câu nhõng nhẽo đến lời dỗ dành, cứ thế, cả hai đã vượt qua hơn 365 ngày xa nhau để chờ đến lần tiếp theo được \"trùng phùng\".</p><p>\"Thực ra đôi khi những người đang yêu nhau nhìn thôi cũng hiểu đối phương đang nghĩ gì rồi chứ không cần nói ra, bởi vậy ngôn ngữ chưa bao giờ là rào cản đối với bọn mình. Cả hai đều là những người thẳng thắn, không thích gì ở đối phương thì nói ra rồi cùng nhau giải quyết nên ít khi có hiểu lầm\", Trang tâm sự.</p><p>Hành trình cùng nhau chiến thắng những lời dị nghị</p><p>Sau khi Trang công khai mối tình \"chú - cháu\" của mình với Jin, lời ra tiếng vào xuất hiện hàng loạt. Người cho rằng những mối quan hệ \"đũa lệch\" chẳng bao giờ bền, một cô nhóc mới chập chững ra đời thì hiểu gì mà yêu một người đàn ông U40, đó chỉ là sự ngộ nhận nhất thời. Người lại ném đá tình yêu của Trang chẳng khác gì tình trạng... \"sugar daddy\" dân tình vẫn lên án, rằng Trang yêu \"ông chú\" người Nhật chắc vì \"ông chú\" giàu có nhiều tiền.</p><p>Hồi đầu cũng ấm ức đến phát khóc mà lâu rồi thành quen, Trang tự nhủ mình không như vậy là được, thiên hạ nói gì mặc thiên hạ. Trang vốn là con út trong gia đình, ba mẹ hơn 40 tuổi mới có Trang nên tính ra người yêu Trang cũng chỉ ngang tuổi anh hai. Cả người thân và bạn bè đều biết Trang không thích yêu con trai bằng tuổi hoặc ít tuổi hơn nên việc cô tìm yêu một người lớn hơn nhiều tuổi, mọi người cũng thấy bình thường, thậm chí hết sức ủng hộ. </p><p>Liên quan đến vấn đề \"đào mỏ\" hay \"moi tiền\" như cư dân mạng đồn đoán, Trang thẳng thắn đáp trả bằng một chữ \"Không\" to đùng. Giữa hai người quả thực có những chênh lệch về kinh tế nhưng theo Trang, không thể dựa vào đó mà phán đoán chuyện có hay không việc lợi dụng bạn trai ở đây.</p><p>Gái xinh gay gắt cho hay: \"Sao họ không nghĩ ngược lại là bản thân mình có giá trị như thế nào thì sẽ gặp được đối phương có giá trị tương xứng? Trong tình yêu muốn tiến xa hơn trong tương lai mà chỉ có yêu thôi thì chưa đủ, cần phải hợp về gia cảnh, trình độ học vấn... Nói tới đây nhiều bạn sẽ nói là cũng có rất nhiều người không tương đồng về gia cảnh và học vấn nhưng vẫn bên nhau đấy thôi. Vâng, vậy hãy để ý xem, ít nhất họ phải có quan điểm sống tương đồng nếu không sẽ khó dài lâu.</p><p>Mình không muốn giải thích gì thêm về vấn đề yêu cách xa tuổi tác có phải vì tiền không, chỉ muốn gửi đến mọi người một câu: Nồi nào úp vung nấy, mây tầng nào gặp mây tầng đó! Gia đình mình không giàu có nhưng cũng không phải là thiếu tiền. Mình không phải là người xuất sắc nhưng cũng chẳng phải người bất tài vô dụng sống dựa vào đồng tiền của đàn ông\".</p><p>Như những cặp đôi khác, người yêu thường tặng Trang quà nhân những dịp đặc biệt. Nhưng để bù lại, Trang cũng sẽ dành cho \"ông chú\" của mình các món quà ngang bằng. Thậm chí, trong list những việc lãng mạn nhất cả hai từng làm cho nhau, kỉ niệm tự tay làm quà Giáng sinh tặng đối phương đã được Trang đặt lên hàng đầu. Hoặc đơn giản hơn, giây phút Trang thấy đáng giá nhất là khi được nghe \"ông chú\" gọi mình bằng biệt danh \"Aka-chan\" (em bé - PV) hoặc dùng giọng tiếng Việt ngọng líu ngọng lô để nói một câu đậm chất ngôn tình: \"Anh đây\".</p><p>Cả hai có nguyên cả bộ sưu tập những cái nắm tay như thế này</p><p>Vì rất hiểu nhau nên Trang và \"chú\" người yêu ít khi cãi vã, nếu có cãi thì vì bé hơn, Trang cũng luôn được nhường nhịn, dỗ dành trước. Đặc biệt, cả hai luôn theo tôn chỉ yêu là cùng nhau cố gắng để trở thành những người tốt hơn:</p><p>\"Thay vì phải bám theo người yêu cả ngày, mình thích dành thời gian đó để trau dồi kiến thức và làm việc hơn. Như vậy thì người yêu mình cũng thoải mái hơn, tập trung hơn cho công việc. Thực ra chú rất bận nhưng mỗi lúc xong việc, chú đều tranh thủ gọi hoặc nhắn tin hỏi thăm mình, dặn mình từng tí một, với mình, vậy là đủ. Nói chung cả hai bọn mình đều là những người cân bằng được tình yêu và công việc. Như thế là tốt nhất\".</p><p>Trang và \"ông chú\" người Nhật cũng đã tính đến những chuyện xa hơn, cụ thể là một happy ending thực sự. Tuy nhiên, tương lai là điều không thể biết trước. Ở thời điểm hiện tại, cặp đôi chỉ đang mong ngóng cho lần gặp mặt tiếp theo, còn mọi thứ cứ từ từ rồi tới cũng chưa muộn.</p><p>Chưa được gặp nhau hơn 1 năm vì dịch, cả hai đang vô cùng mong ngóng đến lần tiếp theo được \"trùng phùng\"</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/1663620488464104692465621279783248874535427n-16171192372771431030402.jpg\" ><figcaption class=\"caption\">Thùy Trang (SN 2000) và người yêu là một \"ông chú\" người Nhật (SN 1984)</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/30/10128850822893040413648691667475049277292544n-16171192059471098932998.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/30/screen-shot-2021-03-30-at-193955-16171193174152042048358.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/screen-shot-2021-03-30-at-193256-1617121066498725587092.png\" ><figcaption class=\"caption\">Cả hai giao tiếp chủ yếu bằng tiếng Anh. Trong ảnh là lời tỏ tình ngọt ngào và đầy bá đạo từ \"ông chú\": \"Tôi yêu em nhiều lắm. Em là của tôi. Tôi chẳng nghĩ được gì khác ngoài em, Trang ạ. Trang là tất cả của tôi\"</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/16576928529541381081984246147721144403578043n-16171210070501828838056.jpg\" ><figcaption class=\"caption\">Vừa yêu cách nhiều tuổi vừa yêu xa, cặp đôi vấp phải rất nhiều lời bàn tán</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/30/screen-shot-2021-03-30-at-193644-16171209427061831836135.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/30/screen-shot-2021-03-30-at-194304-16171212277332041527264.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Ảnh: NVCC</p>', NULL, 'gai-xinh-2k-ke-chuyen-yeu-ong-chu-nguoi-nhat-hon-16-tuoi-minh-khong-song-dua-tien-dan-ong-may-tang-nao-gap-may-tang-do', 0, NULL, NULL),
(112, 17, 5, 1, 1, '10 câu hỏi với Fustic. - nhóm nghệ sĩ thị giác Việt Nam vừa thắng giải LHP danh giá tại Mỹ, từng cộng tác với Billie Eilish, Bad Bunny', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/29/photo1617025128565-1617025128882906105064.jpg', '|', 'Khi trò chuyện với những người trẻ làm công việc sáng tạo, tôi thường tò mò muốn biết lời khuyên họ dành cho những ai muốn bắt đầu hành trình tương tự, và câu trả lời của Fustic. là: “Bắt đầu từ đâu á? Từ đâu cũng được!”. Một tinh thần rất liều, rất tự tin, rất... Gen Z!', '<p>Nếu đủ kiên nhẫn xem hết credit những sản phẩm giải trí/ nghệ thuật như phim điện ảnh, MV hay những bộ ảnh thời trang, bạn sẽ thấy ngày càng có nhiều vị trí mới mẻ, thậm chí khó hình dung người mang title đó làm gì, trách nhiệm ra sao. Visual Artist (V.A - nghệ sĩ thị giác) là một trong số đó. </p><p>Hiểu một cách đơn giản, nếu visual art là nghệ thuật thị giác thì visual artist là người tạo nên những sản phẩm đó, với nhiệm vụ truyền tải các thông điệp, câu chuyện và cảm xúc thông qua việc sáng tạo hình ảnh. Công việc của một nghệ sĩ ở lĩnh vực này rất đa dạng, là sự trải dài, hội tụ và cân bằng của nhiều lĩnh vực như nhiếp ảnh, phim, thiết kế đồ hoạ, hoạt hình, trình diễn ánh sáng và kể cả những loại hình nghệ thuật công nghệ mới mà thế giới chưa kịp định danh. </p><p>Tại Việt Nam, Fustic. là \"ngôi sao đang lên\" trong mảng nghệ thuật thị giác. Dù ở bất kì định dạng nào, artworks (sản phẩm nghệ thuật) của nhóm bạn trẻ thuộc thế hệ Gen Z này đều khác biệt, có màu sắc, hơi thở riêng.</p><p></p><p>Trong năm 2020, Fustic. đã hợp tác với những nghệ sĩ và thương hiệu nổi tiếng toàn cầu như Bad Bunny, Billie Eilish, Adidas, Facebook, Spotify,... Cách đây một tuần, nhóm tiếp tục chiến thắng hạng mục Best Animated Short (Phim hoạt hình ngắn xuất sắc nhất) tại liên hoan phim SXSW tổ chức ở Texas, Mỹ. Đây là một trong những giải thưởng cho phim độc lập danh giá nhất nước Mỹ. </p><p>Thay vì trở thành một công ty hay một creative agency, Fustic. đi theo hướng \"collective\" - tức một tập thể được tuyển chọn để làm việc cùng nhau. 10 người, trải đều ở 3 châu lục là châu Á, châu Âu và Mỹ nhưng với Fustic., khoảng cách địa lý hay múi giờ lệch chưa bao giờ là trở ngại. Phần vì đây là cách làm việc từ ngày đầu, phần vì cả nhóm luôn tin tưởng nhau, dù cho đó là thẩm mỹ, kỹ năng hay cách sống. Trong tương lai, Fustic. sẵn sàng kết nạp thêm nhiều \"homies\" nếu đó là những người phù hợp, có thể giúp nhóm phát triển ở nhiều mảng khác.</p><p>Trong quá trình làm việc, các thành viên của Fustic. vẫn thường chỉnh sửa cho nhau, mọi người luôn có cách tiếp cận khách quan. Tức là vẫn đi theo cái hướng ban đầu mà một thành viên nào đó đã và đang làm, nhưng cả nhóm sẽ tìm ra những cách triển khai tốt nhất, hiệu quả nhất. Nếu có sự thay đổi, nó phải bắt nguồn từ một ý tưởng rõ ràng hoặc mục đích cốt lõi nào đó, chứ không phải chỉnh sửa vô cớ hay dựa vào cảm tính cá nhân. Mọi đóng góp đều cần có tính logic. Bên cạnh đó, để phát triển toàn diện, mỗi thành viên đều cố gắng học hỏi và lắng nghe ý kiến với tâm thế rộng mở.</p><p>Trước khi làm các dự án, tụi mình sẽ bàn trước với nhau để không làm ra những thứ mang nặng màu sắc cá nhân, tránh việc chỉnh sửa. Nếu là dự án riêng thì thoải mái thôi, nhưng khi làm những dự án thương mại thì kinh nghiệm, kiến thức chuyên môn sẽ được đề cao hơn. Cho nên từ trước đến nay cả nhóm chưa từng có xích mích hay bất đồng nào đáng kể.</p><p>Nghệ thuật thị giác là một nghề nghiệp tiềm năng ở Việt Nam nhờ vào sự phổ biến và dễ tiếp cận của internet. Không những vậy, nó còn bao gồm nhiều ngành nghề liên quan đến sáng tạo, phù hợp với những người trẻ.</p><p>Tuy nhiên mô hình như Fustic. vẫn còn khá mới, chủ yếu mọi người đang hoạt động như một studio hoặc những freelancer (người làm việc bán tự do) hơn là một nhóm nghệ sĩ tập hợp lại. Về sự cạnh tranh thì thật sự tụi mình chưa thấy nhiều vì cả tệp khách hàng mà Fustic. hướng tới lẫn những chuẩn mực, tiêu chí, phong cách mà nhóm đang theo đuổi đều rất riêng.</p><p>- Giao tiếp rõ ràng và chi tiết. Từ đó tạo ra sự tin tưởng cho đôi bên.</p><p>- Có sự tôn trọng của đối tác về thế mạnh chuyên môn của team.</p><p>- Dự án mang lại sự trải nghiệm, thử thách và giá trị tích cực.</p><p>Có! Đó chính là việc \"unpack\" (bóc tách) những mong muốn và suy nghĩ của khách hàng nước ngoài. Đôi khi khách hàng có ý tưởng nhưng họ không biết cách diễn tả cụ thể thành lời, nhiệm vụ của tụi mình là phải tập trung lắng nghe, đặt ra những câu hỏi để tháo gỡ từng lớp của vấn đề. Mỗi một ý tưởng được đề xuất đều cần phương án thực hiện cụ thể để đối tác hiểu được và cung cấp những gì mình cần. Cho đến khi nào thống nhất được về hướng đi và quan điểm giữa các bên thì tụi mình mới bắt tay vào làm.</p><p>Ngoài ra mình còn cần tinh thần trách nhiệm cực cao. Giả sử deadline là 10 ngày, trong 10 ngày đó mình phải cập nhật liên tục cho họ về tiến độ thực hiện, mỗi ngày đều phải đảm bảo có thứ gì đó được xử lý, vận hành. Nó khác với kiểu làm việc trong nước, cứ đợi đến sát deadline mới chịu làm, miễn sao xong là được.</p><p>Một trong những project mà Fustic. tự hào nhất là dự án kết hợp với Bad Bunny - nghệ sĩ Mỹ Latin có lượt nghe nhiều nhất Spotify 2020. Trong dự án lần này, Fustic. không chỉ có cơ hội hợp tác với nghệ sĩ quốc tế lớn mà bọn mình tham gia với tư cách chỉ đạo hướng đi nghệ thuật để sáng tạo ra phong cách hình ảnh đặc biệt cho những câu chuyện của Bad Bunny, sản phẩm cuối cùng là một video phỏng vấn kể lại câu chuyện của nghệ sĩ.</p><p>Lần kết hợp với Billie Eilish cũng là trong dự án này, nhưng tụi mình không tham gia sâu như với Bad Bunny. Đây là những dự án mà Fustic. làm trên tinh thần vừa hỗ trợ, vừa giúp mình tạo dựng mối quan hệ tốt hơn cho những lần bắt tay sau này.</p><p>Một số phân cảnh trong bộ phim tài liệu cho Bad Bunny do Fustic. Studio sản xuất năm 2020</p><p>Đây là dự án được khởi xướng bởi đạo diễn Kristian Mercado với câu chuyện xoay quanh văn hoá của người Mỹ Latin cùng những khía cạnh xã hội về màu da, giới showbiz nước Mỹ. Phim ngắn \"Nuevo Rico\" được đồng sản xuất bởi Fustic. Studio, Deux Wave, Tunjayork và Memoma Studio từ giai đoạn cuối 2019 tới hết năm 2020. Mọi công đoạn được thực hiện trên mạng trong thời kỳ dịch bệnh COVID-19 đầy khó khăn.</p><p>Bọn mình cố gắng tạo ra một môi trường sáng tạo xung quanh bằng cách kết nối và chia sẻ những nguồn cảm hứng và thông tin hữu ích cho nhau một cách thường xuyên. Ví dụ như cả nhóm có một group chat trên Instagram, hễ ai thấy gì hay là sẽ quăng vào đó cho anh em cùng coi, cùng học. Khi thấy được những artworks mới, cả nhóm luôn đặt câu hỏi \"Cái này làm thế nào?\", sau đó tự mày mò thử nghiệm cho đến khi làm được thì thôi. Đó là cách mà tụi mình giữ được khả năng sáng tạo lẫn cải thiện kĩ năng.</p><p>Với Fustic., sự sáng tạo là thứ bạn phải học, phải xem liên tục và xây dựng sau một thời gian dài. Nhìn vào một đề bài, bạn phải có ngay những ý tưởng mà bạn từng thấy, từng làm hiện ra trong đầu. Chứ đến lúc đó mới đi tìm hiểu thì quá trình sẽ chậm hơn rất nhiều.</p><p>Fustic.</p><p>8. Một câu hỏi mà rất nhiều người thắc mắc: Công việc này có mức thu nhập ra sao? Liệu con số đó đủ để các bạn tự do sáng tạo?</p><p>Nếu có đủ sự tập trung và nghiêm túc, bọn mình nghĩ công việc này hoàn toàn có thể đem lại một mức thu nhập đủ để nâng cấp trang bị cần thiết và tự do sáng tạo.</p><p>Một số phân cảnh trong các artwork ấn tượng của Fustic.</p><p>Gen Z là thế hệ được thừa hưởng những bước tiến lớn trong công nghệ của các thế hệ trước. Điều đó giúp cho các bạn dễ dàng tiếp cận nhiều kiến thức và thông tin cũng như việc hoạt động nhóm thông qua internet. Nhưng cũng chính sự phát triển của công nghệ và mạng xã hội tạo ra nhiều sự xao nhãng và ảnh hưởng xấu tới chất lượng của sức khỏe, tinh thần và công việc.</p><p>Từ đâu á? Từ đâu cũng được! Thời gian đầu, mình nghĩ các bạn nên học hỏi từ chính những nghệ sĩ và tác phẩm có sẵn. Nhìn, phân tích, học cách để làm ra một thứ tương tự. Đây không phải bắt chước mà là luyện kĩ năng, sau một thời gian thực hành đủ, các bạn sẽ dần dần tìm ra chất riêng và thay đổi nó đúng với phong cách của mình. Về bản chất, tất cả những sản phẩm sáng tạo trên thế giới đều được cộng hưởng từ những thứ có sẵn với những dấu ấn cá nhân của mỗi nghệ sĩ.</p><p>Hãy rèn luyện kỹ năng trước, đừng đặt nặng chuyện ý tưởng ngay từ vạch xuất phát. Sau khi có sẵn một \"skill set\" đủ mạnh, bạn sẽ tự mình biến tấu và triển khai được mọi ý tưởng, không còn bị gò bó bởi năng lực.</p><p>Năm 2020, thương hiệu ASUS VivoBook đồng hành cùng Gen Z trong nhiều hoạt động, đồng thời trở thành nhà tài trợ cho Hạng mục Rising Gen Z của WeChoice Awards nhằm tôn vinh những đóng góp của các bạn trẻ thuộc thế hệ này dành cho cộng đồng.</p><p>Mở đầu 2021, ASUS Vivobook tiếp tục song hành cùng Gen Z trong loạt bài See ya real soon! của Kenh14.vn - trang thông tin dành cho giới trẻ, nhằm lắng nghe và thấu hiểu bạn!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/1-1-16170250513311951358245.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/2-16170167516221851638387.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/3-16170167482571082433091.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/29/1236049103669188047475452748689903353900589n-1617017568999183913398.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/screen-shot-2021-03-29-at-105847-1617017713610455209238.png\" ><figcaption class=\"caption\">Một khung hình (frame) trong video thuộc dự án hợp tác với Billie Eilish của Fustic. Studio</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/nf1b-1617017715025505135280.jpg\" ><figcaption class=\"caption\">Dự án phim ngắn \"Nuevo Rico\" được đồng sản xuất bởi Fustic. Studio, Deux Wave, Tunjayork và Memoma Studio giành giải Phim hoạt hình ngắn xuất sắc nhất tại LHP SXSW tổ chức tại Texas, Mỹ</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/29/nf6-16170182930791339323199.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/4-1617016752258614696103.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Ngoài ra, ASUS vẫn không ngừng mang đến sản phẩm phát triển cùng bạn tại đây .</p>', NULL, '10-cau-hoi-voi-fustic---nhom-nghe-si-thi-giac-viet-nam-vua-thang-giai-lhp-danh-gia-tai-my-tung-cong-tac-voi-billie-eilish-bad-bunny', 0, NULL, NULL),
(113, 17, 5, 1, 1, 'Diễn viên Hoàng Yến vừa chia tay chồng 4: Rất nhiều người phụ nữ ngưỡng mộ tôi vì dám sống', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/29/photo1616995881956-1616995882544709336138.jpeg', '|', '\"Sau khi ly hôn, có rất nhiều người phụ nữ ngưỡng mộ tôi vì dám sống, dám thay đổi\", Hoàng Yến nói.', '<p>Hoàng Yến nổi tiếng với những vai diễn lẳng lơ, đanh đá trên màn ảnh nhỏ. Nhắc tới Hoàng Yến, khán giả hẳn sẽ không quên vai diễn Mai Công chúa của seri hài Làng Ế Vợ và đặc biệt là cô Xuyến của Về Nhà Đi Con.</p><p>Bản thân Hoàng Yến cũng có chuyện đời tư không mấy suôn sẻ. Đầu năm 2020, khi ở độ tuổi U50, Hoàng Yến tuyên bố ly hôn người chồng thứ 4. Nữ diễn viên khẳng định cô là mẫu người phụ nữ dám sống, dám thay đổi vì bản thân.</p><p>Hoàng Yến cho hay, sau khi ly hôn người chồng thứ 4, cô không mấy bận tâm tới chuyện tình yêu: \"Tôi chưa yêu nữa không phải vì tôi sợ xã hội bàn tán. Chỉ là tôi muốn cho trái tim mình nghỉ ngơi, muốn dành thời gian cho những người luôn yêu thương tôi vô điều kiện mà thôi.</p><p>Sau khi ly hôn, có rất nhiều người phụ nữ ngưỡng mộ tôi vì dám sống, dám thay đổi. Họ tìm tới tôi để chia sẻ vui buồn, để tâm sự. Tôi bận yêu thương những người phụ nữ đó, bận dành thời gian cho gia đình tôi\".</p><p>Hoàng Yến cũng cho hay cô luôn nhận được sự ủng hộ, che chở từ những người thân yêu nhất. Gia đình, người thân chưa bao giờ chê trách cô vì những mối quan hệ đổ vỡ. Thay vào đó, mọi người đều động viên, ủng hộ khi cô muốn làm lại từ đầu, dù ở bất cứ độ tuổi nào.</p><p>Nữ diễn viên cho hay, sau lưng cô có 1 vị \"đại gia ngầm\" luôn dành cho cô sự bao dung, che chở và hậu thuẫn tuyệt vời. Thậm chí, \"vị đại gia\" này còn sẵn sàng chi tiền tỷ chỉ để làm cho cô vui:</p><p>\"Tôi may mắn vì luôn có mẹ đồng hành. Ở tuổi U50, tôi vẫn luôn là đứa con bé bỏng, được mẹ chở che, an ủi mọi lúc, mọi nơi.</p><p>Mọi nỗi đau của tôi đều được bàn tay thần kỳ và trái tim bao dung của mẹ xoa dịu hết! Tôi luôn xem mẹ tôi như một vị Phật sống. Mẹ cũng chính là đại gia đích thực của cuộc đời tôi.</p><p>Mẹ ruột mua xe 1,3 tỷ đồng tặng diễn viên Hoàng Yến</p><p>Mới đây, thấy tôi buồn, mẹ tôi đã rút 1,3 tỷ đồng mua một chiếc xe hơi mới cho tôi, chỉ để làm tôi vui lên\".</p><p>Chính vì nhận được tình yêu thương vô bờ bến từ mẹ ruột nên Hoàng Yến luôn nhắc nhở bản thân sống thật tốt để mẹ không phải đau lòng:</p><p>\"Tôi không cho phép bất cứ ai làm tôi đau khổ, tôi cũng chẳng dại gì làm khổ bản thân. Bởi thân xác này là mẹ cho tôi, tôi phải nâng niu, trân trọng nó để mẹ tôi vui lòng.</p><p>Ngày tôi mới đi làm răng, nâng ngực và nâng mũi về, tôi đã trêu mẹ tôi rằng: Đây nhé, con trả lại cho mẹ một đứa con xinh đẹp, trẻ trung. Đây mới đúng là sản phẩm mà mẹ mong muốn nhé! Và mẹ tôi đã cười rất tươi. Mẹ hài lòng khi thấy tôi vui vẻ, tự tin\".</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/photo-1-16169944128521544806817.jpg\" ><figcaption class=\"caption\">Diễn viên Hoàng Yến ở tuổi U50</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/photo-2-1616994414533134326748.jpg\" ><figcaption class=\"caption\">Diễn viên Hoàng Yến cùng mẹ ruột và con gái trong 1 chuyến du lịch</figcaption></figure><p>Ảnh: Tổng hợp</p>', NULL, 'dien-vien-hoang-yen-vua-chia-tay-chong-4-rat-nhieu-nguoi-phu-nu-nguong-mo-toi-vi-dam-song', 0, NULL, NULL),
(114, 17, 5, 1, 1, '20 tuổi, cô gái tranh thủ làm sếp trong lúc gap year: Bà hàng xóm không rảnh để follow và nói về tôi mãi', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/27/photo1616858933881-1616858934385748114445.jpg', '|', 'Cùng kết nối với Vy để hiểu thêm những suy nghĩ của Gen Z trong việc lựa chọn hướng đi cho tương lai, lẫn bản lĩnh đối diện với những luận bàn của thế giới xung quanh nhé!', '<p>Những ngày qua, trên MXH xuất hiện chia sẻ về một cô gái 20 tuổi đang trong giai đoạn gap year nhưng đã lên chức trưởng phòng kinh doanh của một công ty.  Nếu ví việc học và sự nghiệp như những nấc thang, thì cú \"hack thang\" của cô bạn Gen Z này đang nhận về nhiều luồng ý kiến, cả bất ngờ ngưỡng mộ xen lẫn hoài nghi.</p><p>Khi có dịp ngồi lại với nhân vật chính trong câu chuyện ấy  -  Đặng Thuỵ Thảo Vy, mới thấy cô bạn đang đi làm với tâm thế học, một Gen Z có phong cách sống và vẫn miệt mài tìm kiếm những  trải nghiệm thú vị trong hành trình tìm hiểu \"tôi là ai?\".</p><p>Cùng kết nối với Vy để hiểu thêm những suy nghĩ của Gen Z trong việc lựa chọn hướng đi cho tương lai lẫn bản lĩnh đối diện với những luận bàn của thế giới xung quanh nhé!</p><p>Đặng Thuỵ Thảo Vy</p><p>Sinh năm 2000\r\nSống tại Sài Gòn, cựu học sinh THPT Chuyên Lê Hồng Phong</p><p>Tài năng trẻ TP.HCM 2018.</p><p>Top 3 dự án đại diện Việt Nam tham dự Cuộc thi Thử thách thương mại Quốc tế 2018 tại Singapore.</p><p>Giải Nhất - Cuộc thi khoa học, kĩ thuật cấp Quốc gia cho học sinh trung học năm học 2017 - 2018.</p><p>Giải Nhất - Kỳ thi học sinh giỏi cấp Thành phố môn Địa lý - Thành viên đội tuyển tham dự Kỳ thi học sinh giỏi cấp Quốc gia năm học 2017 - 2018.</p><p>Đạt giải Nhất - Cuộc thi Dấn thân vào Khoa học - Trường hè Khoa học Sài Gòn 2018.</p><p>Top 10 học sinh giỏi Nhất lớp 12CV2 trường THPT Chuyên Lê Hồng Phong năm học 2017 - 2018 (GPA 9,3/10).</p><p>Đạt học bổng Odon Vallet 2018 và học bổng tham gia nhiều khóa học ngắn hạn trong nước.</p><p>Trưởng ban Truyền Thông CLB Hướng Nghiệp - trường THPT Chuyên Lê Hồng Phong nhiệm kỳ 2016 - 2017.</p><p>Công dân đại diện tham quan tàu Hải quân Canada HMCS Vancouver - Tổng Lãnh sự quán Canada tại Thành phố Hồ Chí Minh.</p><p>Top 8 cuộc thi Én Vàng Học Đường - Đài truyền hình TP.HCM.</p><p>Tuyển thẳng Đại học Khoa học xã hội và Nhân Văn.</p><p>Năm 2019 - 2020, Vy mở Yoga studio, quán trà sữa và cho thuê căn hộ AirBnB.</p><p>Tháng 3/2020, Vy vào công ty FWD, khoảng 1 năm sau Vy trở thành trưởng phòng kinh doanh trẻ nhất.</p><p>\"Tôi mơ hồ về bản thân, cũng không biết mình thực sự giỏi gì hay thích gì. Trong tập thể có rất nhiều người giỏi, điều gì làm tôi khác biệt, nếu cũng chỉ học giỏi và luyện thi điểm cao như các bạn? Tôi bắt đầu tìm hiểu mình là ai từ thời điểm học cấp 3 và những năm sau đó\".</p><p>Thảo Vy (sinh năm 2000, sống tại Sài Gòn), tốt nghiệp trường THPT Chuyên Lê Hồng Phong năm 2018. Sau khi tốt nghiệp, Vy quyết định tạm hoãn việc vào đại học vì muốn học hỏi nhiều hơn về thế giới xung quanh và đặc biệt là tìm hiểu về bản thân. Thời điểm đó, Vy khá mơ hồ về chính mình nếu phải trả lời câu hỏi: Mình thực sự giỏi cái gì? Thích gì? Điều gì làm mình cảm thấy vui mỗi ngày?</p><p>Năm đầu tiên gap year, Vy tham gia khá nhiều cuộc thi và các khoá học ngắn hạn để mở mang kiến thức và thử thách bản thân. Đến năm thứ 2, Vy đã có ý định đăng ký học lại đại học, song khi đứng trước những cơ hội kinh doanh cô muốn nắm lấy cơ hội.</p><p>Hiện tại, ở tuổi 20 - Vy đang là trưởng phòng kinh doanh trẻ nhất của công ty thuộc tập đoàn đầu tư tài chính - viễn thông - bất động sản, có trụ sở tại Sài Gòn. Dưới Vy có 30 nhân viên.</p><p>Vy từng có ý định du học nhưng ở thời điểm đó Vy không biết chính xác mình muốn học ngành gì. Còn bây giờ, nếu đi du học, cô nghĩ mình muốn học về tài chính, kinh doanh.</p><p>Vy biết mình còn trẻ và có rất nhiều điều để làm, những thành quả ở hiện tại chưa khẳng định được gì vì phía trước hãy còn là một hành trình rất dài cần đến nhiều nỗ lực.</p><p>Thảo Vy sở hữu suy nghĩ và vẻ ngoài trưởng thành hơn tuổi 20</p><p>“Bà hàng xóm là một ví von đại diện cho rất nhiều những quan niệm của người khác đặt lên mình. Tất cả mọi người đều có quyền nói lên quan điểm và phản ứng riêng của họ. Ai tích cực thì mình lấy đó là động lực phấn đấu, còn ai đưa ra những ý kiến tiêu cực, tôi sẽ lắng nghe và cẩn trọng với những cảnh báo của họ. Nhưng mỗi người có một con đường riêng, tôi muốn tập trung vào cuộc sống của mình và trở thành một phiên bản tốt hơn chứ không phải để làm hài lòng tất cả. Không ai có đủ thời gian để mãi dõi theo hành trình của tôi. Bà hàng xóm hay ai khác cũng đều có cuộc sống riêng của mình”.</p><p>Vy nghĩ mình là một người may mắn. May mắn khi từ ngày học cấp 1, cô đã được ba mẹ cho tự quyết định mọi thứ thuộc về mình. Lên cấp 2, khi muốn học thêm môn gì Vy chỉ cần báo với ba mẹ số tiền phải đóng và lịch học là được. Ông Ngoại là người đưa đón Vy đến các lớp học kể cả lớp Yoga. Năm cấp 3, khi hầu hết phụ huynh lẫn con cái của họ căng như dây đàn vì chuyện chọn trường, Vy thong dong vì chuyện đó cô cũng tự quyết nốt. Thậm chí, đến lúc Vy nộp đơn vào Lê Hồng Phong và thi đậu, ba mẹ mới biết: “À, đậu rồi hả. Chúc mừng con!”.</p><p>Tôn trọng và tin tưởng là 2 thứ Vy nhận được từ ba mẹ. Nhưng để có được điều đó, cô nàng luôn phải nỗ lực chứng minh cho gia đình thấy mình có thể làm được. Từ cấp 1 đến cấp 3, Vy luôn có thành tích học tập tốt.</p><p>Cô gái 20 tuổi không muốn nhìn nhận cuộc sống này theo một chuẩn mực nào. Bạn này có đẹp không, bạn kia có thành công không, những khái niệm ấy vốn dĩ đã rất khác nhau ở mỗi người. Và cả việc phải làm gì để được bà hàng xóm xem là “con nhà người ta”, Vy không xác đinh được cái chuẩn đó. Vì chuẩn thì chuẩn so với cái gì?</p><p>“Tôi thất bại nhiều thứ. Khi đứng trước cơ hội có quyền say no, tôi lại sẵn sàng say yes bởi vì tại sao không thử? Mỗi thất bại là một bài học”.</p><p>Bạn nghĩ sau khi gap year thì Vy sẽ bon bon trên con đường tìm hiểu bản thân mình và thành công ngọt ngào sẽ đến ư? Bạn sai rồi. Vy nhận về một chuỗi thất bại.</p><p>Năm 2019, Vy được mẹ tặng cho một khoá học để trở thành HLV Yoga Quốc tế trong thời gian gap year. Tại đây, cô bạn tình cờ quen với một người chị đang có ý định mở phòng Yoga để kinh doanh. Họ bắt tay nhau. Vy bỏ công sức, người kia đầu tư tiền. Đó là dự án kinh doanh đầu tiên của Vy. Nửa đầu năm 2020, Vy còn cùng đối tác mở ra một số mô hình kinh doanh khác (quán trà sữa, căn hộ cho thuê…) nhưng tất cả đều phải dừng lại vì yếu tố khách quan lẫn chủ quan.</p><p>Mỗi lần thất bại là sẽ có một bài học. Xâu chuỗi lại tất cả những điều đã trải qua, Vy thấy bài học lớn nhất chính là: Có thất bại thì mới đạt được thứ mình muốn.</p><p>Mổ xẻ câu chuyện mở quán trà sữa. Vy không biết gì về trà sữa cũng không biết pha chế. Khi kinh doanh, cô buộc phải thuê người có khả năng nấu ra món đó. Đó là cái sai lầm lớn nhất. Khi để cho người ta đi chợ và tự mua nguyên liệu, Vy sẽ không kiểm soát được giá thành, thành phần… Bên cạnh đó, cô cũng không thể can thiệp vào công thức mà chỉ có thể nếm thử và góp ý. Rõ ràng, Vy đang kinh doanh một lĩnh vực mà mình không hề có chuyện môn, phải phụ thuộc vào người khác. Nếu một ngày nào đó, có người trả cho họ lương cao hơn hoặc là họ tích luỹ đủ vốn để ra mở riêng, thì coi như Vy thua trắng.</p><p>Còn đối với dự án Yoga hay căn hộ cho thuê. Câu chuyện mặt bằng rất quan trọng. Để tìm được mặt bằng vừa ý đã khó, việc giải quyết những yêu cầu về hợp đồng lại còn khó hơn. Đó là một trải nghiệm đáng học. Vy nhận ra mình không có kinh nghiệm về việc soạn thảo hợp đồng, đã có rất nhiều trục trặc xảy ra trong quá trình follow các điều khoản trong hợp đồng chứ chưa nói đến chuyện dịch vụ mở ra không có khách hàng. Nó là một chuỗi thất bại, từ khâu tổ chức vận hành chứ không phải chỉ riêng việc ế ẩm vì Covid.</p><p>Từng thất bại nhỏ đã cho cô gái 20 tuổi biết mình cần hoàn thiện điều gì, tích luỹ kinh nghiệm gì và đến với một cơ hội mới trong tâm thế như thế nào. Tháng 9/2020, Vy quyết định đi làm công ty. Công việc chính là tư vấn. Vy có quyền “say no” nhưng khi cơ duyên đến Vy lại sẵn sàng “say yes” bởi vì “why not” (tại sao không)?</p><p>“Nếu ai đó nói với tôi: Đừng làm cái này nha! Và tôi không làm. Sau này khi tôi cảm thấy đó là một quyết định không đúng, tôi lại tiếp tục đổ thừa: Phải chi ngày đó đừng nghe lời ai cả\".</p><p>Vy nói cô luôn muốn tự mình khám phá mọi thứ. Khi có ai đó nhận định tiêu cực về một ai đó/ việc gì đó, cô luôn muốn tự mình trải nghiệm chuyện ấy. Nhờ vậy, Vy có thể khám phá ra nhiều điều thú vị mà bản thân có thể làm. Trả giá cho lối sống liều lĩnh đó cũng có. Nếu sai, Vy sẽ mất nhiều thời gian hơn và mất cả niềm tin vào bản thân.</p><p>Mỗi khi ai đó đưa ra những kinh nghiệm có nghĩa là họ đã sống đủ lâu để có những trải nghiệm đó, lời khuyên hay chỉ dẫn của họ là đáng tiếp thu - Vy nghĩ vậy.</p><p>Nhưng khi đứng trước một cơ hội, Vy lại muốn tự mình trải nghiệm nó theo cách của riêng mình. Điều cốt lõi không phải thứ mình sắp đối diện mà là liệu mình có thể phân tích kỹ, đánh giá được rủi ro và chấp nhận chúng chưa. Nếu có, hãy lên kế hoạch, bắt tay thực hiện và không đổ thừa.</p><p>Nếu có sai thì thà tự trách mình còn hơn nghe lời người khác rồi lại tiếp tục đổ thừa vì đã đưa ra những đúc kết sai.</p><p>“Để ba mẹ đồng ý cho bạn ra sống riêng, không phụ thuộc vào việc bạn đã nói gì lúc đưa ra đề nghị ấy. Mà nó là một hành trình mình cho ba mẹ thấy mình là con người như thế nào. Mình muốn ở riêng, vậy mình đã có những kỹ năng tự chăm sóc cho bản thân tốt chưa, đã độc lập về tài chính chưa. Nếu ba mẹ đã an tâm, thì dù bạn có ở riêng hay làm gì thì ba mẹ cũng sẽ ủng hộ. Không phải cứ đứng cãi to tiếng: Con lớn rồi, con muốn riêng, là sẽ được ở riêng\".</p><p>Vy thuê 1 phòng trong căn nhà phố ở quận Bình Thạnh và bắt đầu sống riêng. Ba cô đã kịch liệt phản đối quyết định này.</p><p>Thời điểm đó, cô chưa chứng minh được mình có thể sống ổn. Mặt khác, ba là tuýp người truyền thống, rất thương con nên khi thấy con gái đã không vào đại học nay lại còn muốn ra ở riêng - ông lo sợ con sẽ lêu lổng, hình thành những thói quen xấu.</p><p>Hiện tại, Vy đã có công việc mang lại thu nhập tốt để tự lo cho mình và những người xung quanh, nên ba rất tin tưởng, ủng hộ. Dù đã chứng minh được cho ba thấy sống riêng ở tuổi 20 không phải là quyết định xốc nổi, song đó là cả một quá trình thử thách đầy gian nan.</p><p>Vy bảo, sống riêng không phải là cách để Gen Z chứng minh mình đã lớn trong mắt ba mẹ. Mà nó là một lựa chọn có cả được và mất:</p><p>Khi ở riêng, Gen Z sẽ có không gian riêng tự do mà mình có thể làm việc với sự tập trung tối đa. Bạn có thể học được cách quản lý tốt tài chính của mình, học được kỹ năng chăm sóc tốt cho bản thân, nấu nướng thế nào, chăm chút nhà cửa ra làm sao.</p><p>Nhưng ra riêng sẽ khiến bạn không thể tiết kiệm tiền nhà như khi ở chung với ba mẹ, cũng không còn được yêu thương chăm sóc từng li từng tí.</p><p>Độc lập nhưng không cô lập. Vy vẫn thường về nhà thăm ba mẹ mỗi tuần. Vy cũng nhận ra khi mình lớn, mình tự động thích gần gũi người thân hơn dù không nhất thiết phải sống  gần họ.</p><p></p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/203336854389633024/2021/3/27/photo1616818963069-16168189633041508453404.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/27/haio3469-1-16168200133981834442831.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/27/copy-of-dsc9029-16168202517171135549880.jpg\" ><figcaption class=\"caption\">Vy nghĩ mình là một người may mắn vì trong việc học, cô được ba mẹ trao cho toàn quyền quyết định</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/27/haio3452-1-161682032410855539655.jpg\" ><figcaption class=\"caption\">Thảo Vy xem thất bại là đường đi tới thành công</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/27/haio3526-1-1616820398518177394578.jpg\" ><figcaption class=\"caption\">\"Tôi không muốn đổ thừa cho ai, tôi thà tự trách mình\", Vy nó</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/3/27/haio3468-1-1616820667879509203441.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>“Tôi muốn một ngày của năm 70 tuổi, có thể thức dậy trong cơ thể của cô gái 20, là tôi của hiện tại, để được làm nhiều điều mình thích hơn”, Vy nói.</p>', NULL, '20-tuoi-co-gai-tranh-thu-lam-sep-trong-luc-gap-year-ba-hang-xom-khong-ranh-de-follow-va-noi-ve-toi-mai', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(115, 17, 5, 1, 1, 'Hiếu PC - “Hacker 3 triệu đô, 7 năm tù trên đất Mỹ” lần đầu tiên nói về tình yêu và cảm giác bất hiếu', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/25/photo1616679461093-16166794612141012670846.jpg', '|', 'Những chia sẻ của Hiếu PC trong bài phỏng vấn cho bạn thấy những góc khác về cuộc sống riêng, bên cạnh câu chuyện hoàn lương quá đặc biệt, ngoạn mục và đầy tính thức tỉnh của Hiếu.', '<p>“Suy nghĩ của một người từng bị bắt như Hiếu PC sẽ như thế nào?”</p><p>Bạn tôi mò mò khi nghe tôi sắp có 1 cuộc gặp với nhân vật này. Tôi bảo: “Để bữa nào kể cho nghe”.</p><p>Và tất cả những gì Hiếu PC và tôi đã nói trong cuộc gặp ấy, đều có cả trong bài viết này. Không riêng gì bạn tôi, những người từng lên Google để search “Hiếu PC là ai” hay nhấp vào link một bài viết nào đó về nhân vật này hẳn đều muốn biết thêm, nhiều hơn nữa những chia sẻ từ phía anh. Đơn giản bởi vì, câu chuyện hoàn lương của Hiếu quá đặc biệt, ngoạn mục và đầy tính thức tỉnh.</p><p>Chào Hiếu,</p><p>Phút đầu tiên được trả tự do bên Mỹ, anh có ngoái đầu nhìn lại nhà tù ở phía sau lưng không?</p><p>Không! Tôi chỉ nhìn về phía trước. Nó như một chương đã cũ và tôi không muốn đọc lại những điều đã biết. Nếu có, tôi chỉ nhìn lại để học hỏi chứ không chìm trong quá khứ. Cuộc sống mà, tôi quyết định bước và viết tiếp một chương mới cho mình.</p><p>Hiếu PC nói về ngày đầu tiên trong tù</p><p>Hơn 2000 ngày sống trong tù, nếu phải nói về 1 ngày trong số đó, Hiếu PC sẽ nói về ngày nào?</p><p>Về ngày đầu tiên. Địa ngục trần gian đối với tôi. Tôi bị bắt sát Tết. Ở một nơi rất xa quê hương, tôi đau đớn nghĩ tới gia đình, tới ba mẹ. Một tuần sau đó, tôi đã rất khủng hoảng đến mức không thể ăn uống. Đến tận 2 tháng sau ngày bị bắt, tôi mới được liên lạc với họ. Giờ ngồi đây kể lại, tôi không thể tưởng tượng được mình đã trải qua những điều ấy.</p><p>Nhưng tôi lại không bao giờ tiếc 7 năm ấy. Tôi nói với ba mẹ: “Nếu con không đi tù, con sẽ chẳng bao giờ trưởng thành. Nếu con về Việt Nam mà vẫn ngạo mạn, không coi ai ra gì như xưa thì thà tiếp tục ngồi tù còn hơn”.</p><p>7 năm đó, tôi nhận ra giá trị cuộc sống, giá trị đạo đức, giá trị con người. Khi ở ngoài, tôi quá bận để tìm hiểu về bản thân. Thời gian tĩnh lặng cách xa mọi thứ, tôi đã hiểu hơn về con người mình, những điểm yếu của bản thân để khắc phục. Nếu lạc quan thì gọi đó là 1 chuyến đi du học cũng được.</p><p>Câu chuyện phạm lỗi rồi hoàn lương của anh nhiều người bảo nghe như phim. Vậy anh nghĩ, đây là thể loại phim gì?</p><p>Là một bộ phim truyền cảm hứng. Lúc ở trong tù, tôi đã gặp rất nhiều người và đa phần trong số họ có hoàn cảnh éo le. Họ thiếu một điều gì đó, có thể gọi là giá trị tâm hồn. Và tôi bắt đầu kể cho những bạn tù nghe câu chuyện đời mình - một chàng trai lớn lên ở một nơi rất xa nước Mỹ, lầm đường lạc lối. Nó có thể tạo ra động lực cho những người xung quanh mình tiếp tục sống và hy vọng, dù là đang ngồi tù đi chăng nữa.</p><p>Anh định mở đầu của bộ phim ấy như thế nào?</p><p>Có lẽ, tôi sẽ đảo kết cục của câu chuyện lên đầu phim - đó là việc tôi bị ở tù. Sau đó, những thước phim mới bắt đầu tua ngược lại những năm tháng trước đây. Bởi vì có như vậy người ta mới bị sốc vì hậu quả và tò mò để tìm hiểu sâu thêm.</p><p>Lời đồn không có thật về Hiếu PC mà anh từng nghe được là gì?</p><p>Họ đồn tôi là siêu hacker (cười). Thật ra, tôi chỉ là 1 người bình thường thôi và biết về kỹ thuật. Tôi cũng thấy mình không tài giỏi hay “number one” gì như những lời đồn thổi. Đối với anh em kỹ thuật khác, tôi vẫn cần phải học hỏi họ rất nhiều. Trong nghề này là vậy, không có ai biết hết tất cả.</p><p>Khi tôi làm về hệ thống Chống lừa đảo, họ đồn tôi là đang đánh cắp thông tin người khác hoặc làm điều gì đó không hay. Nhưng không sao cả, những lời đó tôi chỉ để ngoài tai. Bởi vì khi mình làm chuyện tốt, mình vẫn nên tiếp tục - không có gì phải sợ. Bây giờ mọi thứ đang tiến triển rất tốt, có rất nhiều công ty lớn muốn hợp tác với dự án Chống lừa đảo của tôi. Trời thương! (cười)</p><p>Có người nào của năm 2013 anh đang rất quan tâm, đến năm 2020 anh phải search Google để update thông tin mới về họ?</p><p>Có một người như vậy. Đó là người thầy của tôi - người dẫn dắt tôi vào nghề này từ lúc tôi còn rất bé, chắc khoảng học lớp 9. Tuy nhiên, thông tin về thầy thì không có trên Google nên tôi đã nhờ gia đình tìm thông tin liên lạc với thầy qua số điện thoại.</p><p>Khi thầy nhấc máy, tôi được nghe lại giọng thầy bên kia đầu dây sau nhiều năm mất liên lạc. Bất ngờ, xúc động xen lẫn vui mừng khiến tôi suýt vỡ oà. “Con xin lỗi thầy”- tôi đã nghĩ về câu nói ấy gần trăm lần khi ở tù và tôi đã có dịp thốt lên. Tôi kể cho thầy nghe những lỗi lầm của mình. Thầy đã tha thứ. Giống như trước đây, thầy luôn hiểu tôi. Hai thầy trò tâm sự rất nhiều.</p><p>Ngày xưa, nếu theo thầy, tôi đã không đi tù. Nếu tôi lắng nghe, mọi chuyện có thể cũng đã khác. Mấu chốt của việc trở thành một con người tốt hơn nằm ở việc biết lắng nghe. Không chỉ là lắng nghe những người xung quanh, mà lắng nghe bản thân mình trước tiên.</p><p>Về phía gia đình, hẳn anh cũng cảm thấy dằn vặt lắm?</p><p>Mẹ tôi đã rất khổ sở trong suốt quãng thời gian tôi đi tù, tiếp nữa là phải lo cho ba tôi vì ông bị ung thư giai đoạn cuối. Mẹ đã rất vất vả. Tôi thật sự cảm thấy mình rất bất hiếu.</p><p>Sau khi trở về Việt Nam, tôi dành hầu hết thời gian để ở cạnh gia đình, ba mẹ mình. Nhưng tôi không thể xoá bỏ hoàn toàn cảm giác mình bất hiếu. Không phải chỉ lúc đi tù ở Mỹ tôi mới nhận thức được điều này, mà ngay cả lúc còn đi du học ở New Zealand, tôi cũng phạm lỗi khiến bố mẹ buồn lòng.</p><p>Tôi không bao giờ có thể báo hiếu trọn vẹn được cho ba mẹ mình, tôi nghĩ vậy, có thể là đến hết cả cuộc đời này. Mỗi ngày hiện tại, tôi đều dành thời gian để nói chuyện, ăn cơm với ba mẹ, chúc ba mẹ ngủ ngon. Những điều ngày xưa tôi chưa từng làm.</p><p>Tôi đã từng nghĩ nếu mình có nhiều tiền và lo được cho những người xung quanh là hạnh phúc. Nhưng không phải! Cái hạnh phúc ấy không phải là hạnh phúc. Nó là thứ cảm giác rất tạm bợ. Hạnh phúc thật nhất là được ở bên cạnh gia đình để chia sẻ những khó khăn cũng như vui vẻ cùng với nhau.</p><p>“Mỗi một vết thương đều là một sự trưởng thành”, có vết thương nào đến giờ vẫn còn đau?</p><p>Đau thì không đau nhưng có tiếc. Đó là về chuyện tình cảm. Tôi có một mối tình rất sâu đậm từ ngày tôi còn ở Việt Nam. Trong suốt quãng thời gian tôi đi tù, cô ấy vẫn bên tôi. Chúng tôi yêu nhau tới khoảng năm 2018 thì chia tay. Tôi xin phép chỉ chia sẻ tới đó thôi.</p><p>Một người mà có thể sẽ chẳng bao giờ biết tới cảm giác thất nghiệp như anh, sẽ tìm kiếm điều gì trong công việc?</p><p>Nếu mọi người nghĩ tôi “không bao giờ thất nghiệp” thì cũng hơi quá nhỉ (cười). Tôi cũng bình thường thôi. Thật ra thì công việc hiện tại của tôi ở Trung tâm Giám sát An toàn không gian mạng Quốc gia là công việc chính nhưng tôi vẫn đam mê các hoạt động cộng đồng.</p><p>Tâm niệm của tôi lúc trong tù là sau này phải làm gì có ích cho đời, không cần biết chuyện ấy nhỏ hay lớn tới đâu. Những việc mà tôi đang làm hiện tại đều đã được tôi viết trong 7 cuốn nhật ký lúc ở bên Mỹ.</p><p>Anh có thể chỉ ra một số hành động tưởng chừng bình thường, vô hại nhưng lại vô cùng nguy hiểm trên MXH hiện nay của người dùng mạng?</p><p>Mọi người hay chia sẻ thông tin cá nhân nhạy cảm trên mạng gồm hình ảnh gia đình, hình ảnh người thân cận, số điện thoại, danh sách bạn bè lên Facebook. Nó rất nguy hiểm. Khi một người khác biết quá nhiều về đời sống riêng tư của bạn thì bạn đối diện với rất nhiều nguy cơ.</p><p>Mong ước của tôi là chia sẻ, lan toả đến cộng đồng nhận thức về an toàn thông tin, hiểu rõ giá trị quan trọng của nó.</p><p>Hiện tại, dự án Chống lừa đảo của tôi cũng xây dựng thêm một số tính năng nhằm kiểm tra kiến thức căn bản về an toàn thông tin của người dùng mạng. Bạn sẽ học cách phân biệt đâu là trang web giả và thật. Có khoảng 10 câu hỏi về những vấn đề như vậy. Điều này sẽ luyện cho bạn kỹ năng cảnh giác lừa đảo.</p><p>Bạn phải biết bảo vệ bạn trước thì chúng tôi mới có thể tiến tới mục tiêu hoàn thiện giá trị cốt lõi của an ninh mạng và việc an toàn thông tin.</p><p>Có điều bình thường nào Hiếu PC muốn học để “hoà nhập” với cộng đồng của năm 2020, mà không liên quan đến công nghệ?</p><p>Đó là học về ngôn ngữ của Gen Z, hoặc ngôn ngữ của cư dân mạng. Tôi đọc comment của các bạn trên Facebook, tôi chẳng hiểu gì cả. Tôi phải copy cái dòng đó đi search Google xem ngữ nghĩa nó như thế nào. Có rất nhiều cái rất khó hiểu, rất nhiều từ viết tắt. Nhiều hồi tôi suy nghĩ lung lắm mà không biết mọi người đang nói cái gì, mấy đứa đang nói gì vậy, không hiểu! (cười)</p><p>Hiếu PC và... ngôn ngữ Gen Z</p><p>Nghe nói, anh định viết sách, anh muốn truyền tải điều gì với những người sẽ đọc nó?</p><p>Lạc quan - yêu đời - sống cho hiện tại - biết nghĩ cho mọi người xung quanh, đặc biệt là gia đình mình, những con người gần nhất. Ngoài ra, tôi cũng muốn các bạn trẻ sớm biết và hiểu bản thân mình nên làm gì để không phạm phải những sai lầm.</p><p>Cuốn sách ấy được tôi chắt lọc ra từ 7 cuốn nhật ký đã viết trong thời gian ngồi tù về định hướng cuộc đời, suy nghĩ của tôi và những thứ liên quan đến máy tính. Tôi đã học cách cách im lặng để lắng nghe và viết nhật ký như cách nhìn lại chính mình mỗi ngày. Đến bây giờ tôi vẫn phải học hỏi rất nhiều.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/26/artboard-1-16167280818951945772937.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/25/artboard-2-1616678761725301744125.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/25/artboard-3-1616678761921937549216.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/25/artboard-2-copy-16166787617091144303869.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/25/artboard-3-copy-2-1616678761791711223032.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/25/artboard-2-copy-2-1616678761690350207360.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/25/artboard-3-copy-3-16166787618141502575491.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/25/artboard-3-copy-4-16166787618452126366377.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/25/artboard-3-copy-5-16166787618531422516453.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/25/artboard-3-copy-6-1616680653493357090600.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Tôi nhận ra, trong cuộc đời này có 2 chữ tóm gọn và quan trọng nhất thôi, đó là tình yêu. Tất cả chỉ là tình yêu (yêu ba mẹ, yêu cuộc sống, yêu lấy chính mình…). Phải hiểu được 2 chữ đó mới hiểu được cách sống, đạo lý sống. Nếu không, sẽ không bao giờ sống được.</p>', NULL, 'hieu-pc---hacker-3-trieu-do-7-nam-tu-tren-dat-my-lan-dau-tien-noi-ve-tinh-yeu-va-cam-giac-bat-hieu', 0, NULL, NULL),
(116, 18, 5, 1, 1, 'SHB Finance và TrueMoney hợp tác cung cấp dịch vụ vay tiêu dùng tín chấp, duyệt vay trực tuyến ngay trong ngày', 'https://kenh14cdn.com/thumb_w/300/pr/2021/1617774564789-0-0-866-1387-crop-1617774568967-63753398865759.jpg', '|', 'SHB Finance và TrueMoney Việt Nam đã chính thức ký kết hợp tác và ra mắt dịch vụ cho vay tiêu dùng tín chấp SHB Finance trên ví điện tử TrueMoney cho các khách hàng sử dụng ví điện tử này.', '<p>Ngày 6/4/2021 tại trụ sở SHB Finance, Hà Nội,  SHB Finance -  Công ty tài chính TNHH MTV Ngân hàng TMCP Sài Gòn - Hà Nội và TrueMoney Việt Nam đã tổ chức Lễ ký kết hợp tác và chính thức cung cấp dịch vụ cho vay tiêu dùng tín chấp trên ví điện tử TrueMoney cho các khách hàng sử dụng ví điện tử này. Đây là dịch vụ cho vay ưu việt với quy trình xét duyệt khoản vay nhanh chóng, tiện lợi trên nền tảng thẩm định và phê duyệt tự động. Sản phẩm đánh dấu bước tiến mới mang đến cho khách hàng các gói vay tiêu dùng tối ưu của SHB Finance và chiến lược tiên phong đem lại những trải nghiệm tài chính thuận tiện, tin cậy trên nền tảng số hóa của ví điện tử TrueMoney tại thị trường Việt Nam.</p><p>Đại diện tham dự lễ ký kết có bà Trần Thanh Nữ Tường Vy - Phó Tổng Giám đốc SHB Finance, ông Nguyễn Thiện Tâm - Tổng Giám đốc TrueMoney Việt Nam cùng các lãnh đạo của SHB Finance và TrueMoney.</p><p>Là một nước đông dân thứ 14 thế giới với cơ cấu dân số trẻ, nền kinh tế năng động và thu nhập người dân tăng trưởng ổn định mỗi năm, thị trường tài chính tiêu dùng tại Việt Nam dự báo sẽ tiếp tục phát triển ở tốc độ cao trong những năm tới. Thực tế, tốc độ tăng trưởng hàng năm kép (CAGR) của thị trường tài chính tiêu dùng tại Việt Nam đã đạt mức 30,4%/năm trong vòng 4 năm (2015-2019). Số lượng khoản vay năm 2020 tăng 18,9% so với năm 2019.</p><p>Tuy nhiên trên thực tế, việc khai thác nhu cầu vay tín dụng của người dân hiện nay vẫn chưa thực sự hiệu quả khi chỉ có dưới 20% dân số được tiếp cận dịch vụ của các Ngân hàng hoặc công ty tài chính, chủ yếu tập trung ở các thành phố lớn. Không chứng minh được thu nhập, không có tài sản thế chấp, không tiếp cận được dịch vụ của ngân hàng được cho là những rào cản khiến cho một bộ phận khách hàng tiềm năng của dịch vụ vay tiêu dùng gặp khó khăn. Đây chính là phân khúc thị trường mà sản phẩm vay tiêu dùng của SHB Finance trên nền tảng ứng dụng TrueMoney sẽ nhắm tới với lời giải là quy trình chấm điểm tín dụng ứng dụng trí tuệ nhân tạo để thực hiện thẩm định và phê duyệt khoản vay hoàn toàn tự động, tạo nên trải nghiệm thân thiện và tiện lợi đến cho khách hàng.</p><p>Tại buổi lễ ký kết hợp tác, Bà Trần Thanh Nữ Tường Vy - Phó Tổng Giám đốc SHB Finance cho biết: \"Phát triển dịch vụ vay tiêu dùng trực tuyến là một trong những hướng đi chiến lược của SHB Finance trong việc đẩy mạnh dịch vụ vay tiêu dùng theo mô hình số. Với việc ứng dụng những tiện ích công nghệ như định danh điện tử eKYC, chống gian lận giả mạo sử dụng thị giác máy tính và quy trình chấm điểm tín dụng tự động, SHB Finance mong muốn mang đến cho người dùng trải nghiệm thuận tiện, tin cậy và tiện lợi\".</p><p>Việc ứng dụng nền tảng công nghệ tiên tiến sử dụng Big data và AI trong quy trình chấm điểm tín dụng và duyệt hạn mức vay tiêu dùng từ SHB Finance và Trusting Social - công ty fintech cung cấp giải pháp chấm điểm tín dụng qua công nghệ 4.0, sẽ giúp tối ưu hóa quy trình phê duyệt hồ sơ vay và giúp ngày càng nhiều khách hàng có cơ hội tiếp cận các dịch vụ tài chính một cách dễ dàng hơn. Theo đó, khách hàng sẽ có những trải nghiệm mang tính xuyên suốt, ổn định và chuyên nghiệp về một dịch vụ tài chính chất lượng cao, từ bước thực hiện hồ sơ vay cho tới khi tất toán khoản vay ngay trên Ví điện tử TrueMoney.</p><p>Ông Nguyễn Thiện Tâm - Tổng Giám đốc TrueMoney Việt Nam chia sẻ: \"TrueMoney nhìn thấy tiềm năng phát triển của thị trường vay tiêu dùng ngày càng lớn tại Việt Nam, và hầu hết khách hàng đều mong muốn một dịch vụ riêng tư, kín đáo và tiện lợi từ các tổ chức tài chính - ngân hàng uy tín. Với sản phẩm vay tiêu dùng tín chấp được ra mắt dưới sự hợp tác cùng SHB Finance lần này, chúng tôi kỳ vọng sẽ mang lại nhiều giá trị cộng thêm cho đông đảo khách hàng trẻ năng động, cung cấp thêm nhiều trải nghiệm về các sản phẩm tài chính trong thời đại số\".</p><p>Trong giai đoạn đầu, sản phẩm dự kiến được ra mắt dành riêng cho khách hàng hiện hữu thỏa mãn một số điều kiện của Ví điện tử TrueMoney. Để biết thêm chi tiết, khách hàng vui lòng xem thêm về sản phẩm tại: https://bit.ly/38b5T7A.</p><p>Về SHB Finance</p><p>Công ty tài chính TNHH MTV Ngân hàng TMCP Sài Gòn - Hà Nội (SHB Finance) được thành lập theo Giấy phép thành lập và hoạt động số 77/GP-NHNN do Ngân hàng Nhà nước Việt Nam cấp ngày 12/12/2016 và được phòng Đăng ký kinh doanh - Sở Kế hoạch và Đầu tư thành phố Hà Nội cấp Giấy chứng nhận đăng ký doanh nghiệp lần đầu ngày 28/3/2017 (Mã số doanh nghiệp: 0107779290).</p><p>Tính đến 31/3/2021, SHB Finance có số vốn điều lệ là 1.000 tỷ đồng do Ngân hàng TMCP Sài Gòn - Hà nội (SHB) sở hữu 100%.</p><p>SHB Finance tập trung cung cấp dịch vụ cho vay tiền mặt cho các nhóm khách hàng đại chúng - Cán bộ công nhân viên, công nhân, người kinh doanh nhỏ lẻ và các khách hàng khác có thể cung cấp các hóa đơn dịch vụ.</p><p>Đến nay, SHB Finance đã phục vụ tới 700.000 khách hàng trên 40 tỉnh thành phố cùng với đội ngũ nhân sự hơn 4.000 CBNV.</p><p>Về TrueMoney Việt Nam</p><p>TrueMoney là cánh tay fintech (công nghệ tài chính) của tập đoàn đa ngành hàng đầu thế giới Charoen Pokphand (C.P. Group). Tại Đông Nam Á, TrueMoney là một trong những fintech hàng đầu với mạng lưới khách hàng trải rộng nhất trong khu vực, phục vụ hơn 40 triệu người dùng tại 6 quốc gia. Tại Việt Nam, TrueMoney tập trung phát triển và cung cấp dịch vụ thanh toán và tài chính sáng tạo trên nền tảng ứng dụng di động.</p><p>Để biết thêm chi tiết, xin vui lòng liên hệ:</p><p>Phòng Tiếp thị & Truyền thông, SHB Finance</p><p>P: 024.7109 8888 (Ext: 1806) | M: truyenthongtaichinhshb@shbfinance.com.vn</p><p>Phòng Chăm sóc khách hàng Ví điện tử TrueMoney Việt Nam</p><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/7/photo-1-1617774094849681068128.jpg\" ><figcaption class=\"caption\">Lễ ký kết triển khai sản phẩm cho vay online SHB Finance trên Ví điện tử TrueMoney</figcaption></figure><p>P: 1900636675​   ​​| M: hotro@truemoney.com.vn</p>', NULL, 'shb-finance-va-truemoney-hop-tac-cung-cap-dich-vu-vay-tieu-dung-tin-chap-duyet-vay-truc-tuyen-ngay-trong-ngay', 0, NULL, NULL),
(117, 18, 5, 1, 1, 'Ngọc nữ của làng phim truyền hình bật mí cách cải thiện hôi, ngứa và giữ ẩm vùng kín hiệu quả từ dung dịch vệ sinh phụ nữ Gel X2 Nano nghệ', 'https://kenh14cdn.com/thumb_w/300/pr/2021/1617775504786-193-0-692-800-crop-1617775510191-63753398756163.jpg', '|', 'Hôi, ngứa và giữ ẩm vùng kín khiến nhiều chị em đau đầu, mệt mỏi vì không tìm được giải pháp cải thiện tốt, an toàn. Để vấn đề này sớm được cải thiện tuyệt đối đừng bỏ lỡ cách chăm sóc vùng kín từ dung dịch vệ sinh phụ nữ Gel X2 Nano nghệ của diễn viên Tường Vi nhé!', '<p>Tường Vi chia sẻ về tình trạng hôi, ngứa vùng kín</p><p>Không chỉ được xem là ngọc nữ của làng phim truyền hình với nhiều giải thưởng danh giá, nữ diễn viên được mệnh danh là Cá Ba Sa (Tường Vi) còn khiến nhiều người ngưỡng mộ với nhan sắc ngày càng thăng hạng ở tuổi 32. Đặc biệt, mới đây sự kiện nữ diễn viên chia sẻ \"chuyện thầm kín to nhỏ của chị em\" khiến nhiều chị em đốn tim.</p><p>Khi nói về tình trạng hôi, ngứa vùng kín của phụ nữ, diễn viên Tường Vi chia sẻ: \"Dạo này, đi quay phim thì Tường Vi cũng hay tám đủ chuyện trăng sao với chị em trong đoàn. Rồi nhiều khi cũng nghe một số chị em than thở về việc bị ngứa ngáy, khó chịu vùng nhạy cảm, ảnh hưởng tới quá trình làm việc. Đúng thực là nghe xong đồng cảm luôn ý, vì bản thân Vi thỉnh thoảng cũng gặp phải vấn đề như vậy mà chưa có một giải pháp nào để giải quyết triệt để. Dù lâu lâu mới bị nhưng mà cũng khó chịu lắm. Rồi cũng vì bận, một phần nữa vì thẹn nêu cứ giấu giấu, âm thầm chịu đựng. Nhiều khi cứ tặc lưỡi tự bảo mình ráng nhịn, nhịn đi, nhịn chút xíu, vài hôm là khỏi thôi hà nhưng không đâu nhé!\"</p><p>Vì sao dung dịch vệ sinh sinh phụ nữ Gel X2 Nano nghệ lại được diễn viên Tường Vi tin tưởng sử dụng</p><p>Sau khi chia sẻ về tình trạng \"chỗ ấy\" bị hôi, ngứa thì nữ diễn viên được mệnh danh là Cá Ba Sa (Tường Vi) đã nhắc khéo các chị em phụ nữ tin dùng dung dịch vệ sinh phụ nữ Gel X2 Nano Nghệ.</p><p>Nữ diễn viên chia sẻ lý do mà mình tin tưởng sử dụng sản phẩm xuất phát từ việc mình có người bạn thân là chuyên gia, chuyên chăm lo các vấn đề sức khỏe cho Tường Vi. Vì vậy sau khi tâm sự về vấn đề \"bim bim\" đang gặp phải thì được cô bạn khuyên dùng ngay sản phẩm dung dịch vệ sinh phụ nữ GEL X2 NANO NGHỆ CỎ C Â Y HOA LÁ.</p><p>Bên cạnh đó, Tường Vi chia sẻ thêm về lý do mình quyết định sử dụng sản phẩm này là do đã tìm hiểu kỹ lưỡng và thấy được những điểm ưu việt của sản phẩm mà không phải loại dung dịch vệ sinh nào cũng có được.</p><p>Nữ diễn viên Cá Ba Sa cho biết, Gel X2 Nano nghệ có thành phần chủ yếu là Nano curcumin nghệ, berberin, rễ cây ngưu bàng, chanh tây... Trong đó,  Nano nghệ chính là Nano curcumin được bào chế từ công nghệ tiên tiến bậc nhất hiện nay giúp cho sự hấp thu của hoạt chất tăng cao gấp hàng chục lần. Đặc biệt, cô nàng đã tìm hiểu và biết được Nano nghệ còn có đặc tính kháng viêm, kháng khuẩn giúp khắc phục hiệu quả tình trạng vùng kín có mùi và nấm ngứa.</p><p>Tiếp đến là berberin, đây là hoạt chất vô cùng quen thuộc với người Việt được chiết xuất từ cây vàng đắng có khả năng hỗ trợ chống viêm, kháng khuẩn hiệu quả. Ngoài ra, thành phần kết hợp cùng chanh tây, bạc hà tự nhiên giúp giảm hôi vùng kín hiệu quả, mang lại cho bạn gái cảm giác thơm tho, khô thoáng tuyệt đối.</p><p>Ngoài 2 thành phần nổi bật nói trên thì Tường Vi còn bật mí thêm Gel X2 Nano nghệ còn chứa một thảo dược đặc biệt nữa là rễ cây ngưu bàng. Đây được xem là \"hoạt chất vàng\" giúp thúc đẩy sản xuất collagen.</p><p>Nói về cảm nhận sử dụng dung dịch vệ sinh phụ nữ Gel X2 Nano nghệ, Tường Vi đáp lại, \"cảm giác đầu tiên khi xịt ra tay là nó trong, màu vàng nhẹ của nghệ, có có mùi thơm nhè nhẹ của bạc hà, của chanh tây. Nói chung là ấn tượng đầu tiên khá là dễ chịu, \'cô bé\' như được thư giãn. Chăm chỉ sử dụng khoảng một tuần đầu thì Vi thấy cảm giác ngứa, khó chịu cứ giảm dần. Thậm chí, đến tuần thứ hai thì hết hẳn và hầu như là không bị lại nếu kiên trì sử dụng đều đặn mỗi ngày.\"</p><p>Có lẽ, vì có cô bạn là chuyên gia khuyên sử dụng cộng thêm với hiệu quả cao lại đảm bảo an toàn nên nữ diễn viên Tường Vi đã khẳng định chắc chắn sẽ sử dụng Gel X2 Nano nghệ lâu dài. Đặc biệt, với các fan của Vi cũng như các chị em phụ nữ nói chung nếu có mong muốn mua sản phẩm này đừng quên nhập mã TUONGVI30K để nhận thêm ưu đãi nhé!</p><p>Mua hàng chính hãng tại  https://bit.ly/3fklFRE.</p><p></p><p>Gel X2 Nano nghệ là sản phẩm dung dịch vệ sinh phụ nữ, có tác dụng làm sạch vùng kín, hỗ trợ cải thiện hôi, ngứa, giữ ẩm hiệu quả.</p><p>Website: https://cocayhoala.vn/.</p><p>Hotline: 1900 633 385.</p><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/7/photo-1-1617774770316319538793.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/7/photo-1-1617774773259853732496.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/7/photo-2-16177747732632069925125.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/7/photo-3-16177747732671671754532.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Fanpage: Cỏ Cây Hoa Lá.</p>', NULL, 'ngoc-nu-cua-lang-phim-truyen-hinh-bat-mi-cach-cai-thien-hoi-ngua-va-giu-am-vung-kin-hieu-qua-tu-dung-dich-ve-sinh-phu-nu-gel-x2-nano-nghe', 0, NULL, NULL),
(118, 18, 5, 1, 1, 'Những tác dụng của trà xanh trong làm đẹp chắc chắn nhiều bạn chưa biết', 'https://kenh14cdn.com/thumb_w/300/pr/2021/1617704278054-26-0-414-621-crop-1617704285359-63753327094893.jpg', '|', 'Từ xa xưa, trà xanh vốn không chỉ là đồ uống thường có trong mỗi gia đình mà nó còn góp phần trong việc chữa bệnh và làm đẹp cho các chị, các dì. Ngày nay, tác dụng của trà xanh càng được chứng minh rõ hơn từ những nghiên cứu y khoa và nó dần trở nên phổ biến trên toàn thế giới.', '<p>Vậy trà xanh có công dụng như thế nào đối với sức khỏe và làm đẹp?</p><p>Hãy cùng tìm hiểu qua bài viết dưới đây và trải nghiệm 2 sản phẩm super matcha nổi tiếng được đông đảo phái nữ sử dụng nhé.</p><p>Dùng trà xanh để chăm sóc sắc đẹp chính là một phương pháp an toàn và hiệu quả. Dưới đây là một số tác dụng của trà xanh trong lĩnh vực làm đẹp.</p><p>Trà xanh giúp bạn cải thiện sắc tố da, loại bỏ độc tố, giảm viêm và tăng độ đàn hồi cho da.</p><p>Để làm mặt nạ trà xanh có tác dụng dưỡng trắng, bạn hãy cắt 2 túi trà lọc đã dùng để lấy bã trà xanh, sau đó thêm 1 - 2 muỗng mật ong vào bã trà cùng 1 ít nước cốt chanh.</p><p>Hãy sử dụng mặt nạ trà xanh vừa làm trong 5 - 10 phút rồi rửa sạch bằng nước ấm. Bạn nên dùng mặt nạ này mỗi tuần một lần để có làn da trắng sáng hơn nhé.</p><p>Trà xanh có chứa một số chất kháng khuẩn giúp bạn chữa các loại mụn do vi khuẩn gây ra.</p><p>Để dùng trà xanh trị mụn, bạn hãy áp dụng công thức sau: pha loãng trà xanh mới nấu với nước, sử dụng bông gòn thấm trà và lau lên vùng có mụn rồi giữ yên trong khoảng 10 - 15 phút. Bạn nên áp dụng mỗi ngày 2 lần cho đến khi làn da sạch mụn nhé.</p><p>Các lỗ chân lông của bạn nếu có kích thước lớn thường rất dễ bị tắc và viêm lỗ chân lông. Với công dụng chống oxy hóa hiệu quả, trà xanh sẽ là loại thực vật làm se da để giúp bạn thu nhỏ các lỗ chân lông của mình.</p><p>Trà xanh tốt là như vậy nhưng bạn còn phải tìm chỗ mua lá trà, còn phải chế biến, pha trộn… nó làm bạn thấy chùn bước. Bên cạnh đó, nếu bạn làm sạch và chế biến không đúng cách, có thể gây nhiễm trùng da với những làn da đang có mụn. Giải pháp dành cho các bạn chính là sử dụng những sản phẩm chiết xuất từ trà xanh, vừa tiết kiệm vừa an toàn cho da nè. Hiện nay, có khá nhiều mỹ phẩm có thành phần chính là trà xanh với nhiều chức năng làm đẹp khác nhau. Trong số đó, có 2 sản phẩm toner và mặt nạ đất sét dòng Super Matcha của thương hiệu Some By Mi có thành phần hoàn toàn từ trà xanh thiên nhiên đang được đông đảo chị em yêu thích và sử dụng.</p><p>Sản phẩm được sản xuất từ matcha cao cấp từ đồn điền trà được thiên nhiên ban tặng có tên Bosung. Đây là loại matcha cao cấp thuần túy của vùng đất Bosung, vùng này chuyên trồng trà để cung cấp cho vua chúa ngày xưa vì thổ nhưỡng tốt, khí hậu thuận lợi để trà được phát triển và giữ nguyên dưỡng chất có lợi cho làn da và sức khỏe. Nhờ đó, với Super Matcha Line của Some By Mi cung cấp cho làn da năng lượng sâu và mạnh mẽ. Sau khi loại bỏ thân và gân lá trà xanh sẽ được nghiền thành bột mịn và được hòa trộn cùng với một số thành phần khác tạo nên Toner Super Matcha Pore Tightening.</p><p>Theo review của các tín đồ đồ skincare, tác dụng nổi bật nhất của toner này chính là hỗ trợ se khít lỗ chân lông mạnh mẽ, loại bỏ các tạp chất còn sót lại trên da ngay cả khi rửa mặt. Ngoài ra, thành phần Tannin ngoài tác dụng kháng khuẩn còn giúp chăm sóc da và cấp ẩm cho làn da vô cùng hiệu quả.</p><p>Cùng công nghệ Sebum Controller Powder độc quyền giúp làm sạch bã nhờn và tạp chất để duy trì lỗ chân lông luôn trong trạng thái tươi mới, ổn định lại độ ẩm và lượng dầu tự nhiên trên da. Đồng thời được bổ sung thêm chiết xuất lá diếp cá rất thích hợp cho làn da mụn, sẽ giúp da được bảo vệ khỏi những tác hại của tia UV.</p><p>Theo phân tích của nhãn hàng, ngoài những công dụng ưu việt của trà xanh thì mặt nạ Some By Mi có một điểm cộng chính là thành phần đất sét từ vùng Manicouagan rất giàu các khoáng chất tự nhiên giúp duy trì làn da khỏe mạnh. Đối với làn da khô, đất sét biển này thẩm thấu qua da để nuôi dưỡng các mô và duy trì độ ẩm cao. Còn với làn da nhờn, đất sét biển giúp hấp thụ các dầu nhờn dư thừa, mụn đầu đen.</p><p>Ngoài ra, sản phẩm còn có tác dụng dưỡng ẩm cho da nhờ các tinh dầu có chức năng cung cấp độ ẩm, làm dịu da. Không gây cảm giác khô da sau khi sử dụng mà còn giúp bề mặt da thô ráp trở nên mịn màng hơn.</p><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-1-16177038122612016702195.jpg\" ><figcaption class=\"caption\">Làm đẹp từ trà xanh vừa nhanh, vừa tiết kiệm mà hiệu quả</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/image004-16177040500951479213470.png\" ><figcaption class=\"caption\">Super Matcha Pore Tightening Toner có thành phần chủ yếu là nước trà xanh tươi chiếm 83%</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-1-1617703819799925181981.jpg\" ><figcaption class=\"caption\">Kết cấu sản phẩm dạng nước trong suốt, không bết dính tạo cảm giác dễ chịu</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/image008-1617704099393603619915.png\" ><figcaption class=\"caption\">Super Matcha Pore Clean Clay Mask - mặt nạ hỗ trợ hấp thu bã nhờn, giảm mụn</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-2-1617703819801888490150.jpg\" ><figcaption class=\"caption\">Kết cấu mềm, dễ tán lên da và dễ rửa khi sử dụng</figcaption></figure><p>Cùng với kết cấu mềm mịn, không chứa hạt nên rất dịu nhẹ, kể cả khi da bạn đang nhạy cảm hoặc có mụn.</p>', NULL, 'nhung-tac-dung-cua-tra-xanh-trong-lam-dep-chac-chan-nhieu-ban-chua-biet', 0, NULL, NULL),
(119, 18, 5, 1, 1, 'Nên mua sữa chất lượng ở đâu cho bé tại Thành phố Hồ Chí Minh', 'https://kenh14cdn.com/thumb_w/300/pr/2021/1617703153115-217-0-667-720-crop-1617703165781-63753326819985.jpg', '|', 'Vai trò của sữa trong đời sống mẹ và bé ngày càng quan trọng, Siêu thị hàng Mỹ Úc Nhật luôn cố gắng mỗi ngày mang tới cho các mẹ và em bé những sản phẩm sữa đa dạng, an toàn và chất lượng nhất trên thị trường.', '<p>Ngày nay, sữa cho bé là sản phẩm dinh dưỡng thiết yếu, có vai trò quan trọng đối với mẹ bầu, trẻ sơ sinh và trẻ nhỏ. Sữa giúp bổ sung được hàm lượng các chất còn thiếu trong cơ thể của mẹ và bé, từ đó bổ sung được lượng chất phù hợp trong quá trình phát triển của con và sự khỏe mạnh của mẹ.</p><p>Hiện nay, trên thị trường có khá nhiều khách hàng luôn lo lắng về chất lượng sữa bột đang có trên thị trường và đau đầu để lựa chọn các sản phẩm sữa phù hợp cho con.</p><p>Chắc chắn cũng có không ít những mẹ đã thay việc sử dụng sữa bột cho con mình uống bằng đồ ăn để bổ sung dinh dưỡng trong ngày do mua phải sữa giả, kém chất lượng.</p><p>Hiểu được những nhu cầu, mong muốn của các bà mẹ, Siêu thị hàng Mỹ Úc Nhật luôn cung cấp tới cho người tiêu dùng những sản phẩm có nguồn gốc rõ ràng, nhập khẩu từ nước ngoài theo đường chính ngạch. Tất cả các sản phẩm đều được kiểm định đầy đủ trước khi đưa tới tay người tiêu dùng.</p><p>Có lẽ cái tên Siêu thị hàng Mỹ Úc Nhật không còn quá xa lạ với các mẹ bầu hoặc mẹ có con nhỏ ở Thành phố Hồ Chí Minh. Là siêu thị chuyên phân phối các sản phẩm của mẹ và bé nhập khẩu ở các nước trên thế giới, Siêu thị hàng Mỹ Úc Nhật luôn cố gắng không ngừng mang tới cho các mẹ sự đa dạng trong các sản phẩm sữa của các nước, đồng thời vẫn phải đảm bảo 100% chất lượng sản phẩm được nhập khẩu về.</p><p>Với phương châm: \"Mẹ khỏe, bé ngoan - lớn khôn mỗi ngày\", Siêu thị hàng Mỹ Úc Nhật luôn lấy khách hàng, chất lượng sản phẩm làm kim chỉ nam để kinh doanh và phát triển doanh nghiệp của mình.</p><p>Siêu thị hàng Mỹ Úc Nhật chuyên phân phối các loại sữa thuộc các tập đoàn sữa lớn trên thế giới và có kiểm định chất lượng từ bộ y tế của các nước và giấy kiểm định an toàn với trẻ khi uống. Với đa dạng các hãng sữa như Abbott Hoa Kỳ, Horizon organic, Pedia Sure, Hikid, Blackmore, Bellamy\'s Organic, A2, Similac, Enfamil, Namyang, Anka, Humana, Glico, Ildong, Mead Johnson, Nestle, Meiji, Morinaga, Nutifoods… mang tới sự lựa chọn đa dạng cho các mẹ. Bên cạnh đó, các sản phẩm đều được kiểm định rõ ràng về nguồn gốc xuất xứ, đem tới sản phẩm chất lượng, đa dạng và giá cả hợp lý.</p><p>Bên cạnh đó, Siêu thị hàng Mỹ Úc Nhật cũng có những buổi chia sẻ kinh nghiệm làm mẹ, chăm sóc bé và nhận biết về tình trạng sức khỏe của con thông qua các buổi nói chuyện và trao đổi với các chuyên gia dinh dưỡng, chuyên gia chăm sóc sức khỏe mẹ và bé.</p><p>Ngoài ra, Siêu thị hàng Mỹ Úc Nhật cũng cung cấp cho các mẹ và bé, đa dạng các loại vitamin nhập khẩu, đồ dùng cho bé… giúp các mẹ thuận tiện trong quá trình mua sắm và sử dụng.</p><p>Siêu thị hàng Mỹ Úc Nhật luôn cố gắng phát triển, cung cấp tới cho khách hàng những sản phẩm tốt nhất và hiệu quả nhất cho người tiêu dùng.</p><p>Thông tin tham khảo:</p><p>Siêu thị hàng Mỹ Úc Nhật</p><p>Địa chỉ: 118 Huỳnh Văn Bánh, Phường 12, Quận Phú Nhuận, Thành phố Hồ Chí Minh</p><p>Website: https://hangmyucnhat.com/</p><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-1-16177027012171318706702.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-1-1617702704422681909090.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-2-1617702704427539313798.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Facebook: https://www.facebook.com/hangxachtaymyucnhat/</p>', NULL, 'nen-mua-sua-chat-luong-o-dau-cho-be-tai-thanh-pho-ho-chi-minh', 0, NULL, NULL),
(120, 18, 5, 1, 1, 'Ra mắt đồng hồ không kim ngày Cá tháng Tư, Curnon bất ngờ nhận hơn 500 đơn đặt hàng', 'https://kenh14cdn.com/thumb_w/300/pr/2021/1617703617728-253-0-1190-1500-crop-1617703625503-63753326621355.jpg', '|', 'Với ý tưởng “điên rồ” về chiếc đồng hồ không kim đầu tiên do Việt Nam sản xuất, Curnon tạo nên một cơn sốt trong cộng đồng, nhận hơn 500 đơn đặt hàng trong ngày Cá tháng Tư.', '<p>Ngày 1/4 là dịp để không ít các nhãn hàng công bố những ý tưởng \"điên rồ\" và Curnon cũng không nằm ngoài xu hướng. Đồng hồ đeo tay vẫn luôn mang một vai trò truyền thống giúp bạn theo dõi thời gian. Vậy bạn nghĩ sao với một chiếc đồng hồ không kim, không số? Đây chính là một sản phẩm đặc biệt được thương hiệu đồng hồ Việt - Curnon cho ra mắt ngày Cá tháng Tư khiến dân tình chao đảo vì những giá trị có 1 không 2.</p><p>Thổi bùng tinh thần Why not vào không khí ngày Cá tháng Tư</p><p>Thương hiệu đồng hồ Việt Curnon vốn được biết đến với phong cách hiện đại, trẻ trung cùng sứ mệnh tạo ra nhiều giá trị mới. Là một người bạn đồng hành với giới trẻ và luôn bắt kịp mọi xu hướng, không có lý do gì để Curnon ngồi im trước không khí đầy thú vị ngày Cá tháng Tư. Nhân dịp này, Curnon muốn tạo nên một sự kiện bất ngờ nhằm truyền tải thêm những thông điệp ý nghĩa cho giới trẻ.</p><p>Vẫn là khát vọng đập tan rào cản \"Liệu có quá muộn để bắt đầu từ bây giờ?\" mà thế hệ trẻ đang vật lộn mỗi ngày, Curnon đã thổi bùng tinh thần Why not vào sản phẩm đồng hồ không kim đầu tiên sản xuất tại Việt Nam. Không ít người đặt ra nghi hoặc về độ chân thật của một sản phẩm ra mắt vào ngày Cá tháng Tư. Liệu chiếc đồng hồ này đang ẩn chứa điều bất ngờ gì đây?</p><p>Sự thật đằng sau chiếc đồng hồ không kim đầu tiên sản xuất tại Việt Nam</p><p>Chắc hẳn, có nhiều lúc bạn luôn cảm thấy rằng mình không có đủ thì giờ để làm hết những gì mình muốn. Và Curnon hiểu rằng, đôi khi thời gian chính là rào cản khiến bạn chần chừ trước một quyết định quan trọng. Tạo ra một chiếc đồng hồ không kim giờ, kim phút, không có màn hình hiển thị kỹ thuật số, Curnon mang đến định nghĩa 3 không vô cùng táo bạo về thời gian.</p><p>Không có giới hạn về thời gian - Chiếc đồng hồ không hiển thị thời gian, bởi lẽ tại đây thời gian không hề có giới hạn. Bạn hoàn toàn có thể sở hữu nhiều hơn 24h mỗi ngày để thỏa sức làm mọi việc.</p><p>Không bao giờ hết pin - Đồng hồ không kim cũng chính là hiện thân cho những người trẻ hiện đại. Bạn luôn tràn đầy nhiệt huyết, khát khao cùng đam mê. Và nguồn năng lượng tỏa ra từ chính bạn sẽ không bao giờ cạn kiệt.</p><p>Không còn áp lực về thời gian - Đập tan đi những áp lực về thời gian, bạn cứ thoải mái khám phá bản thân và theo đuổi giấc mơ của chính mình. Chính vào lúc này, bạn đã có trong tay mọi thì giờ trong đời.</p><p>Với nhiều giá trị tuyệt vời, chắc hẳn ai trong chúng ta cũng mong muốn được sở hữu chiếc đồng hồ vạn năng này. Tuy nhiên, đây chỉ là một sản phẩm giả tưởng mà Curnon cho ra mắt để chào mừng ngày Cá tháng Tư. Thế nhưng không vì thế mà cơn sốt và sự quan tâm của giới trẻ dành cho đồng hồ không kim giảm nhiệt, trái lại còn nhận được sự hưởng ứng mạnh mẽ từ cộng đồng.</p><p>Sản phẩm giả tưởng nhận được sự hưởng ứng chân thật từ giới trẻ</p><p>Có thể chiếc đồng hồ không kim Curnon mãi chỉ là giả tưởng nhưng giá trị mà nó truyền tải đã thực sự tác động đến cộng đồng. Minh chứng cho điều này chính là nhiều người dùng sẵn lòng đặt hàng cho một sản phẩm không có thực nhưng vẫn rất hài lòng. Hơn 500 yêu cầu đặt hàng được gửi tới chỉ trong vài giờ ra mắt sản phẩm cho thấy giá trị của đồng hồ không kim không phải là điều viển vông.</p><p>Giới trẻ thích thú truyền tai nhau bàn luận về chiếc \"Đồng hồ không kim đầu tiên do Việt Nam sản xuất\". Quả thực, ý tưởng của Curnon đã chạm được tới cộng đồng và mang lại những động lực cho người trẻ trong thực tế.</p><p>Why not - Sở hữu một chiếc đồng hồ không kim ngay trong chính trái tim nhiệt huyết của người trẻ chưa bao giờ là điều không thể. Còn bạn thì sao? Hãy cùng nguồn cảm hứng mà Curnon lan tỏa, bước ra khỏi vùng an toàn của mình - nghĩ khác, làm khác và sống khác thật ý nghĩa.</p><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-1-1617703352444271873424.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-1-161770335536394065170.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-2-1617703355367907075612.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Trải nghiệm ngay sản phẩm đồng hồ không kim đầu tiên sản xuất ở Việt Nam tại: https://handless.curnonwatch.com/.</p>', NULL, 'ra-mat-dong-ho-khong-kim-ngay-ca-thang-tu-curnon-bat-ngo-nhan-hon-500-don-dat-hang', 0, NULL, NULL),
(121, 18, 5, 1, 1, 'Điểm sáng trong hoạt động xuất khẩu gạo nếp của công ty cung cấp gạo Hoài Dũng', 'https://kenh14cdn.com/thumb_w/300/pr/2021/1617687027076-88-0-1338-2000-crop-1617687034424-63753310374921.jpg', '|', 'Tận dụng lợi thế từ nguồn cung cấp gạo dồi dào tại các tỉnh miền Tây, công ty Hoài Dũng đã có những sản phẩm gạo nếp xuất khẩu với chất lượng cao cùng bao bì thu hút, chuyên nghiệp. Không chỉ đem lại những sản phẩm gạo tốt cho khách hàng trong và ngoài nước, công ty còn góp phần quảng bá chất lượng nông sản Việt.', '<p>Công ty được thành lập bởi CEO 9x tài ba Cao Hoài Dũng, hoạt động chủ yếu trong lĩnh vực gia công sấy, xay xát và cung ứng gạo nếp xuất khẩu. Những ngày tháng đầu khi mới thành lập, công ty phải đối mặt với nhiều khó khăn khi cơ sở vật chất còn thiếu thốn, sản lượng công ty có thể cung cấp kém hơn rất nhiều so với các công ty có nhà máy quy mô lớn trong khu vực. Đây còn là giai đoạn nguồn vốn công ty còn hạn hẹp nên chưa thể đầu tư thêm máy móc, thiết bị.</p><p>Đứng trước tình hình như vậy, CEO Hoài Dũng không hề hoang mang mà rất bình tĩnh, cân nhắc các yếu tố và đưa ra quyết định tập trung vào chất lượng các sản phẩm phân phối ra. CEO Cao Hoài Dũng cho rằng: \"Nếu chưa thể cùng lúc cho ra được sản lượng lớn, tại sao ta không tập trung vào chất lượng, chuẩn hóa quy trình gia công để đóng gói được những sản phẩm tuyệt vời nhất, nhanh chóng chiếm được thiện cảm của phần đông khách hàng, thuận tiện cho việc mở rộng quy mô sau này.\"</p><p>Với sự chăm chỉ, nhiệt huyết cùng kinh nghiệm sẵn có với việc kinh doanh các sản phẩm từ gạo, chàng CEO trẻ tuổi nhanh chóng xây dựng được quy trình xay xát và sấy chuyên nghiệp, đảm bảo vệ sinh. Những hạt lúa được tuyển chọn kỹ lưỡng từ nhà cung cấp uy tín nhất sẽ được đưa vào xay xát, lọc kĩ sạn qua thiết bị chuyên dụng để đảm bảo hạt gạo thành phẩm được chắc mẩy, không bị lẫn các loại hạt lép hay tạp chất, sạn... Để công đoạn này được thực hiện nhanh chóng, ngay từ khâu tuyển chọn, công ty cung cấp gạo Hoài Dũng cũng đã phải dành rất nhiều thời gian để tìm kiếm và chọn lựa được nhà cung cấp lúa chất lượng nhất.</p><p>Với các sản phẩm xuất khẩu, ngoài chất lượng, các yếu tố về bảo quản và đóng gói cũng cần được quan tâm để chất lượng gạo thành phẩm được đảm bảo tốt nhất. Để sau khi đóng gói có thể bảo quản được gạo trong thời gian dài, ngay khâu sấy khô đã cần làm thật cẩn thận. Chỉ cần gạo còn một chút độ ẩm thôi cũng có thể gây ra những hậu quả khó lường trong quá trình bảo quản.</p><p>Bản thân Hoài Dũng cũng hiểu được tầm quan trọng của việc giữ gìn hình ảnh thương hiệu nên chàng CEO trẻ này đã đặt ra những tiêu chí cực nghiêm ngặt cho quá trình kiểm định sản phẩm trước khi đóng gói. Gạo thành phẩm cần phải được sấy khô hoàn toàn, không có bất kỳ sạn hay hạt lép, hạt gạo hỏng nào. Sau khi đảm bảo yếu tố trên mới được đưa vào đóng gói lại theo các mức trọng lượng khác nhau. Một số sản phẩm còn được hút chân không để đảm bảo môi trường bảo quản thuận lợi nhất, giúp thời hạn sử dụng gạo được lâu hơn khi xuất khẩu.</p><p>Dù mới thành lập được ít năm nhưng công ty cung cấp gạo Hoài Dũng đã có chỗ đứng nhất định trong thị trường phân phối nông sản khu vực Long An cũng như trên thị trường xuất khẩu gạo nếp. Điểm nổi bật trong các sản phẩm của công ty có lẽ chính là chất lượng hạt gạo với vẻ ngoài to, tròn, giàu dưỡng chất và có sự tỉ mỉ, đầu tư trong quá trình xay xát, sấy và đóng gói. Đây chính là những yếu tố khách hàng đánh giá cao ở những sản phẩm của công ty phân phối gạo Hoài Dũng.</p><p>Công Ty TNHH Thương Mại Hoài Dũng</p><p>Địa chỉ: 94, Ấp 1 - Xã Tân Đông - Huyện Thạnh Hoá - Tỉnh Long An</p><p>Facebook: https://www.facebook.com/CHDbiz</p><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-1-16176867160061597001731.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-1-16176867190682071341242.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-2-16176867190741436932564.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Hotline: 08888.60.668</p>', NULL, 'diem-sang-trong-hoat-dong-xuat-khau-gao-nep-cua-cong-ty-cung-cap-gao-hoai-dung', 0, NULL, NULL),
(122, 18, 5, 1, 1, 'Săn sale đồ hiệu ở mall xưa rồi, bây giờ thứ Ba ngồi nhà vẫn gom được hàng hiệu mà còn được giảm đến 50%', 'https://kenh14cdn.com/thumb_w/300/pr/2021/1617687468216-0-114-603-1080-crop-1617687478743-63753310264793.jpg', '|', 'Đến hẹn lại lên, Ngày Hội Bách Hoá trên Shopee đã quay trở lại cùng chương trình Hàng hiệu giảm giá 50% mang đến hàng ngàn ưu đãi hấp dẫn từ các thương hiệu tên tuổi.', '<p>Cuộc đổ bộ của các thương hiệu như La Roche-Posay, Durex, Tã Huggies, Bobby, Dầu Đậu Nành Simply, Bia Tiger... trong chương trình  Hàng hiệu giảm giá 50% chắc chắn sẽ làm thứ 3 của bạn thêm phần hứng khởi. 100% các mặt hàng chính hãng được Shopee trợ giá về mức cực ưu đãi.</p><p>Túi xách nữ đeo chéo thời trang NAHA</p><p>Mùa hè đã rất cận kề, vừa đúng lúc phải liệt kê ra vài món để bổ sung cho tủ đồ của nàng. Tham khảo ngay mẫu túi đeo chéo tông hồng pastel xinh xắn từ NAHA để \"nữ tính hóa\" các trang phục mà nàng sẽ diện khi xuống phố hay đi làm.</p><p>Sản phẩm có 1 ngăn chính và 2 ngăn phụ tiện lợi để nàng thoải mái đựng những vật dụng cần thiết khi đi ra ngoài mà không cần phải cân nhắc nên mang gì theo. Ngoài ra, chất liệu da tổng hợp bề mặt láng mịn vừa dễ vệ sinh vừa mang lại cái nhìn sang trọng cho sản phẩm.</p><p>Giày cao gót đế vuông Mica Pixie</p><p>Nếu những đôi giày cao gót nhọn khiến nàng có phần không thoải mái khi diện cả ngày dài thì giày cao gót đế vuông chính xác là một gợi ý hợp lý, góp phần đa dạng hóa phong cách của các nàng ưa điệu đà. Để trẻ trung hơn, nàng có thể cân nhắc những đôi cao gót chất liệu da bóng mang đến cái nhìn thu hút và năng động.</p><p>Mẫu giày da bóng phối gót mica từ Pixie chắc chắn sẽ không làm nàng thất vọng. Chất da bóng thời thượng, tăng thêm độ sang chảnh mà vẫn giữ được vẻ dịu dàng, nữ tính. Đặc biệt chi tiết gót mica như một điểm nhấn cho sản phẩm và tạo nét thu hút cho tổng thể trang phục.</p><p>Set áo và chân váy dạ sang chảnh từ OLLY</p><p>Nếu đang bí ý tưởng trang phục công sở hay đồ dự tiệc mang tính chất trang trọng, một set đồ làm bằng chất liệu dạ chắc chắn là lựa chọn không thể bỏ qua. Dạ vốn là loại vải thường được sử dụng trong thời trang cao cấp để tăng thêm đẳng cấp cho người mặc.</p><p>Set áo và chân váy dạ tông xanh pastel nền nã từ OLLY có làm nàng xao xuyến? Sang trọng mà không hề cứng nhắc hay làm \"già hóa\" vẻ ngoài, nữ tính điệu đà mà còn tôn thêm thần thái của người mặc chính là những điểm nổi bật mà set đồ này mang đến.</p><p>Kính mát thời trang ANNA</p><p>Một chiếc kính mát chống tia UV sẽ giúp nàng dễ dàng vượt qua những ngày nắng, vô cùng hữu dụng khi đi làm và đi chơi. Và lưu ý hãy chọn một chiếc kính to bản để ôm trọn phần lớn khuôn mặt trong những ngày nắng gắt.</p><p>Lựa chọn kính mắt thời trang ANNA với thiết kế trẻ trung, cá tính, đôi mắt nàng vừa được bảo vệ mà vẫn không làm mất đi vẻ cá tính cho tổng thể trang phục. Đặc biệt, với chất liệu cao cấp và an toàn, nàng có thể an tâm tận hưởng cả ngày dài mà không còn e ngại nắng nóng.</p><p>Thông tin ưu đãi:</p><p>Tranh thủ săn ngay các voucher giảm tối đa 50K áp dụng cho các đơn hàng từ 0Đ mở trong các khung giờ 0H - 9H - 12H - 21H trong ngày Hàng hiệu giảm đến 50%. Người dùng còn được hoàn xu 8%, giảm tối đa 100K cho đơn hàng tối thiểu 500K; Giảm giá 5% tối đa 150K cho đơn hàng tối thiểu 1 triệu. Đặc biệt đừng bỏ qua các siêu ưu đãi từ Shopee dành cho Sữa bột Enfagrow A+ 4  cho trẻ từ 2 - 6 tuổi; Nước hoa full size nữ Club De Nuit 105ml Edp và Tã quần Bobby - Đệm thấm mồ hôi lưng Mega Jumbo.</p><p>Ngoài ra Hàng hiệu giảm đến 50% còn có chương trình \"Nhanh như chớp - Nhận voucher 100K\" - cơ hội nhận ngay voucher hàng hiệu giảm 100K đồng áp dụng cho đơn hàng từ 400K.</p><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-1-1617687112858115060469.jpg\" ><figcaption class=\"caption\">Túi đeo chéo NAHA giảm 50% chỉ còn 179K. Mua ngay tại Shopee!</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-1-1617687115172341558883.jpg\" ><figcaption class=\"caption\">Giày cao gót Pixie giảm 45% chỉ còn 179K. Xem chi tiết tại Shopee!</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-2-16176871151771937463126.jpg\" ><figcaption class=\"caption\">Sản phẩm giảm 50% chỉ còn 729K trên Shopee. Tham khảo chi tiết tại Shopee!</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-3-16176871151811364602510.jpg\" ><figcaption class=\"caption\">Sản phẩm giảm 40% chỉ còn 120K trên Shopee</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-4-1617687115184956587650.jpg\" ><figcaption class=\"caption\"></figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/6/photo-4-1617687115184956587650.jpg\" ><figcaption class=\"caption\"></figcaption></figure><p>Tham gia ngay tại đây: https://shopee.vn/m/tram-ty-uu-dai#brand-quiz</p>', NULL, 'san-sale-do-hieu-o-mall-xua-roi-bay-gio-thu-ba-ngoi-nha-van-gom-duoc-hang-hieu-ma-con-duoc-giam-den-50', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(123, 20, 6, 1, 1, 'Check camera, mẹ trẻ hoảng hồn thấy con trai 9 tháng tuổi bị nữ ô sin bế vào toilet rồi khóc ré lên dữ dội', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617793479575-16177934799491257346864.png', '|', 'Linh tính như có ai mách bảo, người mẹ mở camera ở nhà ra xem thì phát hiện, nữ giúp việc không dỗ con mình nín khóc mà còn bế bé vào nhà vệ sinh, nghi tát thẳng mặt bé khiến bé không ngừng khóc dữ dội.', '<p>Cuộc sống ngày một bận rộn, đời sống cũng được nâng lên, tình trạng đô thị hóa, những gia đình hạt nhân có xu hướng tăng lên, nên nhu cầu chăm sóc và trông trẻ là một nhu cầu vô cùng thiết thực, nhất là với các gia đình trẻ.</p><p>Song song với việc có những người giúp việc có tâm thì vẫn luôn tiềm ẩn những người trông trẻ bởi mưu sinh với cơm áo gạo tiền hơn là tình yêu trẻ. Có lẽ bởi thế mà những dã tâm, sự lạnh lùng vô cảm, tàn bạo cộng với những mệt mỏi, áp lực tinh thần của cuộc sống hiện đại họ đã trút lên đầu những đứa trẻ ngây thơ vô tội?</p><p>Mới đây, tài khoản N.T.M (Vinh, Nghệ An) vừa chia sẻ đoạn clip camera ở phòng khách cho thấy người giúp việc mà chị khá tin tưởng, đã có những hành động bạo lực với con trai mới 9 tháng tuổi của chị.</p><p>Theo nội dung đoạn clip ghi lại cho thấy, bé trai được đặt ngồi giữa nhà và đang gào khóc. Một người phụ nữ - được cho là người được thuê để giúp việc và trông bé đã không dỗ bé nín mà cứ đi lại làm việc riêng của mình.</p><p>Mãi 1 lúc sau, thấy bé trai vẫn gào khóc, bà giúp việc mới tiến lại gần sau đó bế bé lên, lúc này bé đã nín khóc. Thế nhưng không dừng ở đó, người phụ nữ này đã đi thẳng vào nhà vệ sinh -  camera không thu được hình, tuy nhiên, âm thanh rất rõ cho thấy tiếng bé trai nhiều lần khóc thét lên vì đau đớn, nghi bị nữ giúp việc đánh.</p><p>Người đăng tải đoạn clip cho biết, hôm đó như linh tính mách bảo, chị xem camera ở nhà thì phát hiện sự việc và vô cùng bàng hoàng. Tối hôm đó, sau bữa cơm, chị M. đã hỏi thẳng người giúp việc lý do đánh con mình.</p><p>Tuy nhiên, ban đầu người này không nhận, sau đó đối chiếu camera thì bà thừa nhận có đánh cháu, và nói là đánh vào mặt cháu. Nữ giúp việc giải thích rằng lúc đó do nóng nảy không kiềm chế được và xin lỗi chủ nhà.</p><p>Cuối cùng, chị M. đã quyết định thanh toán đủ tiền lương cho bà giúp việc này và thôi hợp đồng dù bà này đã xin lỗi và mong muốn rút kinh nghiệm, được làm việc tiếp.</p><p>Được biết, chị M. thuê người trông con cho mình qua lời giới thiệu của 1 người bạn quen thân, bà giúp việc này cũng từng làm việc cho gia đình nhà người bạn đó của chị.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177932669311459323943.png\" ><figcaption class=\"caption\">Bé trai gào khóc, người giúp việc vẫn dửng dưng</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617793267972916288086.png\" ><figcaption class=\"caption\">Mãi khi thấy bé khóc quá to và nhiều, nữ giúp việc mới bế bé lên</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-2-16177932680021748884113.png\" ><figcaption class=\"caption\">Bé đã nín khóc, nhưng người này đã bế bé vào nhà vệ sinh, nghi đánh bé khiến bé khóc dữ dội hơn</figcaption></figure><p>Chia sẻ đoạn clip lên mạng xã hội, chị M. mong muốn mỗi gia đình, đặc biệt là gia đình có con nhỏ, nên tìm hiểu thật kĩ và kiểm soát thường xuyên, tránh để con trẻ bị tổn thương bởi cơn giận vô cớ của những người xa lạ!</p>', NULL, 'check-camera-me-tre-hoang-hon-thay-con-trai-9-thang-tuoi-bi-nu-o-sin-be-vao-toilet-roi-khoc-re-len-du-doi', 0, NULL, NULL),
(124, 20, 6, 1, 1, 'Giữ sạch đôi tay, hòa nhịp sống vui với trụ rửa tay sạch khuẩn nơi công cộng', 'https://kenh14cdn.com/thumb_w/300/pr/2021/1617789639071-55-0-840-1256-crop-1617789645064-63753411760342.jpg', '|', 'Giờ đây, với sự xuất hiện của những trụ rửa tay nơi công cộng để phục vụ nhu cầu giữ sạch đôi tay của người dân, chúng ta có thể an tâm rằng bản thân luôn được bảo vệ mọi lúc mọi nơi khỏi bệnh tật, tự tin hòa nhịp sống vui.', '<p>Giữ tay sạch khuẩn - Thói quen đơn giản nhưng hiệu quả giúp phòng bệnh</p><p>Bạn có biết, hầu hết các bệnh truyền nhiễm đã và đang diễn ra trong cộng đồng đều có thể phòng ngừa được bằng cách vệ sinh tay thường xuyên và đúng cách?</p><p>Nhằm hỗ trợ cộng đồng trong việc duy trì thói quen giữ tay sạch khuẩn khi đến nơi công cộng, từ đó phòng ngừa bệnh tật, nhãn hàng Aiken - Chuyên gia kháng khuẩn - đã tiến hành lắp đặt các trụ rửa tay được trang bị sẵn sản phẩm Gel rửa tay sạch khuẩn của hãng tại các địa điểm nhạy cảm và đông người ở những thành phố lớn trên toàn quốc.</p><p></p><p>Không chỉ dừng lại ở việc lắp đặt trụ rửa tay, Aiken còn tung ra những video tuyên truyền về các biện pháp phòng bệnh truyền nhiễm khi đến nơi đông người. Đây là lời nhắc nhở cộng đồng phải giữ tay sạch khuẩn để ngăn ngừa dịch bệnh ở mọi lúc, mọi nơi.</p><p>Bên cạnh những hoạt động vì cộng đồng ý nghĩa nêu trên, Aiken còn rất thấu hiểu tâm lý của người dân khi e ngại thói quen rửa tay thường xuyên sẽ có thể làm khô da tay. Vì vậy, hãng đã nghiên cứu và cho ra mắt hàng loạt các sản phẩm sạch khuẩn và thân thiện với làn da như: Gel rửa tay khô (chứa: 60 độ cồn giúp diệt khuẩn hiệu quả, chất dưỡng ẩm Glycerin cùng hương thơm nhẹ nhàng từ Cam Bergamot và Hoa Phong Lan); Nước rửa tay (khả năng diệt khuẩn lên đến 99,9%, không paraben và có độ pH cân bằng); Sữa tắm; Xà phòng; Khăn giấy ướt.</p><p>Những hoạt động trên của Aiken như một lời khẳng định rằng: Aiken sẽ luôn là biểu tượng giữ tay sạch khuẩn, cùng cộng đồng bảo vệ sức khỏe khỏi các bệnh truyền nhiễm nguy hiểm, an tâm hòa nhịp sống vui.</p><p>Aiken hân hạnh là nhãn hàng đồng hành cùng cộng đồng trong công tác chung tay đẩy lùi dịch bệnh cả trong giai đoạn dịch COVID-19 diễn biến phức tạp và giai đoạn \"bình thường mới\". Qua các hoạt động thiết thực, Aiken mong muốn sẽ mang đến cho mọi người những đôi tay sạch khuẩn mà không gây hại cho làn da, từ đó góp phần cùng cộng đồng chung tay phòng tránh các dịch bệnh nguy hiểm, an tâm \"giữ tay sạch khuẩn, hòa nhịp sống vui\".</p><p>Là thương hiệu chuyên về các sản phẩm sạch khuẩn, Aiken với nhiều sản phẩm đa dạng, phong phú như: Gel rửa tay khô, nước rửa tay, sữa tắm, xà phòng, khăn giấy ướt sẽ mang đến cho gia đình bạn sự bảo vệ toàn diện khỏi mối đe dọa của các nguy cơ gây bệnh.</p><p>Sản phẩm của Aiken hiện đang có mặt tại các hệ thống siêu thị như Guardian, Aeon Wellness, BigC, Vinmart, Lotte, Coop Mart, Emart... các cửa hàng bán lẻ uy tín trên toàn quốc và thông qua kênh bán hàng online như:</p><p>Shopee: https://shopee.vn/aiken_official_store</p><p>Lazada: https://www.lazada.vn/shop/aiken-official-store</p><p>Tiki: https://tiki.vn/cua-hang/aiken-official-store</p><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/7/photo-1-1617787888371298791815.jpg\" ><figcaption class=\"caption\">Dễ dàng duy trì thói quen rửa tay thường xuyên với trụ rửa tay nơi công cộng</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/7/photo-1-1617787896183332752206.jpg\" ><figcaption class=\"caption\">Trụ rửa tay Aiken được đặt tại các vị trí thuận lợi nơi công cộng, phục vụ nhu cầu giữ tay sạch khuẩn của người dân trên khắp cả nước</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/7/photo-2-16177878961902083900594.jpg\" ><figcaption class=\"caption\">Siêu thị là nơi diễn ra nhiều sự tiếp xúc qua việc lựa chọn và cầm nắm hàng hóa, việc lắp đặt các trụ rửa tay Aiken tại khu vực cổng nhằm giúp người dân dễ dàng vệ sinh đôi tay trước khi vào mua sắm</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/7/photo-3-1617787896198187597878.jpg\" ><figcaption class=\"caption\">Mỗi ngày, các bến xe buýt tập trung rất nhiều người đến sử dụng phương tiện công cộng. Có trụ rửa tay Aiken, bạn yên tâm bản thân sẽ luôn được bảo vệ khỏi các nguy cơ gây bệnh truyền nhiễm</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/7/photo-4-16177878962041583351822.jpg\" ><figcaption class=\"caption\">Tương tự bến xe, cơ sở y tế cũng là nơi tập trung nhiều người dân đến thăm khám mỗi ngày. Vì vậy, các trụ rửa tay Aiken được đặt tại đây để giúp người dân giữ tay sạch khuẩn, bảo vệ sức khỏe</figcaption></figure><figure class=\"image video\"><img src=\"https://channel.mediacdn.vn/2021/4/7/photo-5-16177878962101654899371.jpg\" ><figcaption class=\"caption\">Đồng hành cùng phụ huynh trong việc bảo vệ sức khỏe của các bé khi đến trường, trụ rửa tay Aiken được lắp đặt tại các trường học để các bé vệ sinh tay vào những thời điểm như: trước và sau khi ăn, sau khi đi vệ sinh, sau khi ra chơi và nghỉ giữa giờ, trước khi vào lớp…</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177899021081104247286.jpg\" ><figcaption class=\"caption\"></figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177899021081104247286.jpg\" ><figcaption class=\"caption\"></figcaption></figure><p></p>', NULL, 'giu-sach-doi-tay-hoa-nhip-song-vui-voi-tru-rua-tay-sach-khuan-noi-cong-cong', 0, NULL, NULL),
(125, 20, 6, 1, 1, 'Bị khách tố bán cháo có cả \"ổ giòi\" bò lúc nhúc trong miếng sườn, chủ quán ở Hà Nội nói gì?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617788910595-16177889107951905778025.jpg', '|', 'D.T. cho biết đã mua 2 suất cháo sườn tại quán cô H. ở quận Đống Đa, Hà Nội. Sau khi ăn cháo hơn nửa, D. mới ăn đến sườn, thì thấy bên trong là cả \"ổ sinh vật\" bò lúc nhúc.', '<p>Mới đây, trên một diễn đàn về review ẩm thực, một tài khoản đã đăng tải hình ảnh và clip về miếng sườn bên trong giòi bò lúc nhúc gây xôn xao.</p><p>Theo chia sẻ, thứ 5 tuần trước (ngày 1/4), bạn D.T. mua 2 suất cháo sườn tại quán cô H. ở quận Đống Đa, Hà Nội. Sau khi ăn cháo hơn nửa, D. mới ăn đến sườn, thì thấy bên trong lúc nhúc \"ổ sinh vật\". \"Quá kinh khủng, chúng mình đã nôn ra hết, đến bây giờ nghĩ lại vẫn sợ\", D. viết.</p><p>D. nói thêm, cháo không ngon, trứng cút chảy nước và sườn \"không hiểu mất vệ sinh hay để lâu đến thế nào\". \"Mình quyết định đăng bài để cảnh báo mọi người cẩn thận về sức khỏe, ngộ độc thì các bạn có hối hận không?\".</p><p>Trao đổi với chúng tôi chiều 7/4, bà Nguyễn Lệ H., 52 tuổi, chủ quán cháo sườn H. cho biết không nhận được phản ánh của khách hàng về khúc sườn có giòi bò lúc nhúc. Bà cho biết, hình ảnh đăng tải trên MXH là khúc sườn ống, trong khi gia đình chỉ bán sườn sụn.</p><p>Bà H. cho hay, cháo được nấu chín 2-3 tiếng trước khi bán, \"không thể nào có giòi sống bên trong\". \"Không chỉ tôi mà khách hàng cũng bức xúc thay khi có người nói tôi bán hàng không đảm bảo vệ sinh\".</p><p>Theo chủ quán, hàng ngày, bà nấu 6 nồi cháo ngay tại quán, ướp muối sườn, sơ chế cẩn thận, mọi khách hàng đều có thể trực tiếp quan sát và cảm nhận. \"Nếu không ngon hay có ý kiến, họ sẽ phản hồi ngay\".</p><p>Được biết, quán cháo sườn sụn có tuổi đời 50 năm, mở bán từ 12h trưa đến 22h-22h30 hàng ngày.</p><p>Cách đây vài ngày, một người tiêu dùng cũng đăng tải bài viết, tố có  giòi bò lúc nhúc trong khúc cá kho. Được biết, khúc cá kho thuộc đơn hàng gồm 8 món, được anh T. đặt mua tại chuỗi cửa hàng thực phẩm sạch CleverFood trên phố Ngụy Như Kon Tum. Chủ chuỗi cửa hàng sau đó đã lên tiếng xin lỗi và tạm đóng 10 cửa hàng trên toàn Hà Nội trong vòng 3 ngày để rà soát chất lượng, phản ánh của khách hàng.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/16882768111726668664872787805172625311861860n-1617787564663242567845.png\" ><figcaption class=\"caption\">Bài đăng tố quán cháo sườn sụn có giòi bò lúc nhúc (Ảnh chụp màn hình)</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1680502992407512511354236063103697634155456n-16177888365741766336913.jpg\" ><figcaption class=\"caption\">Quán cháo sườn sụn nơi khách hàng tố khúc sườn có giòi</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/16938844129052112131394964831709267942458675n-1617788805223446220041.jpg\" ><figcaption class=\"caption\">Hình ảnh đàn giòi bò lúc nhúc được đăng tải lên mạng xã hội (Ảnh: FB)</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1704338434654990614570278332259720400065526n-16177888366251414219167.jpg\" ><figcaption class=\"caption\">Bà H. cho biết mỗi ngày nấu 6 nồi cháo khoảng 2-3 tiếng trước khi bán, không thể có giòi bên trong sườn</figcaption></figure><p>Ngay sau khi tiếp nhận thông tin, Đội QLTT số 12 đã cử cán bộ trực tiếp tới cửa hàng trên để kiểm tra làm rõ những thông tin đăng tải trên mạng xã hội. Cơ quan chức năng đã đề xuất mức phạt 17 triệu đồng đối với chủ cửa hàng này. </p>', NULL, 'bi-khach-to-ban-chao-co-ca-o-gioi-bo-luc-nhuc-trong-mieng-suon-chu-quan-o-ha-noi-noi-gi', 0, NULL, NULL),
(126, 20, 6, 1, 1, 'TP.HCM: Sà lan mắc kẹt, chắn ngang dòng kênh Tàu Hủ khiến tàu thuyền không thể di chuyển', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617784313855-16177843142761577672669.jpg', '|', 'Sà lan có trọng tải lớn đang lưu thông trên kênh Tàu Hủ thì bất ngờ mất lái, quay ngang rồi mắc kẹt, chắn hết con kênh khiến tàu thuyền khác không thể lưu thông.', '<p>Đến trưa ngày 7/4, lực lượng chức năng mới khắc phục xong sự cố  sà lan mắc kẹt, chắn hết cả con kênh Tàu Hủ khiến giao thông đường thuỷ gặp nhiều khó khăn.</p><p>Sự việc xảy ra lúc sáng sớm cùng ngày trên kênh Tàu Hủ, đoạn gần cầu Chà Và, quận 8, TP.HCM.</p><p>Thời điểm này, chiếc sà lan (số hiệu TG.11899) do anh Nguyễn Văn Thành (SN 1992) điều khiển lưu thông trên kênh Tàu Hủ, hướng từ cầu Lò Gốm đi quận 1, khi đến khu vực trên thì gặp sự cố khiến tàu quay ngang và mắc kẹt.</p><p>Theo anh Thành, tại thời điểm này do gặp sự cố thuỷ triều nên anh bị mất kiểm soát khiến tàu quay ngang. Sự cố làm tàu mắc kẹt, chắn hết cả con kênh khiến các phương tiện khác không thể di chuyển qua khu vực này.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1690718488111625761749778778248283980252684n-16177842962271508587100.jpg\" ><figcaption class=\"caption\">Sà lan mắc kẹt, chắn hết con kênh khiến tàu thuyền không thể di chuyển</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1695569671266392561004378364510715680807639n-16177842964801454411565.jpg\" ><figcaption class=\"caption\">Lực lượng chức năng \"giải cứu\" chiếc sà lan khỏi bị mắc kẹt</figcaption></figure><p>Vụ việc không gây thiệt hại về người. Sau đó chủ bến đã phối hợp với các cơ quan chức năng khắc phục sự cố, đồng thời đảm bảo trật tự an toàn giao thông tại khu vực.</p>', NULL, 'tphcm-sa-lan-mac-ket-chan-ngang-dong-kenh-tau-hu-khien-tau-thuyen-khong-the-di-chuyen', 0, NULL, NULL),
(127, 20, 6, 1, 1, 'Người dân phải nộp 100 nghìn làm căn cước: Cục trưởng Cục Cảnh sát lập tức gọi điện cho GĐ Công an TP. Hải Phòng', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617787341588-1617787341744885001035.jpg', '|', 'Trong một đoạn clip cho thấy khi người dân nhiều lần thắc mắc về mức phí, một người đàn ông mặc trang phục công an xã làm nhiệm vụ thu tiền trả lời: \"Chúng anh làm dịch vụ\".', '<p></p><p></p><p></p><p></p><p>Theo ghi nhận của báo Vietnamnet, những ngày qua, người dân xã Tiên Minh (huyện Tiên Lãng, TP. Hải Phòng) bức xúc về việc phải đóng lệ phí 100.000 đồng khi đổi chứng minh nhân dân (CMND) 9 số, căn cước công dân 12 số sang thẻ căn cước gắn chip.</p><p>Chia sẻ với PV báo trên, anh Bùi Long (tên nhân vật được thay đổi, 37 tuổi, xã Tiên Minh) cho biết anh thường đi làm ăn xa. Hôm 4/4, anh về quê và nắm được thông tin Công an huyện Tiên Lãng về xã cấp CCCD lưu động. Anh bất ngờ khi nhiều người phải đóng 100.000 đồng lệ phí.</p><p>Trực tiếp đưa bố mẹ lên UBND xã Tiên Minh để làm CCCD, anh Long thấy đúng như vậy. Hàng nghìn người dân được hướng dẫn vào bàn làm việc đặt tại phòng trưởng công an xã để đóng lệ phí 100.000 đồng và sau đó mới được làm các bước tiếp theo.</p><p>Anh Long thắc mắc tại sao người dân phải đóng lệ phí cao như vậy dù quy định hiện nay chỉ 15.000 đồng/thẻ khi chuyển đổi hoặc đổi CCCD bị hư hỏng là 25.000 đồng nhưng không nhận được câu trả lời thỏa đáng.</p><p>Anh Long sau đó đồng ý nộp nhưng yêu cầu công an phải làm phiếu thu cho mình. Tuy nhiên, việc này cũng không được đáp ứng.</p><p>Toàn bộ sự việc được một người dân quay clip lại. Nội dung đoạn video cho thấy khi người dân nhiều lần thắc mắc, một người đàn ông mặc trang phục công an xã làm nhiệm vụ thu tiền trả lời: \"Chúng anh làm dịch vụ\".</p><p>Người dân liền nói: \"Anh không được trả lời như vậy, phục vụ nhân dân chứ không phải dịch vụ\". Nghe vậy, vị này đáp: \"Thế thì lên trên huyện làm\".</p><p>Clip cũng thể hiện một người phụ nữ khi đưa 200.000 đồng để đóng lệ phí, một công an xã nhận và trả lại 100.000 đồng.</p><p>Anh Long đã phản ánh sự việc đến đường dây nóng của Công an TP. Hải Phòng và báo chí. \"Ở quê, người già và học sinh không làm ra tiền. Nếu gia đình có 5 người, phải đóng 500.000 đồng, họ mất gần tạ thóc\", anh Long nói.</p><p>Zing liên hệ với đại tá Phạm Công Nguyên, Cục trưởng Cục Cảnh sát quản lý hành chính về trật tự xã hội (C06) của Bộ Công an.</p><p>Đại tá Nguyên cho biết ông lập tức trao đổi qua điện thoại với Giám đốc Công an TP. Hải Phòng sau khi đọc bài báo trên Zing. Lãnh đạo công an địa phương khẳng định mức thu lệ phí cấp căn cước công dân gắn chip đã được thông báo rộng rãi trên các phương tiện thông tin đại chúng theo đúng quy định của Nhà nước.</p><p>Đại tá Nguyên cho hay Giám đốc Công an TP. Hải Phòng khẳng định sẽ cho kiểm tra và xử lý nghiêm nếu cán bộ làm sai.</p><p>Trên Cổng thông tin điện tử Công an Thành phố Hải Phòng công bố mức lệ phí cấp căn cước công dân gắn chip điện tử (chưa có phí chuyển căn cước công dân) như sau:</p><p>- Công dân chuyển từ CMND 9 số, CMND 12 số sang cấp thẻ CCCD gắn chip điện tử: 15.000 đồng/CCCD (chưa có phí chuyển căn cước công dân).</p><p>- Đổi thẻ CCCD khi bị hư hỏng, không sử dụng được, thay đổi thông tin về họ, chữ đệm, tên, đặc điểm nhận dạng, xác định lại giới tính, có sai sót về thông tin trên thẻ khi công dân có yêu cầu: 25.000 đồng/CCCD (chưa có phí chuyển căn cước công dân).</p><p>- Cấp lại thẻ CCCD khi mất thẻ CCCD, được trở lại quốc tịch Việt Nam: 35.000 đồng/ CCCD (chưa có phí chuyển căn cước công dân).</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177841836761013866487.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617784171740649630375.jpg\" ><figcaption class=\"caption\">Trụ sở UBND xã Tiên Minh (Ảnh: Vietnamnet)</figcaption></figure><p>Tổng hợp</p>', NULL, 'nguoi-dan-phai-nop-100-nghin-lam-can-cuoc-cuc-truong-cuc-canh-sat-lap-tuc-goi-dien-cho-gd-cong-an-tp-hai-phong', 0, NULL, NULL),
(128, 20, 6, 1, 1, 'Xe bồn bị xì lốp và cắt nát dây điện vì tài xế đỗ ở cửa quán cơm nhưng lại đi ăn phở', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617782312160-161778231253355953492.png', '|', 'Dừng chân ăn sáng ven đường quốc lộ 51 từ Vũng Tàu đi TP.HCM, người tài xế phát hiện xe của mình bị xì lốp và cắt dây điện vì đỗ xe sai chỗ.', '<p>Theo như những gì tìm hiểu được, khi đang đi trên quốc lộ 51 khu vực từ Vũng Tàu đến thành phố Hồ Chí Minh, người tài xế xe bồn đã dừng chân nghỉ ngơi và ăn sáng tại một quán phở ven đường.</p><p>Trong khi ăn, người này đã đỗ xe trước cửa một quán cơm vì lúc ấy còn sớm và quán chưa mở hàng. Hơn nữa theo quan sát có thể thấy địa điểm đỗ xe có vỉa hè rất rộng, khách hàng vẫn có thể thoải mái ra vào mà không có cản trở gì.</p><p>Nhưng khi người đàn ông ăn sáng xong quay lại thì phát hiện chiếc xe của mình đã bị xì lốp, thậm chí dây điện cũng bị cắt đứt. Cuối cùng phát hiện thủ phạm gây ra vụ việc này chính là người phụ nữ chủ quán cơm.</p><p>Rất nhiều người khi được biết chuyện này đã bày tỏ thái độ phẫn nộ đối với chủ nhà hàng trong câu chuyện. Thậm chí những người biết đến quán còn có ý định \"tẩy chay\" quán ăn sau vụ việc này.</p><p>\"Quá ác độc. Chuyện xì hơi bánh mình không nói. Nhưng mấy cái dây trên xe mà dám cắt, nếu tài xế không phát hiện thì chắc chắn sẽ gây hậu quả nghiêm trọng cho chính người tài xế và cả những người xung quanh.\"</p><p>\"Hàng xóm xung quanh nên cảnh giác với gia đình này\".</p><p>\"Quán này nghe nói nấu ăn khá ngon! Nhưng ngay sau đây sẽ xoá và chặn số ngay.\"</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/thiet-ke-khong-ten-16177819538931956242138.png\" ><figcaption class=\"caption\">Tài xế phát hiện lốp xe bị xì và dây điện trên xe bị cắt đứt (Nguồn ảnh: OFFB)</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1680008954553167057434164060871293107121643n-16177813474671660197454.jpg\" ><figcaption class=\"caption\">Hình ảnh người phụ nữ xì lốp xe và cắt dây điện (Nguồn ảnh: OFFB)</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1684604514553168290767371218343643198554074n-16177841077751940364878-1617784244236979017001.jpg\" ><figcaption class=\"caption\">Thợ sửa xe tới giải quyết lốp xe và dây điện bị phá hoại  (Nguồn ảnh: OFFB)</figcaption></figure><p>Hiện câu chuyện vẫn đang nhận được sự chú ý của cộng đồng mạng.</p>', NULL, 'xe-bon-bi-xi-lop-va-cat-nat-day-dien-vi-tai-xe-do-o-cua-quan-com-nhung-lai-di-an-pho', 0, NULL, NULL),
(129, 20, 6, 1, 1, 'Nghi vợ có bồ, chồng ra tay sát hại rồi 3 lần tự tử bất thành', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617784539414-1617784540226767558918.jpg', '|', 'Nghi ngờ vợ đi làm ăn xa có người khác, Trung đã dùng dao đâm vợ nhiều nhát dẫn đến tử vong. Đối tượng sau đó 3 lần tự tử nhưng bất thành.', '<p>Khoảng 0h45 sáng 7/4, Công an huyện Bá Thước, tỉnh Thanh Hóa nhận tin báo có vụ án mạng nghiêm trọng xảy ra tại thôn Hồ Quang, xã Điền Quang.</p><p>Theo đó, Phạm Văn Trung, 40 tuổi, trú tại thôn Hồ Quang dùng dao đâm nhiều nhát khiến người vợ là chị P.M.L., 37 tuổi, tử vong. Tại cơ quan công an, Trung khai do nghi ngờ vợ đi làm ăn xa, có người khác nên đã ra tay sát hại.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177843311111771419248.jpg\" ><figcaption class=\"caption\">Ngôi nhà nơi xảy ra vụ án</figcaption></figure><p>Sau khi gây án, đối tượng tìm cách tự tử 3 lần nhưng không thành, bị cơ quan công an bắt giữ. Hiện, Trung đang được cấp cứu tại bệnh viện. Công an xã Điền Quang đã báo cáo Công an huyện Bá Thước phối hợp điều tra, làm rõ vụ việc. </p>', NULL, 'nghi-vo-co-bo-chong-ra-tay-sat-hai-roi-3-lan-tu-tu-bat-thanh', 0, NULL, NULL),
(130, 21, 6, 1, 1, 'UBND TP. Hà Nội chỉ đạo nóng vụ nữ lao công bị sát hại', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617627883965-16176278841311213006494.jpg', '|', 'Chiều 5/4, UBND TP. Hà Nội đã có văn bản chỉ đạo xử lý vụ việc nghiêm trọng xảy ra trên địa bàn quận Cầu Giấy, liên quan đến vụ án nữ công nhân môi trường bị sát hại.', '<p>Trước đó, đêm 4/4, tại phường Dịch Vọng (quận Cầu Giấy) nữ công nhân môi trường đô thị đã bị 1 nam thanh niên bất ngờ dùng gạch tấn công, khiến nạn nhân là chị Vũ Thúy H. (SN 1978, trú tại Phúc Diễn, Bắc Từ Liêm, Hà Nội) tử vong sau đó.</p><p>Về việc này, Chủ tịch UBND Hà Nội chỉ đạo, giao Công an TP. Hà Nội khẩn trương điều tra và xử lý nghiêm theo đúng quy định pháp luật.</p><p>Bên cạnh đó, giao Sở lao động thương binh và xã hội chủ trì phối hợp với UBND quận Cầu Giấy và các đơn vị liên quan tổ chức thăm hỏi, động viên và hỗ trợ gia đình nạn nhân.</p><p>Liên quan đến vụ án trên, chiều ngày 5/4, Công an quận Cầu Giấy (TP. Hà Nội) đã tiến hành thực nghiệm lại hiện trường vụ án xảy ra trước số nhà 302 Cầu Giấy (quận Cầu Giấy, Hà Nội).</p><p>Khoảng 21h ngày 4/4, ngay sau khi nắm được thông tin về vụ án, đơn vị đã phối hợp với Phòng Cảnh sát hình sự - Công an Hà Nội tiến hành truy bắt hung thủ.</p><p>Tại hiện trường, công an thu giữ 1 viên gạch hung thủ dùng để gây án. Nạn nhân được xác định tử vong do đa chấn thương vùng đầu.</p><p>Tiến hành truy xét nóng, Công an Cầu Giấy xác định sau khi thực hiện hành vi giết người, đối tượng đã bỏ chạy về hướng đường Xuân Thủy.</p><p>Chưa đầy 1 giờ sau, lực lượng công an đã bắt giữ được hung thủ Lê Như Toàn (SN 1991, trú tại Tam Điệp, Ninh Bình) để điều tra về hành vi \"Giết người\" khi đối tượng đang lẩn trốn trong khu đô thị làng quốc tế Thăng Long (Dịch Vọng, Cầu Giấy).</p><p>Kết quả điều tra bước đầu xác định Lê Như Toàn là đối tượng không nghề nghiệp, bản thân có tiền sử bệnh tâm thần.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/1684702742925077822651607640872760299662235n-2-16176018901981096996110.jpg\" ><figcaption class=\"caption\">Chiều ngày 5/4, người thân và đồng nghiệp đã đưa nạn nhân về nơi an nghỉ cuối cùng</figcaption></figure><p>Hiện vụ việc đang được điều tra làm rõ.</p>', NULL, 'ubnd-tp-ha-noi-chi-dao-nong-vu-nu-lao-cong-bi-sat-hai', 0, NULL, NULL),
(131, 21, 6, 1, 1, 'Khởi tố người mẹ bạo hành con dã man ở Hải Dương', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617624192355-1617624192486585783886.jpg', '|', 'Chiều 5/4, cơ quan chức năng đã chính thức khởi tố bị can đối với Vũ Thị Thủy (SN 1996, trú xã Định Sơn, huyện Cẩm Giàng, Hải Dương) về tội cố ý gây thương tích. Trước đó vào 6/3, Thuỷ đã bạo hành con đẻ khiến cháu phải nhập viện.', '<p>Chiều 5/4, trao đổi với chúng tôi, lãnh đạo VKSND huyện Cẩm Giàng (tỉnh Hải Dương) cho biết, chiều cùng ngày đã phê chuẩn quyết định khởi tố bị can đối với Vũ Thị Thủy (SN 1996, trú xã Định Sơn, huyện Cẩm Giàng) về tội cố ý gây thương tích.</p><p>Trước đó, vào đêm 6/3, Công an huyện Cẩm Giàng nhận được thông tin về vụ việc cháu C. trong lúc ở nhà bà ngoại ở xã Tân Trường, bị mẹ đẻ là Vũ Thị Thuỷ bạo hành rồi nhốt trong nhà. Ngay sau đó, lực lượng công an đã cùng với đại diện chính quyền và người dân giải cứu, đưa cháu đến bệnh viện điều trị.</p><p>Thông tin thêm với chúng tôi, lãnh đạo UBND xã Định Sơn cho biết, dư luận địa phương rất bức xúc về vụ việc, bởi từ xưa nay chưa từng xảy ra chuyện đáng lên án như vậy.</p><p>Nói thêm về vấn đề xung quanh vụ việc, vị lãnh đạo UBND xã cho hay, Thuỷ có hoàn cảnh gia đình éo le, có 3 con (lần lượt 6 tuổi, 2 tuổi và hiện mang thai được 6 tháng), nhưng không có chồng.</p><p>\"Hoàn cảnh gia đình cũng khó khăn. Có thể do những ảnh hưởng tâm lý nên Thủy hành động như vậy. Sau khi biết thông tin về vụ việc, bên cạnh việc thông báo cơ quan công an giải quyết, chính quyền địa phương cũng đã thăm hỏi, động viên gia đình\", vị lãnh đạo thông tin.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/1671369671988173850607932421866192134614763n-16176241467702119664906.jpg\" ><figcaption class=\"caption\">Bị can Vũ Thị Thủy</figcaption></figure><p>Kết quả xác minh cho thấy, do bức xúc về việc cháu C. thường xuyên đi vệ sinh không bảo mẹ nên liên tiếp vào các ngày 18, 20/2 và ngày 6/3, Thuỷ đã đánh con. Sau khi được bà ngoại và dì ruột chăm sóc, tình hình sức khỏe đã ổn định.</p>', NULL, 'khoi-to-nguoi-me-bao-hanh-con-da-man-o-hai-duong', 0, NULL, NULL),
(132, 21, 6, 1, 1, 'Vụ cháy làm 6 người tử vong tại Sài Gòn: Nạn nhân duy nhất sống sót xuất viện, tinh thần ổn định hơn trước', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617616412674-1617616412918689872901.jpg', '|', 'Ông Lục Chấn Tâm (54 tuổi), nạn nhân sống sót duy nhất trong vụ cháy nhà làm 6 người tử vong tại TP. Thủ Đức đã được xuất viện về nhà sau một tuần điều trị tại BV Lê Văn Thịnh (BV Quận 2 cũ) trong điều kiện sức khỏe, tinh thần ổn định.', '<p>Chiều 5/4, lãnh đạo BV Lê Văn Thịnh cho biết ông Lục Chấn Tâm đã xuất viện, về nhà trong trạng thái  tinh thần ổn định, tiếp xúc tốt, không còn hoảng sợ như trước, tâm lý diễn biến theo chiều hướng tích cực, đồng thời vết thương bỏng độ 2 cũng đã ổn định, liền tốt.  </p><p>Ông Tâm là nạn nhân duy nhất sống sót trong vụ cháy nhà tại số 899 đường Nguyễn Thị Định (phường Cát Lái, TP. Thủ Đức) làm 6 người (gồm vợ, con trai, con gái, con dâu và 2 đứa cháu nội, ngoại của ông Tâm) tử vong.</p><p>Sau khi được lực lượng chức năng đưa đến BV Lê Văn Thịnh rạng sáng ngày 30/3, ông Tâm rơi vào trạng thái hoảng loạn, bật khóc khi nhắc đến vụ cháy, phải nhờ bác sĩ tâm lý của BV đến hỗ trợ, nói chuyện.</p><p>Vì hoàn cảnh gia đình ông Tâm khó khăn nên BV Lê Văn Thịnh cũng đã kêu gọi hỗ trợ. Số tiền  vận động được từ cán bộ nhân viên BV và một số MTA là 18 triệu 800 nghìn đồng. Sau khi chi trả viện phí hết 9 triệu 786 nghìn đồng, số tiền còn lại đã được BV trao cho ông Tâm trong chiều 5/4. </p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/16666393216434278491871337330655939849731453n-16176164037312008344081.jpg\" ><figcaption class=\"caption\">Ông Tâm khỏe mạnh, tâm lý ổn định chiều 5/4 trước khi xuất viện</figcaption></figure><p>Hiện tại, Công an TP.HCM vẫn đang phối hợp với Công an TP. Thủ Đức điều tra xác minh nguyên nhân vụ cháy làm 6 người tử vong. </p>', NULL, 'vu-chay-lam-6-nguoi-tu-vong-tai-sai-gon-nan-nhan-duy-nhat-song-sot-xuat-vien-tinh-than-on-dinh-hon-truoc', 0, NULL, NULL),
(133, 21, 6, 1, 1, 'Mẹ già đau đớn khi nữ lao công bất ngờ bị sát hại: \"Cháu khổ từ bé, từng thoát chết một lần mà giờ lại thế này\"', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617612868651-1617612868925256067160.jpg', '|', 'Mẹ của nữ công nhân môi trường bị sát hại cho biết, con gái bà là người hiếu thuận nhưng số khổ từ nhỏ. Sau lần bị ngã \"thập tử nhất sinh\", tuy may mắn qua khỏi nhưng vì thế mà cô trở nên thấp còi, lúc béo chỉ được 40kg.', '<p>\"Con là người hiếu thảo mà sao số khổ từ nhỏ\"</p><p>Chiều 5/4, căn nhà nhỏ trong ngõ thuộc tổ 2 (phường Phúc Diễn, quận Bắc Từ Liêm, Hà Nội) vẫn đông người thân, làng xóm đến hỏi thăm động viên gia đình nữ công nhân bị kẻ tâm thần sát hại tối 4/4 trên đường Cầu Giấy, Hà Nội.</p><p>Người thân trong gia đình, ai nấy mắt đỏ hoe vì khóc cạn nước mắt trước sự ra đi của người con xấu số, đồng thời là chỗ dựa của bố mẹ già và cháu thơ dại.</p><p>Bà Nguyễn Thị Ngư (71 tuổi, mẹ của nạn nhân) cho biết, ngôi nhà mới được con trai vay mượn xây dựng, cũng là nơi hàng ngày chị Vũ Thị H. đi về lo cơm nước cho cha mẹ già.</p><p>Theo bà Ngư, bản thân chỉ làm nghề nông, còn chồng trước kia là công nhân, nay đã già, chỉ được phụ cấp hơn 1 triệu/tháng. Ông bà có 5 người con gái, 3 người con trai, trong đó chị H. là con gái út, tuy đã 44 tuổi nhưng vẫn chưa xây dựng gia đình.</p><p>\"Cháu là người nết na, hiền lành, bao nhiêu năm qua mọi người giục cháu lấy chồng nhưng cháu không nghe. Bảo ở cùng H. cũng không đồng ý mà ra ở riêng. Chỗ cháu ở chỉ là 1 căn lều lụp xụp vì không có tiền xây nhà\", bà Ngư kể.</p><p>Người mẹ cho biết, người con gái út khổ từ nhỏ, trong một lần bị ngã khi mới được mấy tháng tuổi, tưởng chừng chị H. không thể qua khỏi. May mắn, chị đã tỉnh lại nhưng lại trở nên thấp, còi. Lúc béo nhất, chỉ được 40kg.</p><p>\"Cháu đúng là khổ từ nhỏ, cứ ngỡ một lần gặp nạn không chết nhưng không ngờ hôm qua con lại ra đi như thế này...\", bà Ngư xót xa.</p><p>Ông Vũ Văn Sáu (76 tuổi, bố nạn nhân) xót xa nói: \"Vợ chồng tôi đều bị bệnh nên không làm được gì. Ngoài việc cơm nước, nhiều lúc con gái còn tranh thủ đưa bố mẹ đi khám bệnh\".</p><p>Ông Sáu nghẹn ngào nói thêm, ngoài việc chăm sóc bố mẹ, H. nuôi thêm đứa cháu Oanh (SN 2001, là con của chị gái). Chị gái của H. không may bị tai nạn, bị liệt chân tay.</p><p>Chị của H. sinh được cháu Oanh, nhưng sau khi học hết lớp 9, cháu cũng phải nghỉ học. Cháu Oanh cũng không được nhanh nhẹn như người bình thường. Từ đó, Oanh sống cũng dì, mỗi khi đi làm thì cho Oanh đi theo phụ cho nhanh.</p><p>Thực nghiệm lại hiện trường vụ án</p><p>Liên quan đến vụ án trên, chiều ngày 5/4, Công an quận Cầu Giấy (TP. Hà Nội) cho biết, đã tiến hành thực nghiệm lại hiện trường vụ án xảy ra trước số nhà 302 Cầu Giấy (quận Cầu Giấy, Hà Nội).</p><p>Trưa cùng ngày, đơn vị cho biết, n gay sau khi nắm được thông tin, đơn vị đã phối hợp với Phòng Cảnh sát hình sự - Công an Hà Nội thực hiện lệnh bắt giữ trong trường hợp khẩn cấp với Lê Như Toàn (SN 1991, trú tại Tam Điệp, Ninh Bình) để điều tra về hành vi \"Giết người\".</p><p>Tiếp nhận tin báo, cơ quan công an tiến hành khám nghiệm hiện trường và tổ chức các hoạt động điều tra. Tại hiện trường, công an thu giữ 1 viên gạch hung thủ dùng để gây án. Nạn nhân được xác định tử vong do đa chấn thương vùng đầu.</p><p>Tiến hành truy xét nóng, Công an Cầu Giấy xác định sau khi thực hiện hành vi giết người, đối tượng đã bỏ chạy về hướng đường Xuân Thủy.</p><p>Chưa đầy 1 giờ sau, lực lượng công an đã bắt giữ được hung thủ gây án khi đối tượng đang lẩn trốn trong khu đô thị làng quốc tế Thăng Long (Dịch Vọng, Cầu Giấy).</p><p>Kết quả điều tra bước đầu xác định Lê Như Toàn là đối tượng không nghề nghiệp, bản thân có tiền sử bệnh tâm thần.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/1687312565067698204531351627327239011941426n-1617611982326351219444.jpg\" ><figcaption class=\"caption\">Con ngõ nhỏ dẫn vào nhà chị H.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/1684921817882976087666117678973540024918636n-1617612544228836586043.jpg\" ><figcaption class=\"caption\">Cơ quan công an thực nghiệm lại hiện trường vụ án</figcaption></figure><p>Hiện vụ việc đang được điều tra làm rõ.</p>', NULL, 'me-gia-dau-don-khi-nu-lao-cong-bat-ngo-bi-sat-hai-chau-kho-tu-be-tung-thoat-chet-mot-lan-ma-gio-lai-the-nay', 0, NULL, NULL),
(134, 21, 6, 1, 1, 'Gia cảnh éo le của nữ lao công bị sát hại ở Hà Nội: Không có chồng con, một mình nuôi cháu gái và chăm sóc bố mẹ già', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617603468371-16176034686381817390290.jpg', '|', 'Chị Vũ Thúy H. (SN 1978, trú tại Phúc Diễn, Bắc Từ Liêm, Hà Nội), nữ công nhân môi trường bị sát hại tối 4/4 có hoàn cảnh rất éo le, chị sống cùng một người cháu, hai người nương tựa vào nhau. Hằng ngày chị còn \"chạy đi chạy lại\" lo cơm nước cho bố mẹ.', '<p>Chiều 5/4, tại nghĩa trang Văn Tiến Dũng (quận Bắc Từ Liêm, Hà Nội), cháu Lê Kim Oanh liên tục nhìn vào di ảnh người dì và khóc nghẹn không thành tiếng.</p><p>Trước đó, vào khoảng 21h ngày 4/4, vụ án mạng thương tâm xảy ra tại số 302 đường Cầu Giấy (quận Cầu Giấy, Hà Nội) khi chị Vũ Thúy H. (SN 1978, trú tại Phúc Diễn, Bắc Từ Liêm, Hà Nội) là nhân viên Công ty CP Môi trường và Dịch vụ đô thị Vĩnh Yên đang đẩy xe chở rác thì bất ngờ bị một đối tượng dùng gạch đập nhiều lần vào đầu khiến chị H. tử vong tại chỗ.</p><p>Nạn nhân trong vụ việc kể trên chính là dì của Oanh. Chị H. không có chồng con, nên Oanh được coi như con của chị.</p><p>Anh H. - một đồng nghiệp của nạn nhân cho biết, chị H. có hoàn cảnh éo le. Chị không có chồng con. Cách đây hơn 1 năm, chị gái của H. cũng là công nhân môi trường bị tai nạn không thể đi lại. Do đó, chị H. nuôi thêm cháu Oanh (SN 2001).</p><p>\"Cháu Oanh cũng không được như người bình thường và phải nghỉ học từ năm lớp 9. Hơn 1 năm nay, hằng ngày, cháu và dì H. đi làm cùng nhau. Hai dì cháu nương tựa vào nhau, chị H. còn chạy đi chạy lại chăm sóc bố mẹ đã lớn tuổi\", người đồng nghiệp nói.</p><p>Trước linh vị của dì H., cháu Lê Kim Oanh (20 tuổi) liên tục khóc nấc. Sau nhiều lời an ủi, cháu mới lau được nước mắt. Cháu kể rằng, hàng ngày cháu đi làm cùng dì từ 16h đến 22h đêm. Do cháu không làm được việc gì nên đi cùng phụ giúp dì cho nhanh. Tối 4/4, khi xảy ra sự việc, cháu không làm chỗ dì H.</p><p>\"Lúc nghe tin dì gặp nạn, cháu chỉ biết khóc, mẹ cháu gặp nạn còn giờ dì cháu lại ra đi mãi mãi…\", Oanh nghẹn lời rồi oà khóc.</p><p>Bình tĩnh lại, Oanh kể tiếp, gia đình nhà dì H. có hoàn cảnh khó khăn. Trước kia không được ăn học đầy đủ nên mẹ và dì đều đi làm công nhân môi trường. Hơn một năm nay ở cùng dì, cuộc sống tuy vất vả nhưng dì cháu vẫn có thể nương tựa vào nhau. \"Nhưng dì lại bỏ cháu và mọi người đi rồi\" - dòng nước mắt lại chảy dài trên mặt cô bé.</p><p>\"Anh em trong nhà chị H. không có nhiều. Ngay sau khi biết tin, đồng nghiệp công ty chúng tôi đều có mặt để lo hậu sự cho chị ấy. Chị H. là người chịu khó, tận tâm với công việc. Công nhân chúng tôi đều vất vả nhưng hoàn cảnh của chị ấy còn khó khăn hơn\", một đồng nghiệp ngậm ngùi kể.</p><p>Đối tượng gây án có tiền sử tâm thần</p><p>Liên quan đến vụ án trên, trưa ngày 5/4, Công an quận Cầu Giấy (TP. Hà Nội) cho biết, đã phối hợp với Phòng Cảnh sát hình sự - Công an Hà Nội thực hiện lệnh bắt giữ trong trường hợp khẩn cấp với Lê Như Toàn (SN 1991, trú tại Tam Điệp, Ninh Bình) để điều tra về hành vi \"Giết người\".</p><p>Tiếp nhận tin báo, cơ quan công an tiến hành khám nghiệm hiện trường và tổ chức các hoạt động điều tra. Tại hiện trường, công an thu giữ 1 viên gạch hung thủ dùng để gây án. Nạn nhân được xác định tử vong do đa chấn thương vùng đầu.</p><p>Tiến hành truy xét nóng, Công an Cầu Giấy xác định sau khi thực hiện hành vi giết người, đối tượng đã bỏ chạy về hướng đường Xuân Thủy.</p><p>Chưa đầy 1 giờ sau, lực lượng công an đã bắt giữ được hung thủ gây án khi đối tượng đang lẩn trốn trong khu đô thị làng quốc tế Thăng Long (Dịch Vọng, Cầu Giấy).</p><p>Danh tính đối tượng được làm rõ là Lê Như Toàn (SN 1991, trú tổ 17, phường Trung Sơn, thành phố Tam Điệp, tỉnh Ninh Bình).</p><p>Kết quả điều tra bước đầu xác định Lê Như Toàn là đối tượng không nghề nghiệp, bản thân có tiền sử bệnh tâm thần.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/img1731-1617601527993606951285.jpg\" ><figcaption class=\"caption\">Oanh coi chị H. như mẹ, hàng ngày 2 dì cháu đi làm và nuôi nhau</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/1684702742925077822651607640872760299662235n-2-16176018901981096996110.jpg\" ><figcaption class=\"caption\">Người thân và đồng nghiệp tiếc thương nạn nhân</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/hjgjfn--1617595237803143185892.jpg\" ><figcaption class=\"caption\">Đối tượng Toàn</figcaption></figure><p>Hiện vụ việc đang được điều tra làm rõ.</p>', NULL, 'gia-canh-eo-le-cua-nu-lao-cong-bi-sat-hai-o-ha-noi-khong-co-chong-con-mot-minh-nuoi-chau-gai-va-cham-soc-bo-me-gia', 0, NULL, NULL),
(135, 21, 6, 1, 1, 'Công an thông tin chính thức vụ nữ lao công quét rác bị sát hại dã man ở Hà Nội: Nghi phạm có tiền sử bệnh tâm thần', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617635028234-161763502907049542144.jpg', '|', 'Liên quan đến vụ nữ công nhân môi trường bị sát hại ở Cầu Giấy, Hà Nội đêm 4/4, hiện cơ quan công an đã có báo cáo sơ bộ về vụ án.', '<p>Ngày 5/4, Công an quận Cầu Giấy (TP. Hà Nội) cho biết, đơn vị đã phối hợp với Phòng Cảnh sát hình sự - Công an Hà Nội thực hiện lệnh bắt giữ người trong trường hợp khẩn cấp với Lê Như Toàn (SN 1991, trú tại Tam Điệp, Ninh Bình) để điều tra về hành vi \"Giết người\".</p><p>Trước đó, vào khoảng 21h tối 4/4, Công an quận Cầu Giấy nhận được tin báo chị Vũ Thúy Hà (SN 1978, trú tại Bắc Từ Liêm, Hà Nội) là nhân viên Công ty CP Môi trường và Dịch vụ đô thị Vĩnh Yên đang đẩy xe chở rác trên đường Cầu Giấy hướng về đường Xuân Thủy thì bất ngờ bị một đối tượng nam thanh niên (không rõ nhân thân lai lịch) dùng gạch đập nhiều lần vào đầu khiến chị Hà tử vong tại chỗ.</p><p>Tiếp nhận tin báo, cơ quan công an tiến hành khám nghiệm hiện trường và tổ chức các hoạt động điều tra. Tại hiện trường, công an thu giữ 1 viên gạch hung thủ dùng để gây án. Nạn nhân được xác định tử vong do đa chấn thương vùng đầu.</p><p>Tiến hành truy xét nóng, Công an Cầu Giấy xác định sau khi thực hiện hành vi giết người, đối tượng đã bỏ chạy về hướng đường Xuân Thủy.</p><p>Chưa đầy 1 giờ sau, lực lượng công an đã bắt giữ được hung thủ gây án khi đối tượng Toàn đang lẩn trốn trong khu đô thị làng quốc tế Thăng Long (Dịch Vọng, Cầu Giấy).</p><p>Kết quả điều tra bước đầu xác định, Toàn là đối tượng không nghề nghiệp, bản thân có tiền sử bệnh tâm thần. Thời điểm gây án đối tượng lang thang và có ý đồ giết người.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/hjgjfn-1617595237803143185892-1617634987805177350171.jpg\" ><figcaption class=\"caption\">Đối tượng bị bắt giữ sau 1 giờ bỏ trốn</figcaption></figure><p>Hiện vụ việc đang được điều tra làm rõ.</p>', NULL, 'cong-an-thong-tin-chinh-thuc-vu-nu-lao-cong-quet-rac-bi-sat-hai-da-man-o-ha-noi-nghi-pham-co-tien-su-benh-tam-than', 0, NULL, NULL),
(136, 21, 6, 1, 1, 'Nghệ An: Khởi tố, tạm giam các đối tượng trùm đầu “chôn sống” nam thanh niên', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617594398937-1617594399275180686634.jpg', '|', 'Bực tức vì V. mượn 2 xe máy không trả, Dương chỉ đạo đàn em tìm bắt V. để đe dọa thực hiện hành vi \"chôn sống\".', '<p>Sáng 5/4, Trung tá Phạm Thành Long - Phó Trưởng Công an TP. Vinh (Nghệ An) cho biết, đơn vị này vừa có quyết định khởi tố vụ án, khởi tố đối với 9 bị can trong vụ \"nam thiếu niên bị chôn sống dưới hố cát\" gây xôn xao dư luận.</p><p>Các đối tượng bị khởi tố gồm: Kẻ cầm đầu Nguyễn Trọng Dương (SN 1989, trú phường Trường Thi, TP. Vinh).</p><p>8 bị can còn lại là Hồ Trọng Khánh (SN 1995), Lê Nguyễn Hưng (SN 2004, cùng trú phường Trung Đô), Nguyễn Tiến Nam (SN 2003, trú xã Nghi Phú), Phùng Duy Ngọc Anh (SN 2003, trú huyện Nghi Lộc), Trần Mạnh Tài (SN 2001, trú phường Hưng Dũng), Võ Quang Hoàng (SN 2004, trú phường Đông Vĩnh), Hoàng Quang Thanh (SN 2004, trú phường Hưng Bình), Nguyễn Đình Huy (SN 2000, trú Hà Tĩnh).</p><p>Theo điều tra ban đầu, N.Q.V (17 tuổi, thị trấn Xuân An, Nghi Xuân, Hà Tĩnh), thuê xe máy của Nguyễn Trọng Dương nhưng sau đó không trả. Bực tức vì không đòi được xe, Dương chỉ đạo đàn em truy tìm nam thanh niên này.</p><p>Ngày 24/3, nhóm này bắt gặp V. đang ở TP. Vinh nên giữ lại lột quần áo, hành hung. Cả nhóm sau đó dẫn V. sang bãi đất trống ở ốc đảo Hồng Lam (nằm giữa sông Lam, thuộc địa bàn huyện Nghi Xuân), rồi đánh đập.</p><p>Nhóm này còn đào hố, lấy bao tải trùm đầu V. rồi đẩy xuống hố lấp đất…, bất chấp lời cầu xin của nạn nhân. Một lúc sau, V. được kéo lên đưa ra sông tắm rửa.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/d1-1617594041423602642738.jpg\" ><figcaption class=\"caption\">Đối tượng Dương được xác định là kẻ cầm đầu</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/d2-16175940413511848304090.jpg\" ><figcaption class=\"caption\">Các đối tượng còn lại tại cơ quan công an</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/d3-1617594041443623536718.jpeg\" ><figcaption class=\"caption\">Nạn nhân bị chôn sống</figcaption></figure><p>Đến ngày 26/3, video về vụ việc được chia sẻ trên mạng xã hội gây sốc với dư luận. Nhận được thông tin, chỉ sau 3 giờ, Công an TP. Vinh đã bắt 13 người trong nhóm thanh niên này và triệu tập nạn nhân lên làm việc. Sau đó, Công an TP. Vinh bắt giữ Nguyễn Trọng Dương khi anh ta đang trốn ở Hà Nội.</p>', NULL, 'nghe-an-khoi-to-tam-giam-cac-doi-tuong-trum-dau-chon-song-nam-thanh-nien', 0, NULL, NULL),
(137, 22, 6, 1, 1, 'Xe bồn bị xì lốp và cắt nát dây điện vì tài xế đỗ ở cửa quán cơm nhưng lại đi ăn phở', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617782312160-161778231253355953492.png', '|', 'Dừng chân ăn sáng ven đường quốc lộ 51 từ Vũng Tàu đi TP.HCM, người tài xế phát hiện xe của mình bị xì lốp và cắt dây điện vì đỗ xe sai chỗ.', '<p>Theo như những gì tìm hiểu được, khi đang đi trên quốc lộ 51 khu vực từ Vũng Tàu đến thành phố Hồ Chí Minh, người tài xế xe bồn đã dừng chân nghỉ ngơi và ăn sáng tại một quán phở ven đường.</p><p>Trong khi ăn, người này đã đỗ xe trước cửa một quán cơm vì lúc ấy còn sớm và quán chưa mở hàng. Hơn nữa theo quan sát có thể thấy địa điểm đỗ xe có vỉa hè rất rộng, khách hàng vẫn có thể thoải mái ra vào mà không có cản trở gì.</p><p>Nhưng khi người đàn ông ăn sáng xong quay lại thì phát hiện chiếc xe của mình đã bị xì lốp, thậm chí dây điện cũng bị cắt đứt. Cuối cùng phát hiện thủ phạm gây ra vụ việc này chính là người phụ nữ chủ quán cơm.</p><p>Rất nhiều người khi được biết chuyện này đã bày tỏ thái độ phẫn nộ đối với chủ nhà hàng trong câu chuyện. Thậm chí những người biết đến quán còn có ý định \"tẩy chay\" quán ăn sau vụ việc này.</p><p>\"Quá ác độc. Chuyện xì hơi bánh mình không nói. Nhưng mấy cái dây trên xe mà dám cắt, nếu tài xế không phát hiện thì chắc chắn sẽ gây hậu quả nghiêm trọng cho chính người tài xế và cả những người xung quanh.\"</p><p>\"Hàng xóm xung quanh nên cảnh giác với gia đình này\".</p><p>\"Quán này nghe nói nấu ăn khá ngon! Nhưng ngay sau đây sẽ xoá và chặn số ngay.\"</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/thiet-ke-khong-ten-16177819538931956242138.png\" ><figcaption class=\"caption\">Tài xế phát hiện lốp xe bị xì và dây điện trên xe bị cắt đứt (Nguồn ảnh: OFFB)</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1680008954553167057434164060871293107121643n-16177813474671660197454.jpg\" ><figcaption class=\"caption\">Hình ảnh người phụ nữ xì lốp xe và cắt dây điện (Nguồn ảnh: OFFB)</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/1684604514553168290767371218343643198554074n-16177841077751940364878-1617784244236979017001.jpg\" ><figcaption class=\"caption\">Thợ sửa xe tới giải quyết lốp xe và dây điện bị phá hoại  (Nguồn ảnh: OFFB)</figcaption></figure><p>Hiện câu chuyện vẫn đang nhận được sự chú ý của cộng đồng mạng.</p>', NULL, 'xe-bon-bi-xi-lop-va-cat-nat-day-dien-vi-tai-xe-do-o-cua-quan-com-nhung-lai-di-an-pho', 0, NULL, NULL),
(138, 22, 6, 1, 1, 'Bắc Ninh: Nữ học viên tập lái ô tô rồi đâm nát đầu xe, thầy giáo ngồi cạnh máu me đầy mặt không nói nên lời', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/5/photo1617601244911-16176012449931371221812.png', '|', 'Vụ tai nạn xảy ra tại trường dạy lái xe Bắc Hà - Từ Sơn (Bắc Ninh) vào chiều ngày 4/4 vừa qua.', '<p>Theo đó, chiếc xe này thuộc trung tâm đào tạo và sát hạch lái xe Bắc Hà tại Từ Sơn, Bắc Ninh. Trong buổi chiều ngày 4/4, một học viên nữ điều khiển và mất lái dẫn tới va chạm với xe khác làm phần đầu ô tô bị đâm biến dạng, hư hỏng rất nặng phần động cơ.</p><p>Sau cú va chạm, thầy giáo bị chảy máu ở vùng tai và cằm, còn nữ học viên thì may mắn không có thương tích đáng kể.  Có thể thấy rõ thầy giáo đang vô cùng sốc vì vẫn chưa hiểu chuyện gì xảy ra, quên luôn việc mình đang bị chảy máu đầy mặt.</p><p>\"Bao nhiêu sóng gió cuộc đời thầy đều đã trải nghiệm và vượt qua. Nhưng lần này thì là sóng thần, thầy nên bỏ cuộc thì hơn\".</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/3-16175875998971490178508-16176011661571625928155-161760182442112316527.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/2-16175876004491156253914-1617601166458792657772-1617601845011218580358.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/5/1-16175876009021918019572-1617601166634175219606.jpeg\" ><figcaption class=\"caption\">Hiện trường vụ tai nạn (Nguồn: MXH)</figcaption></figure><p>\"Năm tôi học ở đây, chuyện các chị học lái xe đâm đổ tường rồi vọt qua mương máng, lao xuống ao là chuyện như cơm bữa\" - một cư dân mạng từng học và thi lấy bằng tại đây chia sẻ.</p>', NULL, 'bac-ninh-nu-hoc-vien-tap-lai-o-to-roi-dam-nat-dau-xe-thay-giao-ngoi-canh-mau-me-day-mat-khong-noi-nen-loi', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(139, 22, 6, 1, 1, 'Bốc đầu xe khoe \"chiến tích\" trên TikTok, 3 thanh niên bị phạt hơn 30 triệu đồng', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/30/photo1617070460613-16170704610681183073775.jpeg', '|', 'Nhóm thanh thiếu niên chạy xe máy trên quốc lộ 7, bốc đầu xe rồi khoe \"chiến tích\", đã bị công an xử phạt hơn 30 triệu đồng.', '<p>Chiều tối 29/3, thông tin từ Công an huyện Anh Sơn (Nghệ An) cho biết, cơ quan vừa ra quyết định xử phạt hành chính 3 thanh thiếu niên bốc đầu xe, lạng lách trên quốc lộ 7 với tổng số tiền 31 triệu đồng.</p><p>Theo đó, Nguyễn Quang Huy (SN 2006, trú thị trấn Con Cuông, huyện Con Cuông) và Nguyễn Đăng Huy (SN 2003, trú xã Yên Sơn, huyện Đô Lương) mỗi trường hợp bị phạt 7,5 triệu đồng. Riêng, Nguyễn Văn Sự (SN 2003, trú xã Đỉnh Sơn, huyện Anh Sơn) bị xử phạt 16 triệu đồng do có chất ma túy trong cơ thể.</p><p>Trước đó, trên mạng xã hội đăng tải và chia sẻ đoạn video clip ghi lại cảnh một nhóm thanh thiếu niên điều khiển xe máy lạng lách, đánh võng, bốc đầu xe trên quốc lộ 7A thuộc địa bàn huyện Anh Sơn.</p><p>Công an xác định nhóm này có nhiều lỗi vi phạm: không có giấy phép lái xe, xe không có gương chiếu hậu, gắn biển số bị che lấp, có chất ma túy trong cơ thể.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/30/photo1617070365498-1617070366502780222003.jpeg\" ><figcaption class=\"caption\">Nhóm thanh thiếu niên bốc đầu xe bị triệu tập</figcaption></figure><p>Trong những lần bốc đầu xe, lạng lách, đánh võng, nhóm này đã quay video clip rồi đăng tải lên TikTok để khoe “chiến tích”.</p>', NULL, 'boc-dau-xe-khoe-chien-tich-tren-tiktok-3-thanh-nien-bi-phat-hon-30-trieu-dong', 0, NULL, NULL),
(140, 22, 6, 1, 1, 'Bị tiếng hát karaoke \"tra tấn\", gã thanh niên cùng bạn nhậu đánh chết người hàng xóm', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/29/photo1617005635653-16170056359411517580304.jpg', '|', 'Do mâu thuẫn giữa 2 gia đình từ chuyện hát karaoke gây ồn ào nên sau khi nhậu xong, Đặng Quang Hùng cùng đồng bọn mang theo hung khí đến nhà đánh chết người đàn ông cùng xóm với mình.', '<p>Ngày 29/3, Bệnh viện Đa khoa tỉnh Bình Định xác nhận ông Nguyễn Minh Thinh (63 tuổi; ngụ thôn Xuân Phương, xã Phước Sơn, huyện Tuy Phước, tỉnh Bình Định) đã tử vong trong quá trình cấp cứu, do vết thương quá nặng.</p><p>Kết quả điều tra ban đầu cho thấy tuy ở cùng xóm nhưng giữa gia đình ông Nguyễn Minh Thinh và Đặng Quang Hùng (36 tuổi) thường xuyên xảy ra mâu thuẫn, trong đó một phần là do tiếng ồn từ dàn karaoke nhà ông Thinh thường xuyên \"tra tấn\" hàng xóm.</p><p>Chiều 28/3, Đặng Quang Hùng cùng Võ Văn Huy (25 tuổi, ngụ cùng địa phương) và một số người khác tổ chức ăn nhậu. Đến hơn 22 giờ cùng ngày, sau khi nhậu say, cho rằng nhà ông Thinh hát karaoke gây ồn ào, Hùng rủ Huy và một số bạn nhậu mang theo dao, rựa... đến nhà ông Thinh gây sự.</p><p>Sau một hồi lời qua tiếng lại, Hùng cùng đồng bọn đã dùng hung khí tấn công ông Thinh gây thương tích nặng. Ngay sau đó, gia đình đã đưa ông Thinh đến Bệnh viện Đa khoa tỉnh Bình Định cấp cứu. Do vết thương quá nặng, ông đã không qua khỏi.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/29/photo-1-161700561215812104618.jpg\" ><figcaption class=\"caption\">Ảnh minh họa</figcaption></figure><p>Vụ việc đang được các cơ quan chức năng điều tra, làm rõ để xử lý theo quy định.</p>', NULL, 'bi-tieng-hat-karaoke-tra-tan-ga-thanh-nien-cung-ban-nhau-danh-chet-nguoi-hang-xom', 0, NULL, NULL),
(141, 22, 6, 1, 1, 'Clip: Đang đi đường, nam thanh niên bất ngờ bị thanh sắt từ xe tải đâm vào người gây chấn thương nặng', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/25/photo1616663624051-1616663624243680888966.gif.png', '|', 'Theo những gì camera ghi lại được, một xe tải chở sắt đã không cẩn thận khiến những cây sắt đâm vào người một thanh niên đang ngồi sau xe máy.', '<p>Vào lúc 14h chiều ngày 22/3 đã xảy ra một vụ tai nạn trên đoạn đường Ngã 4 chợ Giang Liễu, Quế Võ, Bắc Ninh. Một chiếc xe tải chuyên vận chuyển vật liệu xây dựng, khi đang vận chuyển thanh sắt dài trên đường bằng cách kéo mà không có bất cứ biện pháp an toàn nào.</p><p>Clip: Bị thanh sắt đâm vào người, thanh niên ngã khỏi xe và nhập viện vì chấn thương đầu</p><p>Khi đi đến đoạn ngã tư, thanh sắt trượt thấp và đâm vào người một thanh niên ngồi sau xe máy. Vụ tai nạn khiến thanh niên ngã nhào khỏi xe, bị thương nặng ở phần đầu và ngất xỉu. Ngay sau khi tai nạn xảy ra, thanh niên đã được người dân xung quanh đưa đi cấp cứu.</p><p>Nguyên nhân vụ tai nạn rõ ràng có thể thấy do người điều khiển chiếc xe chở vật liệu xây dựng hết sức chủ quan và không tuân thủ các nguyên tắc an toàn khi vận chuyển.</p><p>Nhiều người xem xong clip ghi lại vụ tai nạn bày tỏ sự bức xúc đối với sự bất cẩn và vô trách nhiệm này của người tài xế.</p><p>\"Cả đống thanh sắt dài gác lên chẳng buộc, uốn gì. Quả này thì \"ăn\" no tiền phạt với tiền bồi thường.\"</p><p>\"Sao không uốn lại cho dễ chở nhỉ? Ẩu quá rồi.\"</p><p>\"Đâm trúng bạn thanh niên này cũng không đội mũ bảo hiểm nên mới bị thương ở đầu nữa. Xảy ra tai nạn rồi mới thấy mũ bảo hiểm quan trọng như nào.\"</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/25/532mu7-1616663550566415664738.gif.mp4\" ></video><div class=\"caption\">Thanh niên ngồi sau xe máy bị thanh sắt đâm phải</figcaption></figure><p>Xem xét sự việc đáng tiếc này, lỗi thuộc về cả người lái xe tải lẫn nam thanh niên bị tai nạn. Chỉ cần mỗi người có ý thức hơn một chút khi tham gia giao thông, chắc chắn không chỉ bảo vệ mình mà còn có thể bảo vệ cả những người xung quanh. </p>', NULL, 'clip-dang-di-duong-nam-thanh-nien-bat-ngo-bi-thanh-sat-tu-xe-tai-dam-vao-nguoi-gay-chan-thuong-nang', 0, NULL, NULL),
(142, 22, 6, 1, 1, 'Clip: Gây tai nạn rồi \"cầm nhầm\" xe của nạn nhân định bỏ trốn, thanh niên nhận cái kết đắng ngắt ngay sau đó', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/25/photo1616661595290-1616661595505631259078.gif.png', '|', 'Camera ghi lại va chạm giữa 2 xe máy, điều đáng nói là hành động của chàng trai ngay sau khi tai nạn xảy ra.', '<p>Vào khoảng 9h tối ngày 24/03, trên đoạn đường Lý Thường Kiệt, TP. Dĩ An, Bình Dương đã xảy ra một vụ va chạm giữa hai xe máy. Sự việc đã được toàn bộ camera an ninh của nhà người dân ngay sát khu vực xảy ra tai nạn ghi lại.</p><p>Clip: Gây tai nạn rồi tiện tay \"xách\" luôn xe của người ta đi mất, thanh niên lập tức bị lôi lại xử lý</p><p>Trong phạm vi quan sát của camera, người thanh niên mặc áo đỏ điều khiển xe không đội mũ bảo hiểm đã va chạm mạnh với người phụ nữ điều khiển xe máy gần đó khiến cả hai người cùng bị ngã. Riêng thanh niên lộn nhào một vòng rồi lồm cồm ngồi dậy. Người dân xung quanh lập tức chạy ra giúp đỡ hai người dựng xe và dìu người phụ nữ vào trong.</p><p>Tuy nhiên thay vì đến hỏi han người phụ nữ bị ngã sau khi xảy ra tai nạn, thanh niên lại ung dung đứng dậy dựng kiểm tra xe của người phụ nữ và... phóng đi mất. Hành động bất ngờ này khiến người đàn ông đứng cạnh nghĩ ngay rằng thanh niên đã gây ra tai nạn còn định tráo xe rồi bỏ trốn nên đã lập tức phản ứng giữ lại xe, đồng thời vung tay \"dằn mặt\" một cú và lôi cậu chàng vào lề đường.</p><p>Sự việc cũng nhanh chóng được giải quyết. Xe máy bị hư hỏng nhẹ nhưng may mắn không có ai bị thương nặng. Nhiều người lên tiếng chỉ trích chàng trai và vẽ nên nhiều \"drama\" khác nhau:</p><p>\"Có khi cố tình đâm vào để lấy xe thì sao?\"</p><p>\"Khả năng thanh niên này mới giật đồ hoặc đá xe nên bị đâm cái là hoảng quá chạy luôn.\"</p><p>Cũng có một số người tỏ ra thông cảm với chàng trai khi anh chàng mới bị đâm xong nên còn hoảng loạn nên mới nhận nhầm xe:</p><p>\"Nhìn kỹ lại thì anh áo đỏ hình như bị va vào trước rồi nên mới nhào vào cô ấy mà, chứ không phải cố tình đâm vào hay chạy nhanh rồi đâm vào đâu.\"</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/25/532jfw-1616661445813816764955.gif.mp4\" ></video><div class=\"caption\">Hình ảnh vụ va chạm xảy ra</figcaption></figure><p>\"Khổ thân, chắc mới bị đâm lên hơi choáng, nhầm lẫn xíu, làm gì mà căng.\"</p>', NULL, 'clip-gay-tai-nan-roi-cam-nham-xe-cua-nan-nhan-dinh-bo-tron-thanh-nien-nhan-cai-ket-dang-ngat-ngay-sau-do', 0, NULL, NULL),
(143, 22, 6, 1, 1, 'Clip: Mặc xe cứu thương hú còi xin vượt, tài xế container nhất quyết chèn ép gần 10km', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/21/photo1616321437301-1616321437751218800343.gif.png', '|', 'Camera hành trình đã ghi lại hành vi gây phẫn nộ của một tài xế container khi chèn ép xe cứu thương trên một đoạn đường dài gần 10km tại Thanh Hóa.', '<p>Mới đây, camera hành trình đã ghi lại hình ảnh một chiếc xe container đã không ngừng ép xe cứu thương đến gần 10km, gây bức xúc lớn trên mạng xã hội.</p><p>Clip: Mặc xe cứu thương hú còi xin vượt, tài xế container nhất quyết chèn ép xe cứu thương đến gần 10km</p><p>Trên đoạn đường từ cầu vượt Hoàng Quỳ, Hoàng Hoa đến khu vực đền Bà Triệu, Hộc Lộc, tỉnh Thanh Hóa, chiếc xe container đã liên tục có hành vi ép xe và không để xe cứu thương biển số Nghệ An vượt. Mặc dù tài xế xe cứu thương đã liên tục phát tín hiệu xin nhường đường nhưng chiếc xe container vẫn không hề có bất cứ động thái tích cực nào.</p><p>Clip sau khi đăng tải đã nhận được sự phản ứng gay gắt của cư dân mạng về hành vi vô nhân tính và coi thường pháp luật đến từ phía tài xế xe container.</p><p>\"Luật pháp chưa đủ sức mạnh để răn đe hay sao ấy nhỉ? Nên các tài xế vẫn cứ hiên ngang mặc kệ. Hay họ không học luật giao thông?\"</p><p>\"Xe cứu thương đang làm một nhiệm vụ thiêng liêng để giành lại sự sống cho một con người. Tài xế xe container vì bất cứ lý do gì cũng đã vi phạm luật lệ giao thông cũng như vi phạm đạo đức của một con người\"</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/21/52jyva-16163212760261067663960.gif.mp4\" ></video><div class=\"caption\">Xe cứu thương liên tục bị chèn ép</figcaption></figure><p>Luật pháp Việt Nam quy định Người tham gia giao thông không nhường đường, cản trở xe ưu tiên (trong đó có xe cứu thương) có thể bị phạt đến 5 triệu đồng thậm chí có thể bị truy cứu trách nhiệm hình sự nếu gây ra hậu quả nghiêm trọng với khung hình phạt cao nhất là phạt tù 15 năm.</p>', NULL, 'clip-mac-xe-cuu-thuong-hu-coi-xin-vuot-tai-xe-container-nhat-quyet-chen-ep-gan-10km', 0, NULL, NULL),
(144, 23, 6, 1, 1, 'Chuyện về Nguyễn Nguyệt Linh và lễ khai giảng không bóng bay mang tên mình', 'https://kenh14cdn.com/thumb_w/300/2020/1/6/cover-1577053952590340296530-15782993467951269120968-crop-157829935464796064238.jpg', '|', 'Trong bức thư của mình, em Nguyễn Nguyệt Linh (lớp 5, trường Marie Curie) bày tỏ mong muốn các trường học không thả bóng bay ngày khai giảng. Thông điệp em muốn truyền tải tuy đơn giản nhưng khiến chúng ta phải ngẫm nghĩ: \"Thả bóng bay lên trời: Bay cao ước mơ của các học sinh - giết ước mơ của bao chú chim và rùa biển\".', '<p>Trước tình trạng ô nhiễm môi trường do sử dụng quá nhiều túi nilon và rác thải nhựa, mới đây, Nguyễn Nguyệt Linh - học sinh lớp 6 của Trường Marie Curie (Hà Nội) đã mạnh dạn viết email gửi tới hơn 40 trường học ở Hà Nội kêu gọi ngừng thả bóng bay trong lễ khai giảng.</p><p>Bức thư được Nguyệt Linh viết dưới dạng thư điện tử và gửi tới hơn 40 trường học ở Thủ đô đề nghị không thả bóng bay trong ngày lễ khai giảng sắp tới. Bức thư ngắn gọn nhưng khiến nhiều người lớn phải suy nghĩ. Trong thư, Nguyệt Linh trình bày lý do không nên thả bóng bay trong ngày khai giảng với lập luận dựa trên kiến thức mà em tìm hiểu được.</p><p>\"Con biết là hằng năm khi bắt đầu khai giảng, nhà trường thường cho các lớp thả bóng bay lên trời. Sau những thông tin con tìm thấy được thì bóng bay được làm từ nilon tức là nhựa, và khi thả bóng bay các chú chim hay động vật khác nuốt vào, nó có thể bị chặn đường ruột và dẫn đến chết đói.</p><p>Còn khi bóng bay rơi xuống đất hoặc biển thì những chú rùa biển và các sinh vật biển sẽ bị nhầm với sứa. Ruy băng và dây buộc bóng có thể khiến chúng mắc kẹt và dẫn tới cái chết\", Nguyệt Linh viết.</p><p>Bức thư được Nguyệt Linh viết dưới dạng thư điện tử và gửi tới hơn 40 trường học ở Thủ đô đề nghị không thả bóng bay trong ngày lễ khai giảng sắp tới.</p><p>Hưởng ứng bức thư kêu gọi không thả bóng bay của em Nguyễn Nguyệt Linh, trường THPT Phan Huy Chú - Đống Đa (Hà Nội) đã cắt bỏ tiết mục thả bóng trong lễ khai giảng ngày 5/9. Thay vào đó, học sinh sẽ ghi những ước mơ lên giấy màu rồi dán trong lớp học, như một cách nhắc nhở các em phấn đấu học tập và lưu giữ kỷ niệm.</p><p>Chia sẻ với Tuổi Trẻ Online, thầy Nguyễn Xuân Khang, Hiệu trưởng trường Marie Curie, Hà Nội cho biết sau khi nhận được lá thư trên của học sinh ông rất bất ngờ, xúc động và ông đã viết thư gửi lại cho cô học trò.</p><p>\"Thầy nhận được lá thư của con do một người bạn thân gửi đến, thầy bất ngờ và xúc động vô cùng. Không thả bóng bay lên trời để bảo vệ môi trường! Một ý tưởng đẹp, rất đẹp và ý nghĩa sâu sắc\" - Thầy Nguyễn Xuân Khang, Hiệu trưởng trường Marie Curie, Hà Nội</p><p>Thầy Nguyễn Xuân Khang cho biết, trong lễ khai giảng năm học sắp tới, trường Marie Curie, Hà Nội sẽ không thả bóng bay lên trời, như mong muốn của Nguyệt Linh. \"Thầy hy vọng việc làm có ý nghĩa tốt đẹp của thầy trò chúng ta sẽ được nhiều nơi hưởng ứng\" - Thầy Nguyễn Xuân Khang viết trong thư cho cô học trò của mình.</p><p>Lễ khai giảng năm học 2019 - 2020 cũng được đặt là \"Lễ Khai Giảng Nguyệt Linh\" để ghi nhận ý kiến tuyệt vời của cô học trò nhỏ. \"Thầy hy vọng việc làm có ý nghĩa tốt đẹp của thầy trò chúng ta sẽ được nhiều nơi hưởng ứng. Thầy hạnh phúc vì con!\".</p><p>Tuy nhiên, đây không phải là lần đầu tiên Nguyệt Linh kêu gọi hành động vì môi trường. Trước đó, em từng dự thi chương trình mang tên Green Leader với bài viết \"Trẻ em nói không với rác thải nhựa\". Trong đó, Linh sử dụng những hình ảnh phản ánh thực trạng ô nhiễm môi trường do rác thải nhựa gây ra. Những con số thống kê như Việt Nam là 1 trong 4 quốc gia có lượng rác thải nhựa lớn nhất thế giới; mỗi gia đình Việt thải ra môi trường hàng chục triệu túi nilon mỗi ngày, hay Hà Nội chính là nơi \"dẫn đầu\" cả nước về rác thải… thực sự khiến người xem phải suy nghĩ.</p><p>Hiện đề cử Nguyễn Nguyệt Linh đang có mặt trong hạng mục Kẻ Trộm Nhựa - Wechoice Awards 2019. Truy cập wechoice.vn để bình chọn cho cô bé Nguyệt Linh nhé!</p><p>WeChoice Awards - Giải thưởng thường niên do Công ty cổ phần VCCorp tổ chức, với mong muốn tôn vinh những con người, kể những câu chuyện truyền cảm hứng nhất, những sự kiện, sản phẩm và công trình có ảnh hưởng tích cực tới cộng đồng - đã quay trở lại với thông điệp mới: Điều phi thường nhỏ bé.</p><p>Đó là câu chuyện về những điều phi thường được tạo nên từ những con người bình thường, giản dị nhất. Chính những phép màu, những điều dẫu nhỏ bé nhưng phi thường này, càng khiến chúng ta thêm tin vào vẻ đẹp của cuộc sống, của lòng nhân ái, và của chính con người.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/1/6/photo-1-15782991210611622922225.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/1/6/photo-1-15782991254232126244794.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/1/6/photo-2-15782991254251440758678.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/1/6/photo-3-15782991254261480962279.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/1/6/photo-4-15782991254282061856506.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/1/6/photo-5-15782991254291507900786.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/2019/12/30/banner-chan-bai-3-1577690433121420367987.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Hãy cùng chúng tôi tôn vinh và bình chọn cho những câu chuyện, nhân vật mà bạn thấy xứng đáng tại WeChoice Awards 2019. Thời gian bình chọn từ ngày 31/12/2019 đến 23h59 ngày 11/01/2020. Truy cập wechoice.vn để lan tỏa niềm cảm hứng ngay hôm nay.</p>', NULL, 'chuyen-ve-nguyen-nguyet-linh-va-le-khai-giang-khong-bong-bay-mang-ten-minh', 0, NULL, NULL),
(145, 23, 6, 1, 1, 'Có một sự nhầm lẫn \"không hề nhẹ\" nếu chúng ta nói rằng: “Trẻ con mà, chúng nó biết gì đâu\" vì ngày nay, trẻ em biết nhiều hơn so với những gì ta tưởng đấy!', 'https://kenh14cdn.com/thumb_w/300/2019/11/15/namng-4-15738187648641595296493-crop-15738242765001777054698.jpg', '|', 'Những lá thư được viết tay nắn nót, đính kèm những câu chuyện, những tâm sự,  những lập luận đáng yêu và đầy trách nhiệm của các bạn nhỏ tại “Green Talk - Trồng tương lai” khiến ai cũng phải trầm trồ và bất ngờ.', '<p>Người lớn chúng mình dễ dàng bỏ qua hoặc xem nhẹ những ý kiến của con trẻ, đơn giản vì chúng ta nghĩ rằng trẻ con thì chẳng biết cái gì.  Thế nhưng, đã bao giờ, chúng ta thử dừng lại, lắng nghe những tâm tư, suy nghĩ hay những câu chuyện của các bạn nhỏ?</p><p>Tháng 6 vừa qua, câu chuyện về em bé Nguyễn Nguyệt Linh, một học sinh lớp 6 gửi thư tới các trường tiểu học đề xuất việc dừng thả bóng bay trong ngày lễ khai giảng để bảo vệ môi trường, đã gây xôn xao cho cả truyền thông và cộng đồng. Quá bất ngờ, khi cô bạn chỉ mới 11 tuổi thôi nhưng đã có những lập luận rất sắc sảo về vấn đề môi trường hiện nay, và mạnh dạn nói lên tiếng nói của mình.</p><p>Nếu thế hệ trẻ là những người đang tràn đầy nhiệt huyết, nhận lấy trách nhiệm và sẵn sàng hành động để giữ gìn và bảo vệ lấy môi trường sống thì lớp \"măng non\" - thế hệ kế thừa sẽ chính là người nắm giữ tương lai bởi vậy, hơn bao giờ hết, bản thân các em có những nhận thức rõ ràng và nghiêm túc về bảo vệ môi trường.</p><p>Tại \"Green Talk - Trồng tương lai\" - sự kiện nằm trong chiến dịch WeDo Trồng cây, tiếng nói môi trường cất lên từ các bạn nhỏ thực sự khiến những người lớn phải khâm phục và bất ngờ.</p><p>Những lá thư được viết tay nắn nót, đính kèm những câu chuyện, những tâm sự,  những lập luận đáng yêu cho thấy việc các em rất quan tâm đến những mảng xanh trong cuộc sống.</p><p>Một bạn nhỏ gửi bức thư đến ông bà nội kể lại những kỉ niệm về mùa hè \"màu xanh\" được trải nghiệm ở quê cùng ông bà. Trong thư có viết: \"Cháu nhớ cả khu vườn của ông với đủ mọi loại cây và nhớ những lần theo bà đi trồng cây rau cải.  Cháu còn nhớ chiếc võng được ông nội móc giữa hai cây nhãn tán lá xum xuê che nắng mặt trời, cháu thích được nằm đó, nghe ông kể chuyện và hít thở bầu không khí trong lành.\"</p><p>Một cô bạn khác thì lại có bức thư vô cùng sáng tạo khi tưởng tượng tương lai 30 năm sau và gửi bức thư cho một nhân vật vô cùng đặc biệt là... chính bản thân mình ở hiện tại.</p><p></p><p>\" Có phải cậu đang sống trong một môi trường rất trong lành và tươi đẹp đúng không? Chà, tôi nhớ cảm giác ấy quá, khi mỗi sáng thức dậy thấy ban mai dịu êm, đi học dưới những tán lá xanh tươi rợp bóng râm. Cảm giác ấy tuyệt qúa !</p><p>Cậu hãy cố tận hưởng đi vì khi cậu lớn lên trở thành tôi - cậu sẽ chẳng có được môi trường trong lành như thế đâu…</p><p>Cuộc sống của tôi giờ hiện đại lắm, robot trở thành người giúp việc mà nhà nào cũng có. Những toà nhà cao đến tận trời mọc lên. Thế nhưng, vì quá hiện đại, nên con người thải ra môi trường quá nhiều chất độc. Cây cối cũng bị chặt đi nhường chỗ xây những toà nhà cao tầng. Người ta nghĩ chặt 1 cái cây thì chẳng sao. Nhưng k biết rằng những hành động nhỏ ấy cũng đủ làm tổn thương môi trường, và khi tích dần qua năm tháng thì nó trở nên rất nghiêm trọng.\"</p><p>Những mảng xanh trong cuộc sống đang mất dần, tạo cơ hội cho những mảng xám xịt \"lên ngôi\", hơn hết, cuộc sống của chính ta đang bị đe doạ. Và các bạn nhỏ, dù còn ít tuổi nhưng đã tự nhận thức rất rõ điều này. Trống cây xanh, con trẻ trống luôn những kỉ niệm, những khoảnh khắc được vui chơi ngoài thiên nhiên đẹp đẽ.</p><p>Một bức thư đặc biệt để lại ấn tượng cho mọi người là cô bạn trong vai là cái cây và gửi thư cho loài người: \"Con người ơi, các bạn có thể quan tâm đến chúng tôi không. Chúng tôi cũng biết đau, biết buồn, biết cô đơn lắm. Chúng tôi rất yêu quý các bạn và luôn cố gắng bảo vệ để mang đến cho các bạn một cuộc sống trong lành, tươi đẹp.  Hãy quan tâm đến nhà cây chúng tôi, chúng tôi sẽ tạo ra những điều tốt đẹp cho môi trường khi được đứng cạnh nhau, làm việc cùng nhau. Vì vậy, con người ơi, xin đừng \"làm hại\" chúng tôi. Chúng ta hãy sống cùng nhau, trở thành những người bạn của nhau nhé!\"</p><p>Những lời cảnh báo đến từ những \"mầm non\" tương lai vừa ngô nghê đáng yêu nhưng cũng đủ khiến người lớn phải suy nghĩ. Có lẽ, chưa bao giờ tiếng nói con trẻ lại khiến người lớn được truyền cảm hứng mạnh mẽ đến như vậy.</p><p>MC Thuỳ Minh - khách mời của Green Talk chia sẻ sự bất ngờ vì không nghĩ rằng ở lứa tuổi nhỏ như thế, các em đã có ý thức sống xanh để bảo vệ môi trường.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/11/15/namng-9-15738186602341655834132.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/11/15/namng-4-15738187648641595296493.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/2019/11/15/namng-49-1573819245850681190779.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/11/15/namng-47-1-1573819360921234397026.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/11/15/namng-2-1-15738241999382013086753.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Cuộc chiến bảo vệ môi trường chắc chắn sẽ là một hành trình lâu dài và bền bỉ mà ở đó chúng ta cần tất cả mọi người, ở mọi thế hệ cùng \"bật\" trách nhiệm và chủ động thay đổi những thói quen xấu dù là nhỏ nhất để \"thương\" môi trường hơn và chung tay bảo vệ chính cuộc sống của ta.</p>', NULL, 'co-mot-su-nham-lan-khong-he-nhe-neu-chung-ta-noi-rang-tre-con-ma-chung-no-biet-gi-dau-vi-ngay-nay-tre-em-biet-nhieu-hon-so-voi-nhung-gi-ta-tuong-day', 0, NULL, NULL),
(146, 23, 6, 1, 1, '500kg rác thải treo lơ lửng trên đầu: Triển lãm ấn tượng ở Hà Nội khiến người xem ngộp thở', 'https://kenh14cdn.com/thumb_w/300/2019/7/15/va09176-15632049321211687450164-crop-15632071096621592304127.jpg', '|', '\"Hãy xả rác ít thôi!\" - lời kêu gọi tuy giản đơn nhưng mang tính cấp thiết khi mà con người đang dần chết ngập trong rác thải sinh hoạt mỗi ngày. Khoảng 500kg rác thải đã được thu gom và xử lý để tạo nên những tác phẩm sắp đặt cực ấn tượng.', '<p>Mọi thùng rác đều đã đầy tràn, mọi đô thị đều ngập rác. Mỗi ngày, Hà Nội thải ra hơn 6400 tấn rác sinh hoạt. Con số đó vẫn không ngừng gia tăng qua mỗi năm, gây nên những ảnh hưởng tai hại đến môi trường và sức khoẻ người dân. </p><p>Triển lãm \"Xả rác ít thôi!\" được văn phòng hợp tác giữa Ủy ban Nhân dân thành phố Hà Nội và vùng Île-de-France, PRX-Vietnam thực hiện với sự hỗ trợ của Cơ quan phát triển Pháp - AFD. Triển lãm đề cập đến một chủ đề vô cùng thiết yếu nhưng ít được nhắc đến ở Việt Nam: Giảm thiểu lượng rác thải.</p><p>Trong khuôn khổ triển lãm, BTC đem đến một tác phẩm sắp đặt mang tên \"Sóng dâng trào\". Với sắp đặt này, trần sảnh trung tâm triển lãm L’Espace (24 Tràng Tiền) được bao phủ toàn bộ bằng rác thải vô cơ, tạo cảm giác người xem như bị nhấn chìm trong làn sóng rác thải, thể hiện rõ nét lựa chọn của người tiêu dùng và tác động của lựa chọn đó đến môi trường.</p><p>Bắt đầu từ ngày 15/7, triển lãm \"Xả rác ít thôi\" chính thức được khai mạc, kéo dài đến hết ngày 31/8, nhằm mục đích mang lại một cách nhìn mới về các phương thức sản xuất và tiêu dùng. Thông qua những hành động đơn giản hàng ngày, tất cả chúng ta đều có thể hành động vì một môi trường trong sạch hơn.</p><p>500kg rác thải treo lơ lửng trên đầu: Triển lãm ấn tượng ở Hà Nội khiến người xem ngộp thở. Thực hiện: Minh Nhân. </p><p>Rác thải hiện hữu mỗi ngày xung quanh chúng ta, nhưng đôi khi chính chúng ta lại khước từ sự thật đáng báo động này.</p><p>Triển lãm \"Hãy xả rác ít thôi\" thu hút sự quan tâm của các bạn trẻ.</p><p>Hoàn, sinh viên ĐH Kiến trúc Hà Nội, là một thành viên của nhóm sáng tạo triển lãm \"Xả rác ít thôi\". Trong vòng 3 tuần, nhóm sinh viên tình nguyện vừa thu gom rác thải, vừa tiến hành phân loại và xử lý. Được biết, khoảng 500kg rác thải sinh hoạt được tận dụng để thiết kế toàn bộ tác phẩm trong khuôn khổ triển lãm.</p><p>\"Những ngày đầu bọn mình làm chưa có đủ rác. Bọn mình vừa đi xin, vừa tự gom từ các bãi rác trên đường. Trong vòng 3 tuần, lúc nào trong đầu mình cũng chỉ có rác, rác và rác! Có nhiều thành phần rác sẽ được bọn mình xử lý, hoặc nếu không thì mang tới xưởng tái chế. Một số rác thải y tế trộn lẫn khiến nhóm bị ngứa, hoặc mùi hôi thối của những vỏ sữa cũng làm bọn mình ốm mấy ngày\" - Hoàn chia sẻ.</p><p>Thông điệp của triển lãm rất rõ ràng: Xả rác ít thôi! Bên cạnh đó, nhóm tác giả khát khao giới thiệu những  mẹo vặt và lựa chọn thay thế hết sức thiết thực cho một cuộc sống lành mạnh hơn.  Túi nylon hoàn toàn có thể được thay thế bằng túi vải, ống hút nhựa dường như là không còn cần thiết nữa, các loại cốc dùng một lần nên được hạn chế bằng cốc thuỷ tinh. Và thay vì mua cơm ngoài đựng trong hộp xốp, các bạn có thể mang theo hộp đựng cơm từ nhà. </p><p>\"Với cấu trúc triển lãm tựa như một làn sóng về rác, khi bạn đứng vào không gian này, số rác trên có thể khiến bạn ngột ngạt đến khó thở. Chúng ta nên biết cách để xả rác ít thôi nếu không tương lai gần sẽ phải sống chung với chúng. Sau khi kết thúc triển lãm, bọn mình sẽ phân loại và gửi rác đến các làng tái chế khác nhau. Riêng vỏ sữa sẽ được gửi vào Bình Dương để làm những tấm tôn\"- Hoàn chia sẻ. </p><p>Chúng ta tìm thấy trong bãi rác mọi vật phẩm của cuộc sống hàng ngày.</p><p>Triển lãm giới thiệu nhiều mẹo vặt và các sản phẩm thay thế túi nylon, cốc nhựa dùng một lần,...</p><p>WeDo Cuộc Chiến Trộm Nhựa (nằm trong khuôn khổ WeChoice) là một thử thách môi trường hoàn toàn khác mang trong mình sứ mệnh của các \"phản anh hùng\" bất bình với thực trạng rác nhựa.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09118-1563204932096206805722.jpg\" ><figcaption class=\"caption\">Từ ngày 15/7, triển lãm \"Xả rác ít thôi\" với các tác phẩm sắp đặt vô cùng ấn tượng được tổ chức tại trung tâm tiếng Pháp L\'Espace thu hút sự quan tâm của đông đảo người dân.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09156-15632049321131353023388.jpg\" ><figcaption class=\"caption\">Ngay từ lối vào, tác phẩm \"Sóng dâng trào\" sẽ khiến người xem phải kinh ngạc trước mức độ xả rác đáng báo động hiện nay.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/2019/7/15/va09115-1563204932093833313303.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09208-1563204932137176370749.jpg\" ><figcaption class=\"caption\">Triển lãm tập hợp khoảng 500kg rác thải, từ túi nylon, vỏ sữa, chai nhựa,...</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09164-15632049321171638232495.jpg\" ><figcaption class=\"caption\">Thông điệp thiết thực đến mức cấp thiết: Chúng ta ăn những gì mình bỏ rác!</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09188-15632049321251176637738.jpg\" ><figcaption class=\"caption\">Chúng ta vứt rất nhiều thứ vào môi trường, kể cả thiết bị điện tử như thế này.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09155-15632049321122120154056.jpg\" ><figcaption class=\"caption\">Con số cảnh báo về tình trạng rác thải của Hà Nội mỗi ngày.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09135-1563204932102587330111.jpg\" ><figcaption class=\"caption\">Đến năm 2050, Hà Nội có thể sẽ không còn đủ sức để \"gánh\" rác thải nữa.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/2019/7/15/va09138-1563204932103931577508.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09176-15632049321211687450164.jpg\" ><figcaption class=\"caption\">Hoàn - sinh viên ĐH Kiến trúc Hà Nội, đại diện nhóm sinh viên.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09219-15632049321451672521696.jpg\" ><figcaption class=\"caption\">Phải mất 3 tuần chìm ngập trong rác, nhóm sinh viên cùng BTC mới có thể cho ra đời các tác phẩm sắp đặt sáng tạo.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09204-15632049321351404865105.jpg\" ><figcaption class=\"caption\">Túi nylon và chai nhựa đang ngày một huỷ diệt chúng ta.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09150-1563204932110389924387.jpg\" ><figcaption class=\"caption\">Con người dần ngập ngụa trong rác.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/2019/7/15/va09192-15632049321291806341656.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09161-15632049321151198972198.jpg\" ><figcaption class=\"caption\">Khi phân huỷ, nhựa tan rã thành các hạt siêu nhỏ làm ô nhiễm đất và nước. Các sản phẩm chăm sóc thân thể như kem đánh răng, kem trang điểm hoặc sản phẩm tẩy tế bào chết cũng chứa các hạt này. Vì vậy, nếu ăn cá, rất có thể bạn cũng đang ăn nhựa.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/2019/7/15/va09179-1563204932122420767718.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/1000/2019/7/15/va09153-15632049321111481737278.jpg\" ><figcaption class=\"caption\">Chúng ta thải rác mỗi ngày, vì thế, hãy xả rác ít thôi!</figcaption></figure><p>Chiến dịch gồm chuỗi 30 thử thách nhằm thay đổi thói quen sử dụng nhựa của người trẻ. Cuộc Chiến Trộm Nhựa đã chính thức bắt đầu, cùng theo dõi các thông tin từ chương trình tại website: https://cuocchientromnhua.wechoice.vn  và đừng quên tham gia để có cơ hội trở thành những Kẻ Trộm Nhựa đình đám - những hiệp sĩ bảo vệ môi trường nhé!</p>', NULL, '500kg-rac-thai-treo-lo-lung-tren-dau-trien-lam-an-tuong-o-ha-noi-khien-nguoi-xem-ngop-tho', 0, NULL, NULL),
(147, 23, 6, 1, 1, 'Đây là gia tài \"siêu to khổng lồ\" của Ủn - Chú chó thích đi nhặt ve chai ở Hà Nội', 'https://kenh14cdn.com/thumb_w/300/2019/6/19/landscape-avatar-15609623856791793036499-crop-1560962395098960340049.jpg', '|', 'Có niềm thích thú với các vỏ lon, chai lọ từ nhỏ, Ủn thường hay đi “lượm nhặt” chai lọ ở khắp nơi mang về “cất” vào kho. “Ban đầu gia đình cũng không biết, đến khi thấy lượng chai lọ lớn trong kho mới để ý”, anh Thu - chủ nhân của Ủn chia sẻ.', '<p>Thỉnh thoảng, người ta thấy một chú chó nhỏ, miệng ngậm vỏ lon, chai nhựa chạy lon ton về nhà. Hỏi ra mới biết, chú chó này đã tích trữ được cả một kho vỏ chai lọ \"siêu to khổng lồ\" mà chủ nhân của chú khi \"phát giác\" cũng phải giật mình.</p><p>Theo anh Hồ Xuân Thu (trú tại phường Biên Gia, quận Hà Đông, Hà Nội) là chủ nhân của chú chó cho biết, anh nhận nuôi chú chó từ một người bạn tặng anh làm kỷ niệm và đặt tên là Ủn. Đến hiện tại, Ủn đã được 15 tháng tuổi.</p><p>Clip: Bé Ủn - chú chó \"ve chai\". Thực hiện: Long Quyền.</p><p>\"Từ nhỏ, nó vẫn thường hay đi mang những vỏ chai lọ về kho ở nhà để. Ban đầu gia đình cũng không thích và cấm thì nó không mang về nhà nữa, nhưng cu cậu lại len lén... để ngoài gốc cây ở cổng, khi nó cất ở đó nhiều quá thì mới biết\", anh Thu cười, kể lại.</p><p>Việc Ủn hay đi mang những vỏ chai lọ về là do chú chó tự làm chứ không có ai dạy, anh Thu cho biết.</p><p>\"Ban đầu cũng chỉ tưởng nó mang về nghịch lung tung rồi vứt đi đâu đó thôi, ai ngờ một thời gian ra kho thì mới thấy nó mang về để một đống, nếu không dọn mà tích đến bây giờ có khi cũng được một xe tải nhỏ vỏ chai rồi cũng nên.</p><p>Thời gian gần đây, thấy nó thích việc này quá mà cũng không ảnh hưởng gì, trái lại còn góp phần làm sạch môi trường nên gia đình cũng không ngăn cấm nữa. Tôi cũng chuẩn bị sẵn cho Ủn riêng một thùng xốp ở cổng để khi mang chai lọ về nó để đó cho sạch, cũng may nó chỉ hoàn toàn mang chai lọ chứ không mang những thứ khác về\", anh Thu chia sẻ.</p><p>\"Bộ đồ với cái kính là fan hâm mộ gửi tặng mình đó\". </p><p>Do rất thông minh và lanh lợi nên chú chó có rất nhiều người đến hỏi mua với giá cao. Tính đến hiện tại, đã có người đến trả giá hơn 100 triệu đồng.</p><p>Theo chủ nhân của chú chó, Ủn rất thông minh, nghe lời, sống tình cảm với chủ và không cắn hay tấn công ai bao giờ. Chính vì vậy, anh không có ý định bán mà sẽ nuôi Ủn đến già.</p><p>\"Anh Thu đi xe phân khối lớn nên mình cũng hơi sợ... ngã\"</p><p>Được nhiều người biết đến về sự thông minh nên chú chó nhỏ cũng từng bị kẻ gian đến bắt trộm nhiều lần. Có lần anh Thu cùng người dân đã vây bắt tận tay một kẻ gian đến bắt trộm và giao cho cơ quan công an giải quyết.</p><p>\"Do đây là giống chó nhỏ, hiện Ủn nó cũng chỉ được 14kg nên rất khó chống cự khi bị kẻ gian bắt trộm, giờ tôi cũng lo vì không ở nhà trông được nhiều. Thời gian tới, gia đình sẽ lắp camera để đảm bảo an ninh hơn\", chủ nhân của chú chó nói.</p><p>Ngoài việc \"lượm nhặt\" vỏ chai lọ mang về, Ủn cũng hay phụ giúp chủ nhân các việc lặt vặt trong nhà như vứt rác vào thùng. Khi chủ nhân đi chợ, chú chó cũng hay đi theo để phụ giúp mang đồ.</p><p>WeDo Cuộc Chiến Trộm Nhựa (nằm trong khuôn khổ WeChoice) là một thử thách môi trường hoàn toàn khác mang trong mình sứ mệnh của các \"phản anh hùng\" bất bình với thực trạng rác nhựa.</p><p>Chiến dịch gồm chuỗi 30 thử thách nhằm thay đổi thói quen sử dụng nhựa của người trẻ. Cuộc Chiến Trộm Nhựa đã chính thức bắt đầu, cùng theo dõi các thông tin từ chương trình tại website: https://cuocchientromnhua.wechoice.vn/ và đừng quên tham gia để có cơ hội trở thành những Kẻ Trộm Nhựa đình đám - những hiệp sĩ bảo vệ môi trường nhé!</p><p>Chiến dịch với sự đồng hành của AnEco – Tập đoàn An Phát Holdings trong hành trình sống xanh, thay đổi thói quen sử dụng nhựa của người trẻ. AnEco là sản phẩm sinh học phân hủy hoàn toàn (compostable) được sản xuất trên dây chuyền công nghệ hiện đại của Công ty CP Nhựa và Môi trường xanh An Phát (AAA) thành viên của Tập đoàn An Phát Holdings.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/19/va05926-15609549192961348606209.jpg\" ><figcaption class=\"caption\">\"Xin chào các bạn, mình là Ủn, 15 tháng tuổi. Đi chơi đâu mình cũng tranh thủ \"nhặt\" vỏ chai đem về\".</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/19/va05989-15609549193051044081911.jpg\" ><figcaption class=\"caption\">\"Đây là gia tài siêu to khổng lồ của mình, chuyên dùng để đựng chai lọ đó. Anh Thu kể, tính từ khi mình 3 tháng tuổi đến bây giờ, số phế liệu mình nhặt được chất đầy cả xe tải nhỏ đó\".</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/19/va05974-15609549193031656652655.jpg\" ><figcaption class=\"caption\">\"Không ai được chạm vào thùng xốp của mình hết!\".</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/19/va05866-1560954919285668643396.jpg\" ><figcaption class=\"caption\">\"Ngồi uống trà đá hóng gió với sen, mình cũng tranh thủ nhặt vỏ chai nè mấy bạn\".</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/19/va06019-15609549193111345043065.jpg\" ><figcaption class=\"caption\">\"Bên cạnh sở thích lượm ve chai, mình còn rất thích hóa trang, các bạn thấy mình có ngầu không?\"</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/2019/6/19/va06045-15609549193161391344996.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/19/va05921-15609549192951079103954.jpg\" ><figcaption class=\"caption\">\"Chiều chiều mát mẻ anh Thu lại đưa mình đi dạo phố\".</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/2019/6/19/va05966-15609549193021225185454.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/19/va05899-1560954919292587949841.jpg\" ><figcaption class=\"caption\">\"Anh Thu bảo nếu mình ngoan, anh sẽ mua cho mình 1 chiếc xe tương tự\".</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/19/va06003-1560954919307132903187.jpg\" ><figcaption class=\"caption\">\"Đây là căn nhà của mình, rất là thoáng đãng và mát mẻ luôn\".</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/2019/6/4/banner-chan-bai-1-1559643775542959223069.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Với thông điệp \"Tớ không phải nylon\" các sản phẩm túi bột ngô AnEco thu hút được đông đảo sự quan tâm của khách hàng tiêu dùng, và tăng niềm tin vào sản phẩm thay thế túi ni-lông. Công ty Nhựa và Môi trường xanh An Phát là đơn vị Việt Nam duy nhất là thành viên Hiệp hội sinh học Châu Âu. Chi tiết xem tại http://www.aneco.com.vn/.</p>', NULL, 'day-la-gia-tai-sieu-to-khong-lo-cua-un---chu-cho-thich-di-nhat-ve-chai-o-ha-noi', 0, NULL, NULL),
(148, 23, 6, 1, 1, 'Vlogger đặt mua 5.000 ống hút để làm \"nhà khổng lồ\" câu view, cộng đồng bức xúc: \"Số lượng nhựa này sẽ đi về đâu?\"', 'https://kenh14cdn.com/thumb_w/300/2019/6/7/6203420316399661661477174727940183150821376n-1559918647681473408450-crop-15599194232251629507780.jpg', '|', 'Nam Vlogger NTN thực hiện thử thách làm 1 ngôi nhà từ 5000 ống hút nhựa. Trong khi nhiều người phản đối việc sử dụng quá nhiều ống hút nhựa gây hại cho môi trường, một số bạn trẻ lại hưởng ứng đoạn vlog 1 cách vô lý: \"Số ống hút này anh ấy không mua thì cũng có người khác mua thôi\"?!', '<p>Sau nhiều vlog khó hiểu và kỳ cục như tắm trong chậu mỳ tôm hay giả đặt bom trên phố đi bộ, mới đây vlogger NTN (Nguyễn Thành Nam) lại gây tranh cãi khi đăng tải vlog thực hiện thử thách làm ngôi nhà từ 5.000 ống hút nhựa.</p><p>Theo đó, vlog này được anh chàng phát hành trên Youtube vào ngày 6/6 vừa qua, dù mới chỉ qua 1 ngày nhưng đã nhận về đến hơn 1,2 triệu lượt xem. Như trong rất nhiều vlog trước đó của mình, NTN xưng \"tôi\", gọi mọi người là \"ông\", tuyên bố dùng 5000 ống hút nhựa (loại màu đen, thường dùng để uống trà sữa trân châu) để thực hiện thử thách tạo \"ngôi nhà làm từ ống hút to nhất thế giới\". </p><p>\"Hôm nay tôi sẽ thực hiện 1 cái video mà liên quan đến 5000 CÁI ỐNG HÚT NHỰA, ở đây có 5000 cái ống hút các bạn nhé, các bạn có thể slow lại hoặc stop để đếm xem có đủ 5000 cái ống hút không nhá. Tôi sẽ thực hiện cái ý tưởng là dùng 5000 cái ống hút này, tôi sẽ dính nó lại thành 1 ngôi nhà ống hút khổng lồ... 5000 cái ống hút này là phải đi đặt về đấy. Và đây có lẽ sẽ là ngôi nhà ống hút lớn nhất thế giới\" - vlogger đến từ Thái Bình nói trong video.</p><p>Sau tuyên bố trên, NTN cùng nhiều bạn bè của mình tiến hành dựng ngôi nhà. Theo đó, nam vlogger dùng keo nến dính ống hút thành từng tấm, mỗi tấm gồm 33 ống. NTN còn sử dụng các ống nhựa sơn màu đen để làm khung cho ngôi nhà, sau khi dựng khung nhà xong thì lợp các tấm ống hút nhựa lên trên.</p><p>NTN nói \"tự hào\" trong video, rằng việc dán 5000 chiếc ống hút thành các tấm nhựa mất đến 8 tiếng đồng hồ, hoàn thành \"ngôi nhà làm từ ống hút to nhất thế giới\" phải hết 3 ngày liên tiếp.</p><p>Cũng trong vlog, NTN cho biết nếu video của mình được 200k likes trên Youtube thì sẽ tiếp tục thực hiện thử thách 24h thử sống trong ngôi nhà từ ống hút nhựa nói trên. </p><p>Dẫu biết vlogger NTN luôn gây tranh cãi, khó hiểu với những video của mình, tuy nhiên video thử thách làm nhà từ 5000 ống hút nhựa này lại đặc biệt nhận rất nhiều phản đối, nhất là đối với  cộng đồng Zero Waste . </p><p>Trong khi phong trào giảm thiểu ống hút, chai lọ cũng như các vật dụng làm bằng nhựa đang lan rộng và ngày càng được cộng đồng hưởng ứng, nhiều người cho rằng việc nam vlogger mua quá nhiều ống hút nhựa về để quay video như vậy là không hề bảo vệ môi trường, nếu anh sử dụng vật liệu nhựa đã qua sử dụng hoặc thực hiện làm căn nhà từ ống hút tre thì có lẽ sẽ mang nhiều ý nghĩa hơn.</p><p>Thành viên T.V bình luận dưới video: \"Nếu như anh thu gom, sử dụng và tái chế những chiếc ống hút đã qua sử dụng, để làm ngôi nhà này thì video sẽ thật sự tuyệt vời và ý nghĩa hơn ạ!!\".</p><p>\"Người ta đang có chiến dịch hạn chế rác thải nhựa mà bạn mua nhiều ống hút thế, quay clip xong sẽ làm gì, hay lại vứt đi bừa bãi. Làm 1 người có ảnh hưởng trên MXH như bạn nên có những hành động nêu gương nhân văn hơn cho giới trẻ\" - bạn T.T.H viết.</p><p>Rất nhiều người phản đối việc làm này.</p><p>Đáp lại những ý kiến phản đối từ phần lớn cư dân mạng, như thường lệ, nhiều fan của vlogger NTN đã để lại các bình luận hưởng ứng và bảo vệ \"thần tượng\" một cách vô lý khiến nhiều người ngán ngẩm:</p><p>\"Ống hút người ta làm ra rồi, anh ấy không mua cũng có người mua, không có ai mua người ta cũng đem vứt \", \"Thấy ngứa mắt thì đừng xem, ai mượn vào đây xem rồi chửi người ta đâu\", \"Bảo người ta ngừng sản xuất ống hút nhựa đi thì ngừng mua\", \"Những chiến dịch hạn chế rác thải là vô nghĩa, dọn xong cũng đâu vào đấy\"...</p><p>Hiện đoạn vlog của NTN vẫn đang nhận nhiều phản đối, chỉ trích trên MXH.</p><p>Trên các phương tiện truyền thông hiện nay, rất dễ bắt gặp các tin tức về việc các loài động vật ăn phải nhựa và sau đó thiệt mạng, hay môi trường sống của chúng bị hủy hại nghiêm trọng vì ô nhiễm rác thải nhựa.</p><p>Trong khi nhựa nói chung đều gây hại đối với các sinh vật biển thì ống hút nhựa lại là một đe dọa thậm chí còn nguy hiểm hơn. Bởi theo tổ chức Strawless Ocean, tính riêng tại Mỹ đã có tới hơn 500 triệu đơn vị ống hút được sử dụng. Chúng xuất hiện ở khắp mọi nơi, từ thùng rác, bãi biển, cống thoát nước và trên đại dương.</p><p>Ngay cả khi ống hút nhựa được vứt đúng chỗ trong thùng rác, chúng cũng khó lòng được xử lý đúng cách (do kích thước nhỏ) và cuối cùng lại trôi ra ngoài biển.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/7/6203420316399661661477174727940183150821376n-1559918647681473408450.jpg\" ><figcaption class=\"caption\">5.000 chiếc ống hút cùng loại cùng màu, được đặt hàng mua về chỉ để làm căn nhà khổng lồ câu view trên Youtube.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/7/anh-chup-man-hinh-2019-06-07-luc-210628-15599163970411926896818.png\" ><figcaption class=\"caption\">Số ống hút đặt mua về được thanh niên này đổ từ các chậu nhựa ra nền nhà - Ảnh cắt từ clip.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/7/anh-chup-man-hinh-2019-06-07-luc-210736-15599166101411495489714.png\" ><figcaption class=\"caption\">Nam vlogger dùng keo nến dán các ống hút thành từng tấm - Ảnh cắt từ clip</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/7/anh-chup-man-hinh-2019-06-07-luc-210911-1559916610139645852390.png\" ><figcaption class=\"caption\">Anh cùng nhiều bạn bè dựng ngôi nhà từ ống hút nhựa và các ống nước - Ảnh cắt từ clip</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/7/anh-chup-man-hinh-2019-06-07-luc-210958-15599166101371947578811.png\" ><figcaption class=\"caption\">NTN trải chiếu nằm thử bên trong \"căn nhà\" từ 5.000 ống hút nhựa - một con số rất buồn cho môi trường - Ảnh cắt từ clip</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/2019/6/7/screen-shot-2019-06-07-at-100322-pm-1559920572164501149951.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/7/screen-shot-2019-06-07-at-95527-pm-15599205721521719202110.png\" ><figcaption class=\"caption\">Nhiều người bình luận về tác hại của  5.000 ống hút này khi thải ra môi trường, fan hâm mộ NTN vẫn vào cười khẩy: Thế bạn quay video bảo vệ môi trường đi.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/7/screen-shot-2019-06-07-at-93244-pm-copy-15599207872592138728554.png\" ><figcaption class=\"caption\">Những bình luận đáng buồn của các tài khoản yêu thích chủ kênh NTN này.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/5/2/photo-1-15523808761361369377037-15567304866461335340436.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Tại Việt Nam, mặc dù chưa có thống kê chính thức từ tổ chức nào về số lượng sử dụng ống hút nhựa ở Việt Nam, tuy vậy, từ thực tế hàng ngày, có thể khẳng định, con số này không nhỏ. Riêng đối với các sản phẩm về nhựa, năm 2017, Việt Nam nằm trong top 5 nước xả nhiều rác thải nhựa ra biển nhất thế giới, theo thống kê của Hiệp hội Bảo tồn đại dương.</p>', NULL, 'vlogger-dat-mua-5000-ong-hut-de-lam-nha-khong-lo-cau-view-cong-dong-buc-xuc-so-luong-nhua-nay-se-di-ve-dau', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(149, 23, 6, 1, 1, 'Gặp cô bán rau vui tính ở Sài Gòn với tấm bảng \"không bán túi nilon\": Nhiều khách bảo cô làm trò xàm xí!', 'https://kenh14cdn.com/thumb_w/300/2019/6/4/landscape-avatar-15595817975501924992350-crop-15595818632531562231418.png', '|', 'Từ ngày có chiếc bảng nhỏ xinh với thông điệp hạn chế sử dụng túi nilon, bảo vệ môi trường, cô Hương gặp không ít lời gièm pha cho rằng làm điều vô bổ, nhưng cô chỉ mỉm cười, bởi với cô không thể thay đổi mọi người trong một sớm một chiều.', '<p>Cách đây ít ngày, cộng đồng mạng một phen trầm trồ trước sự \"chơi lớn\" của các cô bán rau ở Sài Gòn khi đồng loạt thông báo: \"Tui bán rau, không bán túi nilon!\"</p><p>Tấm bảng thông báo dễ thương do một bạn gái có tên Quỳnh Hương đã làm tặng các cô trong hẻm, xuất phát từ trăn trở của các cô về việc ngày càng sử dụng quá nhiều túi nilon. Việc làm của các cô tuy chẳng to tát nhưng ít nhiều đã giúp người dân quanh hẻm nâng cao hơn ý thức bảo vệ môi trường. </p><p>Cụ thể, các cô khuyến khích khách hàng khi tới mua nên chủ động mang theo túi, giỏ xách... để tránh phải dùng túi nilon, nếu nhà gần thì có thể cầm tay về. Bên cạnh đó, các cô cũng kêu gọi các khách hàng của mình, nếu có túi nilon ở nhà thì mang tới \"góp\" để cô tái sử dụng lại. Những hành động tuy nhỏ mà thiết thực được các cô phấn khởi thực hiện, với lý do rất đơn giản: \"Để sau này tụi con nít được nhờ\".</p><p>Trở lại con hẻm đáng yêu vào một ngày đầu tuần, tôi gặp cô Bảo Hương và tấm bảng rực rỡ giữa gánh hàng rau của cô.</p><p>- Chị bán em bó rau muống!</p><p>- Rau muống của em đây, mà chị để vào trong bịch của em luôn nhen, tại ở đây đang phát động phong trào hạn chế sử dụng bao nilon.</p><p>Cô Hương vừa cười vừa chỉ vào tấm bảng trên sạp, vị khách tò mò đọc những dòng chữ đầy màu sắc được viết nắn nót, rồi hỏi:</p><p>- Ủa bán rau mà không có túi nilon thì đựng bằng cái gì?</p><p>- Thì mình hạn chế thôi em. Trên bảng có ghi đó, ví dụ như ở gần thì cầm tay về, còn không thì mình dùng 1 bịch đựng chung nhiều món.</p><p>Cô Bảo Hương năm nay đã gần 50 tuổi nhưng tính tình vui vẻ nên nhìn trẻ hoài. Chia sẻ về tấm bảng và tinh thần sống xanh của mình, cô bảo: \"Trước đây có một cụ bà bán rau ở trong hẻm này, ngày nào cụ cũng giặt lại các túi nilon để tái sử dụng. Cô thấy vậy nên cũng học theo, thứ nhất là mình tiết kiệm được kinh tế, thứ hai là hạn chế xả rác ra môi trường. Sau này cô nghĩ nhiều cách để có thể ít sử dụng túi nilon nhất có thể\".</p><p>Trước đây các bà các mẹ đi chợ thường mang theo giỏ, hễ mua món gì cũng có thể dựng trong giỏ, nhưng từ ngày xã hội phát triển, chiếc giỏ xách nhường chỗ lại cho chiếc túi nilon, mua gì cũng đòi túi, mua 2 quả trứng vịt cũng xin túi, mua 2 ngàn ớt cũng xin túi, mấy cọng xả cũng xin túi...về đến nhà thì tiện tay vứt đi khiến lượng rác thải ngày một nhiều. </p><p>Hơn ai hết, những người bán buôn ở chợ như cô Hương hiểu điều này, chính vì vậy họ luôn trăn trở cách nào để hạn chế. Cô tâm sự: \"Mấy hôm cô để tấm bảng này lên nhưng khách hàng cũng không đồng tình lắm, họ bảo cô làm mấy cái trò xàm xí, nhưng cô không giận, chỉ mỉm cười thôi, rồi mình từ từ giải thích, thay đổi phải từ từ, đâu thể ngày một ngày hai mà thay đổi được liền\".</p><p>Dẫu vậy cũng có rất nhiều khách hàng ủng hộ hết mình phong trào của cô Hương. Họ đem sẵn bịch nilon của mình đến để đựng rau, hoặc ai nhà gần có thể cầm tay đem về, hay giặt sạch những chiếc túi đã qua sử dụng để đem ra cho cô Hương tái sử dụng. \"Mình làm từ hôm nay, là để cho tương lai của con cháu mình mà\" - cô Hương cười.</p><p>Những hành động của cô dù bé tẹo teo thôi, nhưng đầy thiết thực như cái cách mà cô luôn dạy các cậu con trai của mình từ ngày còn bé, rằng không được vứt rác bừa bãi, luôn giữ vệ sinh sạch sẽ.</p><p>Mỗi ngày cô Hương dậy từ 3 giờ sáng để chuẩn bị hàng buôn bán, công việc vốn nhiều nhưng cô lại thích ôm nhọc vào người, hễ đi ngoài đường thấy mấy chú mèo con bị bỏ rơi là cô lại ôm về nhà để chăm sóc.</p><p>\"Nghe tiếng tụi nó kêu cô thấy thương quá chừng. Cái rồi ôm tụi nó về nhà chăm sóc, nuôi như con mọn vậy đó con. Đến khi tụi nó lớn thì cô hỏi mọi người xung quanh xem có ai muốn nuôi mèo không thì cô đem qua cho. Có lúc nhà cô nuôi tới 30 con mèo lận đó\" - cô thiệt tình kể.</p><p>Tôi tò mò: \"Công việc vốn đã vất vả, thêm phần phải chăm lo cho gia đình sao cô lại ôm thêm việc chăm cho lũ mèo con?\". Cô tâm sự: \"Mình biết đói, thì tụi nó cũng biết đói mà con. Thôi kệ, cô vẫn làm được. Giờ thì ở nhà còn 4 con chó, với 5 con mèo, mấy đứa này là thân thương nên cô không đem cho ai\".</p><p>\"Cô vẫn còn thời gian xem bóng đá mà\" - cô Hương cười hà hà. Tôi trầm trồ: \"Ối chà, quá dữ luôn cô ơi\". Cô Hương bảo: \"Ừa cô mê bóng đá lắm, nhưng mấy trận khuya thì không coi được, tại phải dậy sớm đi bán, nên mấy trận đó cô toàn coi lại không à\".</p><p>Hai cô cháu trò chuyện rôm rả, chốc chốc lại có mấy vị khách ghé sạp rau để mua hàng, ai nấy cũng tò mò về chiếc bảng nhỏ xinh, và lần nào cô Hương cũng tận tình hướng dẫn mọi người cách để hạn chế những chiếc túi nilon. </p><p>WeDo - Cuộc Chiến Trộm Nhựa (nằm trong khuôn khổ WeChoice) là một thử thách môi trường hoàn toàn khác mang trong mình sứ mệnh của các \"phản anh hùng\" bất bình với thực trạng rác nhựa.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/3/batchdscf2030-15595740495231848184272.jpg\" ><figcaption class=\"caption\">Chiếc bản thông báo \"cute lạc lối\" của cô bán rau ở Sài Gòn.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/3/batchdscf2012-15595740495071693582212.jpg\" ><figcaption class=\"caption\">Cô Hương giải thích cho mọi người về chiếc bảng.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/3/batchdscf2015-15595740495111140311993.jpg\" ><figcaption class=\"caption\">Cô Hương vô cùng vui tính.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/3/batchdscf2021-1559574049516977942779.jpg\" ><figcaption class=\"caption\">Người Việt bị phụ thuộc quá nhiều vào túi nilon.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/3/batchdscf2026-15595740495212078922142.jpg\" ><figcaption class=\"caption\">Những khách hàng ủng hộ cô bằng cách cầm rau về nhà mà không dùng túi nilon.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/3/batchdscf2024-1559574049520432858938.jpg\" ><figcaption class=\"caption\">Cô Hương gói ớt bằng giấy báo cũ.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/3/batchdscf2014-15595740495091414236138.jpg\" ><figcaption class=\"caption\">Hàng rau đã theo cô hơn 17 năm.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/3/batchdscf2017-15595740495131568375540.jpg\" ><figcaption class=\"caption\">Cô Hương là một người rất yêu thương động vật.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/3/batchdscf2033-155957404952459623214.jpg\" ><figcaption class=\"caption\">Sài Gòn luôn dễ thương bình dị vậy đó!</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/2019/6/4/banner-chan-bai-1-1559643775542959223069.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Chiến dịch gồm chuỗi 30 thử thách nhằm thay đổi thói quen sử dụng nhựa của người trẻ. Cuộc Chiến Trộm Nhựa đã chính thức bắt đầu, cùng theo dõi các thông tin từ chương trình tại website: https://cuocchientromnhua.wechoice.vn/ và đừng quên tham gia để có cơ hội trở thành những Kẻ Trộm Nhựa đình đám - những hiệp sĩ bảo vệ môi trường nhé!</p>', NULL, 'gap-co-ban-rau-vui-tinh-o-sai-gon-voi-tam-bang-khong-ban-tui-nilon-nhieu-khach-bao-co-lam-tro-xam-xi', 0, NULL, NULL),
(150, 23, 6, 1, 1, 'Người tặng tấm biển \"Tui bán rau, không bán túi nilon\" cho các sạp hàng ở Sài Gòn: Các cô chú làm được thì chúng mình cũng làm được!', 'https://kenh14cdn.com/thumb_w/300/2019/5/31/landscape-avatar-15593204245421887748047-crop-15593204508961332911288.png', '|', 'Tấm bảng với thông điệp đơn giản, dễ thương được các cô bán rau ở Sài Gòn gửi tới khách hàng về việc hạn chế sử dụng túi nilion \"cho con nít được nhờ\" đang nhận được sự hưởng ứng mạnh mẽ từ cộng đồng mạng.', '<p>Trước giờ, bảo vệ môi trường vốn là khái niệm tương đối trừu tượng, xa vời với hầu hết tất cả chúng ta. Chỉ tới vài năm trở lại đây, khi thời tiết ở một số nơi bất ngờ trở nên \"đỏng đảnh\" đến đáng sợ, những thông tin về các loài sinh vật biển chết bởi nuốt phải rác thải nhựa, những bãi rác ngày càng chất cao trong khu đô thị, người ta mới giật mình tự hỏi: \"Chúng ta đã làm gì môi trường thế này?\".</p><p>Nghĩ xanh và sống xanh - một trong những trào lưu được hưởng ứng mạnh mẽ nhất nửa đầu 2019 cũng được hình thành từ chính cái \"giật mình\" đó. Thay vì ngồi một chỗ và chờ những thứ vĩ mô như lỗ hổng tầng Ozon, hiệu ứng nhà kính... thu hẹp hay thuyên giảm, giờ đây, nhiều người bắt đầu hiện thực hóa \"bảo vệ môi trường\" từ những hành động nhỏ nhặt, giản đơn mà thiết thực.</p><p>Một ví dụ điển hình cho thấy trào lưu sống xanh và zero waste đang ngày càng phổ biến sâu rộng hơn trong cộng đồng vừa xuất hiện trên MXH, đó là tấm bảng với thông điệp về việc hạn chế tối đa sử dụng túi nilon, tại sạp hàng của các cô bán rau hay bác bán thịt trong một con hẻm nhỏ ở quận Bình Thạnh, TP HCM.</p><p>\"Tui bán rau, không bán túi nilon\", ngắn gọn, súc tích, thông điệp xanh đầy mạnh mẽ mà các cô truyền đi đang làm nức lòng hàng nghìn cư dân mạng.</p><p>Nếu bạn để ý thì có thể nhận thấy rằng, các sạp rau vốn là một trong những điểm tiêu thụ nhiều túi nilon nhất. Điều này cũng xuất phát từ thói quen tiêu dùng của chính chúng ta, khi mà mỗi mớ rau, vài quả chanh hay mấy củ hành là lại đi kèm 1 chiếc túi để đựng. Cứ thế, một sạp rau mỗi ngày có khi tiêu thụ tới vài lạng, thậm chí là cả cân túi nilon lớn nhỏ. Mà sau đó, chắc chắn chúng sẽ được thẳng tay xả ra môi trường.</p><p>Xuất phát từ trăn trở này, một tấm biển thông báo đã được tặng cho các sạp hàng của các cô, các chị tại con hẻm số 10 ở quận Bình Thạnh. Tấm biển còn có phương pháp rất rõ ràng để cùng với bà con hiện thực hóa việc làm đầy ý nghĩa này.</p><p>Tui bán rau, không bán túi nilon!</p><p>Bà con thân mến! Từ nay sạp tui hạn chế sử dụng túi nilon, nên:</p><p>1. Chủ động mang túi.</p><p>2. Nhà gần thì cầm tay.</p><p>3. Bà con có túi nilon cũ, sạch, còn sử dụng được thì mang tới đây, tui sẽ sử dụng lại.</p><p>Cả xóm cùng bớt rác, cho con nít được nhờ!</p><p>Cụ thể, các cô khuyến khích khách hàng khi tới mua nên chủ động mang theo túi, giỏ xách... để tránh phải dùng túi nilon, nếu nhà gần thì có thể cầm tay về. Bên cạnh đó, các cô cũng kêu gọi các khách hàng của mình, nếu có túi nilon ở nhà thì mang tới \"góp\" để cô tái sử dụng lại. Những hành động tuy nhỏ mà thiết thực được các cô phấn khởi thực hiện, với lý do rất đơn giản: \"cho con nít được nhờ\".</p><p>Ngay sau khi được đăng tải lên MXH, bài post về các cô bán rau, bác bán thịt yêu môi trường ở Sài Gòn đã lập tức gây chú ý và khiến cộng đồng mạng vô cùng yêu thích. 2.000 lượt chia sẻ chỉ trong một thời gian ngắn là bằng chứng rõ ràng cho sự hưởng ứng và tính lan tỏa mạnh mẽ từ cộng đồng cho hành động đẹp đẽ này.</p><p>\"Thật tuyệt vời. Ngay các cô bán rau ở chợ, những người vốn được biết đến với sự lam lũ, vất vả và thu nhập bèo bọt cũng đã có hành động cụ thể để bảo vệ môi trường. Nhất định mình sẽ hưởng ứng nhiệt tình, từ giờ sẽ mang theo làn đi chợ!\", bạn K. A. bình luận.</p><p>\"Chẳng cần làm gì to tát cả, hạn chế dùng túi nilon, ngay từ những khu chợ hay siêu thị sẽ giúp giảm áp lực tiêu cực đến môi trường. Ủng hộ hết mình các cô, các bác, hi vọng mọi sạp rau trên đất nước này đều học theo!\", bạn T. H. bình luận.</p><p>Nguyễn Hữu Quỳnh Hương, admin fanpage Mình là Hũ, người trực tiếp ghi nhận và đăng tải những hình ảnh nói trên cho biết bạn thực sự bị ấn tượng cực mạnh bởi những trăn trở rất thật về môi trường của các cô bán rau trong lần tình cờ gặp gỡ cách đây không lâu. Tuy điều kiện làm việc đặc thù, vất vả lại có thu nhập bấp bênh, nhưng những việc mà các cô đã làm và đang dự định làm thì không phải ai cũng làm được.</p><p>Kể về lần gặp cô bán rau tại con hẻm này trước đó, Hương vẫn nhớ như in những chia sẻ của cô như sau:</p><p>\"Cô đang suy nghĩ nhiều lắm về cái chuyện dùng túi nào để đỡ ảnh hưởng môi trường đây. Bây giờ người ta dùng nhiều quá, mình ngăn người ta cũng không được, mà sắm cho mỗi người một cái túi giấy cũng khó quá, không được. Cô có bốn người con đều làm ở nhà hàng chay của cô ca sĩ nọ, bên đó cũng đang thay đổi nhiều lắm về túi mang về nhưng cô bán cái sạp nhỏ thì sao mà chịu phí cho đặng. Nhưng mà cô nghe loáng thoáng có cái túi nào đỡ hại hơn, nếu cao hơn tí cô cũng chấp nhận dùng để thay đổi từ từ.</p><p>Con biết không, nhiều khi cô làm vậy người ta bảo cô có mỗi cái sạp rau nhỏ cũng chơi nổi. Nhưng mà cô thuyết phục người ta, người ta không chịu nghe. Có mấy người nhà gần xịt đây cô bảo cầm tay về đi cho sạch, nhưng không chịu. Chưa kể cô có ý thức bỏ nhiều thứ vô một bịch thôi để tránh xả nhiều quá nhưng dăm ba quả ớt, củ tỏi người ta cũng bắt cô bỏ từng bịch từng bịch.</p><p>Cô muốn lắm, muốn lắm, muốn thay đổi lắm, con ơi con biết cái nào chỉ cho cô nha.\"</p><p>Từ đó, Quỳnh Hương (hiện cũng đang làm công việc liên quan tới môi trường tại Quận 1, TP HCM) quyết định sẽ làm tặng các cô tấm bảng thông báo \"siêu cute\" trên, nhằm gây chú ý với khách hàng. Sự ủng hộ, lan tỏa từ cộng đồng mạng với bài post của mình khiến Hương cảm thấy khá bất ngờ và hạnh phúc.</p><p>\"Mình rất mừng khi bài viết của page với thông điệp sống xanh, hạn chế túi nilon lại nhận được nhiều sự đón nhận đến vậy. Điều này thể hiện việc hầu hết chúng ta đều đang rất quan tâm và ủng hộ những hành động đẹp với môi trường. Chỉ có điều, vì lý do nào đó mà mọi người chưa, hoặc ngần ngại hành động như các cô bán rau đáng yêu này thôi.</p><p>Quan điểm của mình, mỗi người đều có thể sống sao cho bớt rác mỗi ngày và làm đúng phần việc nhỏ bé đó của mình. Mỗi ngày khi nghe những tin tức xấu khủng khiếp về môi trường, mỗi người lại có cách hành xử khác nhau. Nhiều người chọn cách đấu tranh và làm những thứ lớn lao, cũng có người chỉ biết ngồi và hoảng sợ.</p><p>Quan trọng nhất, chúng ta hãy biết từ chối một cách từ từ những hành động có khả năng gây hại cho môi trường, như túi nilon chẳng hạn. Và thực hiện ngay, từ những thứ nhỏ nhặt nhất, bắt đầu từ chính mình, đến nơi mình ở rồi lan tỏa dần ra các cộng đồng mà mình là thành viên. Đừng kêu gọi xa xôi, to lớn mà lại nhất thời thì hiệu quả sẽ chẳng đến đâu cả. Các cô chú đã làm được, chúng mình cũng sẽ làm được\", Hương nói.</p><p>Trên tấm bảng này, Hương đã cố gắng chọn những từ những dễ hiểu nhất cho bà con cô bác hiểu, và không quên nhắn \"Cả xóm cùng giảm rác, cho con nít được nhờ\". Hôm nay là ngày Quốc tế Thiếu Nhi, các cô bán rau đã tặng cho bọn trẻ trong xóm một niềm hy vọng: Mỗi người trong cái hẻm 10 ngày dù làm việc gì, chỉ cần chung tay với nhau, giữ lại chút không khí sạch, giữ lại chút rau xanh – đó chính là món quà ý nghĩa nhất cho trẻ con! Quà tặng cho con nít ngày Quốc tế Thiếu Nhi thiết thực nhất không phải là những món đồ đắt tiền, mỗi người giảm một chút rác, dành lại tài nguyên cho thế hệ sau, ấy là tốt rồi.</p><p></p><p>\"Những cô bán rau trong hẻm 10 làm được. Mình là một phần trong cái hẻm nhỏ ấy cũng góp sức cùng các cô và hạn chế rác khi đi chợ. Bạn thương thế hệ sau, bạn muốn tặng cho các bạn nhỏ món quà, thế bạn cùng giảm rác được không?\", Hương để lại lời nhắn gửi đáng yêu trên fanpage của mình.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/5/31/6159652410795566055649609021055772720627712o-1559290108778793599733.jpg\" ><figcaption class=\"caption\">Tấm biển được đặt trang trọng cạnh sạp rau của cô Hương (Nguồn: fanpage Mình là Hũ)</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/5/31/6153889110795580522314821662440338289065984o-15592901087731061072873.jpg\" ><figcaption class=\"caption\">Tui bán rau không bán túi nilon nha!</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/5/31/617797801079559955564625111677537767129088o-1559290108783733754175.jpg\" ><figcaption class=\"caption\">Túi nilon được thu gom để tái sử dụng.</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/5/31/6122400610795591088980435643503266491793408o-15592919577401182357647.jpg\" ><figcaption class=\"caption\">Tui bán thịt, cũng không bán túi nilon!</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/6/1/6117001710737147128158168968196175689678848o-1559322400094134220334.jpg\" ><figcaption class=\"caption\">Trăn trở của cô bán rau tại hẻm 10 chính là động lực giúp Hương nghĩa rằng đã đến lúc phải \"hành động\".</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/5/31/6156625610795588688980678937836774156140544o-1559290108776459413530.jpg\" ><figcaption class=\"caption\">Admin Mình là Hũ - Nguyễn Hữu Quỳnh Hương và cô bán rau hẻm 10!</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/5/31/6148433810795581522314723180998018031681536o-15592919577492138984535.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/2019/6/1/6173928010795605388979001769770956835782656n-1559322723351887276161.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/2019/6/4/banner-chan-bai-1-1559643775542959223069.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>\"Zero Waste\" là một chuyên đề do Kenh14 thực hiện nhằm nâng cao nhận thức của giới trẻ về bảo vệ môi trường, đồng thời đem đến cho các bạn cái nhìn gần gũi, tự nhiên hơn về chủ đề nghe có vẻ thô cứng này. Đến với chuyên đề \"Zero Waste\", bạn sẽ được lắng nghe những câu chuyện từ chính những bạn trẻ đã và đang cố gắng từng ngày bằng những hành động thiết thực nhất nhằm cứu lấy môi trường.</p>', NULL, 'nguoi-tang-tam-bien-tui-ban-rau-khong-ban-tui-nilon-cho-cac-sap-hang-o-sai-gon-cac-co-chu-lam-duoc-thi-chung-minh-cung-lam-duoc', 0, NULL, NULL),
(151, 24, 7, 1, 1, 'Indonesia: Lũ lụt và sạt lở kinh hoàng, ít nhất 119 người chết', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617798428632-1617798428978150930736.jpg', '|', 'Số người thiệt mạng vì sạt lở ở phía Đông của Indonesia đã tăng lên 119 người, giới chức cho biết hôm 7-4 giữa lúc mưa lớn tiếp tục càn quét khu vực và cản trở công tác tìm kiếm hàng chục người mất tích.', '<p>Làng Lamanele trên đảo Adonara chịu ảnh hưởng nặng nề nhất với 60 thi thể được tìm thấy đến thời điểm hiện tại và 12 người mất tích.</p><p>Tại đảo Lembata gần đó, sạt lở do mưa lớn ở hàng chục ngôi làng cũng đã cướp đi sinh mạng của ít nhất 28 người và khiến 44 người mất tích, theo Cơ quan Giảm nhẹ Thiên tai Quốc gia Indonesia (NDMA).</p><p>Hàng trăm cảnh sát, binh sĩ và cư dân đào bới mảnh vỡ bằng tay không, xẻng và cuốc để tìm kiếm những nạn nhân bị chôn vùi.</p><p>Tổng cộng, đã có ít nhất 119 người thiệt mạng trên nhiều hòn đảo ở Indonesia và 27 người thiệt mạng ở quốc gia láng giềng Đông Timor trong đợt mưa lũ và sạt lở xảy ra từ ngày 4-4.</p><p>Hàng ngàn ngôi nhà bị phá hủy và hàng ngàn người phải sơ tán vì điều kiện thời tiết nêu trên, được dự báo tiếp diễn ít nhất đến ngày 9-4 khi bão di chuyển theo hướng Nam đến Úc.</p><p>Các nỗ lực tìm kiếm và cứu hộ đang bị mưa lớn và khoảng cách cản trở, đặc biệt là tại những nơi đường sá bị tàn phá.</p><p>Lực lượng cứu hộ cùng hàng tấn thực phẩm và thuốc đang được triển khai từ TP Makassar trên đảo Sulawesi, song quá trình vận chuyển gặp nhiều khó khăn vì thiếu phương tiện hàng hải. Giám đốc NDMA Doni Monardo kêu gọi lĩnh vực tư nhân hỗ trợ nỗ lực cứu nạn.</p><p>Tổng thống Indonesia Joko Widodo hôm 6-4 tổ chức một cuộc họp Nội các ở thủ đô Jakarta để đẩy nhanh chiến dịch tìm kiếm và cứu hộ.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617798384170864015905.jpg\" ><figcaption class=\"caption\">Ảnh chụp từ trên cao tại tỉnh East Nusa Tenggara - Indonesia hôm 5-4. Ảnh: Reuters</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177983861691039707324.jpg\" ><figcaption class=\"caption\">Ít nhất 119 người đã thiệt mạng trong đợt mưa lũ và sạt lở từ ngày 4-4. Ảnh: Reuters</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-2-16177983867831243730210.jpg\" ><figcaption class=\"caption\">Mưa lũ và sạt lở khiến hàng ngàn căn nhà bị phá hủy. Ảnh: Reuters</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-3-16177983863061724824557.jpg\" ><figcaption class=\"caption\">Lực lượng cứu hộ tìm kiếm thi thể một nạn nhân tại tỉnh East Nusa Tenggara - Indonesia hôm 5-4. Ảnh: Reuters</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-4-16177983887851343123174.jpeg\" ><figcaption class=\"caption\">Ảnh: AP</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-5-16177983876372083290403.jpeg\" ><figcaption class=\"caption\">Ảnh: AP</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-6-16177983868061230314484.jpeg\" ><figcaption class=\"caption\">Ảnh: AP</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-7-1617798388269584932201.jpeg\" ><figcaption class=\"caption\">Ảnh: AP</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-8-16177983867431605489164.jpeg\" ><figcaption class=\"caption\">Ảnh: AP</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-9-1617798387723475434064.jpeg\" ><figcaption class=\"caption\">Ảnh: AP</figcaption></figure><p>Một số hình ảnh khác về mưa lũ và sạt lở ở Indonesia:</p>', NULL, 'indonesia-lu-lut-va-sat-lo-kinh-hoang-it-nhat-119-nguoi-chet', 0, NULL, NULL),
(152, 24, 7, 1, 1, 'Đại học Oxford tạm dừng thử nghiệm vaccine ngừa COVID-19 ở trẻ em', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617782788133-16177827891322136454095.jpeg', '|', 'Ngày 6/4, Đại học Oxford của Anh đã công bố tạm dừng việc thử nghiệm lâm sàng loại vaccine ngừa COVID-19 mà trường này phát triển cùng với công ty AstraZeneca.', '<p>Việc thử nghiệm vaccine COVID-19 nói trên được thực hiện trên khoảng 300 tình nguyện viên từ 6 đến 17 tuổi.</p><p>Đại học Oxford cho biết đã quyết định dừng nghiên cứu của mình để đợi các thông tin bổ sung từ Cơ quan quản lý Thuốc và Sản phẩm chăm sóc sức khỏe Vương quốc Anh sau khi xem xét các trường hợp đông máu và giảm tiểu cầu ở một số người lớn khi tiêm chủng. Tuy nhiên, Oxford cho biết, \"không có lo ngại về an toàn trong nghiên cứu nhi khoa này\".</p><p>Phát biểu trước việc Đại học Oxford tạm dừng thử nghiệm, ông Nadhim Zahawi, Quốc vụ khanh phụ trách vấn đề vaccine của Vương quốc Anh, đã trấn an người dân, khẳng định Cơ quan quản lý Thuốc và Sản phẩm chăm sóc sức khỏe Vương quốc Anh sẽ tiếp tục theo dõi bất kỳ tác dụng phụ không tốt nào của vaccine Oxford/ AstraZeneca .</p><p>Ông nói: \"Các cơ quan quản lý đang xem xét một cách chắc chắn, rất chặt chẽ bất kỳ tác dụng phụ có hại nào\".</p><p>Cơ quan quản lý Thuốc và Sản phẩm chăm sóc sức khỏe Vương quốc Anh và Cơ quan Dược phẩm châu Âu đều dự kiến trong tuần này sẽ đưa ra bản đánh giá rủi ro mới sau các sự cố về rối loạn máu hiếm gặp ở một số người ở châu Âu được tiêm chủng vaccine của Oxford/AstraZeneca. Cả hai cơ quan quản lý này trước đây đều khẳng định, không có mối liên hệ thuyết phục nào giữa việc tiêm chủng và các trường hợp bị đông máu.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617782674749705091347.jpeg\" ><figcaption class=\"caption\">Việc thử nghiệm vaccine AstraZeneca được thực hiện trên khoảng 300 tình nguyện viên từ 6 - 17 tuổi. (Ảnh: AP)</figcaption></figure><p>Tuy nhiên, các cơ quan này có thể khuyến nghị hạn chế sử dụng vaccine ở người trẻ tuổi, những người dễ bị đông máu bất thường hơn, trong khi khả năng bị COVID-19 nghiêm trọng lại thấp hơn rất nhiều.</p>', NULL, 'dai-hoc-oxford-tam-dung-thu-nghiem-vaccine-ngua-covid-19-o-tre-em', 0, NULL, NULL),
(153, 24, 7, 1, 1, 'Hỏa hoạn tại tòa nhà trung tâm thương mại ở thành phố Makati, Philippines', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617772020254-16177720204561710288381.png', '|', 'Cục Phòng cháy chữa cháy Philippines (BFP) cho biết, chiều 6/4, một vụ hỏa hoạn lớn đã xảy ra tại một trung tâm thương mại ở thành phố Makati.', '<p>Ông Ian Guerrero, Giám đốc BFP chi nhánh thành phố Makati, cho biết, vụ cháy diễn ra tại tòa nhà trung tâm thương mại 15 tầng nằm trên phố Leviste, Salcedo Village, thành phố Makati.</p><p>\"Đám cháy bắt đầu diễn ra ở tầng 15 của tòa nhà. Một công ty đang sử dụng tầng này làm văn phòng. Vào thời điểm xảy ra hỏa hoạn, nhân viên công ty đang làm việc ở tầng này. Hiện số lượng nhân viên có mặt tại văn phòng là rất ít do các lệnh hạn chế, cách ly\", ông Guerrero nói.</p><p>Đoạn phim quay lại hiện trường cho thấy, ngọn lửa bùng phát mạnh, kèm theo khói đen cuồn cuộn, nhấn chìm tầng trên cùng của tòa nhà trung tâm thương mại.</p><p>Ông Guerrero cho biết, ngọn lửa không cháy lan sang các tầng khác vì lực lượng cứu hỏa đã nhanh chóng dập tắt vụ hỏa hoạn trên tầng 15 của tòa nhà.</p><p>Theo lực lượng cảnh sát cứu hỏa địa phương, ngọn lửa đã được dập tắt vào khoảng 16h cùng ngày. Được biết, không có người bị thương sau vụ cháy. Hiện chưa có thống kê về thiệt hại do vụ hỏa hoạn.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177719426381161177006.png\" ><figcaption class=\"caption\">Vụ cháy diễn ra tại tòa nhà trung tâm thương mại 15 tầng ở thành phố Makati (Ảnh: Manila Bulletin)</figcaption></figure><p>Các cơ quan chức năng Philippines đang điều tra nguyên nhân vụ cháy. Tòa nhà này nằm ở thành phố Makati, một trong những trung tâm tài chính lớn của Philippines.</p>', NULL, 'hoa-hoan-tai-toa-nha-trung-tam-thuong-mai-o-thanh-pho-makati-philippines', 0, NULL, NULL),
(154, 24, 7, 1, 1, 'Va chạm giữa xe tải và xe bus tại Mexico, hàng chục người thương vong', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617771721958-16177717229241075840772.jpg', '|', 'Ít nhất 16 người thiệt mạng và 14 người khác bị thương sau một vụ va chạm giữa xe tải và xe bus tại thành phố Caborca, Đông Bắc bang Sonora, Mexico.', '<p>Vụ va chạm giữa xe tải và xe bus nói trên xảy ra vào rạng sáng 6/4 (theo giờ địa phương). Một xe tải nhỏ chở công nhân mỏ và một xe bus chở khách du lịch đã va chạm với nhau vào khoảng 3h36 ngày 6/4 tại kilomet 156 trên đường cao tốc nối Caborca với thị trấn Sonoyta giáp biên giới Mỹ, trước lối vào mỏ Penmont.</p><p>Các báo cáo chỉ ra rằng, tất cả nạn nhân thiệt mạng đều là hành khách trên chiếc xe bus chở khách du lịch thuộc hãng vận tải Ejecutivos Premiere, trong đó có 3 phụ nữ. Các nạn nhân này trong độ tuổi từ 18 đến 42.</p><p>Ngoài các nạn nhân thiệt mạng, công ty khai thác mỏ Minera Penmont xác nhận rằng, 13 công nhân bị thương đang trong tình trạng ổn định, trong khi tài xế xe tải đang trong tình trạng sức khỏe nguy kịch. Tất cả những người bị thương đang được điều trị tại các bệnh viện ở thành phố Caborca.</p><p>Công ty Minera Penmont cho biết trong một thông báo: \"Ngay sau nhận được thông tin về vụ tai nạn, các dịch vụ khẩn cấp đã được điều động. Công ty Minera Penmont vô cùng lấy làm tiếc về vụ tai nạn giao thông này và thường xuyên liên lạc với chính quyền địa phương để tạo điều kiện thuận lợi cho việc điều tra\".</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617771657241891303717.jpg\" ><figcaption class=\"caption\">Hiện trường vụ tai nạn ở Caborca (Ảnh: News Mexico Daily)</figcaption></figure><p>Nguyên nhân của vụ tai nạn hiện đang được điều tra làm rõ.</p>', NULL, 'va-cham-giua-xe-tai-va-xe-bus-tai-mexico-hang-chuc-nguoi-thuong-vong', 0, NULL, NULL),
(155, 24, 7, 1, 1, 'Vụ cụ bà gốc Á 65 tuổi bị đánh dã man giữa phố ở New York: Hình phạt thích đáng dành cho những kẻ thờ ơ', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617769152450-1617769152870888079373.gif.png', '|', 'Vụ tấn công đã khiến cộng đồng cảm thấy phẫn nộ, trong bối cảnh làn sóng thù địch người gốc Á tại Mỹ đang gia tăng.', '<p>Như đã biết, làn sóng bạo lực và thù địch nhắm vào người gốc Á tại Mỹ hiện nay đang hết sức căng thẳng. Như sự việc cách đây hơn 1 tuần vào ngày 29/3, một người phụ nữ gốc Á 65 tuổi đã vô cớ bị đánh rất dã man bởi một gã đàn ông da màu lực lưỡng, ngay trên đường phố New York.</p><p>Theo đoạn video do cảnh sát công bố, gã đàn ông da màu đột nhiên đạp thẳng vào bụng người phụ nữ gốc Á, khiến bà ngã lăn ra đất ngay trước cửa một tòa nhà. Nhưng chưa hết, gã còn tiếp tục đạp mạnh vào đầu nạn nhân, đồng thời có những lời lẽ xúc phạm đến người châu Á. Gã sau đó bỏ đi, để mặc nạn nhân ôm vết thương đau đớn ở đó.</p><p>Người phụ nữ vô cớ bị tấn công hôm 29/3</p><p>Hung thủ được xác nhận là Brandon Elliot, bị bắt giữ ngày 31/3 và đối mặt với 2 tội danh hành hung mức độ 2 có yếu tố thù địch. Nạn nhân bị thương nặng, rạn cả xương chậu, nhưng đã được điều trị tại bệnh viện Manhattan và có tiến triển tốt.</p><p>Nhưng đáng chú ý là ở thời điểm ấy, có ít nhất 3 người chứng kiến cảnh tượng này, nhưng không ai giúp đỡ nạn nhân cả. Thậm chí theo như video, 2 người đàn ông gác cửa tại tòa nhà còn nhanh chóng đóng cửa lại, bỏ mặc nạn nhân. Sự việc vì thế gây ra phẫn nộ rất lớn cho cộng đồng, đặc biệt là trong bối cảnh làn sóng thù địch với người gốc Á tại Mỹ đang gia tăng.</p><p>Dẫu vậy thì mới đây theo CNN ghi nhận, những kẻ thờ ơ đã nhận được hình phạt thích đáng. Brodsky Organization - công ty sở hữu tòa nhà cho biết họ đã hoàn tất điều tra về phản ứng của những người gác cửa ở thời điểm đó.</p><p>\"Đoạn video toàn cảnh cho thấy sau khi kẻ tấn công rời đi, các nhân viên gác cửa đã xuất hiện để hỗ trợ nạn nhân, đồng thời gọi cảnh sát. Nhưng rõ ràng là các quy chuẩn khẩn cấp và an toàn đã không được tuân thủ,\" - công ty cho biết. \"Chúng tôi rất sốc vì sự việc, và sẽ luôn đứng về phía nạn nhân.\"</p><p>2 nhân viên gác cửa đã bị đình chỉ ngay sau khi vụ việc xảy ra, và ban quản lý tòa nhà cũng đã chính thức sa thải họ sau đó. Tuy nhiên cư dân tòa nhà lại tỏ ra khá bênh vực cho 2 nhân viên này, cho rằng đoạn video \"chưa phản ánh đúng\" hành động của họ. Công đoàn địa phương SEIU 32BJ xác nhận cả hai đều là thành viên của công đoàn, và cho biết họ có quyền đệ đơn khiếu nại, dù thời gian xử lý sẽ mất nhiều tuần hoặc tháng.</p><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/4/7/ezgif-6-ad897f2f5b69-1617770967908109061228.gif.mp4\" ></video><div class=\"caption\">Những người gác cửa đã ra đóng cửa lại, thay vì giúp đỡ nạn nhân</figcaption></figure><p>\"Chúng tôi xem việc thù ghét người gốc Á và mọi hình thức phân biệt khác là cực kỳ nghiêm trọng,\" - Chủ tịch công đoàn Kyle Bragg cho biết.</p>', NULL, 'vu-cu-ba-goc-a-65-tuoi-bi-danh-da-man-giua-pho-o-new-york-hinh-phat-thich-dang-danh-cho-nhung-ke-tho-o', 0, NULL, NULL),
(156, 24, 7, 1, 1, 'Dẫn gã đàn ông khác đến nhà tình tứ thì chồng đi làm về, vợ tung chiêu giúp nhân tình tẩu thoát, chẳng ngờ bị camera vạch mặt', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617761768364-16177617686081163882421.jpg', '|', 'Nghi ngờ lòng chung thủy của vợ, người đàn ông đã bí mật lắp camera để rồi thiết bị ấy giúp anh vạch trần hành vi bội phản của bạn đời.', '<p>Theo Insight đưa tin, vào ngày 28/3 vừa qua, tài khoản YouTube Si Gendut Official đã đăng tải một đoạn clip thu hút hơn 200.000 lượt xem có nội dung một người phụ nữ đang tình tứ với người đàn ông khác thì chồng trở về nhà đột ngột. Thế là người phụ nữ phải tung đủ thể loại chiêu trò để thu hút sự chú ý của chồng, tạo điều kiện cho gã nhân tình trốn khỏi nhà. Cô không hề hay biết rằng tất cả hành động của mình đã được camera an ninh ghi lại.</p><p>Sự việc được cho là diễn ra tại nhà riêng của vợ chồng người phụ nữ ở Jakarta, Indonesia. Theo thông tin từ Si Gendut Official, người chồng cảm thấy nghi ngờ lòng chung thủy của vợ nên đã bí mật lắp camera tại nhà, theo dõi hành động của bạn đời trong lúc mình ra ngoài làm việc.</p><p>Linh tính của người chồng đã đúng khi mà mở đầu đoạn clip, người phụ nữ bị bắt gặp đang tình tứ cùng với một người đàn ông khác tại căn bếp. Đúng lúc này, người chồng đi làm về khiến cặp đôi bối rối, gã nhân tình lập tức trốn dưới bồn rửa trong lúc người vợ thì chạy ra mở cửa cho chồng.</p><p>Người phụ nữ đang tình tứ với nhân tình thì chồng đi làm về</p><p>Ngay từ cửa, cô nàng đã không ngừng thể hiện tình cảm với chồng bằng những cái ôm hôn thắm thiết nhằm thu hút mọi sự chú ý từ đối phương rồi kéo anh vào ghế sô pha. Trong lúc này, gã nhân tình vẫn trốn dưới bồn rửa, chưa thể rời đi được.</p><p>Người chồng đi vào bếp nhưng vẫn không thoát được sự bám dính như keo của vợ, cô nàng cố gắng không để anh đến gần với bồn rửa. Cuối cùng, người vợ cũng lôi được chồng vào phòng ngủ thành công. Gã nhân tình lợi dụng thời cơ này lấy chiếc điện thoại đang nằm trên bồn rửa vội chạy ra khỏi cửa và tẩu thoát thành công. Tổng cộng thời gian từ lúc lẩn trốn đến lúc thoát thân của gã này là khoảng 45 phút.</p><p>Có lẽ là người chồng đã nghe thấy tiếng mở cửa nên chạy ra kiểm tra nhưng không thấy ai. Anh tỏ vẻ nghi hoặc và tra hỏi vợ. Lúc này do đã đạt được mục đích là giúp nhân tình tẩu thoát nên cô vợ đã thay đổi thái độ 180 độ, không còn âu yếm chồng mà trở nên giận dữ, dùng gối ném vào đối phương. Cô trách móc chồng không tin tưởng mình khiến người đàn ông này cảm thấy có lỗi, chủ động \"xuống nước\" xin lỗi vợ.</p><p>Thế nhưng, \"giấy không gói được lửa\", người chồng đã kiểm tra camera an ninh và phát hiện toàn bộ hành vi ngoại tình lẫn bao che cho nhân tình của vợ. Đoạn clip kết thúc ngay thời điểm đôi vợ chồng cãi nhau, không rõ hôn nhân của họ sẽ đi đâu về đâu nhưng nhiều người cho rằng mối quan hệ của họ chẳng thể bền lâu được nữa khi mà người chồng tận mắt nhìn thấy bằng chứng vợ mình tình tứ với gã đàn ông khác ngay tại nhà riêng.</p><p>Sau khi đoạn clip được đăng tải đã thu hút sự quan tâm của đông đảo dân mạng. Nhiều người bức xúc thay cho người chồng, nói rằng chính họ cũng dễ dàng nhận ra có điều bất thường ở người vợ bởi thái độ nũng nịu quá đáng của cô. Trong khi đó, không ít ý kiến bày tỏ sự nghi ngờ đoạn clip là sản phẩm dàn dựng vì gã nhân tình trốn thoát quá dễ dàng.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/660/203336854389633024/2021/4/7/photo-1-1617761580411492484718.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-4-16177615823661293115864.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-5-16177615828501046523004.jpeg\" ><figcaption class=\"caption\">Người phụ nữ ra sức thu hút sự chú ý của chồng, đánh lạc hướng để nhân tình lựa thời cơ tẩu thoát</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-6-1617761584113842090970.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-7-1617761583452986082743.jpeg\" ><figcaption class=\"caption\">Gã nhân tình lợi dụng cặp đôi đi vào phòng ngủ để tẩu thoát</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-8-16177615828841348760682.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p></p>', NULL, 'dan-ga-dan-ong-khac-den-nha-tinh-tu-thi-chong-di-lam-ve-vo-tung-chieu-giup-nhan-tinh-tau-thoat-chang-ngo-bi-camera-vach-mat', 0, NULL, NULL),
(157, 24, 7, 1, 1, 'Bố chồng bế con dâu từ xe hoa vào nhà, phản ứng chú rể gây tranh cãi', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/4/7/photo1617767593166-16177675938031489229859.jpg', '|', 'Trong khi cô dâu tỏ ra không thoải mái và cúi đầu xấu hổ thì chú rể đứng bên cạnh lại bật cười vui vẻ.', '<p>Ngày 5/4  Sohu đưa tin về một đám cưới khiến dư luận và cộng đồng mạng Trung Quốc tranh cãi không ngớt.</p><p>Địa điểm diễn ra lễ cưới là tại thành phố Hoàng Cương, tỉnh Hồ Bắc. Trong những hình ảnh được quan khách ghi lại, cô dâu xinh đẹp trong chiếc váy đỏ truyền thống vừa bước xuống từ xe hoa. Tuy nhiên thay vì được chú rể tiến tới nắm tay, dắt vào hôn trường thì cô dâu được một người đàn ông lớn tuổi tóc bạc, mặc bộ vest đen tiến lại gần và bế vào nhà. Đáng nói, đây chính là bố chú rể.</p><p>Dù tuổi cao sức yếu nhưng nhờ sự trợ giúp của những người xung quanh, ông bố chồng cuối cùng đã bế được con dâu từ xe hoa vào nơi tổ chức lễ cưới với khoảng cách vài trăm mét.</p><p>Trong khi đó chú rể bật cười vui vẻ, những người có mặt cũng tỏ thái độ bình thường, coi như chuyện hiển nhiên. Dường như đã biết trước chuyện này nhưng cô dâu vẫn không thoải mái cho lắm và cúi thấp đầu ngượng ngùng, xấu hổ. Một lúc sau, cô quay sang hôn phu nói khẽ: \"Em không muốn bố anh bế em\".</p><p>Theo tìm hiểu, đây là phong tục truyền thống trong đám cưới ở một số vùng thuộc thành phố Hoàng Cương và thành phố Nghi Xương, tỉnh Hồ Bắc, Trung Quốc. Trong ngày cưới, xe hoa sẽ dừng cách nhà khoảng vài trăm mét. Sau đó, bố chú rể hay người đại diện sẽ phụ trách việc bế cô dâu vào nhà, giống như một kiểu \"đón dâu\", chào mừng người con mới của gia đình, chúc cho đôi trẻ hạnh phúc viên mãn.</p><p>Sau khi xuất hiện trên mạng những hình ảnh trên gây ra làn sóng tranh cãi. Nhiều người cho rằng phong tục truyền thống này đáng được tôn trọng nhưng số khác cho rằng điều này thực sự phản cảm, cần phải loại bỏ: “Phong tục gì chứ, là hủ tục thì đúng hơn”, “Họ không cảm thấy việc này đáng xấu hổ và ngại ngùng sao?”, “Nên cân nhắc phong tục nào nên bỏ phong tục nào nên giữ\"…, nhiều người bức xúc để lại bình luận.</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-1617767232171215552561.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/4/7/photo-1-16177672344681880080968.jpg\" ><figcaption class=\"caption\">Cô dâu ngượng ngùng khi được bố chồng bế vào nhà</figcaption></figure><p>Cũng có người đưa ra gợi ý bố chú rể có thể dắt con dâu mới vào nhà, vừa đỡ vất vả, phù hợp với lễ nghi lại trông đỡ “nhức mắt”, phản cảm. Hiện vụ việc vẫn đang nhận được sự quan tâm lớn của cộng đồng mạng xứ Trung.</p>', NULL, 'bo-chong-be-con-dau-tu-xe-hoa-vao-nha-phan-ung-chu-re-gay-tranh-cai', 0, NULL, NULL),
(158, 25, 7, 1, 1, 'Loạt ảnh giúp con người mở mang tầm mắt rồi giật mình nhận ra kiến thức của mình về thế giới này vẫn còn ít ỏi lắm', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/28/photo1616919319082-1616919319416254118599.jpg', '|', 'Thế giới muôn màu muôn vẻ, khi bạn nghĩ mình đã có nhiều kiến thức về cuộc sống quanh ta lắm rồi thì loạt ảnh này chứng minh điều ngược lại.', '<figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-1-16169192226552086905547.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-1-1616919224592699216264.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-2-16169192290801543584797.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-3-1616919227600414397694.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-4-1616919226558217307382.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-5-16169192281682100908767.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-6-16169192296131937334433.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-7-16169192260331745904086.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-8-16169192301451587234966.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-9-1616919228567550943968.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-10-16169192259862057697128.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-11-16169192252301069910369.jpeg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-12-16169192252221609255835.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/28/photo-13-1616919225509652174950.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Một trong những ưu điểm trong sự phát triển vượt bậc của Internet là cho phép con người khám phá ra những điều lạ kỳ, hay ho trên khắp quả địa cầu mà không cần phải đến tận nơi. Chỉ cần một vài cú click chuột thì chúng ta sẽ được mở mang tầm mắt, nhìn thấy những điều thú vị mà trước giờ chưa từng nhìn thấy. Giống như loạt ảnh dưới đây sẽ chứng minh cho mọi người thấy thế giới của chúng ta không bao giờ thiếu điều kỳ diệu.</p>', NULL, 'loat-anh-giup-con-nguoi-mo-mang-tam-mat-roi-giat-minh-nhan-ra-kien-thuc-cua-minh-ve-the-gioi-nay-van-con-it-oi-lam', 0, NULL, NULL),
(159, 25, 7, 1, 1, 'Loạt ý tưởng “dị vô đối” khiến ai nhìn vào cũng phải bật cười nắc nẻ, có lẽ đây là thành quả của những người vừa sáng tạo lại “hề hước” đây!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/3/12/photo1615542674832-16155426752241537782678.png', '|', 'Sức sáng tạo của con người đúng là không có biên giới, cái gì cũng nghĩ ra được!', '<p>Internet có lẽ là phát minh vĩ đại nhất trong lịch sử khi nhờ nó mà chúng ta đã được học nhiều biết nhiều, mở mang đáng kể. Có nhiều điều mới lạ mà chẳng bao giờ bạn nghĩ có tồn tại nhưng hóa ra... trên đời chả thiếu điều gì, giả như những ý tưởng đến từ những bộ não hài hước và kỳ quặc dưới đây!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/12/1-16155420185701822122784.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/12/2-1615542018589869841056.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/12/3-16155420185941766070873.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/12/4-1615542018599612123045.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/12/5-16155420189721888138.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/12/6-16155420189821444719570.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/12/7-16155420189951143648831.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/12/8-16155420190041165096441.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/12/9-16155420190141797192146.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><video loop=\"loop\" preload=\"auto\" src=\"https://kenh14cdn.com/203336854389633024/2021/3/12/10-1615542019043520126396.gif.mp4\" ></video><div class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/12/d17-1601287299690487043887-161554241792471833316.png\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/3/12/d16-16012872996861018543204-1615542447065785619041.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn: Brightside</p>', NULL, 'loat-y-tuong-di-vo-doi-khien-ai-nhin-vao-cung-phai-bat-cuoi-nac-ne-co-le-day-la-thanh-qua-cua-nhung-nguoi-vua-sang-tao-lai-he-huoc-day', 0, NULL, NULL);
INSERT INTO `news` (`id`, `chill_category`, `category`, `author`, `source`, `title`, `image`, `images`, `first_look`, `description`, `url`, `slug`, `hot`, `created_at`, `updated_at`) VALUES
(160, 25, 7, 1, 1, 'Những biển toilet siêu buồn cười “thấy một lần là nhớ cả đời”, đúng là óc sáng tạo và hài hước của con người là bất tận!', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/2/28/photo1614487939584-16144879402561627394336.jpg', '|', 'Trời ơi, biển báo như thế này thì ai dám đi nhầm phòng toilet nữa đây?', '<p>Óc sáng tạo và hài hước của các nhà thiết kế được phát huy mọi lúc mọi nơi, ngay cả ở trong các phòng... vệ sinh. Bạn đã bao giờ phải thốt lên ngỡ ngàng hay cười ngặt nghẽo chỉ vì những biển báo chỉ dẫn ở phòng toilet chưa? Nếu chưa thì dưới đây là một trong những ít biển báo \"thấy một lần là nhớ cả đời\":</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/3-1614487288005411863408.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/4-16144872880151668407010.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/5-1614487288032811331702.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/6-16144872880521519203845.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/7-16144872880671545569536.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/8-16144872880761739469913.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/9-1614487288090936943432.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/10-16144872881331393152361.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/11-1614487288287685590137.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/12-16144872883024855934.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/14-1614487292926731822485.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/15-1614487292947682971608.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/28/2327ab53a2a889c059a4a9b053-16145203726121523869808.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><p>Nguồn: Brightside</p>', NULL, 'nhung-bien-toilet-sieu-buon-cuoi-thay-mot-lan-la-nho-ca-doi-dung-la-oc-sang-tao-va-hai-huoc-cua-con-nguoi-la-bat-tan', 0, NULL, NULL),
(161, 25, 7, 1, 1, 'Bất ngờ với hình ảnh của loạt sao thế giới được đặt cạnh con mình lúc bằng tuổi, liệu khí chất ngôi sao có được giữ nguyên vẹn cho thế hệ sau?', 'https://kenh14cdn.com/thumb_w/300/203336854389633024/2021/2/10/photo1612893096940-16128930972351510483859.jpg', '|', 'Không biết có bao nhiêu \"bản sao\" của những tên tuổi nổi tiếng từng là huyền thoại thế giới một thời đây?', '<p>Con mang nhiều nét của bố mẹ là chuyện dễ hiểu nhưng chúng ta luôn luôn thích thú khi được nhìn thấy \"bản sao\" của ai đó trong đời thực. Đối với con cái của các ngôi sao thế giới thì lại còn tò mò nữa. Họ liệu có may mắn thừa hưởng được nét đẹp cũng như khí chất ngôi sao từ bố mẹ mình không, nào cùng khám phá nhé!</p><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/9/1-16128817133251359632159.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/9/2-16128817134871500206956.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/9/3-1612881713631193445365.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/9/4-1612881713641829026649.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/9/5-1612881713833257767209.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https://kenh14cdn.com/thumb_w/620/203336854389633024/2021/2/9/6-16128817140631944638421.jpg\" ><figcaption class=\"caption\">Nguồn ảnh: kênh 14</figcaption></figure><figure class=\"image video\"><img src=\"https:/